INSERT INTO BIBLIA_VERSION (name) VALUES ('Nova Versão Internacional');
INSERT INTO BIBLIA_BOOK (name) VALUES ('Gênesis');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 1, 'No princípio Deus criou os céus e a terra. ');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 2, 'Era a terra sem forma e vazia; trevas cobriam a face do abismo, e o Espírito de Deus se movia sobre a face das águas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 3, 'Disse Deus: “Haja luz”, e houve luz.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 4, 'Deus viu que a luz era boa, e separou a luz das trevas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 5, 'Deus chamou à luz dia, e às trevas chamou noite. Passaram-se a tarde e a manhã; esse foi o primeiro dia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 6, 'Depois disse Deus: “Haja entre as águas um firmamento que separe águas de águas”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 7, 'Então Deus fez o firmamento e separou as águas que ficaram abaixo do firmamento das que ficaram por cima. E assim foi.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 8, 'Ao firmamento Deus chamou céu. Passaram-se a tarde e a manhã; esse foi o segundo dia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 9, 'E disse Deus: “Ajuntem-se num só lugar as águas que estão debaixo do céu, e apareça a parte seca”. E assim foi.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 10, 'À parte seca Deus chamou terra, e chamou mares ao conjunto das águas. E Deus viu que ficou bom.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 11, 'Então disse Deus: “Cubra-se a terra de vegetação: plantas que dêem sementes e árvores cujos frutos produzam semen­tes de acordo com as suas espécies”. E assim foi.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 12, 'A terra fez brotar a vegetação: plantas que dão sementes de acordo com as suas espécies, e árvores cujos frutos produzem sementes de acordo com as suas espécies. E Deus viu que ficou bom.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 13, 'Passaram-se a tarde e a manhã; esse foi o terceiro dia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 14, 'Disse Deus: Haja luminares no firmamento do céu para separar o dia da noite. Sirvam eles de sinais para marcar estações, dias e anos,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 15, 'e sirvam de luminares no firmamento do céu para iluminar a terra. E assim foi.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 16, 'Deus fez os dois grandes luminares: o maior para governar o dia e o menor para governar a noite; fez também as estrelas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 17, 'Deus os colocou no firmamento do céu para iluminar a terra,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 18, 'governar o dia e a noite, e separar a luz das trevas. E Deus viu que ficou bom.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 19, 'Passaram-se a tarde e a manhã; esse foi o quarto dia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 20, 'Disse também Deus: “Encham-se as águas de seres vivos, e voem as aves sobre a terra, sob o firmamento do céu”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 21, 'Assim Deus criou os grandes animais aquáticos e os demais seres vivos que povoam as águas, de acordo com as suas espécies; e todas as aves, de acordo com as suas espécies. E Deus viu que ficou bom.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 22, 'Então Deus os abençoou, dizendo: “Sejam férteis e multipliquem-se! Encham as águas dos mares! E multipliquem-se as aves na terra”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 23, 'Passaram-se a tarde e a manhã; esse foi o quinto dia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 24, 'E disse Deus: “Produza a terra seres vivos de acordo com as suas espécies: rebanhos domésticos, animais selvagens e os demais seres vivos da terra, cada um de acordo com a sua espécie”. E assim foi.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 25, 'Deus fez os animais selvagens de acordo com as suas espécies, os rebanhos domésticos de acordo com as suas espécies, e os demais seres vivos da terra de acordo com as suas espécies. E Deus viu que ficou bom.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 26, 'Então disse Deus: “Façamos o homem à nossa imagem, conforme a nossa semelhança. Domine ele sobre os peixes do mar, sobre as aves do céu, sobre os grandes animais de toda a terra e sobre todos os pequenos animais que se movem rente ao chão”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 27, 'Criou Deus o homem à sua imagem, à imagem de Deus o criou; homem e mulher os criou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 28, 'Deus os abençoou, e lhes disse: “Sejam férteis e multipliquem-se! Encham e subjuguem a terra! Dominem sobre os peixes do mar, sobre as aves do céu e sobre todos os animais que se movem pela terra”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 29, 'Disse Deus: Eis que lhes dou todas as plantas que nascem em toda a terra e produzem sementes, e todas as árvores que dão frutos com sementes. Elas servirão de alimento para vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 30, 'E dou todos os vegetais como alimento a tudo o que tem em si fôlego de vida: a todos os grandes animais da terra­, a todas as aves do céu e a todas as criaturas que se movem rente ao chão. E assim foi.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 1, 31, 'E Deus viu tudo o que havia feito, e tudo havia ficado muito bom. Passaram-se a tarde e a manhã; esse foi o sexto dia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 1, 'Assim foram concluídos os céus e a terra, e tudo o que neles há.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 2, 'No sétimo dia Deus já havia concluído a obra que realizara, e nesse dia descansou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 3, 'Abençoou Deus o sétimo dia e o santificou, porque nele descansou de toda a obra que realizara na criação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 4, 'Esta é a história das origens dos céus e da terra, no tempo em que foram criados: Quando o Senhor Deus fez a terra e os céus,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 5, 'ainda não tinha brotado nenhum arbusto no campo, e nenhuma planta havia germinado, porque o Senhor Deus ainda não tinha feito chover sobre a terra, e também não havia homem para cultivar o solo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 6, 'Todavia brotava água da terra e irrigava toda a superfície do solo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 7, 'Então o Senhor Deus for­mou o homem do pó da terra e soprou em suas narinas o fôlego de vida, e o homem se tornou um ser vivente.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 8, 'Ora, o Senhor Deus tinha plantado um jardim no Éden, para os lados do leste, e ali colocou o homem que formara.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 9, 'Então o Senhor Deus fez nascer do solo todo tipo de árvores agradáveis aos olhos e boas para alimento. E no meio do jardim estavam a árvore da vida e a árvore do conhecimento do bem e do mal.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 10, 'No Éden nascia um rio que irrigava o jardim, e depois se dividia em quatro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 11, 'O nome do primeiro é Pisom. Ele percorre toda a terra de Havilá, onde existe ouro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 12, 'O ouro daquela terra é excelente; lá também existem o bdélio e a pedra de ônix.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 13, 'O segundo, que percorre toda a terra de Cuxe, é o Giom.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 14, 'O terceiro, que corre pelo lado leste da Assíria, é o Tigre. E o quarto rio é o Eufrates.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 15, 'O Senhor Deus colocou o homem no jardim do Éden para cuidar dele e cultivá-lo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 16, 'E o Senhor Deus ordenou ao homem: Coma livremente de qualquer árvore do jardim,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 17, 'mas não coma da árvore do conhecimento do bem e do mal, porque no dia em que dela comer, certamente você morrerá.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 18, 'Então o Senhor Deus declarou: “Não é bom que o homem esteja só; farei para ele alguém que o auxilie e lhe corresponda”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 19, 'De­pois que formou da terra todos os animais do campo e todas as aves do céu, o Senhor Deus os trouxe ao homem para ver como este lhes chamaria; e o nome que o homem desse a cada ser vivo, esse seria o seu nome.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 20, 'Assim o homem deu nomes a todos os rebanhos domésticos, às aves do céu e a todos os animais selvagens. Todavia não se encontrou para o homem alguém que o auxiliasse e lhe correspondesse.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 21, 'Então o Senhor Deus fez o homem cair em profundo sono e, enquanto este dormia, tirou-lhe uma das costelas, fechando o lugar com carne.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 22, 'Com a costela que havia tirado do homem, o Senhor Deus fez uma mulher e a levou até ele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 23, 'Disse então o homem: “Esta, sim, é osso dos meus ossos e carne da minha carne! Ela será chamada mulher, porque do homem foi tirada”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 24, 'Por essa razão, o homem deixará pai e mãe e se unirá à sua mulher, e eles se tornarão uma só carne.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 2, 25, 'O homem e sua mulher viviam nus, e não sentiam vergonha.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 3, 1, 'Ora, a serpente era o mais astuto de todos os animais selvagens que o Senhor Deus tinha feito. E ela perguntou à mulher: “Foi isto mesmo que Deus disse: “Não comam de nenhum fruto das árvores do jardim”?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 3, 2, 'Respondeu a mulher à serpente: Podemos comer do fruto das árvores do jardim,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 3, 3, 'mas Deus disse: “Não comam do fruto da árvore que está no meio do jardim, nem toquem nele; do contrário vocês morrerão”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 3, 4, 'Disse a serpente à mulher: Certamente não morrerão!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 3, 5, 'Deus sabe que, no dia em que dele comerem, seus olhos se abrirão, e vocês, como Deus, serão conhecedores do bem e do mal.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 3, 6, 'Quando a mulher viu que a árvore parecia agradável ao paladar, era atraente aos olhos e, além disso, desejável para dela se obter discernimento, tomou do seu fruto, comeu-o e ­o deu a seu marido, que comeu também.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 3, 7, 'Os olhos dos dois se abriram, e perceberam que estavam nus; então juntaram folhas de figueira para cobrir-se.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 3, 8, 'Ouvindo o homem e sua mulher os passos do Senhor Deus que andava pelo jardim quando soprava a brisa do dia, esconderam-se da presença do Senhor Deus entre as árvores do jardim.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 3, 9, 'Mas o Senhor Deus chamou o homem, perguntando: “Onde está você?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 3, 10, 'E ele respondeu: “Ouvi teus passos no jardim e fiquei com medo, porque estava nu; por isso me escondi”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 3, 11, 'E Deus perguntou: “Quem lhe disse que você estava nu? Você comeu do fruto da árvore da qual lhe proibi comer?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 3, 12, 'Disse o homem: “Foi a mulher que me deste por companheira que me deu do fruto da árvore, e eu comi”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 3, 13, 'O Senhor Deus perguntou então à mulher: “Que foi que você fez?” Respondeu a mulher: “A serpente me enganou, e eu comi”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 3, 14, 'Então o Senhor Deus declarou à ser­pente: Uma vez que você fez isso, maldita é você entre todos os rebanhos domésticos e entre todos os animais selvagens! Sobre o seu ventre você rastejará, e pó comerá todos os dias da sua vida.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 3, 15, 'Porei inimizade entre você e a mulher, entre a sua descendência e o descendente dela; este lhe ferirá a cabeça, e você lhe ferirá o calcanhar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 3, 16, 'À mulher, ele declarou: “Multiplicarei grandemente o seu sofrimento na gravidez; com sofrimento você dará à luz filhos. Seu desejo será para o seu marido, e ele a dominará”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 3, 17, 'E ao homem declarou: Visto que você deu ouvidos à sua mulher e comeu do fruto da árvore da qual eu lhe ordenara que não comesse, maldita é a terra por sua causa; com sofrimento vocês e alimentará dela todos os dias da sua vida.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 3, 18, 'Ela lhe dará espinhos e ervas daninhas, e você terá que alimentar-se das plantas do campo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 3, 19, 'Com o suor do seu rosto você comerá o seu pão, até que volte à terra, visto que dela foi tirado; porque você é pó, e ao pó voltará.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 3, 20, 'Adão deu à sua mulher o nome de Eva, pois ela seria mãe de toda a humanidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 3, 21, 'O Senhor Deus fez roupas de pele e com elas vestiu Adão e sua mulher.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 3, 22, 'Então disse o Senhor Deus: “Agora o homem se tornou como um de nós, conhecendo o bem e o mal. Não se deve, pois, permitir que ele tome também do fruto da árvore da vida e o coma, e viva para sempre”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 3, 23, 'Por isso o Senhor Deus o mandou embora do jardim do Éden para cultivar o solo do qual fora tirado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 3, 24, 'Depois de expulsar o homem, colocou a leste do jardim do Éden querubins e uma espada flamejante que se movia, guardando o caminho para a árvore da vida.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 1, 'Adão teve relações com Eva, sua mulher, e ela engravidou e deu à luz Caim. Disse ela: “Com o auxílio do Senhor tive um filho homem”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 2, 'Voltou a dar à luz, desta vez a Abel, irmão dele. Abel tornou-se pastor de ovelhas, e Caim, agricultor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 3, 'Passado algum tempo, Caim trouxe do fruto da terra uma oferta ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 4, 'Abel, por sua vez, trouxe as partes gordas das primeiras crias do seu rebanho. O Senhor aceitou com agrado Abel e sua oferta,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 5, 'mas não aceitou Caim e sua oferta. Por isso Caim se enfureceu e o seu rosto se transtornou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 6, 'O Senhor disse a Caim: Por que você está furioso? Por que se transtornou o seu rosto?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 7, 'Se você fizer o bem, não será aceito? Mas se não o fizer, saiba que o pecado o ameaça à porta; ele deseja conquistá-lo, mas você deve dominá-lo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 8, 'Disse, porém, Caim a seu irmão Abel: “Vamos para o campo”.  Quando estavam lá, Caim atacou seu irmão Abel e o matou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 9, 'Então o Senhor perguntou a Caim: “Onde está seu irmão Abel?” Respondeu ele: “Não sei; sou eu o responsável por meu irmão?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 10, 'Disse o Senhor: O que foi que você fez? Escute! Da terra o sangue do seu irmão está clamando.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 11, 'Agora amaldiçoado é você pela terra, que abriu a boca para receber da sua mão o sangue do seu irmão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 12, 'Quando você cultivar a terra, esta não lhe dará mais da sua força. Você será um fugitivo errante pelo mundo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 13, 'Disse Caim ao Senhor: Meu castigo é maior do que posso suportar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 14, 'Hoje me expulsas desta terra, e terei que me esconder da tua face; serei um fugitivo errante pelo mundo, e qualquer que me encontrar me matará.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 15, 'Mas o Senhor lhe respondeu: “Não será assim; se alguém matar Caim, sofrerá sete vezes a vingança”. E o Senhor colocou em Caim um sinal, para que ninguém que viesse a encontrá-lo o matasse.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 16, 'Então Caim afastou-se da presença do Senhor e foi viver na terra de Node, a leste do Éden.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 17, 'Caim teve relações com sua mulher, e ela engravidou e deu à luz Enoque. Depois Caim fundou uma cidade, à qual deu o nome do seu filho Enoque.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 18, 'A Enoque nasceu Irade, Irade gerou a Meujael, Meujael a Metusael, e Metusael a Lameque.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 19, 'Lameque tomou duas mulheres: uma chamava-se Ada e a outra, Zilá.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 20, 'Ada deu à luz Jabal, que foi o pai daqueles que moram em tendas e criam rebanhos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 21, 'O nome do irmão dele era Jubal, que foi o pai de todos os que tocam harpa e flauta.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 22, 'Zilá também deu à luz um filho, chamado Tubalcaim, que fabricava todo tipo de ferramentas de bronze e de ferro. Tubalcaim teve uma irmã chamada Naamá.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 23, 'Disse Lameque às suas mulheres: Ada e Zilá, ouçam-me; mulheres de Lameque, escutem minhas palavras: Eu matei um homem porque me feriu, e um menino, porque me machucou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 24, 'Se Caim é vingado sete vezes, Lameque o será setenta e sete.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 25, 'Novamente Adão teve relações com sua mulher, e ela deu à luz outro filho, a quem chamou Sete, dizendo: “Deus me concedeu um filho no lugar de Abel, visto que Caim o matou”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 4, 26, 'Também a Sete nasceu um filho, a quem deu o nome de Enos. Nessa época começou-se a invocar o nome do Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 1, 'Este é o registro da descendência de Adão: Quando Deus criou o homem, à semelhança de Deus o fez;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 2, 'homem e mulher os criou. Quando foram criados, ele os abençoou e os cha­mou Homem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 3, 'Aos 130 anos, Adão gerou um filho à sua semelhança, conforme a sua imagem; e deu-lhe o nome de Sete.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 4, 'Depois que gerou Sete, Adão viveu 800 anos e gerou outros filhos e filhas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 5, 'Viveu ao todo 930 anos e morreu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 6, 'Aos 105 anos, Sete gerou Enos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 7, 'Depois que gerou Enos, Sete viveu 807 anos e gerou outros filhos e filhas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 8, 'Viveu ao todo 912 anos e morreu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 9, 'Aos 90 anos, Enos gerou Cainã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 10, 'Depois que gerou Cainã, Enos viveu 815 anos e gerou outros filhos e filhas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 11, 'Viveu ao todo 905 anos e morreu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 12, 'Aos 70 anos, Cainã gerou Maalaleel.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 13, 'Depois que gerou Maalaleel, Cainã viveu 840 anos e gerou outros filhos e filhas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 14, 'Viveu ao todo 910 anos e morreu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 15, 'Aos 65 anos, Maalaleel gerou Jarede.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 16, 'Depois que gerou Jarede, Maalaleel viveu 830 anos e gerou outros filhos e filhas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 17, 'Viveu ao todo 895 anos e morreu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 18, 'Aos 162 anos, Jarede gerou Enoque.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 19, 'Depois que gerou Eno­que, Jarede viveu 800 anos e gerou outros filhos e filhas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 20, 'Viveu ao todo 962 anos e morreu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 21, 'Aos 65 anos, Enoque gerou Matusalém.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 22, 'Depois que gerou Matusalém, Enoque andou com Deus 300 anos e gerou outros filhos e filhas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 23, 'Viveu ao todo 365 anos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 24, 'Enoque andou com Deus; e já não foi encontrado, pois Deus o havia arrebatado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 25, 'Aos 187 anos, Matusalém gerou Lameque.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 26, 'Depois que gerou Lameque, Matusalém viveu 782 anos e gerou outros filhos e filhas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 27, 'Viveu ao todo 969 anos e morreu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 28, 'Aos 182 anos, Lameque gerou um filho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 29, 'Deu-lhe o nome de Noé e disse: “Ele nos aliviará do nosso trabalho e do sofrimento de nossas mãos, causados pela terra que o Senhor amaldiçoou”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 30, 'Depois que Noé nasceu, Lameque viveu 595 anos e gerou outros filhos e filhas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 31, 'Viveu ao todo 777 anos e morreu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 5, 32, 'Aos 500 anos, Noé tinha gerado Sem, Cam e Jafé.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 6, 1, 'Quando os homens começaram a multiplicar-se na terra e lhes nasceram filhas,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 6, 2, 'os filhos de Deus viram que as filhas dos homens eram bonitas, e escolheram para si aquelas que lhes agradaram.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 6, 3, 'Então disse o Senhor: “Por causa da perversidade do homem, meu Espírito não contenderá com ele para sempre; ele só viverá cento e vinte anos”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 6, 4, 'Naqueles dias havia nefilins na terra, e também posteriormente, quando os filhos de Deus possuíram as filhas dos homens e elas lhes deram filhos. Eles foram os heróis do passado, homens famosos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 6, 5, 'O Senhor viu que a perversidade do homem tinha aumentado na terra e que toda a inclinação dos pensamentos do seu coração era sempre e somente para o mal.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 6, 6, 'Então o Senhor arrependeu-se de ter feito o homem sobre a terra, e isso cortou-lhe o coração.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 6, 7, 'Disse o Senhor: “Farei desaparecer da face da terra o homem que criei, os homens e também os grandes animais e os pequenos e as aves do céu. Arrependo-me de havê-los feito”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 6, 8, 'A Noé, porém, o Senhor mostrou benevolência.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 6, 9, 'Esta é a história da família de Noé: Noé era homem justo, íntegro entre o povo da sua época; ele andava com Deus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 6, 10, 'Noé gerou três filhos: Sem, Cam e Jafé.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 6, 11, 'Ora, a terra estava corrompida aos olhos de Deus e cheia de violência.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 6, 12, 'Ao ver como a terra se corrompera, pois toda a humanidade havia corrompido a sua conduta,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 6, 13, 'Deus disse a Noé: Darei fim a todos os seres humanos, porque a terra encheu-se de violência por causa deles. Eu os destruirei com a terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 6, 14, 'Você, porém, fará uma arca de madeira de cipreste; divida-a em compartimentos e revista-a de piche por dentro e por fora.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 6, 15, 'Faça-a com cento e trinta e cinco metros de comprimento, vinte e dois metros e meio de largura e treze metros e meio de altura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 6, 16, 'Faça-lhe um teto com um vão de quarenta e cinco centímetros entre o teto e corpo da arca. Coloque uma porta lateral na arca e faça um andar superior, um médio e um inferior.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 6, 17, 'Eis que vou trazer águas sobre a terra, o Dilúvio, para destruir debaixo do céu toda criatura que tem fôlego de vida. Tudo o que há na terra perecerá.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 6, 18, 'Mas com você estabelecerei a minha aliança, e você entrará na arca com seus filhos, sua mulher e as mulheres de seus filhos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 6, 19, 'Faça entrar na arca um casal de cada um dos seres vivos, macho e fêmea, para conservá-los vivos com você.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 6, 20, 'De cada espécie de ave, de cada espécie de animal grande e de cada espécie de animal pequeno que se move rente ao chão virá um casal a você para que sejam conservados vivos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 6, 21, 'E armazene todo tipo de alimento, para que você e eles tenham mantimento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 6, 22, 'Noé fez tudo exatamente como Deus lhe tinha ordenado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 7, 1, 'Então o Senhor disse a Noé: Entre na arca, você e toda a sua família, porque você é o único justo que encontrei nesta geração.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 7, 2, 'Leve com você sete casais de cada espécie de animal puro, macho e fêmea, e um casal de cada espécie de animal impuro, macho e fêmea,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 7, 3, 'e leve também sete casais de aves de cada espécie, macho e fêmea, a fim de preservá-las em toda a terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 7, 4, 'Daqui a sete dias farei chover sobre a terra quarenta dias e quarenta noites, e farei desaparecer da face da terra todos os seres vivos que fiz.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 7, 5, 'E Noé fez tudo como o Senhor lhe tinha ordenado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 7, 6, 'Noé tinha seiscentos anos de idade quando as águas do Dilúvio vieram sobre a terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 7, 7, 'Noé, seus filhos, sua mulher e as mulheres de seus filhos entraram na arca, por causa das águas do Dilúvio.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 7, 8, 'Casais de animais grandes, puros e impuros, de aves e de todos os animais pequenos que se movem rente ao chão');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 7, 9, 'vieram a Noé e entraram na arca, como Deus tinha ordenado a Noé.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 7, 10, 'E depois dos sete dias, as águas do Dilúvio vieram sobre a terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 7, 11, 'No dia em que Noé completou seiscentos anos, um mês e dezessete dias, nesse mesmo dia todas as fontes das grandes profundezas jorraram, e as comportas do céu se abriram.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 7, 12, 'E a chuva caiu sobre a terra quarenta dias e quarenta noites.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 7, 13, 'Naquele mesmo dia, Noé e seus filhos, Sem, Cam e Jafé, com sua mulher e com as mulheres de seus três filhos, entraram na arca.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 7, 14, 'Com eles entraram todos os animais de acordo com as suas espécies: todos os animais selvagens, todos os rebanhos domésticos, todos os demais seres vivos que se movem rente ao chão e todas as criaturas que têm asas: todas as aves e todos os outros animais que voam.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 7, 15, 'Casais de todas as criaturas que tinham fôlego de vida vieram a Noé e entraram na arca.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 7, 16, 'Os animais que entraram foram um macho e uma fêmea de cada ser vivo, conforme Deus ordenara a Noé. Então o Senhor fechou a porta.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 7, 17, 'Quarenta dias durou o Dilúvio, e as águas aumentaram e elevaram a arca acima da terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 7, 18, 'As águas prevaleceram, aumentando muito sobre a terra, e a arca flutuava na superfície das águas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 7, 19, 'As águas dominavam cada vez mais a terra, e foram cobertas todas as altas montanhas debaixo do céu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 7, 20, 'As águas subiram até quase sete metros acima das montanhas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 7, 21, 'Todos os seres vivos que se movem sobre a terra pereceram: aves, rebanhos domésticos, animais selvagens, todas as pequenas criaturas que povoam a terra e toda a humanidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 7, 22, 'Tudo o que havia em terra seca e tinha nas narinas o fôlego de vida morreu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 7, 23, 'Todos os seres vivos foram exterminados da face da terra; tanto os homens, como os animais grandes, os animais pequenos que se movem rente ao chão e as aves do céu foram exterminados da terra. Só restaram Noé e aqueles que com ele estavam na arca.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 7, 24, 'E as águas prevaleceram sobre a terra cento e cinqüenta dias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 8, 1, 'Então Deus lembrou-se de Noé e de todos os animais selvagens e rebanhos domésticos que estavam com ele na arca, e enviou um vento sobre a terra, e as águas começaram a baixar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 8, 2, 'As fontes das profundezas e as comportas do céu se fecharam, e a chuva parou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 8, 3, 'As águas foram baixando pouco a pouco sobre a terra. Ao fim de cento e cinqüenta dias, as águas tinham diminuído,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 8, 4, 'e, no décimo sétimo dia do sétimo mês, a arca pousou nas montanhas de Ararate.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 8, 5, 'As águas continuaram a baixar até o décimo mês, e no primeiro dia do décimo mês apareceram os topos das montanhas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 8, 6, 'Passados quarenta dias, Noé abriu a janela que fizera na arca.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 8, 7, 'Esperando que a terra já tivesse aparecido, Noé soltou um corvo, mas este ficou dando voltas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 8, 8, 'Depois soltou uma pomba para ver se as águas tinham diminuído na superfície da terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 8, 9, 'Mas a pomba não encontrou lugar onde pousar os pés porque as águas ainda cobriam toda a superfície da terra e, por isso, voltou para a arca, a Noé. Ele estendeu a mão para fora, apanhou a pomba e a trouxe de volta para dentro da arca.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 8, 10, 'Noé esperou mais sete dias e soltou novamente a pomba.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 8, 11, 'Ao entardecer, quando a pomba voltou, trouxe em seu bico uma folha nova de oliveira. Noé então ficou sabendo que as águas tinham diminuído sobre a terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 8, 12, 'Esperou ainda outros sete dias e de novo soltou a pomba, mas desta vez ela não voltou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 8, 13, 'No primeiro dia do primeiro mês do ano seiscentos e um da vida de Noé, secaram-se as águas na terra. Noé então removeu o teto da arca e viu que a superfície da terra estava seca.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 8, 14, 'No vigésimo sétimo dia do segundo mês, a terra estava completamente seca.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 8, 15, 'Então Deus disse a Noé:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 8, 16, 'Saia da arca, você e sua mulher, seus filhos e as mulheres deles.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 8, 17, 'Faça que saiam também todos os animais que estão com você: as aves, os grandes animais e os pequenos que se movem rente ao chão. Faça-os sair para que se espalhem pela terra, sejam férteis e se multipliquem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 8, 18, 'Então Noé saiu da arca com sua mulher e seus filhos e as mulheres deles,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 8, 19, 'e com todos os grandes animais e os pequenos que se movem rente ao chão ­e todas as aves. Tudo o que se move sobre a terra saiu da arca, uma espécie após outra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 8, 20, 'Depois Noé construiu um altar dedicado ao Senhor e, tomando alguns animais e aves puros, ofereceu-os como holocausto, queimando-os sobre o altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 8, 21, 'O Senhor sentiu o aroma agradável e disse a si mesmo: Nunca mais amaldiçoarei a terra por causa do homem, pois o seu coração é inteiramente inclinado para o mal desde a infância. E nunca mais destruirei todos os seres vivos como fiz desta vez.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 8, 22, '“Enquanto durar a terra, plantio e colheita, frio e calor, verão e inverno, dia e noite jamais cessarão”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 1, 'Deus abençoou Noé e seus filhos, dizendo-lhes: Sejam férteis, multipliquem-se e encham a terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 2, 'Todos os animais da terra tremerão de medo diante de vocês: os animais selvagens, as aves do céu, as criaturas que se movem rente ao chão e os peixes do mar; eles estão entregues em suas mãos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 3, 'Tudo o que vive e se move servirá de alimento para vocês. Assim como lhes dei os vegetais, agora lhes dou todas as coisas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 4, 'Mas não comam carne com sangue, que é vida.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 5, 'A todo aquele que derramar sangue, tanto homem como animal, pedirei contas; a cada um pedirei contas da vida do seu próximo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 6, 'Quem derramar sangue do homem, pelo homem seu sangue será derramado; porque à imagem de Deus foi o homem criado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 7, '“Mas vocês, sejam férteis e multipliquem-se; espalhem-se pela terra e proliferem nela”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 8, 'Então disse Deus a Noé e a seus filhos, que estavam com ele:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 9, 'Vou estabelecer a minha aliança com vocês e com os seus futuros descendentes,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 10, 'e com todo ser vivo que está com vocês: as aves, os rebanhos domésticos e os animais selvagens, todos os que saíram da arca com vocês, todos os seres vivos da terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 11, 'Estabeleço uma aliança com vocês: Nunca mais será ceifada nenhuma forma de vida pelas águas de um dilúvio; nunca mais haverá dilúvio para destruir a terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 12, 'E Deus prosseguiu: Este é o sinal da aliança que estou fazendo entre mim e vocês e com todos os seres vivos que estão com vocês, para todas as gerações futuras:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 13, 'o meu arco que coloquei nas nuvens. Será o sinal da minha aliança com a terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 14, 'Quando eu trouxer nuvens sobre a terra e nelas aparecer o arco-íris,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 15, 'então me lembrarei da minha aliança com vocês e com os seres vivos de todas as espécies. Nunca mais as águas se tornarão um dilúvio para destruir toda forma de vida.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 16, 'Toda vez que o arco-íris estiver nas nuvens, olharei para ele e me lembrarei da aliança eterna entre Deus e todos os seres vivos de todas as espécies que vivem na terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 17, 'Concluindo, disse Deus a Noé: “Esse é o sinal da aliança que estabeleci entre mim e toda forma de vida que há sobre a terra”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 18, 'Os filhos de Noé que saíram da arca foram Sem, Cam e Jafé. Cam é o pai de Canaã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 19, 'Esses foram os três filhos de Noé; a partir deles toda a terra foi povoada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 20, 'Noé, que era agricultor, foi o primeiro a plantar uma vinha.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 21, 'Bebeu do vinho, embriagou-se e ficou nu dentro da sua tenda.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 22, 'Cam, pai de Canaã, viu a nudez do pai e foi contar aos dois irmãos que estavam do lado de fora.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 23, 'Mas Sem e Jafé pegaram a capa, levantaram-na sobre os ombros e, andando de costas para não verem a nudez do pai, cobriram-no.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 24, 'Quando Noé acordou do efeito do vinho e descobriu o que seu filho caçula lhe havia feito,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 25, 'disse: “Maldito seja Canaã! Escravo de escravos será para os seus irmãos”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 26, 'Disse ainda: Bendito seja o Senhor, o Deus de Sem! E seja Canaã seu escravo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 27, 'Amplie Deus o território de Jafé; habite ele nas tendas de Sem, e seja Canaã seu escravo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 28, 'Depois do Dilúvio Noé viveu trezentos e cinqüenta anos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 9, 29, 'Vi­veu ao todo novecentos e cinqüenta anos e morreu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 1, 'Este é o registro da descendência de Sem, Cam e Jafé, filhos de Noé. Os filhos deles nasceram depois do Dilúvio.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 2, 'Estes foram os filhos de Jafé: Gômer, Magogue, Madai, Javã, Tubal, Meseque e Tirás.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 3, 'Estes foram os filhos de Gômer: Asquenaz, Rifate e Togarma.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 4, 'Estes foram os filhos de Javã: Elisá, Társis, Quitim e Rodanim.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 5, 'Deles procedem os povos marítimos, os quais se separaram em seu território, conforme a sua língua, cada um segundo os clãs de suas nações.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 6, 'Estes foram os filhos de Cam: Cuxe, Mizraim, Fute e Canaã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 7, 'Estes foram os filhos de Cuxe: Sebá, Havilá, Sabtá, Raamá e Sabtecá. Estes foram os filhos de Raamá: Sabá e Dedã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 8, 'Cuxe gerou também Ninrode, o primeiro homem poderoso na terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 9, 'Ele foi o mais valente dos caçadores, e por isso se diz: “Valente como Ninrode”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 10, 'No início o seu reino abran­gia Babel, Ereque, Acade e Calné, na terra de Sinear.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 11, 'Dessa terra ele partiu para a Assíria, onde fundou Nínive, Reobote-Ir, Calá');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 12, 'e Resém, que fica entre Nínive e Calá, a grande cidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 13, 'Mizraim gerou os luditas, os anamitas, os leabitas, os naftuítas,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 14, 'os patrusitas, os casluítas, dos quais se originaram os filisteus, e os caftoritas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 15, 'Canaã gerou Sidom, seu filho mais velho, e Hete,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 16, 'como também os jebuseus, os amorreus, os girgaseus,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 17, 'os heveus, os arqueus, os sineus,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 18, 'os arvadeus, os zemareus e os hamateus. Posteriormente, os clãs cananeus se espalharam.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 19, 'As fronteiras de Canaã estendiam-se desde Sidom, iam até Gerar, e chegavam a Gaza e, de lá, prosseguiam até Sodoma, Gomorra, Admá e Zeboim, chegando até Lasa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 20, 'São esses os descendentes de Cam, conforme seus clãs e línguas, em seus territórios e nações.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 21, 'Sem, irmão mais velho de Jafé, também gerou filhos. Sem foi o antepassado de todos os filhos de Héber.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 22, 'Estes foram os filhos de Sem: Elão, Assur, Arfaxade, Lude e Arã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 23, 'Estes foram os filhos de Arã: Uz, Hul, Géter e Meseque.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 24, 'Arfaxade gerou Salá, e este gerou Héber.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 25, 'A Héber nasceram dois filhos: um deles se chamou Pelegue, porque em sua época a terra foi dividida; seu irmão chamou-se Joctã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 26, 'Joctã gerou Almodá, Salefe, Hazarmavé, Jerá,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 27, 'Adorão, Uzal, Dicla,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 28, 'Obal, Abimael, Sabá,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 29, 'Ofir, Havilá e Jobabe. Todos esses foram filhos de Joctã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 30, 'A região onde viviam estendia-se de Messa até Sefar, nas colinas ao leste.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 31, 'São esses os descendentes de Sem, conforme seus clãs e línguas, em seus territórios e nações.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 10, 32, 'São esses os clãs dos filhos de Noé, distribuídos em suas nações, conforme a história da sua descendência. A partir deles, os povos se dispersaram pela terra, depois do Dilúvio.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 1, 'No mundo todo havia apenas uma língua, um só modo de falar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 2, 'Saindo os homens do Oriente, encontraram uma planície em Sinear e ali se fixaram.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 3, 'Disseram uns aos outros: “Vamos fazer tijolos e queimá-los bem”. Usavam tijolos em lugar de pedras, e piche em vez de argamassa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 4, 'Depois disseram: “Vamos construir uma cidade, com uma torre que alcance os céus. Assim nosso nome será famoso e não seremos espalhados pela face da terra”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 5, 'O Senhor desceu para ver a cidade e a torre que os homens estavam construindo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 6, 'E disse o Senhor: Eles são um só povo e falam uma só língua, e começaram a construir isso. Em breve nada poderá impedir o que planejam fazer.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 7, 'Venham, desçamos e confundamos a língua que falam, para que não entendam mais uns aos outros.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 8, 'Assim o Senhor os dispersou dali por toda a terra, e pararam de construir a cidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 9, 'Por isso foi chamada Babel, porque ali o Senhor confundiu a língua de todo o mundo. Dali o Senhor os espalhou por toda a terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 10, 'Este é o registro da descendência de Sem: Dois anos depois do Dilúvio, aos 100 anos de idade, Sem gerou Arfaxade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 11, 'E depois de ter gerado Arfaxade, Sem viveu 500 anos e gerou outros filhos e filhas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 12, 'Aos 35 anos, Arfaxade gerou Salá.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 13, 'Depois que gerou Salá, Arfaxade viveu 403 anos e gerou outros filhos e filhas. ');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 14, 'Aos 30 anos, Salá gerou Héber.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 15, 'Depois que gerou Héber, Salá viveu 403 anos e gerou outros filhos e filhas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 16, 'Aos 34 anos, Héber gerou Pelegue.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 17, 'Depois que gerou Pelegue, Héber viveu 430 anos e gerou outros filhos e filhas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 18, 'Aos 30 anos, Pelegue gerou Reú.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 19, 'Depois que gerou Reú, Pelegue viveu 209 anos e gerou outros filhos e filhas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 20, 'Aos 32 anos, Reú gerou Serugue.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 21, 'Depois que gerou Serugue, Reú viveu 207 anos e gerou outros filhos e filhas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 22, 'Aos 30 anos, Serugue gerou Naor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 23, 'Depois que gerou Naor, Serugue viveu 200 anos e gerou outros filhos e filhas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 24, 'Aos 29 anos, Naor gerou Terá.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 25, 'Depois que gerou Terá, Naor viveu 119 anos e gerou outros filhos e filhas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 26, 'Aos 70 anos, Terá havia gerado Abrão, Naor e Harã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 27, 'Esta é a história da família de Terá: Terá gerou Abrão, Naor e Harã. E Harã gerou Ló.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 28, 'Harã morreu em Ur dos caldeus, sua terra natal, quando ainda vivia Terá, seu pai.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 29, 'Tanto Abrão como Naor casaram-se. O nome da mulher de Abrão era Sarai, e o nome da mulher de Naor era Milca; esta era filha de Harã, pai de Milca e de Iscá.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 30, 'Ora, Sarai era estéril; não tinha filhos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 31, 'Terá tomou seu filho Abrão, seu neto Ló, filho de Harã, e sua nora Sarai, mulher de seu filho Abrão, e juntos partiram de Ur dos caldeus para Canaã. Mas, ao chegarem a Harã, estabeleceram-se ali.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 11, 32, 'Terá viveu 205 anos e morreu em Harã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 12, 1, 'Então o Senhor disse a Abrão: Saia da sua terra, do meio dos seus parentes e da casa de seu pai, e vá para a terra que eu lhe mostrarei.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 12, 2, 'Farei de você um grande povo, e o abençoarei. Tornarei famoso o seu nome, e você será uma bênção.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 12, 3, 'Abençoarei os que o abençoarem e amaldiçoarei os que o amaldiçoarem; e por meio de você todos os povos da terra serão abençoados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 12, 4, 'Partiu Abrão, como lhe ordenara o Senhor, e Ló foi com ele. Abrão tinha setenta e cinco anos quando saiu de Harã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 12, 5, 'Levou sua mulher Sarai, seu sobrinho Ló, todos os bens que haviam acumulado e os seus servos, comprados em Harã; partiram para a terra de Canaã e lá chegaram.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 12, 6, 'Abrão atravessou a terra até o lugar do carvalho de Moré, em Siquém. Naque­la época os cananeus habitavam essa terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 12, 7, 'O Senhor apareceu a Abrão e disse: “À sua descendência darei esta terra”. Abrão construiu ali um altar dedicado ao Senhor, que lhe havia aparecido.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 12, 8, 'Dali prosseguiu em direção às colinas a leste de Betel, onde armou acampamento, tendo Betel a oeste e Ai a leste. Construiu ali um altar dedicado ao Senhor e invocou o nome do Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 12, 9, 'Depois Abrão partiu e prosseguiu em direção ao Neguebe. Abrão no Egito');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 12, 10, 'Houve fome naquela terra, e Abrão desceu ao Egito para ali viver algum tempo, pois a fome era rigorosa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 12, 11, 'Quando estava chegando ao Egito, disse a Sarai, sua mulher: Bem sei que você é bonita.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 12, 12, 'Quando os egípcios a virem, dirão: “Esta é a mulher dele”. E me matarão, mas deixarão você viva.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 12, 13, 'Diga que é minha irmã, para que me tratem bem por amor a você e minha vida seja poupada por sua causa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 12, 14, 'Quando Abrão chegou ao Egito, viram os egípcios que Sarai era uma mulher muito bonita.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 12, 15, 'Vendo-a, os homens da corte do faraó a elogiaram diante do faraó, e ela foi levada ao seu palácio.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 12, 16, 'Ele tratou bem a Abrão por causa dela, e Abrão recebeu ovelhas e bois, jumentos e jumentas, servos e servas, e camelos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 12, 17, 'Mas o Senhor puniu o faraó e sua corte com graves doenças, por causa de Sarai, mulher de Abrão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 12, 18, 'Por isso o faraó mandou chamar Abrão e disse: O que você fez comigo? Por que não me falou que ela era sua mulher?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 12, 19, 'Por que disse que era sua irmã? Foi por isso que eu a tomei para ser minha mulher. Aí está a sua mulher. Tome-a e vá!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 12, 20, 'A seguir o faraó deu ordens para que providenciassem o necessário para que Abrão partisse, com sua mulher e com tudo o que possuía.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 13, 1, 'Saiu, pois, Abrão do Egito e foi para o Neguebe, com sua mulher e com tudo o que possuía, e Ló foi com ele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 13, 2, 'Abrão tinha enriquecido muito, tanto em gado como em prata e ouro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 13, 3, 'Ele partiu do Neguebe em direção a Betel, indo de um lugar a outro, até que chegou ao lugar entre Betel e Ai onde já havia armado acampamento anteriormente');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 13, 4, 'e onde, pela primeira vez, tinha construído um altar. Ali Abrão invocou o nome do Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 13, 5, 'Ló, que acompanhava Abrão, também possuía rebanhos e tendas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 13, 6, 'E não podiam morar os dois juntos na mesma região, porque possuíam tantos bens que a terra não podia sustentá-los.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 13, 7, 'Por isso surgiu uma desavença entre os pastores dos rebanhos de Abrão e os de Ló. Nessa época os cananeus e os ferezeus habitavam aquela terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 13, 8, 'Então Abrão disse a Ló: Não haja desavença entre mim e você, ou entre os seus pastores e os meus; afinal somos irmãos!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 13, 9, 'Aí está a terra inteira diante de você. Vamos separar-nos. Se você for para a esquerda, irei para a direita; se for para a direita, irei para a esquerda.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 13, 10, 'Olhou então Ló e viu todo o vale do Jordão, todo ele bem irrigado, até Zoar; era como o jardim do Senhor, como a terra do Egito. Isto se deu antes de o Senhor destruir Sodoma e Gomorra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 13, 11, 'Ló escolheu todo o vale do Jordão e partiu em direção ao leste. Assim os dois se separaram:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 13, 12, 'Abrão ficou na terra de Canaã, mas Ló mudou seu acampamento para um lugar próximo a Sodoma, entre as cidades do vale.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 13, 13, 'Ora, os homens de Sodoma eram extremamente perversos e pecadores contra o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 13, 14, 'Disse o Senhor a Abrão, depois que Ló separou-se dele: De onde você está, olhe para o norte, para o sul, para o leste e para o oeste:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 13, 15, 'toda a terra que você está vendo darei a você e à sua descendência para sempre.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 13, 16, 'Tornarei a sua descendência tão numerosa como o pó da terra. Se for possível contar o pó da terra, também se poderá contar a sua descendência.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 13, 17, 'Percorra esta terra de alto a baixo, de um lado a outro, porque eu a darei a você.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 13, 18, 'Então Abrão mudou seu acampamento e passou a viver próximo aos carvalhos de Manre, em Hebrom, onde construiu um altar dedicado ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 14, 1, 'Naquela época Anrafel, rei de Sinear, Arioque, rei de Elasar, Quedorlaomer, rei de Elão, e Tidal, rei de Goim,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 14, 2, 'foram à guerra contra Bera, rei de Sodoma, contra Birsa, rei de Gomorra, contra Sinabe, rei de Admá, contra Semeber, rei de Zeboim, e contra o rei de Belá, que é Zoar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 14, 3, 'Todos esses últimos juntaram suas tropas no vale de Sidim, onde fica o mar Salgado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 14, 4, 'Doze anos estiveram sujeitos a Quedorlaomer, mas no décimo terceiro ano se rebelaram.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 14, 5, 'No décimo quarto ano, Quedorlaomer e os reis que a ele tinham-se aliado derrotaram os refains em Asterote-Carnaim, os zuzins em Hã, os emins em Savé-Quiriataim');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 14, 6, 'e os horeus desde os montes de Seir até El-Parã, próximo ao deserto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 14, 7, 'Depois, voltaram e foram para En-Mispate, que é Cades, e conquistaram todo o território dos amalequitas e dos amorreus que viviam em Hazazom-Tamar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 14, 8, 'Então os reis de Sodoma, de Gomorra, de Admá, de Zeboim e de Belá, que é Zoar, marcharam e tomaram posição de combate no vale de Sidim');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 14, 9, 'contra Quedorlaomer, rei de Elão, contra Tidal, rei de Goim, contra Anrafel, rei de Sinear, e contra Arioque, rei de Elasar. Eram quatro reis contra cinco.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 14, 10, 'Ora, o vale de Sidim era cheio de poços de betume e, quando os reis de Sodoma e de Gomorra fugiram, alguns dos seus homens caíram nos poços e o restante escapou para os montes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 14, 11, 'Os vencedores saquearam todos os bens de Sodoma e de Gomorra e todo o seu mantimento, e partiram.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 14, 12, 'Levaram também Ló, sobrinho de Abrão, e os bens que ele possuía, visto que morava em Sodoma.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 14, 13, 'Mas alguém que tinha escapado veio e relatou tudo a Abrão, o hebreu, que vivia próximo aos carvalhos de Manre, o amorreu. Manre e os seus irmãos Escol e Aner eram aliados de Abrão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 14, 14, 'Quando Abrão ouviu que seu parente fora levado prisioneiro, mandou convocar os trezentos e dezoito homens treinados, nascidos em sua casa, e saiu em perseguição aos inimigos até Dã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 14, 15, 'Atacou-os durante a noite em grupos, e assim os derrotou, perseguindo-os até Hobá, ao norte de Damasco.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 14, 16, 'Recuperou todos os bens e trouxe de volta seu parente Ló com tudo o que possuía, com as mulheres e o restante dos prisioneiros.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 14, 17, 'Voltando Abrão da vitória sobre Quedorlaomer e sobre os reis que a ele se haviam aliado, o rei de Sodoma foi ao seu encontro no vale de Savé, isto é, o vale do Rei.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 14, 18, 'Então Melquisedeque, rei de Salém e sacerdote do Deus Altíssimo, trouxe pão e vinho');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 14, 19, 'e abençoou Abrão, dizendo: Bendito seja Abrão pelo Deus Altíssimo, Criador dos céus e da terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 14, 20, 'E bendito seja o Deus Altíssimo, que entregou seus inimigos em suas mãos. E Abrão lhe deu o dízimo de tudo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 14, 21, 'O rei de Sodoma disse a Abrão: “Dê-me as pessoas e pode ficar com os bens”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 14, 22, 'Mas Abrão respondeu ao rei de Sodoma: De mãos levantadas ao Senhor, o Deus Altíssimo, Criador dos céus e da terra, juro');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 14, 23, 'que não aceitarei nada do que lhe pertence, nem mesmo um cordão ou uma correia de sandália, para que você jamais venha a dizer: “Eu enriqueci Abrão”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 14, 24, 'Nada aceitarei, a não ser o que os meus servos comeram e a porção pertencente a Aner, Escol e Manre, os quais me acompanharam. Que eles recebam a sua porção.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 15, 1, 'Depois dessas coisas o Senhor falou a Abrão numa visão: “Não tenha medo, Abrão! Eu sou o seu escudo; grande será a sua recompensa!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 15, 2, 'Mas Abrão perguntou: “Ó Soberano Senhor, que me darás, se continuo sem filhos e o herdeiro do que possuo é Eliézer de Damasco?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 15, 3, 'E acrescentou: “Tu não me deste filho algum! Um servo da minha casa será o meu herdeiro!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 15, 4, 'Então o Senhor deu-lhe a seguinte resposta: “Seu herdeiro não será esse. Um filho gerado por você mesmo será o seu herdeiro”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 15, 5, 'Levando-o para fora da tenda, disse-lhe: “Olhe para o céu e conte as estrelas, se é que pode contá-las”. E prosseguiu: “Assim será a sua descendência”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 15, 6, 'Abrão creu no Senhor, e isso lhe foi creditado como justiça.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 15, 7, 'Disse-lhe ainda: “Eu sou o Senhor, que o tirei de Ur dos caldeus para dar-lhe esta terra como herança”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 15, 8, 'Perguntou-lhe Abrão: “Ó Soberano Senhor, como posso saber que tomarei posse dela?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 15, 9, 'Respondeu-lhe o Senhor: “Traga-me uma novilha, uma cabra e um carneiro, todos com três anos de vida, e também uma rolinha e um pombinho”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 15, 10, 'Abrão trouxe todos esses animais, cortou-os ao meio e colocou cada metade em frente à outra; as aves, porém, ele não cortou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 15, 11, 'Nisso, aves de rapina começaram a descer sobre os cadáveres, mas Abrão as enxotava.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 15, 12, 'Ao pôr-do-sol, Abrão foi tomado de sono profundo, e eis que vieram sobre ele trevas densas e apavorantes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 15, 13, 'Então o Senhorlhe disse: Saiba que os seus descendentes serão estrangeiros numa terra que não lhes pertencerá, onde também serão escravizados e oprimidos por quatrocentos anos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 15, 14, 'Mas eu castigarei a nação a quem servirão como escravos e, depois de tudo, sairão com muitos bens.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 15, 15, 'Você, porém, irá em paz a seus antepassados e será sepultado em boa velhice.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 15, 16, 'Na quarta geração, os seus descendentes voltarão para cá, porque a maldade dos amorreus ainda não atingiu a medida com­pleta.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 15, 17, 'Depois que o sol se pôs e veio a escuridão, eis que um fogareiro esfumaçante, com uma tocha acesa, passou por entre os pedaços dos animais.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 15, 18, 'Naquele dia o Senhor fez a seguinte aliança com Abrão: Aos seus descendentes dei esta terra, desde o ribeiro do Egito até o grande rio, o Eufrates:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 15, 19, 'a terra dos queneus, dos quenezeus, dos cadmoneus,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 15, 20, 'dos hititas, dos ferezeus, dos refains,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 15, 21, 'dos amorreus, dos cananeus, dos girgaseus e dos jebuseus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 16, 1, 'Ora, Sarai, mulher de Abrão, não lhe dera nenhum filho. Como tinha uma serva egípcia, chamada Hagar,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 16, 2, 'disse a Abrão: “Já que o Senhor me impediu de ter filhos, possua a minha serva; talvez eu possa formar família por meio dela”. Abrão atendeu à proposta de Sarai.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 16, 3, 'Quando isso aconteceu, já fazia dez anos que Abrão, seu marido, vivia em Canaã. Foi nessa ocasião que Sarai, sua mulher, lhe entregou sua serva egípcia Hagar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 16, 4, 'Ele possuiu Hagar, e ela engravidou. Quando se viu grávida, começou a olhar com desprezo para a sua senhora.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 16, 5, 'Então Sarai disse a Abrão: “Caia sobre você a afronta que venho sofrendo. Coloquei minha serva em seus braços e, agora que ela sabe que engravidou, despreza-me. Que o Senhor seja o juiz entre mim e você”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 16, 6, 'Respondeu Abrão a Sarai: “Sua serva está em suas mãos. Faça com ela o que achar melhor”. Então Sarai tanto maltratou Hagar que esta acabou fugindo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 16, 7, 'O Anjo do Senhor encontrou Hagar perto de uma fonte no deserto, no caminho de Sur,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 16, 8, 'e perguntou-lhe: “Hagar, serva de Sarai, de onde você vem? Para onde vai?” Respondeu ela: “Estou fugindo de Sarai, a minha senhora”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 16, 9, 'Disse-lhe então o Anjo do Senhor: “Volte à sua senhora e sujeite-se a ela”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 16, 10, 'Disse mais o Anjo: “Multiplicarei tanto os seus descendentes que ninguém os poderá contar”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 16, 11, 'Disse-lhe ainda o Anjo do Senhor: Você está grávida e terá um filho, e lhe dará o nome de Ismael, porque o Senhor a ouviu em seu sofrimento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 16, 12, 'Ele será como jumento selvagem; sua mão será contra todos, e a mão de todos contra ele, e ele viverá em hostilidadecontra todos os seus irmãos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 16, 13, 'Este foi o nome que ela deu ao Senhor que lhe havia falado: “Tu és o Deus que me vê”, pois dissera: “Teria eu visto Aquele que me vê?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 16, 14, 'Por isso o poço, que fica entre Cades e Berede, foi chamado Beer-Laai-Roi.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 16, 15, 'Hagar teve um filho de Abrão, e este lhe deu o nome de Ismael.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 16, 16, 'Abrão estava com oitenta e seis anos de idade quando Hagar lhe deu Ismael.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 1, 'Quando Abrão estava com noventa e nove anos de idade o Senhor lhe apareceu e disse: Eu sou o Deus todo-poderoso; ande segundo a minha vontade e seja íntegro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 2, 'Estabelecerei a minha aliança entre mim e você e multiplicarei muitíssimo a sua descendência.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 3, 'Abrão prostrou-se com o rosto em terra, e Deus lhe disse:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 4, 'De minha parte, esta é a minha aliança com você. Você será o pai de muitas nações.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 5, 'Não será mais chamado Abrão; seu nome será Abraão, porque eu o constituí pai de muitas nações.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 6, 'Eu o tornarei extremamente prolífero; de você farei nações e de você procederão reis.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 7, 'Estabelecerei a minha aliança como aliança eterna entre mim e você e os seus futuros descendentes, para ser o seu Deus e o Deus dos seus descendentes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 8, 'Toda a terra de Canaã, onde agora você é estrangeiro, darei como propriedade perpétua a você e a seus descendentes; e serei o Deus deles.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 9, '“De sua parte”, disse Deus a Abraão, “guarde a minha aliança, tanto você como os seus futuros descendentes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 10, 'Esta é a minha aliança com você e com os seus descendentes, aliança que terá que ser guardada: Todos os do sexo masculino entre vocês serão circuncidados na carne.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 11, 'Terão que fazer essa marca, que será o sinal da aliança entre mim e vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 12, 'Da sua geração em diante, todo menino de oito dias de idade entre vocês terá que ser circuncidado, tanto os nascidos em sua casa quanto os que forem comprados de estrangeiros e que não forem descendentes de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 13, 'Sejam nascidos em sua casa, sejam comprados, terão que ser circuncidados. Minha aliança, marcada no corpo de vocês, será uma aliança perpétua.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 14, 'Qualquer do sexo masculino que for incircunciso, que não tiver sido circuncidado, será eliminado do meio do seu povo; quebrou a minha aliança.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 15, 'Disse também Deus a Abraão: De agora em diante sua mulher já não se chamará Sarai; seu nome será Sara.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 16, 'Eu a abençoarei e também por meio dela darei a você um filho. Sim, eu a abençoarei e dela procederão nações e reis de povos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 17, 'Abraão prostrou-se com o rosto em terra; riu-se e disse a si mesmo: “Poderá um homem de cem anos de idade gerar um filho? Poderá Sara dar à luz aos noventa anos?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 18, 'E Abraão disse a Deus: “Permite que Ismael seja o meu herdeiro! ”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 19, 'Então Deus respondeu: Na verdade Sara, sua mulher, lhe dará um filho, e você lhe chamará Isaque. Com ele estabelecerei a minha aliança, que será aliança eterna para os seus futuros descendentes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 20, 'E no caso de Ismael, levarei em conta o seu pedido. Também o abençoarei; eu o farei prolífero e multiplicarei muito a sua descendência. Ele será pai de doze príncipes e dele farei um grande povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 21, 'Mas a minha aliança, eu a estabelecerei com Isaque, filho que Sara lhe dará no ano que vem, por esta época.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 22, 'Quando terminou de falar com Abraão, Deus subiu e retirou-se da presença dele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 23, 'Naquele mesmo dia Abraão tomou seu filho Ismael, todos os nascidos em sua casa e os que foram comprados, todos os do sexo masculino de sua casa, e os circuncidou, como Deus lhe ordenara.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 24, 'Abraão tinha noventa e nove anos quando foi circuncidado,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 25, 'e seu filho Ismael tinha treze;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 26, 'Abraão e seu filho Ismael foram circuncidados naquele mesmo dia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 17, 27, 'E com Abraão foram circuncidados todos os de sua casa, tanto os nascidos em casa como os comprados de estrangeiros.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 1, 'O Senhor apareceu a Abraão perto dos carvalhos de Manre, quando ele estava sentado à entrada de sua tenda, na hora mais quente do dia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 2, 'Abraão ergueu os olhos e viu três homens em pé, a pouca distância. Quando os viu, saiu da entrada de sua tenda, correu ao encontro deles e curvou-se até o chão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 3, 'Disse ele: Meu senhor, se mereço o seu favor, não passe pelo seu servo sem fazer uma parada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 4, 'Mandarei buscar um pouco d’água para que lavem os pés e descansem debaixo desta árvore.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 5, 'Vou trazer-lhes também o que comer, para que recuperem as forças e prossigam pelo caminho, agora que já chegaram até este seu servo”. “Está bem; faça como está dizendo”, responderam.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 6, 'Abraão foi apressadamente à tenda e disse a Sara: “Depressa, pegue três medidas da melhor farinha, amasse-a e faça uns pães”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 7, 'Depois correu ao rebanho e escolheu o melhor novilho, e o deu a um servo, que se apressou em prepará-lo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 8, 'Trouxe então coalhada, leite e o novilho que havia sido preparado, e os serviu. Enquanto comiam, ele ficou perto deles em pé, debaixo da árvore.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 9, '“Onde está Sara, sua mulher?”, perguntaram. “Ali na tenda”, respondeu ele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 10, 'Então disse o Senhor : “Voltarei a você na primavera, e Sara, sua mulher, terá um filho”. Sara escutava à entrada da tenda, atrás dele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 11, 'Abraão e Sara já eram velhos, de idade bem avançada, e Sara já tinha passado da idade de ter filhos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 12, 'Por isso riu consigo mesma, quando pensou: “Depois de já estar velha e meu senhor já idoso, ainda terei esse prazer?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 13, 'Mas o Senhor disse a Abraão: Por que Sara riu e disse: “Poderei realmente dar à luz, agora que sou idosa?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 14, 'Existe alguma coisa impossível para o Senhor? Na primavera voltarei a você, e Sara terá um filho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 15, 'Sara teve medo, e por isso mentiu: “Eu não ri”. Mas ele disse: “Não negue, você riu”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 16, 'Quando os homens se levantaram para partir, avistaram lá embaixo Sodoma; e Abraão os acompanhou para despedir-se.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 17, 'Então o Senhor disse: Esconderei de Abraão o que estou para fazer?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 18, 'Abraão será o pai de uma nação grande e poderosa, e por meio dele todas as nações da terra serão abençoadas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 19, 'Pois eu o escolhi, para que ordene aos seus filhos e aos seus descendentes que se conservem no caminho do Senhor, fazendo o que é justo e direito, para que o Senhor faça vir a Abraão o que lhe prometeu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 20, 'Disse-lhe, pois, o Senhor: As acusações contra Sodoma e Gomorra são tantas e o seu pecado é tão grave');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 21, 'que descerei para ver se o que eles têm feito corresponde ao que tenho ouvido. Se não, eu saberei.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 22, 'Os homens partiram dali e foram para Sodoma, mas Abraão permaneceu diante do Senhor. ');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 23, 'Abraão aproximou-se dele e disse: Exterminarás o justo com o ímpio?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 24, 'E se houver cinqüenta justos na cidade? Ainda a destruirás e não pouparás o lugar por amor aos cinqüenta justos que nele estão?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 25, 'Longe de ti fazer tal coisa: matar o justo com o ímpio, tratando o justo e o ímpio da mesma maneira. Longe de ti! Não agirá com justiça o Juiz de toda a terra?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 26, 'Respondeu o Senhor: “Se eu encontrar cinqüenta justos em Sodoma, pouparei a cidade toda por amor a eles”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 27, 'Mas Abraão tornou a falar: Sei que já fui muito ousado ao ponto de falar ao Senhor, eu que não passo de pó e cinza.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 28, 'Ainda assim pergunto: E se faltarem cinco para completar os cinqüenta justos? Destruirás a cidade por causa dos cinco?” Disse ele: “Se encontrar ali quarenta e cinco, não a destruirei”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 29, '“E se encontrares apenas quarenta?”, insistiu Abraão. Ele respondeu: “Por amor aos quarenta não a destruirei”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 30, 'Então continuou ele: “Não te ires, Senhor, mas permite-me falar. E se apenas trinta forem encontra­dos ali?” Ele respondeu: “Se encontrar trinta, não a destruirei”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 31, 'Prosseguiu Abraão: “Agora que já fui tão ousado falando ao Senhor, pergunto: E se apenas vinte forem encontrados ali?” Ele respondeu: “Por amor aos vinte não a destruirei”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 32, 'Então Abraão disse ainda: “Não te ires, Senhor, mas permite-me falar só mais uma vez. E se apenas dez forem encontrados?” Ele respondeu: “Por amor aos dez não a destruirei”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 18, 33, 'Tendo acabado de falar com Abraão, o Senhor partiu, e Abraão voltou para casa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 1, 'Os dois anjos chegaram a Sodoma ao anoitecer, e Ló estava sentado à porta da cidade. Quando os avistou, levantou-se e foi recebê-los. Prostrou-se com o rosto em terra,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 2, 'e disse: “Meus senhores, por favor, acompanhem-me à casa do seu servo. Lá poderão lavar os pés, passar a noite e, pela manhã, seguir caminho”. “Não, passaremos a noite na praça”, responderam.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 3, 'Mas ele insistiu tanto com eles que, finalmente, o acompanharam e entraram em sua ca­sa. Ló mandou preparar-lhes uma refeição e assar pão sem fermento, e eles comeram.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 4, 'Ainda não tinham ido deitar-se, quando todos os homens de toda parte da cidade de Sodoma, dos mais jovens aos mais velhos, cercaram a casa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 5, 'Chamaram Ló e lhe disseram: “Onde estão os homens que vieram à sua casa esta noite? Traga-os para nós aqui fora para que tenhamos relações com eles”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 6, 'Ló saiu da casa, fechou a porta atrás de si');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 7, 'e lhes disse: Não, meus amigos! Não façam essa perversidade!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 8, 'Olhem, tenho duas filhas que ainda são virgens. Vou trazê-las para que vocês façam com elas o que bem entenderem. Mas não façam nada a estes homens, porque se acham debaixo da proteção do meu teto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 9, '“Saia da frente!”, gritaram. E disseram: “Este homem chegou aqui como estrangeiro, e agora quer ser o juiz! Faremos a você pior do que a eles”. Então empurraram Ló com violência e avançaram para arrombar a porta.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 10, 'Nisso, os dois visitantes agarraram Ló, puxaram-no para dentro e fecharam a porta.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 11, 'Depois feriram de cegueira os homens que estavam à porta da casa, dos mais jovens aos mais velhos, de maneira que não conseguiam encontrar a porta.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 12, 'Os dois homens perguntaram a Ló: Você tem mais alguém na cidade — genros, filhos ou filhas, ou qualquer outro parente? Tire-os daqui,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 13, 'porque estamos para destruir este lugar. As acusações feitas ao Senhor contra este povo são tantas que ele nos enviou para destruir a cidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 14, 'Então Ló foi falar com seus genros, os quais iam casar-se com suas filhas, e lhes disse: “Saiam imediatamente deste lugar, porque o Senhor está para destruir a cidade!” Mas eles pensaram que ele estava brincando.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 15, 'Ao raiar do dia, os anjos insistiam com Ló, dizendo: “Depressa! Leve daqui sua mulher e suas duas filhas, ou vocês também serão mortos quando a cidade for castigada”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 16, 'Tendo ele hesitado, os homens o agarraram pela mão, como também a mulher e as duas filhas, e os tiraram dali à força e os deixaram fora da cidade, porque o Senhor teve misericórdia deles.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 17, 'Assim que os tiraram da cidade, um deles disse a Ló: “Fuja por amor à vida! Não olhe para trás e não pare em lugar nenhum da planície! Fuja para as montanhas, ou você será morto!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 18, 'Ló, porém, lhes disse: Não, meu senhor!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 19, 'Seu servo foi favorecido por sua benevolência, pois o senhor foi bondoso comigo, poupando-me a vida. Não posso fugir para as montanhas, senão esta calamidade cairá sobre mim, e morrerei.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 20, 'Aqui perto há uma cidade pequena. Está tão próxima que dá para correr até lá. Deixe-me ir para lá! Mesmo sendo tão pequena, lá estarei a salvo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 21, '“Está bem”, respondeu ele. “Também lhe atenderei esse pedido; não destruirei a cidade da qual você fala.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 22, 'Fuja depressa, porque nada poderei fazer enquanto você não chegar lá. Por isso a cidade foi chamada Zoar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 23, 'Quando Ló chegou a Zoar, o sol já havia nascido sobre a terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 24, 'Então o Senhor, o próprio Senhor, fez chover do céu fogo e enxofre sobre Sodoma e Gomorra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 25, 'Assim ele destruiu aquelas cidades e toda a planície, com todos os habitantes das cidades e a vegetação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 26, 'Mas a mulher de Ló olhou para trás e se transformou numa coluna de sal.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 27, 'Na manhã seguinte, Abraão se levantou e voltou ao lugar onde tinha estado diante do Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 28, 'E olhou para Sodoma e Gomorra, para toda a planície, e viu uma densa fumaça subindo da terra, como fumaça de uma fornalha.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 29, 'Quando Deus arrasou as cidades da planície, lembrou-se de Abraão e tirou Ló do meio da catástrofe que destruiu as cidades onde Ló vivia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 30, 'Ló partiu de Zoar com suas duas filhas e passou a viver nas montanhas, porque tinha medo de permanecer em Zoar. Ele e suas duas filhas ficaram morando numa caverna.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 31, 'Um dia, a filha mais velha disse à mais jovem: Nosso pai já está velho, e não há homens nas redondezas que nos possuam, segundo o costume de toda a terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 32, 'Vamos dar vinho a nosso pai e então nos deitaremos com ele para preservar a sua linhagem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 33, 'Naquela noite deram vinho ao pai, e a filha mais velha entrou e se deitou com ele. E ele não percebeu quando ela se deitou nem quando se levantou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 34, 'No dia seguinte a filha mais velha disse à mais nova: “Ontem à noite deitei-me com meu pai. Vamos dar-lhe vinho também esta noite, e você se deitará com ele, para que preservemos a linhagem de nosso pai”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 35, 'Então, outra vez deram vinho ao pai naquela noite, e a mais nova foi e se deitou com ele. E ele não percebeu quando ela se deitou nem quando se levantou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 36, 'Assim, as duas filhas de Ló engravidaram do próprio pai.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 37, 'A mais velha teve um filho, e deu-lhe o nome de Moabe; este é o pai dos moabitas de hoje.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 19, 38, 'A mais nova também teve um filho, e deu-lhe o nome de Ben-Ami; este é o pai dos amonitas de hoje.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 20, 1, 'Abraão partiu dali para a região do Neguebe e foi viver entre Cades e Sur. Depois morou algum tempo em Gerar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 20, 2, 'Ele dizia que Sara, sua mulher, era sua irmã. Então Abimeleque, rei de Gerar, mandou buscar Sara e tomou-a para si.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 20, 3, 'Certa noite Deus veio a Abimeleque num sonho e lhe disse: “Você morrerá! A mulher que você tomou é casada”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 20, 4, 'Mas Abimeleque, que ainda não havia tocado nela, disse: Senhor, destruirias um povo inocente?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 20, 5, 'Não foi ele que me disse: “Ela é minha irmã”? E ela também não disse: “Ele é meu irmão”? O que fiz foi de coração puro e de mãos limpas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 20, 6, 'Então Deus lhe respondeu no sonho: Sim, eu sei que você fez isso de coração puro. Eu mesmo impedi que você pecasse contra mim e por isso não lhe permiti tocá-la.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 20, 7, 'Agora devolva a mulher ao marido dela. Ele é profeta, e orará em seu favor, para que você não morra. Mas se não a devolver, esteja certo de que você e todos os seus morrerão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 20, 8, 'Na manhã seguinte, Abimeleque convocou todos os seus conselheiros e, quando lhes contou tudo o que acontecera, tiveram muito medo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 20, 9, 'Depois Abimeleque chamou Abraão e disse: “O que fizeste conosco? Em que foi que pequei contra ti para que trouxesses tamanha culpa sobre mim e sobre o meu reino? O que me fizeste não se faz a ninguém!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 20, 10, 'E perguntou Abimeleque a Abraão: “O que te levou a fazer isso?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 20, 11, 'Abraão respondeu: Eu disse a mim mesmo: Certamente ninguém teme a Deus neste lugar, e irão matar-me por causa da minha mulher.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 20, 12, 'Além disso, na verdade ela é minha irmã por parte de pai, mas não por parte de mãe; e veio a ser minha mulher.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 20, 13, 'E quando Deus me fez sair errante da casa de meu pai, eu disse a ela: Assim você me provará sua lealdade: em qualquer lugar aonde formos, diga que sou seu irmão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 20, 14, 'Então Abimeleque trouxe ovelhas e bois, servos e servas, deu-os a Abraão e devolveu-lhe Sara, sua mulher.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 20, 15, 'E disse Abimeleque: “Minha terra está diante de ti; podes ficar onde quiseres”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 20, 16, 'A Sara ele disse: “Estou dando a seu irmão mil peças de prata, para reparar a ofensa feita a você diante de todos os seus; assim todos saberão que você é inocente”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 20, 17, 'A seguir Abraão orou a Deus, e Deus curou Abimeleque, sua mulher e suas servas, de forma que puderam novamente ter filhos,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 20, 18, 'porque o Senhor havia tornado estéreis todas as mulheres da casa de Abimeleque por causa de Sara, mulher de Abraão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 1, 'O Senhor foi bondoso com Sara, como lhe dissera, e fez por ela o que prometera.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 2, 'Sara engravidou e deu um filho a Abraão em sua velhice, na época fixada por Deus em sua promessa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 3, 'Abraão deu o nome de Isaque ao filho que Sara lhe dera.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 4, 'Quando seu filho Isaque tinha oito dias de vida, Abraão o circuncidou, conforme Deus lhe havia ordenado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 5, 'Estava ele com cem anos de idade quando lhe nasceu Isaque, seu filho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 6, 'E Sara disse: “Deus me encheu de riso, e todos os que souberem disso rirão comigo”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 7, 'E acrescentou: “Quem diria a Abraão que Sara amamentaria filhos? Contudo eu lhe dei um filho em sua velhice!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 8, 'O menino cresceu e foi desmamado. No dia em que Isaque foi desmamado, Abraão deu uma grande festa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 9, 'Sara, porém, viu que o filho que Hagar, a egípcia, dera a Abraão estava rindo de Isaque,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 10, 'e disse a Abraão: “Livre-se daquela escrava e do seu filho, porque ele jamais será herdeiro com o meu filho Isaque”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 11, 'Isso perturbou demais Abraão, pois envolvia um filho seu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 12, 'Mas Deus lhe disse: Não se perturbe por causa do menino e da escrava. Atenda a tudo o que Sara lhe pedir, porque será por meio de Isaque que a sua descendência há de ser considerada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 13, 'Mas também do filho da escrava farei um povo; pois ele é seu descendente.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 14, 'Na manhã seguinte, Abraão pegou alguns pães e uma vasilha de couro cheia d’água, entregou-os a Hagar e, tendo-os colocado nos ombros dela, despediu-a com o menino. Ela se pôs a caminho e ficou vagando pelo deserto de Berseba.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 15, 'Quando acabou a água da vasilha, ela deixou o menino debaixo de um arbusto');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 16, 'e foi sentar-se perto dali, à distância de um tiro de flecha, porque pensou: “Não posso ver o menino morrer”. Sentada ali perto, começou a chorar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 17, 'Deus ouviu o choro do menino, e o anjo de Deus, do céu, chamou Hagar e lhe disse: O que a aflige, Hagar? Não tenha medo; Deus ouviu o menino chorar, lá onde você o deixou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 18, 'Levante o menino e tome-o pela mão, porque dele farei um grande povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 19, 'Então Deus lhe abriu os olhos, e ela viu uma fonte. Foi até lá, encheu de água a vasilha e deu de beber ao menino.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 20, 'Deus estava com o menino. Ele cresceu, viveu no deserto e tornou-se flecheiro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 21, 'Vivia no deserto de Parã, e sua mãe conseguiu-lhe uma mulher da terra do Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 22, 'Naquela ocasião, Abimeleque, acompanhado de Ficol, comandante do seu exército, disse a Abraão: Deus está contigo em tudo o que fazes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 23, 'Agora, jura-me, diante de Deus, que não vais enganar-me, nem a mim nem a meus filhos e descendentes. Trata ­a nação que te acolheu como estrangeiro com a mesma bondade com que te tratei.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 24, 'Respondeu Abraão: “Eu juro!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 25, 'Todavia Abraão reclamou com Abimeleque a respeito de um poço que os servos de Abimeleque lhe tinham tomado à força.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 26, 'Mas Abimeleque lhe respondeu: “Não sei quem fez isso. Nunca me disseste nada, e só fiquei sabendo disso hoje”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 27, 'Então Abraão trouxe ovelhas e bois, deu-os a Abimeleque, e os dois firmaram um acordo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 28, 'Abraão separou sete ovelhas do rebanho,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 29, 'pelo que Abimeleque lhe perguntou: “Que significam estas sete ovelhas que separaste das demais?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 30, 'Ele respondeu: “Aceita estas sete ovelhas de minhas mãos como testemunho de que eu cavei este poço”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 31, 'Por isso aquele lugar foi chamado Berseba, porque ali os dois fizeram um juramento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 32, 'Firmado esse acordo em Berseba, Abimeleque e Ficol, comandante das suas tropas, voltaram para a terra dos filisteus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 33, 'Abraão, por sua vez, plantou uma tamargueira em Berseba e ali invocou o nome do Senhor, o Deus Eterno.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 21, 34, 'E morou Abraão na terra dos filisteus por longo tempo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 22, 1, 'Passado algum tempo, Deus pôs Abraão à prova, dizendo-lhe: “Abraão!” Ele respondeu: “Eis-me aqui”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 22, 2, 'Então disse Deus: “Tome seu filho, seu único filho, Isaque, a quem você ama, e vá para a região de Moriá. Sacrifique-o ali como holocausto num dos montes que lhe indicarei”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 22, 3, 'Na manhã seguinte, Abraão levantou-se e preparou o seu jumento. Levou consigo dois de seus servos e Isaque, seu filho. Depois de cortar lenha para o holocausto, partiu em direção ao lugar que Deus lhe havia indicado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 22, 4, 'No terceiro dia de viagem, Abraão olhou e viu o lugar ao longe.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 22, 5, 'Disse ele a seus servos: “Fiquem aqui com o jumento enquanto eu e o rapaz vamos até lá. Depois de adorarmos, voltaremos”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 22, 6, 'Abraão pegou a lenha para o holocausto e a colocou nos ombros de seu filho Isaque, e ele mesmo levou as brasas para o fogo, e a faca. E caminhando os dois juntos,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 22, 7, 'Isaque disse a seu pai Abraão: “Meu pai!” “Sim, meu filho”, respondeu Abraão. Isaque perguntou: “As brasas e a lenha estão aqui, mas onde está o cordeiro para o holocausto?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 22, 8, 'Respondeu Abraão: “Deus mesmo há de prover o cordeiro para o holocausto, meu filho”. E os dois continuaram a caminhar juntos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 22, 9, 'Quando chegaram ao lugar que Deus lhe havia indicado, Abraão construiu um altar e sobre ele arrumou a lenha. Amarrou seu filho Isaque e o colocou sobre o altar, em cima da lenha.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 22, 10, 'Então estendeu a mão e pegou a faca para sacrificar seu filho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 22, 11, 'Mas o Anjo do Senhor o chamou do céu: “Abraão! Abraão!” “Eis-me aqui”, respondeu ele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 22, 12, '“Não toque no rapaz”, disse o Anjo. “Não lhe faça nada. Agora sei que você teme a Deus, porque não me negou seu filho, o seu único filho.”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 22, 13, 'Abraão ergueu os olhos e viu um carneiro preso pelos chifres num arbusto. Foi lá pegá-lo, e o sacrificou como holocausto em lugar de seu filho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 22, 14, 'Abraão deu àquele lugar o nome de “O ­SenhorProverá”. Por isso até hoje se diz: “No monte do Senhor se proverá”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 22, 15, 'Pela segunda vez o Anjo do Senhor chamou do céu a Abraão');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 22, 16, 'e disse: “Juro por mim mesmo”, declara o Senhor, “que por ter feito o que fez, não me negando seu filho, o seu único filho,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 22, 17, 'esteja certo de que o abençoarei e farei seus descendentes tão numerosos como as estrelas do céu e como a areia das praias do mar. Sua descendência conquistará as cidades dos que lhe forem inimigos');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 22, 18, 'e, por meio dela, todos os povos da terra serão abençoados, por­que você me obedeceu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 22, 19, 'Então voltou Abraão a seus servos, e juntos partiram para Berseba, onde passou a viver.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 22, 20, 'Passado algum tempo, disseram a Abraão que Milca dera filhos a seu irmão Naor:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 22, 21, 'Uz, o mais velho, Buz, seu irmão, Quemuel, pai de Arã,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 22, 22, 'Quésede, Hazo, Pildas, Jidlafe e Betuel,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 22, 23, 'pai de Rebeca. Estes foram os oito filhos que Milca deu a Naor, irmão de Abraão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 22, 24, 'E sua concubina, chamada Reumá, teve os seguintes filhos: Tebá, Gaã, Taás e Maaca.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 23, 1, 'Sara viveu cento e vinte e sete anos');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 23, 2, 'e morreu em Quiriate-Arba, que é Hebrom, em Canaã; e Abraão foi lamentar e chorar por ela.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 23, 3, 'Depois Abraão deixou ali o corpo de sua mulher e foi falar com os hititas:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 23, 4, '“Sou apenas um estrangeiro entre vocês. Cedam-me alguma propriedade para sepultura, para que eu tenha onde enterrar a minha mulher”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 23, 5, 'Responderam os hititas a Abraão:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 23, 6, '“Ouça-nos, senhor; o senhor é um príncipe de Deus em nosso meio. Enterre a sua mulher nu­ma de nossas sepulturas, na que lhe parecer melhor. Nenhum de nós recusará ceder-lhe sua sepultura para que enterre a sua mulher”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 23, 7, 'Abraão levantou-se, curvou-se perante o povo daquela terra, os hititas,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 23, 8, 'e disse-lhes: Já que vocês me dão permissão para sepultar minha mulher, peço que intercedam por mim junto a Efrom, filho de Zoar,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 23, 9, 'a fim de que ele me ceda a caverna de Macpela, que lhe pertence e se encontra na divisa do seu campo. Peçam-lhe que a ceda a mim pelo preço justo, para que eu tenha uma propriedade para sepultura entre vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 23, 10, 'Efrom, o hitita, estava sentado no meio do seu povo e respondeu a Abraão, sendo ouvido por todos os hititas que tinham vindo à porta da cidade:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 23, 11, '“Não, meu senhor. Ouça-me, eu lhe cedo o campo e também a caverna que nele está. Cedo-os na presença do meu povo. Sepulte a sua mulher”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 23, 12, 'Novamente Abraão curvou-se perante o povo daquela terra');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 23, 13, 'e disse a Efrom, sendo ouvido por todos: “Ouça-me, por favor. Pagarei o preço do campo. Aceite-o, para que eu possa sepultar a minha mulher”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 23, 14, 'Efrom respondeu a Abraão:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 23, 15, '“Ouça-me, meu senhor: aquele pedaço de terra vale quatrocentas peças de prata, mas o que significa isso entre mim e você? Sepulte a sua mulher”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 23, 16, 'Abraão concordou com Efrom e pesou-lhe o valor por ele estipulado diante dos hititas: quatrocentas peças de prata, de acordo com o peso corrente entre os mercadores.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 23, 17, 'Assim o campo de Efrom em Macpela, perto de Manre, o próprio campo com a caverna que nele há e todas as árvores dentro das divisas do campo, foi transferido');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 23, 18, 'a Abraão como sua propriedade diante de todos os hititas que tinham vindo à porta da cidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 23, 19, 'Depois disso, Abraão sepultou sua mulher Sara na caverna do campo de Macpela, perto de Manre, que se encontra em Hebrom, na terra de Canaã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 23, 20, 'Assim o campo e a caverna que nele há foram transferidos a Abraão pelos hititas como propriedade para sepultura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 1, 'Abraão já era velho, de idade bem avançada, e o Senhorem tudo o abençoara.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 2, 'Dis­se ele ao servo mais velho de sua casa, que era o responsável por tudo quanto tinha: Ponha a mão debaixo da minha coxa');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 3, 'e jure pelo Senhor, o Deus dos céus e o Deus da terra, que não buscará mulher para meu filho entre as filhas dos cananeus, no meio dos quais estou vivendo,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 4, 'mas irá à minha terra e buscará entre os meus parentes uma mulher para meu filho Isaque.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 5, 'O servo lhe perguntou: “E se a mulher não quiser vir comigo a esta terra? Devo então levar teu filho de volta à terra de onde vieste?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 6, '“Cuidado!”, disse Abraão, “Não deixe o meu filho voltar para lá.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 7, 'O Senhor, o Deus dos céus, que me tirou da casa de meu pai e de minha terra natal e que me prometeu sob juramento ­que à minha descendência daria esta terra, enviará o seu anjo adiante de você para que de lá traga uma mulher para meu filho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 8, 'Se a mulher não quiser vir, você estará livre do juramento. Mas não leve o meu filho de volta para lá.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 9, 'Então o servo pôs a mão debaixo da coxa de Abraão, seu senhor, e jurou cumprir aquela palavra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 10, 'O servo partiu, com dez camelos do seu senhor, levando também do que o seu senhor tinha de melhor. Partiu para a Mesopotâmia, em direção à cidade onde Naor tinha morado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 11, 'Ao cair da tarde, quando as mulheres costumam sair para buscar água, ele fez os camelos se ajoelharem junto ao poço que ficava fora da cidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 12, 'Então orou: Senhor, Deus do meu senhor Abraão, dá-me neste dia bom êxito e seja bondoso com o meu senhor Abraão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 13, 'Como vês, estou aqui ao lado desta fonte, e as jovens do povo desta cidade estão vindo para tirar água.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 14, 'Concede que a jovem a quem eu disser: Por favor, incline o seu cântaro e dê-me de beber, e ela me responder: “Bebe. Também darei água aos teus camelos”, seja essa a que escolheste para teu servo Isaque. Saberei assim que foste bondoso com o meu senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 15, 'Antes que ele terminasse de orar, surgiu Rebeca, filha de Betuel, filho de Milca, mulher de Naor, irmão de Abraão, trazendo no ombro o seu cântaro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 16, 'A jovem era muito bonita e virgem; nenhum homem tivera relações com ela. Rebeca desceu à fonte, encheu seu cântaro e voltou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 17, 'O servo apressou-se ao encontro dela e disse: “Por favor, dê-me um pouco de água do seu cântaro”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 18, '“Beba, meu senhor”, disse ela, e tirou rapidamente dos ombros o cântaro e o serviu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 19, 'Depois que lhe deu de beber, disse: “Tirarei água também para os seus camelos até saciá-los”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 20, 'Assim ela esvaziou depressa seu cântaro no bebedouro e correu de volta ao poço para tirar mais água para todos os camelos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 21, 'Sem dizer nada, o homem a observava atentamente para saber se o Senhor tinha ou não coroado de êxito a sua missão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 22, 'Quando os camelos acabaram de beber, o homem deu à jovem um pendente de ouro de seis gramas e duas pulseiras de ouro de cento e vinte gramas,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 23, 'e perguntou: “De quem você é filha? Diga-me, por favor, se há lugar na casa de seu pai para eu e meus companheiros passarmos a noite”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 24, '“Sou filha de Betuel, o filho que Milca deu a Naor”, respondeu ela;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 25, 'e acrescen­tou: “Temos bastante palha e forragem, e também temos lugar para vocês passarem a noite”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 26, 'Então o homem curvou-se em adoração ao Senhor,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 27, 'dizendo: “Bendito seja o Senhor, o Deus do meu senhor Abraão, que não retirou sua bondade e sua fidelidade do meu senhor. Quanto a mim, o Senhor me conduziu na jornada até a casa dos parentes do meu senhor”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 28, 'A jovem correu para casa e contou tudo à família de sua mãe.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 29, 'Rebeca tinha um irmão chamado Labão. Ele saiu apressado à fonte para conhecer o homem,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 30, 'pois tinha visto o pendente e as pulseiras no braço de sua irmã, e ouvira Rebeca contar o que o homem lhe dissera. Saiu, pois, e foi encontrá-lo parado junto à fonte, ao lado dos camelos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 31, 'E disse: “Venha, bendito do Senhor! Por que ficar aí fora? Já arrumei a casa e um lugar para os camelos”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 32, 'Assim o homem dirigiu-se à casa, e os camelos foram descarregados. Deram palha e forragem aos camelos, e água ao homem e aos que estavam com ele para lavarem os pés.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 33, 'Depois lhe trouxeram comida, mas ele disse: “Não comerei enquanto não disser o que tenho para dizer”. Disse Labão: “Então fale”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 34, 'E ele disse: Sou servo de Abraão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 35, 'O Senhor o abençoou muito, e ele se tornou muito rico. Deu-lhe ovelhas e bois, prata e ouro, servos e servas, camelos e jumentos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 36, 'Sara, mulher do meu senhor, na velhice lhe deu um filho, que é o herdeiro de tudo o que Abraão possui.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 37, 'E meu senhor fez-me jurar, dizendo: “Você não buscará mulher para meu filho entre as filhas dos cananeus, em cuja terra estou vivendo,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 38, 'mas irá à família de meu pai, ao meu próprio clã, buscar uma mulher para meu filho”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 39, 'Então perguntei a meu senhor: E se a mulher não quiser me acompanhar?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 40, 'Ele respondeu: “O Senhor, a quem tenho servido, enviará seu anjo com você e coroará de êxito a sua missão, para que você traga para meu filho uma mulher do meu próprio clã, da família de meu pai.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 41, 'Quando chegar aos meus parentes, você estará livre do juramento se eles se recusarem a entregá-la a você. Só então você estará livre do juramento”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 42, 'Hoje, quando cheguei à fonte, eu disse: Ó Senhor, Deus do meu senhor Abraão, se assim desejares, dá êxito à missão de que fui incumbido.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 43, 'Aqui estou em pé diante desta fonte; se uma moça vier tirar água e eu lhe disser: Por favor, dê-me de beber um pouco de seu cântaro,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 44, 'e ela me responder: “Bebe. Também darei água aos teus camelos”, seja essa a que o Senhor escolheu para o filho do meu senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 45, 'Antes de terminar de orar em meu coração, surgiu Rebeca, com o cântaro ao ombro. Dirigiu-se à fonte e tirou água, e eu lhe disse: Por favor, dê-me de beber.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 46, 'Ela se apressou a tirar o cântaro do ombro e disse: “Bebe. Também darei água aos teus camelos”. Eu bebi, e ela deu de beber também aos camelos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 47, 'Depois lhe perguntei: De quem você é filha? Ela me respondeu: “De Betuel, filho de Naor e Milca”. Então coloquei o pendente em seu nariz e as pulseiras em seus braços,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 48, 'e curvei-me em adoração ao Senhor. Bendisse ao ­Senhor, o Deus do meu senhor Abraão, que me guiou pelo caminho certo para buscar para o filho dele a neta do irmão do meu senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 49, 'Agora, se quiserem mostrar fidelidade e bondade a meu senhor, digam-me; e, se não quiserem, digam-me também, para que eu decida o que fazer.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 50, 'Labão e Betuel responderam: Isso vem do Senhor; nada lhe podemos dizer, nem a favor, nem contra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 51, 'Aqui está Rebeca; leve-a com você e que ela se torne a mulher do filho do seu senhor, como disse o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 52, 'Quando o servo de Abraão ouviu o que disseram, curvou-se até o chão diante do Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 53, 'Então o servo deu jóias de ouro e de prata e vestidos a Rebeca; deu também presentes valiosos ao irmão dela e à sua mãe.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 54, 'Depois ele e os homens que o acompanhavam comeram, beberam e ali passaram a noite. Ao se levantarem na manhã seguinte, ele disse: “Deixem-me voltar ao meu senhor”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 55, 'Mas o irmão e a mãe dela responderam: “Deixe a jovem ficar mais uns dez dias conosco; então você poderá partir”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 56, 'Mas ele disse: “Não me detenham, agora que o Senhor coroou de êxito a minha missão. Vamos despedir-nos, e voltarei ao meu senhor”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 57, 'Então lhe disseram: “Vamos chamar a jovem e ver o que ela diz”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 58, 'Chamaram Rebeca e lhe perguntaram: “Você quer ir com este homem?” “Sim, quero”, respondeu ela.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 59, 'Despediram-se, pois, de sua irmã Rebeca, de sua ama, do servo de Abraão e dos que o acompanhavam.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 60, 'E abençoaram Rebeca, dizendo-lhe: “Que você cresça, nossa irmã, até ser milhares de milhares; e que a sua descendência conquiste as cidades dos seus inimigos”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 61, 'Então Rebeca e suas servas se aprontaram, montaram seus camelos e partiram com o homem. E assim o servo partiu levando Rebeca.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 62, 'Isaque tinha voltado de Beer-Laai-Roi, pois habitava no Neguebe.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 63, 'Certa tarde, saiu ao campo para meditar. Ao erguer os olhos, viu que se aproximavam camelos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 64, 'Rebeca também ergueu os olhos e viu Isaque. Ela desceu do camelo');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 65, 'e perguntou ao servo: “Quem é aquele homem que vem pelo campo ao nosso encontro?” “É meu senhor”, respondeu o servo. Então ela se cobriu com o véu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 66, 'Depois o servo contou a Isaque tudo o que havia feito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 24, 67, 'Isaque levou Rebeca para a tenda de sua mãe Sara; fez dela sua mulher, e a amou; assim Isaque foi consolado após a morte de sua mãe.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 1, 'Abraão casou-se com outra mulher, chamada Quetura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 2, 'Ela lhe deu os seguintes filhos: Zinrã, Jocsã, Medã, Midiã, Isbaque e Suá.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 3, 'Jocsã gerou Sabá e Dedã; os descendentes de Dedã foram os assuritas, os letusitas e os leumitas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 4, 'Os filhos de Midiã foram Efá, Éfer, Enoque, Abida e Elda. Todos esses foram descendentes de Quetura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 5, 'Abraão deixou tudo o que tinha para Isaque.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 6, 'Mas para os filhos de suas concubinas deu presentes; e, ainda em vida, enviou-os para longe de Isaque, para a terra do oriente.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 7, 'Abraão viveu cento e setenta e cinco anos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 8, 'Morreu em boa velhice, em idade bem avançada, e foi reunido aos seus antepassados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 9, 'Seus filhos, Isaque e Ismael, o sepultaram na caverna de Macpela, perto de Manre, no campo de Efrom, filho de Zoar, o hitita,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 10, 'campo que Abraão comprara dos hititas. Foi ali que Abraão e Sara, sua mulher, foram sepultados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 11, 'Depois da morte de Abraão, Deus abençoou seu filho Isaque. Isaque morava próximo a Beer-Laai-Roi.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 12, 'Este é o registro da descendência de Ismael, o filho de Abraão que Hagar, a serva egípcia de Sara, deu a ele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 13, 'São estes os nomes dos filhos de Ismael, alistados por ordem de nascimento: Nebaiote, o filho mais velho de Ismael, Quedar, Adbeel, Mibsão,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 14, 'Misma, Dumá, Massá,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 15, 'Hadade, Temá, Jetur, Nafis e Quedemá.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 16, 'Foram esses os doze filhos de Ismael, que se tornaram os líderes de suas tribos; os seus povoados e acampamentos receberam os seus nomes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 17, 'Ismael viveu cento e trinta e sete anos. Morreu e foi reunido aos seus antepassados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 18, 'Seus descendentes se estabeleceram na região que vai de Havilá a Sur, próximo à fronteira com o Egito, na direção de quem vai para Assur. E viveram em hostilidade contra todos os seus irmãos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 19, 'Esta é a história da família de Isaque, filho de Abraão: Abraão gerou Isaque,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 20, 'o qual aos quarenta anos se casou com Rebeca, filha de Betuel, o arameu de Padã-Arã, e irmã de Labão, também arameu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 21, 'Isaque orou ao Senhor em favor de sua mulher, porque era estéril. O Senhor respondeu à sua oração, e Rebeca, sua mulher, engravidou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 22, 'Os meninos se empurravam dentro dela, pelo que disse: “Por que está me acontecendo isso?” Foi então consultar o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 23, 'Disse-lhe o Senhor: “Duas nações estão em seu ventre, já desde as suas entranhas dois povos se separarão; um deles será mais forte que o outro, mas o mais velho servirá ao mais novo”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 24, 'Ao chegar a época de dar à luz, confirmou-se que havia gêmeos em seu ventre.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 25, 'O primeiro a sair era ruivo, e todo o seu corpo era como um manto de pêlos; por isso lhe deram o nome de Esaú.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 26, 'Depois saiu seu irmão, com a mão agarrada no calcanhar de Esaú; pelo que lhe deram o nome de Jacó. Tinha Isaque sessenta anos de idade quando Rebeca os deu à luz.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 27, 'Os meninos cresceram. Esaú tornou-se caçador habilidoso e vivia percorrendo os campos, ao passo que Jacó cuidava do rebanho e vivia nas tendas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 28, 'Isaque preferia Esaú, porque gostava de comer de suas caças; Rebeca preferia Jacó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 29, 'Certa vez, quando Jacó preparava um ensopado, Esaú chegou faminto, voltando do campo,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 30, 'e pediu-lhe: “Dê-me um pouco desse ensopado vermelho aí. Estou faminto!” Por isso também foi chamado Edom.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 31, 'Respondeu-lhe Jacó: “Venda-me primeiro o seu direito de filho mais velho”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 32, 'Disse Esaú: “Estou quase morrendo. De que me vale esse direito?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 33, 'Jacó, porém, insistiu: “Jure primeiro”. Ele fez um juramento, vendendo o seu direito de filho mais velho a Jacó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 25, 34, 'Então Jacó serviu a Esaú pão com ensopado de lentilhas. Ele comeu e bebeu, levantou-se e se foi. Assim Esaú desprezou o seu direito de filho mais velho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 1, 'Houve fome naquela terra, como tinha acontecido no tempo de Abraão. Por isso Isaque foi para Gerar, onde Abimeleque era o rei dos filisteus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 2, 'O Senhor apareceu a Isaque e disse: Não desça ao Egito; procure estabelecer-se na terra que eu lhe indicar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 3, 'Permaneça nesta terra mais um pouco, e eu estarei com você e o abençoarei. Porque a você e a seus descendentes darei todas estas terras e confirmarei o juramento que fiz a seu pai, Abraão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 4, 'Tornarei seus descendentes tão numerosos como as estrelas do céu e lhes darei todas estas terras; e por meio da sua descendência todos os povos da terra serão abençoados,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 5, 'porque Abraão me obedeceu e guardou meus preceitos, meus mandamentos, meus decretos e minhas leis.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 6, 'Assim Isaque ficou em Gerar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 7, 'Quando os homens do lugar lhe perguntaram sobre a sua mulher, ele disse: “Ela é minha irmã”. Teve medo de dizer que era sua mulher, pois pensou: “Os homens deste lugar podem matar-me por causa de Rebeca, por ser ela tão bonita”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 8, 'Isaque estava em Gerar já fazia muito tempo. Certo dia, Abimeleque, rei dos filisteus, estava olhando do alto de uma janela quando viu Isaque acariciando Rebeca, sua mulher.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 9, 'Então Abimeleque chamou Isaque e lhe disse: “Na verdade ela é tua mulher! Por que me disseste que ela era tua irmã?” Isaque respondeu: “Porque pensei que eu poderia ser morto por causa dela”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 10, 'Então disse Abimeleque: “Tens idéia do que nos fizeste? Qualquer homem bem poderia ter-se deitado com tua mulher, e terias trazido culpa sobre nós”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 11, 'E Abimeleque advertiu todo o povo: “Quem tocar neste homem ou em sua mulher certamente morrerá!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 12, 'Isaque formou lavoura naquela terra e no mesmo ano colheu a cem por um, porque o Senhor o abençoou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 13, 'O homem enriqueceu, e a sua riqueza continuou a aumentar, até que ficou riquíssimo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 14, 'Possuía tantos rebanhos e servos que os filisteus o invejavam.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 15, 'Estes taparam todos os poços que os servos de Abraão, pai de Isaque, tinham cavado na sua época, enchendo-os de terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 16, 'Então Abimeleque pediu a Isaque: “Sai de nossa terra, pois já és poderoso demais para nós”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 17, 'Por isso Isaque mudou-se de lá, acampou no vale de Gerar e ali se estabeleceu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 18, 'Isaque reabriu os poços cavados no tempo de seu pai Abraão, os quais os filisteus fecharam depois que Abraão morreu, e deu-lhes os mesmos nomes que seu pai lhes tinha dado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 19, 'Os servos de Isaque cavaram no vale e descobriram um veio d’água.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 20, 'Mas os pastores de Gerar discutiram com os pastores de Isaque, dizendo: “A água é nossa!” Por isso Isaque deu ao poço o nome de Eseque, porque discutiram por causa dele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 21, 'Então os seus servos cavaram outro poço, mas eles também discutiram por causa dele; por isso o chamou Sitna.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 22, 'Isaque mudou-se dali e cavou outro poço, e ninguém discutiu por causa dele. Deu-lhe o nome de Reo­bote, dizendo: “Agora o Senhor nos abriu espaço e prosperaremos na terra”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 23, 'Dali Isaque foi para Berseba.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 24, 'Naque­la noite, o Senhor lhe apareceu e disse: “Eu sou o Deus de seu pai Abraão. Não tema, porque estou com você; eu o abençoarei e multiplicarei os seus descendentes por amor ao meu servo Abraão”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 25, 'Isaque construiu nesse lugar um altar e invocou o nome do Senhor. Ali armou acampamento, e os seus servos cavaram outro poço.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 26, 'Por aquele tempo, veio a ele Abimeleque, de Gerar, com Auzate, seu conselheiro pessoal, e Ficol, o comandante dos seus exércitos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 27, 'Isaque lhes perguntou: “Por que me vieram ver, uma vez que foram hostis e me mandaram embora?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 28, 'Eles responderam: Vimos claramente que o Senhor está contigo; por isso dissemos: Façamos um juramento entre nós. Queremos firmar um acordo contigo:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 29, 'Tu não nos farás mal, assim como nada te fizemos, mas sempre te tratamos bem e te despedimos em paz. Agora sabemos que o Senhor te tem abençoado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 30, 'Então Isaque ofereceu-lhes um banquete, e eles comeram e beberam.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 31, 'Na manhã seguinte os dois fizeram juramento. Depois Isaque os despediu e partiram em paz.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 32, 'Naquele mesmo dia os servos de Isaque vieram falar-lhe sobre o poço que tinham cavado, e disseram: “Achamos água!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 33, 'Isaque deu-lhe o nome de Seba e, por isso, até o dia de hoje aquela cidade é conhecida como Berseba.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 34, 'Tinha Esaú quarenta anos de idade quando escolheu por mulher a Judite, filha de Beeri, o hitita, e também a Basemate, filha de Elom, o hitita.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 26, 35, 'Elas amarguraram a vida de Isaque e de Rebeca.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 1, 'Tendo Isaque envelhecido, seus olhos ficaram tão fracos que ele já não podia enxergar. Certo dia chamou Esaú, seu filho mais velho, e lhe disse: “Meu filho!” Ele respondeu: “Estou aqui”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 2, 'Disse-lhe Isaque: Já estou velho e não sei o dia da minha morte.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 3, 'Pegue agora suas armas, o arco e a aljava, e vá ao campo caçar alguma coisa para mim.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 4, 'Prepare-me aquela comida saborosa que tanto aprecio e traga-me, para que eu a coma e o abençoe antes de morrer.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 5, 'Ora, Rebeca estava ouvindo o que Isaque dizia a seu filho Esaú. Quando Esaú saiu ao campo para caçar,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 6, 'Rebeca disse a seu filho Jacó: Ouvi seu pai dizer a seu irmão Esaú:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 7, '“Traga-me alguma caça e prepare-me aquela comida saborosa, para que eu a coma e o abençoe na presença do Senhor antes de morrer”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 8, 'Agora, meu filho, ouça bem e faça o que lhe ordeno:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 9, 'Vá ao rebanho e traga-me dois cabritos escolhidos, para que eu prepare uma comida saborosa para seu pai, como ele aprecia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 10, 'Leve-a então a seu pai, para que ele a coma e o abençoe antes de morrer.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 11, 'Disse Jacó a Rebeca, sua mãe: Mas o meu irmão Esaú é homem peludo, e eu tenho a pele lisa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 12, 'E se meu pai me apalpar? Vai parecer que estou tentando enganá-lo, fazendo-o de tolo e, em vez de bênção, trarei sobre mim maldição.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 13, 'Disse-lhe sua mãe: “Caia sobre mim a maldição, meu filho. Faça apenas o que eu digo: Vá e traga-os para mim”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 14, 'Então ele foi, apanhou-os e os trouxe à sua mãe, que preparou uma comida saborosa, como seu pai apreciava.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 15, 'Rebeca pegou as melhores roupas de Esaú, seu filho mais velho, roupas que tinha em casa, e colocou-as em Jacó, seu filho mais novo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 16, 'Depois cobriu-lhe as mãos e a parte lisa do pescoço com as peles dos cabri­tos,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 17, 'e por fim entregou a Jacó a refeição saborosa e o pão que tinha feito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 18, 'Ele se dirigiu ao pai e disse: “Meu pai”. Respondeu ele: “Sim, meu filho. Quem é você?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 19, 'Jacó disse a seu pai: “Sou Esaú, seu filho mais velho. Fiz como o senhor me disse. Agora, assente-se e coma do que cacei para que me abençoe”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 20, 'Isaque perguntou ao filho: “Como encontrou a caça tão depressa, meu filho?” Ele respondeu: “O Senhor, o seu Deus, a colocou no meu caminho”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 21, 'Então Isaque disse a Jacó: “Chegue mais perto, meu filho, para que eu possa apalpá-lo e saber se você é realmente meu filho Esaú”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 22, 'Jacó aproximou-se do seu pai Isaque, que o apalpou e disse: “A voz é de Jacó, mas os braços são de Esaú”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 23, 'Não o reconheceu, pois seus braços estavam peludos como os de Esaú, seu irmão; e o abençoou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 24, 'Isaque perguntou-lhe outra vez: “Você é mesmo meu filho Esaú?” E ele respondeu: “Sou”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 25, 'Então lhe disse: “Meu filho, traga-me da sua caça para que eu coma e o abençoe”. Jacó a trouxe, e seu pai comeu; também trouxe vinho, e ele bebeu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 26, 'Então seu pai Isaque lhe dis­se: “Venha cá, meu filho, dê-me um bei­jo”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 27, 'Ele se aproximou e o beijou. Quando sentiu o cheiro de suas roupas, Isaque o abençoou, dizendo: Ah, o cheiro de meu filho é como o cheiro de um campo que o Senhor abençoou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 28, 'Que Deus lhe conceda do céu o orvalho e da terra a riqueza, com muito cereal e muito vinho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 29, 'Que as nações o sirvam e os povos se curvem diante de você. Seja senhor dos seus irmãos, e curvem-se diante de você os filhos de sua mãe. Malditos sejam os que o amaldiçoarem e benditos sejamos que o abençoarem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 30, 'Quando Isaque acabou de abençoar Jacó, mal tendo ele saído da presença do pai, seu irmão Esaú chegou da caçada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 31, 'Ele também preparou uma comida saborosa e a trouxe a seu pai. E lhe disse: “Meu pai, levante-se e coma da minha caça, para que o senhor me dê sua bênção”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 32, 'Perguntou-lhe seu pai Isaque: “Quem é você?” Ele respondeu: “Sou Esaú, seu filho mais velho”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 33, 'Profundamente abalado, Isaque começou a tremer muito e disse: “Quem então apanhou a caça e a trouxe para mim? Acabei de comê-la antes de você entrar e a ele abençoei; e abençoado ele será!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 34, 'Quando Esaú ouviu as palavras de seu pai, deu um forte grito e, cheio de amargura, implorou ao pai: “Abençoe também a mim, meu pai!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 35, 'Mas ele respondeu: “Seu irmão chegou astutamente e recebeu a bênção que pertencia a você”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 36, 'E disse Esaú: “Não é com razão que o seu nome é Jacó? Já é a segunda vez que ele me engana! Primeiro tomou o meu direito de filho mais velho, e agora recebeu a minha bênção!” Então perguntou ao pai: “O senhor não reservou nenhuma bênção para mim?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 37, 'Isaque respondeu a Esaú: “Eu o constituí senhor sobre você, e a todos os seus parentes tornei servos dele; a ele supri de cereal e de vinho. Que é que eu poderia fazer por você, meu filho?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 38, 'Esaú pediu ao pai: “Meu pai, o senhor tem apenas uma bênção? Abençoe-me também, meu pai!” Então chorou Esaú em alta voz.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 39, 'Seu pai Isaque respondeu-lhe: Sua habitação será longe das terras férteis, distante do orvalho que desce do alto céu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 40, 'Você viverá por sua espada e servirá a seu irmão. Mas quando você não suportar mais, arrancará do pescoço o jugo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 41, 'Esaú guardou rancor contra Jacó por causa da bênção que seu pai lhe dera. E disse a si mesmo: “Os dias de luto pela morte de meu pai estão próximos; então matarei meu irmão Jacó”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 42, 'Quando contaram a Rebeca o que seu filho Esaú dissera, ela mandou chamar Jacó, seu filho mais novo, e lhe disse: Esaú está se consolando com a idéia de matá-lo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 43, 'Ouça, pois, o que lhe digo, meu filho: Fuja imediatamente para a casa de meu irmão Labão, em Harã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 44, 'Fique com ele algum tempo, até que passe o furor de seu irmão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 45, 'Quando seu irmão não estiver mais irado contra você e esquecer o que você lhe fez, mandarei buscá-lo. Por que perderia eu vocês dois num só dia?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 27, 46, 'Então Rebeca disse a Isaque: “Estou desgostosa da vida, por causa destas mulheres hititas. Se Jacó escolher esposa entre as mulheres desta terra, entre mulheres hititas como estas, perderei a razão de viver”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 28, 1, 'Então Isaque chamou Jacó, deu-lhe sua bênção e lhe ordenou: Não se case com mulher cananéia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 28, 2, 'Vá a Padã-Arã, à casa de Betuel, seu avô materno, e case-se com uma das filhas de Labão, irmão de sua mãe.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 28, 3, 'Que o Deus todo-poderoso o abençoe, faça-o prolífero e multiplique os seus descendentes, para que você se torne uma comunidade de povos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 28, 4, 'Que ele dê a você e a seus descendentes a bênção de Abraão, para que você tome posse da terra na qual vive como estrangeiro, a terra dada por Deus a Abraão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 28, 5, 'Então Isaque despediu Jacó e este foi a Padã-Arã, a Labão, filho do arameu Betuel, irmão de Rebeca, mãe de Jacó e Esaú.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 28, 6, 'Esaú viu que Isaque havia abençoado a Jacó e o havia mandado a Padã-Arã para escolher ali uma mulher e que, ao abençoá-lo, dera-lhe a ordem de não se casar com mulher cananéia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 28, 7, 'Também soube que Jacó obedecera a seu pai e a sua mãe e fora para Padã-Arã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 28, 8, 'Percebendo então Esaú que seu pai Isaque não aprovava as mulheres cananéias,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 28, 9, 'foi à casa de Ismael e tomou a Maalate, irmã de Nebaiote, filha de Ismael, filho de Abraão, além das outras mulheres que já tinha.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 28, 10, 'Jacó partiu de Berseba e foi para Harã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 28, 11, 'Chegando a determinado lugar, parou para pernoitar, porque o sol já se havia posto. Tomando uma das pedras dali, usou-a como travesseiro e deitou-se.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 28, 12, 'E teve um sonho no qual viu uma escada apoiada na terra; o seu topo alcançava os céus, e os anjos de Deus subiam e desciam por ela.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 28, 13, 'Ao lado dele estava o Senhor, que lhe disse: Eu sou o Senhor, o Deus de seu pai Abraão e o Deus de Isaque. Darei a você e a seus descendentes a terra na qual você está deitado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 28, 14, 'Seus descendentes serão como o pó da terra, e se espalharão para o Oeste e para o Leste, para o Norte e para o Sul. Todos os povos da terra serão abençoados por meio de você e da sua descendência.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 28, 15, 'Estou com você e cuidarei de você, aonde quer que vá; e eu o trarei de volta a esta terra. Não o deixarei enquanto não fizer o que lhe prometi.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 28, 16, 'Quando Jacó acordou do sono, disse: “Sem dúvida o Senhor está neste lugar, mas eu não sabia!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 28, 17, 'Teve medo e disse: “Temível é este lugar! Não é outro, senão a casa de Deus; esta é a porta dos céus”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 28, 18, 'Na manhã seguinte, Jacó pegou a pedra que tinha usado como travesseiro, colocou-a em pé como coluna e derramou óleo sobre o seu topo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 28, 19, 'E deu o nome de Betel àquele lugar, embora a cidade anteriormente se chamasse Luz.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 28, 20, 'Então Jacó fez um voto, dizendo: Se Deus estiver comigo, cuidar de mim nesta viagem que estou fazendo, prover-me de comida e roupa,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 28, 21, 'e levar-me de volta em segurança à casa de meu pai, então o Senhor será o meu Deus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 28, 22, 'E esta pedra que hoje coloquei como coluna servirá de santuário de Deus; e de tudo o que me deres certamente te darei o dízimo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 1, 'Então Jacó seguiu viagem e chegou à Mesopotâmia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 2, 'Certo dia, olhando ao redor, viu um poço no campo e três rebanhos de ovelhas deitadas por perto, pois os rebanhos bebiam daquele poço, que era tapado por uma grande pedra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 3, 'Por isso, quando todos os rebanhos se reuniam ali, os pastores rolavam a pedra da boca do poço e davam água às ovelhas. Depois recolocavam a pedra em seu lugar, sobre o poço.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 4, 'Jacó perguntou aos pastores: “Meus amigos, de onde são vocês?” “Somos de Harã”, responderam.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 5, '“Vocês conhecem Labão, neto de Naor?”, perguntou-lhes Jacó. Eles responderam: “Sim, nós o conhecemos”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 6, 'Então Jacó perguntou: “Ele vai bem?” “Sim, vai bem”, disseram eles, “e ali vem sua filha Raquel com as ovelhas.”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 7, 'Disse ele: “Olhem, o sol ainda vai alto e não é hora de recolher os rebanhos. Dêem de beber às ovelhas e levem-nas de volta ao pasto”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 8, 'Mas eles responderam: “Não podemos, enquanto os rebanhos não se agruparem e a pedra não for removida da boca do poço. Só então daremos de beber às ovelhas”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 9, 'Ele ainda estava conversando, quando chegou Raquel com as ovelhas de seu pai, pois ela era pastora.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 10, 'Quando Jacó viu Raquel, filha de Labão, irmão de sua mãe, e as ovelhas de Labão, aproximou-se, removeu a pedra da boca do poço e deu de beber às ovelhas de seu tio Labão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 11, 'Depois Jacó beijou Raquel e começou a chorar bem alto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 12, 'Então contou a Raquel que era parente do pai dela e filho de Rebeca. E ela foi correndo contar tudo a seu pai.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 13, 'Logo que Labão ouviu as notícias acerca de Jacó, seu sobrinho, correu ao seu encontro, abraçou-o e o beijou. Depois, levou-o para casa, e Jacó contou-lhe tudo o que havia ocorrido.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 14, 'Então Labão lhe disse: “Você é sangue do meu sangue”. Já fazia um mês que Jacó estava na casa de Labão,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 15, 'quando este lhe disse: “Só por ser meu parente você vai trabalhar de graça? Diga-me qual deve ser o seu salário”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 16, 'Ora, Labão tinha duas filhas; o nome da mais velha era Lia, e o da mais nova, Raquel.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 17, 'Lia tinha olhos meigos, mas Raquel era bonita e atraente.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 18, 'Como Jacó gostava muito de Raquel, disse: “Trabalharei sete anos em troca de Raquel, sua filha mais nova”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 19, 'Labão respondeu: “Será melhor dá-la a você do que a algum outro homem. Fique aqui comigo”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 20, 'Então Jacó trabalhou sete anos por Raquel, mas lhe pareceram poucos dias, pelo tanto que a amava.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 21, 'Então disse Jacó a Labão: “Entregue-me a minha mulher. Cumpri o prazo previsto e quero deitar-me com ela”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 22, 'Então Labão reuniu todo o povo daquele lugar e deu uma festa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 23, 'Mas quando a noite chegou, deu sua filha Lia a Jacó, e Jacó deitou-se com ela.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 24, 'Labão também entregou sua serva Zilpa à sua filha, para que ficasse a serviço dela.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 25, 'Quando chegou a manhã, lá estava Lia. Então Jacó disse a Labão: “Que foi que você me fez? Eu não trabalhei por Raquel? Por que você me enganou?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 26, 'Labão respondeu: Aqui não é costume entregar em casamento a filha mais nova antes da mais velha.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 27, 'Deixe passar esta semana de núpcias e lhe daremos também a mais nova, em troca de mais sete anos de traba­lho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 28, 'Jacó concordou. Passou aquela semana de núpcias com Lia, e Labão lhe deu sua filha Raquel por mulher.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 29, 'Labão deu a Raquel sua serva Bila, para que ficasse a serviço dela.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 30, 'Jacó deitou-se também com Raquel, que era a sua preferida. E trabalhou para Labão outros sete anos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 31, 'Quando o Senhor viu que Lia era desprezada, concedeu-lhe filhos; Raquel, porém, era estéril.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 32, 'Lia engravidou, deu à luz um filho, e deu-lhe o nome de Rúben, pois dizia: “O Senhor viu a minha infelicidade. Agora, certamente o meu marido me amará”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 33, 'Lia engravidou de novo e, quando deu à luz outro filho, disse: “Porque o Senhor ouviu que sou desprezada, deu-me também este”. Pelo que o chamou Simeão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 34, 'De novo engravidou e, quando deu à luz mais um filho, disse: “Agora, finalmente, meu marido se apegará a mim, porque já lhe dei três filhos”. Por isso deu-lhe o nome de Levi.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 29, 35, 'Engravidou ainda outra vez e, quando deu à luz mais outro filho, disse: “Desta vez louvarei o Senhor”. Assim deu-lhe o nome de Judá. Então parou de ter filhos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 1, 'Quando Raquel viu que não dava filhos a Jacó, teve inveja de sua irmã. Por isso disse a Jacó: “Dê-me filhos ou morrerei!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 2, 'Jacó ficou irritado e disse: “Por acaso estou no lugar de Deus, que a impediu de ter filhos?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 3, 'Então ela respondeu: “Aqui está Bila, minha serva. Deite-se com ela, para que tenha filhos em meu lugar e por meio dela eu também possa formar família”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 4, 'Por isso ela deu a Jacó sua serva Bila por mulher. Ele deitou-se com ela,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 5, 'Bila engravidou e deu-lhe um filho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 6, 'Então Raquel disse: “Deus me fez justiça, ouviu o meu clamor e deu-me um filho”. Por isso deu-lhe o nome de Dã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 7, 'Bila, serva de Raquel, engravidou novamente e deu a Jacó o segundo filho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 8, 'Então disse Raquel: “Tive grande luta com minha irmã e venci”. Pelo que o chamou Naftali.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 9, 'Quando Lia viu que tinha parado de ter filhos, tomou sua serva Zilpa e a deu a Jacó por mulher.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 10, 'Zilpa, serva de Lia, deu a Jacó um filho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 11, 'Então disse Lia: “Que grande sorte!”  Por isso o chamou Gade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 12, 'Zilpa, serva de Lia, deu a Jacó mais um filho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 13, 'Então Lia exclamou: “Como sou feliz! As mulheres dirão que sou feliz”. Por isso lhe deu o nome de Aser.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 14, 'Durante a colheita do trigo, Rúben saiu ao campo, encontrou algumas mandrágoras e as trouxe a Lia, sua mãe. Então Raquel disse a Lia: “Dê-me algumas mandrágoras do seu filho”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 15, 'Mas ela respondeu: “Não lhe foi suficiente tomar de mim o marido? Vai tomar também as mandrágoras que o meu filho trouxe?” Então disse Raquel: “Jacó se deitará com você esta noite, em troca das mandrágoras trazidas pelo seu filho”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 16, 'Quando Jacó chegou do campo naquela tarde, Lia saiu ao seu encontro e lhe disse: “Hoje você me possuirá, pois eu comprei esse direito com as mandrágoras do meu filho”. E naquela noite ele se deitou com ela.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 17, 'Deus ouviu Lia, e ela engravidou e deu a Jacó o quinto filho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 18, 'Disse Lia: “Deus me recompensou por ter dado a minha serva ao meu marido”. Por isso deu-lhe o nome de Issacar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 19, 'Lia engravidou de novo e deu a Jacó o sexto filho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 20, 'Disse Lia: “Deus presenteou-me com uma dádiva preciosa. Agora meu marido me tratará melhor; afinal já lhe dei seis filhos”. Por isso deu-lhe o nome de Zebulom.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 21, 'Algum tempo depois, ela deu à luz uma menina a quem chamou Diná.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 22, 'Então Deus lembrou-se de Raquel. Deus ouviu o seu clamor e a tornou fértil.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 23, 'Ela engravidou, e deu à luz um filho e disse: “Deus tirou de mim a minha humilhação”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 24, 'Deu-lhe o nome de José e disse: “Que o Senhor me acrescente ainda outro filho”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 25, 'Depois que Raquel deu à luz José, Jacó disse a Labão: Deixe-me voltar para a minha terra natal.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 26, 'Dê-me as minhas mulheres, pelas quais o servi, e os meus filhos, e partirei. Você bem sabe quanto trabalhei para você.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 27, 'Mas Labão lhe disse: “Se mereço sua consideração, peço-lhe que fique. Por meio de adivinhação descobri que o Senhor me abençoou por sua causa”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 28, 'E acrescentou: “Diga o seu salário, e eu lhe pagarei”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 29, 'Jacó lhe respondeu: Você sabe quanto trabalhei para você e como os seus rebanhos cresceram sob os meus cuidados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 30, 'O pouco que você possuía antes da minha chegada aumentou muito, pois o Senhor o abençoou depois que vim para cá. Contudo, quando farei algo em favor da minha própria família?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 31, 'Então Labão perguntou: “Que você quer que eu lhe dê?” “Não me dê coisa alguma”, respondeu Jacó. “Voltarei a cuidar dos seus rebanhos se você concordar com o seguinte:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 32, 'hoje passarei por todos os seus rebanhos e tirarei do meio deles todas as ovelhas salpicadas e pintadas, todos os cordeiros pretos e todas as cabras pintadas e salpicadas. Eles serão o meu salário.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 33, 'E a minha honestidade dará testemunho de mim no futuro, toda vez que você resolver verificar o meu salário. Se estiver em meu poder alguma cabra que não seja salpicada ou pintada, e algum cordeiro que não seja preto, poderá considerá-los roubados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 34, 'E disse Labão: “De acordo. Seja como você disse”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 35, 'Naquele mesmo dia Labão separou todos os bodes que tinham listras ou manchas brancas, todas as cabras que tinham pintas ou manchas brancas, e todos os cordeiros pretos e os colocou aos cuidados de seus filhos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 36, 'Afastou-se então de Jacó, à distância equivalente a três dias de viagem, e Jacó continuou a apascentar o resto dos rebanhos de Labão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 37, 'Jacó pegou galhos verdes de estoraque, amendoeira e plátano e neles fez listras brancas, descascando-os parcialmente e expondo assim a parte branca interna dos galhos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 38, 'Depois fixou os galhos descascados junto aos bebedouros, na frente dos rebanhos, no lugar onde costumavam beber água. Na época do cio, os rebanhos vinham beber e');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 39, 'se acasalavam diante dos galhos. E geravam filhotes listrados, salpicados e pintados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 40, 'Jacó separava os filhotes do rebanho dos demais, e fazia com que esses ficassem juntos dos animais listrados e pretos de Labão. Assim foi formando o seu próprio rebanho que separou do de Labão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 41, 'Toda vez que as fêmeas mais fortes estavam no cio, Jacó colocava os galhos nos bebedouros, em frente dos animais, para que se acasalassem perto dos galhos;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 42, 'mas, se os animais eram fracos, não os colocava ali. Desse modo, os animais fracos ficavam para Labão e os mais fortes para Jacó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 30, 43, 'Assim o homem ficou extremamente rico, tornando-se dono de grandes rebanhos e de servos e servas, camelos e jumentos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 1, 'Jacó, porém, ouviu falar que os filhos de Labão estavam dizendo: “Jacó tomou tudo que o nosso pai tinha e juntou toda a sua riqueza à custa do nosso pai”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 2, 'E Jacó percebeu que a atitude de Labão para com ele já não era a mesma de antes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 3, 'E o Senhor disse a Jacó: “Volte para a terra de seus pais e de seus parentes, e eu estarei com você”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 4, 'Então Jacó mandou chamar Raquel e Lia para virem ao campo onde estavam os seus rebanhos,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 5, 'e lhes disse: Vejo que a atitude do seu pai para comigo não é mais a mesma, mas o Deus de meu pai tem estado comigo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 6, 'Vocês sabem que trabalhei para seu pai com todo o empenho,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 7, 'mas ele tem me feito de tolo, mudando o meu salário dez vezes. Contudo, Deus não permitiu que ele me prejudicasse.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 8, 'Se ele dizia: “As crias salpicadas serão o seu salário”, todos os rebanhos geravam filhotes salpicados; e se ele dizia: “As que têm listras serão o seu salário”, todos os rebanhos geravam filhotes com listras.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 9, 'Foi assim que Deus tirou os rebanhos de seu pai e os deu a mim.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 10, 'Na época do acasalamento, tive um sonho em que olhei e vi que os machos que fecundavam o rebanho tinham listras, eram salpicados e malhados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 11, 'O Anjo de Deus me disse no sonho: “Jacó!” Eu respondi: Eis-me aqui!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 12, 'Então ele disse: “Olhe e veja que todos os machos que fecundam o rebanho têm listras, são salpicados e malhados, porque tenho visto tudo o que Labão lhe fez.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 13, 'Sou o Deus de Betel, onde você ungiu uma coluna e me fez um voto. Saia agora desta terra e volte para a sua terra natal”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 14, 'Raquel e Lia disseram a Jacó: Temos ainda parte na herança dos bens de nosso pai?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 15, 'Não nos trata ele como estrangeiras? Não apenas nos vendeu como também gastou tudo o que foi pago por nós!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 16, 'Toda a riqueza que Deus tirou de nosso pai é nossa e de nossos filhos. Portanto, faça tudo quanto Deus lhe ordenou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 17, 'Então Jacó ajudou seus filhos e suas mulheres a montar nos camelos,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 18, 'e conduziu todo o seu rebanho, junto com todos os bens que havia acumulado em Padã-Arã, para ir à terra de Canaã, à casa de seu pai, Isaque.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 19, 'Enquanto Labão tinha saído para tosquiar suas ovelhas, Raquel roubou de seu pai os ídolos do clã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 20, 'Foi assim que Jacó enganou a Labão, o arameu, fugindo sem lhe dizer nada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 21, 'Ele fugiu com tudo o que tinha e, atravessando o Eufrates, foi para os montes de Gileade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 22, 'Três dias depois, Labão foi informado de que Jacó tinha fugido.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 23, 'Tomando consigo os homens de sua família, perseguiu Jacó por sete dias e o alcançou nos montes de Gileade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 24, 'Então, de noite, Deus veio em sonho a Labão, o arameu, e o advertiu: “Cuidado! Não diga nada a Jacó, não lhe faça promessas nem ameaças”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 25, 'Labão alcançou Jacó, que estava acampado nos montes de Gileade. Então Labão e os homens se acamparam ali também.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 26, 'Ele perguntou a Jacó: Que foi que você fez? Não só me enganou como também raptou minhas filhas como se fossem prisioneiras de guerra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 27, 'Por que você me enganou, fugindo em segredo, sem avisar-me? Eu teria celebrado a sua partida com alegria e cantos, ao som dos tamborins e das harpas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 28, 'Você sequer me deixou beijar meus netos e minhas filhas para despedir-me deles. Você foi insensato.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 29, 'Tenho poder para prejudicá-los; mas, na noite passada, o Deus do pai de vocês me advertiu: “Cuidado! Não diga nada a Jacó, não lhe faça promessas nem ameaças”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 30, 'Agora, se você partiu porque tinha saudade da casa de seu pai, por que roubou meus deuses?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 31, 'Jacó respondeu a Labão: Tive medo, pois pensei que você tiraria suas filhas de mim à força.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 32, 'Quanto aos seus deuses, quem for encontrado com eles não ficará vivo. Na presença dos nossos parentes, veja você mesmo se está aqui comigo qualquer coisa que lhe pertença, e, se estiver, leve-a de volta. Ora, Jacó não sabia que Raquel os havia roubado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 33, 'Então Labão entrou na tenda de Jacó, e nas tendas de Lia e de suas duas servas, mas nada encontrou. Depois de sair da tenda de Lia, entrou na tenda de Raquel.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 34, 'Raquel tinha colocado os ídolos dentro da sela do seu camelo e estava sentada em cima. Labão vasculhou toda a tenda, mas nada encontrou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 35, 'Raquel disse ao pai: “Não se irrite, meu senhor, por não poder me levantar em sua presença, pois estou com o fluxo das mulheres”. Ele procurou os ídolos, mas não os encontrou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 36, 'Jacó ficou irado e queixou-se a Labão­: Qual foi meu crime? Que pecado cometi para que você me persiga furiosamente?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 37, 'Você já vasculhou tudo o que me pertence. Encontrou algo que lhe pertença? Então coloque tudo aqui na frente dos meus parentes e dos seus, e que eles julguem entre nós dois.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 38, 'Vinte anos estive com você. Suas ovelhas e cabras nunca abortaram, e jamais comi um só carneiro do seu rebanho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 39, 'Eu nunca levava a você os animais despedaçados por feras; eu mesmo assumia o prejuízo. E você pedia contas de todo animal roubado de dia ou de noite.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 40, 'O calor me consumia de dia, e o frio de noite, e o sono fugia dos meus olhos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 41, 'Foi assim nos vinte anos em que fiquei em sua casa. Trabalhei para você catorze anos em troca de suas duas filhas e seis anos por seus rebanhos, e dez vezes você alterou o meu salário.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 42, 'Se o Deus de meu pai, o Deus de Abraão, o Temor de Isaque, não estivesse comigo, certamente você me despediria de mãos vazias. Mas Deus viu o meu sofrimento e o trabalho das minhas mãos e, na noite passada, ele manifestou a sua decisão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 43, 'Labão respondeu a Jacó: As mulheres são minhas filhas, os filhos são meus, os rebanhos são meus. Tudo o que você vê é meu. Que posso fazer por essas minhas filhas ou pelos filhos que delas nasceram?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 44, 'Façamos agora, eu e você, um acordo que sirva de testemunho entre nós dois.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 45, 'Então Jacó tomou uma pedra e a colocou em pé como coluna.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 46, 'E disse aos seus parentes: “Juntem algumas pedras”. Eles apanharam pedras e as amontoaram. Depois comeram ali, ao lado do monte de pedras.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 47, 'Labão o chamou Jegar-Saaduta, e Jacó o chamou Galeede.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 48, 'Labão disse: “Este monte de pedras é uma testemu­nha entre mim e você, no dia de hoje”. Por isso foi chamado Galeede.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 49, 'Foi também chamado Mispá, por­que ele declarou: Que o Senhor nos vigie, a mim e a você, quando estivermos separados um do outro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 50, 'Se você maltratar minhas filhas ou menosprezá-las, tomando outras mulheres além delas, ainda que ninguém saiba, lembre-se de que Deus é testemunha entre mim e você.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 51, 'Disse ainda Labão a Jacó: Aqui estão este monte de pedras e esta coluna que coloquei entre mim e você.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 52, 'São testemunhas de que não passarei para o lado de lá para prejudicá-lo, nem você passará para o lado de cá para prejudicar-me.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 53, 'Que o Deus de Abraão, o Deus de Naor, o Deus do pai deles, julgue entre nós. Então Jacó fez um juramento em nome do Temor de seu pai Isaque.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 54, 'Ofereceu um sacrifício no monte e chamou os parentes que lá estavam para uma refeição. Depois de comerem, passaram a noite ali.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 31, 55, 'Na manhã seguinte, Labão beijou seus netos e suas filhas e os abençoou, e depois voltou para a sua terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 1, 'Jacó também seguiu o seu caminho, e anjos de Deus vieram ao encontro dele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 2, 'Quando Jacó os avistou, disse: “Este é o exército de Deus!” Por isso deu àquele lugar o nome de Maanaim.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 3, 'Jacó mandou mensageiros adiante dele a seu irmão Esaú, na região de Seir, território de Edom.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 4, 'E lhes ordenou: Vocês dirão o seguinte ao meu senhor Esaú: Assim diz teu servo Jacó: Morei na casa de Labão e com ele permaneci até agora.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 5, 'Tenho bois e jumentos, ovelhas e cabras, servos e servas. Envio agora esta mensagem ao meu senhor, para que me recebas bem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 6, 'Quando os mensageiros voltaram a Jacó, disseram-lhe: “Fomos até seu irmão Esaú, e ele está vindo ao seu encontro, com quatrocentos homens”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 7, 'Jacó encheu-se de medo e foi tomado de angústia. Então dividiu em dois grupos todos os que estavam com ele, bem como as ovelhas, as cabras, os bois e os camelos,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 8, 'pois assim pensou: “Se Esaú vier e atacar um dos grupos, o outro poderá escapar”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 9, 'Então Jacó orou: Ó Deus de meu pai Abraão, Deus de meu pai Isaque, ó Senhor que me disseste: “Volte para a sua terra e para os seus parentes e eu o farei prosperar”;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 10, 'não sou digno de toda a bondade e lealdade com que trataste o teu servo. Quando atravessei o Jordão eu tinha apenas o meu cajado, mas agora possuo duas caravanas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 11, 'Livra-me, rogo-te, das mãos de meu irmão Esaú, porque tenho medo que ele venha nos atacar, tanto a mim como às mães e às crianças.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 12, 'Pois tu prometeste: “Esteja certo de que eu o farei prosperar e farei os seus descendentes tão numerosos como a areia do mar, que não se pode contar”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 13, 'Depois de passar ali a noite, escolheu entre os seus rebanhos um presente para o seu irmão Esaú:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 14, 'duzentas cabras e vinte bodes, duzentas ovelhas e vinte carneiros,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 15, 'trinta fêmeas de camelo com seus filhotes, quarenta vacas e dez touros, vinte jumentas e dez jumentos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 16, 'Colocou cada rebanho sob o cuidado de um servo, e disse-lhes: “Vão à minha frente e mantenham certa distância entre um rebanho e outro”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 17, 'Ao que ia à frente deu a seguinte instrução: Quando meu irmão Esaú encontrar-se com você e lhe perguntar: “A quem você pertence, para onde vai e de quem é todo este rebanho à sua frente?”,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 18, 'você responderá: É do teu servo Jacó. É um presente para o meu senhor Esaú; e ele mesmo está vindo atrás de nós.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 19, 'Também instruiu o segundo, o terceiro e todos os outros que acompanhavam os rebanhos: Digam também a mesma coisa a Esaú quando o encontrarem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 20, 'E acrescentem: Teu servo Jacó está vindo atrás de nós”. Porque pensava: “Eu o apaziguarei com esses presentes que estou enviando antes de mim; mais tarde, quando eu o vir, talvez me receba”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 21, 'Assim os presentes de Jacó seguiram à sua frente; ele, porém, passou a noite no acampamento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 22, 'Naquela noite Jacó levantou-se, tomou suas duas mulheres, suas duas servas e seus onze filhos para atravessar o lugar de passagem do Jaboque.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 23, 'Depois de havê-los feito atravessar o ribeiro, fez passar também tudo o que possuía.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 24, 'E Jacó ficou sozinho. Então veio um homem que se pôs a lutar com ele até o amanhecer.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 25, 'Quando o homem viu que não poderia dominá-lo, tocou na articulação da coxa de Jacó, de forma que lhe deslocou a coxa, enquanto lutavam.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 26, 'Então o homem disse: “Deixe-me ir, pois o dia já desponta”. Mas Jacó lhe respondeu: “Não te deixarei ir, a não ser que me abençoes”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 27, 'O homem lhe perguntou: “Qual é o seu nome?” “Jacó”, respondeu ele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 28, 'Então disse o homem: “Seu nome não será mais Jacó, mas sim Israel, porque você lutou com Deus e com homens e venceu”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 29, 'Prosseguiu Jacó: “Peço-te que digas o teu nome”. Mas ele respondeu: “Por que pergunta o meu nome?” E o abençoou ali.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 30, 'Jacó chamou àquele lugar Peniel, pois disse: “Vi a Deus face a face e, todavia, minha vida foi poupada”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 31, 'Ao nascer do sol atravessou Peniel, mancando por causa da coxa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 32, 32, 'Por isso, até o dia de hoje, os israelitas não comem o músculo ligado à articulação do quadril, porque nesse músculo Jacó foi ferido.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 33, 1, 'Quando Jacó olhou e viu que Esaú estava se aproximando, com quatrocentos homens, dividiu as crianças entre Lia, Raquel e as duas servas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 33, 2, 'Colocou as servas e os seus filhos à frente, Lia e seus filhos depois, e Raquel com José por último.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 33, 3, 'Ele mesmo passou à frente e, ao aproximar-se do seu irmão, curvou-se até o chão sete vezes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 33, 4, 'Mas Esaú correu ao encontro de Jacó e abraçou-se ao seu pescoço, e o beijou. E eles choraram.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 33, 5, 'Então Esaú ergueu o olhar e viu as mulheres e as crianças. E perguntou: “Quem são estes?” Jacó respondeu: “São os filhos que Deus concedeu ao teu servo”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 33, 6, 'Então as servas e os seus filhos se aproximaram e se curvaram.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 33, 7, 'Depois, Lia e os seus filhos vieram e se curvaram. Por último, chegaram José e Raquel, e também se curvaram.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 33, 8, 'Esaú perguntou: “O que você pretende com todos os rebanhos que encontrei pelo caminho?” “Ser bem recebido por ti, meu senhor”, respondeu Jacó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 33, 9, 'Disse, porém, Esaú: “Eu já tenho muito, meu irmão. Guarde para você o que é seu”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 33, 10, 'Mas Jacó insistiu: Não! Se te agradaste de mim, aceita este presente de minha parte, porque ver a tua face é como contemplar a face de Deus; além disso, tu me recebeste tão bem!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 33, 11, 'Aceita, pois, o presente que te foi trazido, pois Deus tem sido favorável para comigo, e eu já tenho tudo o que necessito. Jacó tanto insistiu que Esaú acabou aceitando.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 33, 12, 'Então disse Esaú: “Vamos seguir em frente. Eu o acompanharei”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 33, 13, 'Jacó, porém, lhe disse: Meu senhor sabe que as crianças são frágeis e que estão sob os meus cuidados ovelhas e vacas que amamentam suas crias. Se forçá-las demais na caminhada, um só dia que seja, todo o rebanho morrerá.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 33, 14, 'Por isso, meu senhor, vai à frente do teu servo, e eu sigo atrás, devagar, no passo dos rebanhos e das crianças, até que eu chegue ao meu senhor em Seir.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 33, 15, 'Esaú sugeriu: “Permita-me, então, deixar alguns homens com você”. Jacó perguntou: “Mas para quê, meu senhor? Ter sido bem recebido já me foi suficiente!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 33, 16, 'Naquele dia Esaú voltou para Seir.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 33, 17, 'Jacó, todavia, foi para Sucote, onde construiu uma casa para si e abrigos para o seu gado. Foi por isso que o lugar recebeu o nome de Sucote.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 33, 18, 'Tendo voltado de Padã-Arã, Jacó chegou a salvo à cidade de Siquém, em Canaã, e acampou próximo da cidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 33, 19, 'Por cem peças de prata comprou dos filhos de Hamor, pai de Siquém, a parte do campo onde tinha armado acampamento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 33, 20, 'Ali edificou um altar e lhe cha­mou El Elohe Israel.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 1, 'Certa vez, Diná, a filha que Lia dera a Jacó, saiu para conhecer as mulheres daquela terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 2, 'Siquém, filho de Hamor, o heveu, governador daquela região, viu-a, agarrou-a e a violentou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 3, 'Mas o seu coração foi atraído por Diná, filha de Jacó, e ele amou a moça e falou-lhe com ternura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 4, 'Por isso Siquém foi dizer a seu pai Hamor: “Consiga-me aquela moça para que seja minha mulher”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 5, 'Quando Jacó soube que sua filha Diná tinha sido desonrada, seus filhos estavam no campo, com os rebanhos; por isso esperou calado até que regressassem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 6, 'Então Hamor, pai de Siquém, foi conversar com Jacó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 7, 'Quando os filhos de Jacó voltaram do campo e souberam de tudo, ficaram profundamente entristecidos e irados, porque Siquém tinha cometido um ato vergonhoso em Israel, ao deitar-se com a filha de Jacó — coisa que não se faz.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 8, 'Mas Hamor lhes disse: Meu filho Siquém apaixonou-se pela filha de vocês. Por favor, entreguem-na a ele para que seja sua mulher.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 9, 'Casem-se entre nós; dêem-nos suas filhas e tomem para si as nossas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 10, 'Estabeleçam-se entre nós. A terra está aberta para vocês: habitem-na, façam comércio nela e adquiram propriedades.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 11, 'Então Siquém disse ao pai e aos irmãos de Diná: Concedam-me este favor, e eu lhes darei o que me pedirem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 12, 'Aumentem quanto quiserem o preço e o presente pela noiva, e pagarei o que me pedirem. Tão somente me dêem a moça por mulher.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 13, 'Os filhos de Jacó, porém, responderam com falsidade a Siquém e a seu pai Hamor, por ter Siquém desonrado Diná, a irmã deles.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 14, 'Disseram: Não podemos fazer isso; jamais entregaremos nossa irmã a um homem que não seja circuncidado. Seria uma vergonha para nós.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 15, 'Daremos nosso consentimento a vocês com uma condição: que vocês se tornem como nós, circuncidando todos os do sexo masculino.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 16, 'Só então lhes daremos as nossas filhas e poderemos casar-nos com as suas. Nós nos estabeleceremos entre vocês e seremos um só povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 17, 'Mas se não aceitarem circuncidar-se, tomaremos nossa irmã e partire­mos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 18, 'A proposta deles pareceu boa a Hamor e a seu filho Siquém.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 19, 'O jovem, que era o mais respeitado de todos os da casa de seu pai, não demorou em cumprir o que pediram, porque realmente gostava da filha de Jacó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 20, 'Assim Hamor e seu filho Siquém dirigiram-se à porta da cidade para conversar com os seus concidadãos. E disseram:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 21, 'Esses homens são de paz. Permitam que eles habitem em nossa terra e façam comércio entre nós; a terra tem bastante lugar para eles. Poderemos casar com as suas filhas, e eles com as nossas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 22, 'Mas eles só consentirão em viver conosco como um só povo sob a condição de que todos os nossos homens sejam circuncidados, como eles.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 23, 'Lembrem-se de que os seus rebanhos, os seus bens e todos os seus outros animais passarão a ser nossos. Aceitemos então a condição para que se estabeleçam em nosso meio.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 24, 'Todos os que saíram para reunir-se à porta da cidade concordaram com Hamor e com seu filho Siquém, e todos os homens e meninos da cidade foram circuncidados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 25, 'Três dias depois, quando ainda sofriam dores, dois filhos de Jacó, Simeão e Levi, irmãos de Diná, pegaram suas espadas e atacaram a cidade desprevenida, matando todos os homens.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 26, 'Mataram ao fio da espada Hamor e seu filho Siquém, tiraram Diná da casa de Siquém e partiram.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 27, 'Vieram então os outros filhos de Jacó e, passando pelos corpos, saquearam a cidade onde sua irmã tinha sido desonrada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 28, 'Apoderaram-se das ovelhas, dos bois e dos jumentos, e de tudo o que havia na cidade e no campo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 29, 'Levaram as mulheres e as crianças, e saquearam todos os bens e tudo o que havia nas casas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 30, 'Então Jacó disse a Simeão e a Levi: “Vocês me puseram em grandes apuros, atraindo sobre mim o ódio dos cananeus e dos ferezeus, habitantes desta terra. Somos poucos, e se eles juntarem suas forças e nos atacarem, eu e a minha família seremos destruídos”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 34, 31, 'Mas eles responderam: “Está certo ele tratar nossa irmã como uma prostituta?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 1, 'Deus disse a Jacó: “Suba a Betel e estabeleça-se lá, e faça um altar ao Deus que lhe apareceu quando você fugia do seu irmão Esaú”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 2, 'Disse, pois, Jacó aos de sua casa e a todos os que estavam com ele: Livrem-se dos deuses estrangeiros que estão entre vocês, purifiquem-se e troquem de roupa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 3, 'Venham! Vamos subir a Betel, onde farei um altar ao Deus que me ouviu no dia da minha angústia e que tem estado comigo por onde tenho andado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 4, 'Então entregaram a Jacó todos os deuses estrangeiros que possuíam e os brincos que usavam nas orelhas, e Jacó os enterrou ao pé da grande árvore, próximo a Siquém.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 5, 'Quando eles partiram, o terror de Deus caiu de tal maneira sobre as cidades ao redor que ninguém ousou perseguir os filhos de Jacó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 6, 'Jacó e todos os que com ele estavam chegaram a Luz, que é Betel, na terra de Canaã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 7, 'Nesse lugar construiu um altar e lhe deu o nome de El-Betel, porque ali Deus havia se revelado a ele, quando fugia do seu irmão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 8, 'Débora, ama de Rebeca, morreu e foi sepultada perto de Betel, ao pé do Carvalho, que por isso foi chamado Alom-Bacute.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 9, 'Depois que Jacó retornou de Padã-Arã, Deus lhe apareceu de novo e o abençoou,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 10, 'di­zendo: “Seu nome é Jacó, mas você não será mais chamado Jacó; seu nome será Israel”. Assim lhe deu o nome de Israel.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 11, 'E Deus ainda lhe disse: Eu sou o Deus todo-poderoso; seja prolífero e multiplique-se. De você procederão uma nação e uma comunidade de nações, e reis estarão entre os seus descendentes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 12, 'A terra que dei a Abraão e a Isaque, dou a você; e também aos seus futuros descendentes darei esta terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 13, 'A seguir, Deus elevou-se do lugar onde estivera falando com Jacó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 14, 'Jacó levantou uma coluna de pedra no lugar em que Deus lhe falara, e derramou sobre ela uma oferta de bebidas e a ungiu com óleo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 15, 'Jacó deu o nome de Betel ao lugar onde Deus tinha falado com ele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 16, 'Eles partiram de Betel, e quando ainda estavam a certa distância de Efrata, Raquel começou a dar à luz com grande dificuldade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 17, 'E, enquanto sofria muito, tentando dar à luz, a parteira lhe disse: “Não tenha medo, pois você ainda terá outro menino”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 18, 'Já a ponto de sair-lhe a vida, quando estava morrendo, deu ao filho o nome de Benoni. Mas o pai deu-lhe o nome de Benjamim.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 19, 'Assim morreu Raquel e foi sepultada junto do caminho de Efrata, que é Belém.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 20, 'Sobre a sua sepultura Jacó levantou uma coluna, e até o dia de hoje aquela coluna marca o túmulo de Raquel.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 21, 'Israel partiu novamente e armou acampamento adiante de Migdal-Éder.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 22, 'Na época em que Israel vivia naquela região, Rúben deitou-se com Bila, concubina de seu pai. E Israel ficou sabendo disso. Jacó teve doze filhos:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 23, 'Estes foram seus filhos com Lia: Rúben, o filho mais velho de Jacó, Simeão, Levi, Judá, Issacar e Zebulom.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 24, 'Estes foram seus filhos com Raquel: José e Benjamim.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 25, 'Estes foram seus filhos com Bila, serva de Raquel: Dã e Naftali.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 26, 'Estes foram seus filhos com Zilpa, serva de Lia: Gade e Aser. Foram esses os filhos de Jacó, nascidos em Padã-Arã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 27, 'Depois Jacó foi visitar seu pai Isaque em Manre, perto de Quiriate-Arba, que é Hebrom, onde Abraão e Isaque tinham morado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 28, 'Isaque viveu cento e oitenta anos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 35, 29, 'Morreu em idade bem avançada e foi reunido aos seus antepassados. E seus filhos Esaú e Jacó o sepultaram.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 1, 'Esta é a história da família de Esaú, que é Edom.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 2, 'Esaú casou-se com mulheres de Canaã: com Ada, filha de Elom, o hitita, e com Oolibama, filha de Aná e neta de Zibeão, o heveu;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 3, 'e também com Basemate, filha de Ismael e irmã de Nebaiote.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 4, 'Ada deu a Esaú um filho chamado Elifaz; Basemate deu-lhe Reuel;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 5, 'e Ooli­bama deu-lhe Jeús, Jalão e Corá. Esses foram os filhos de Esaú que lhe nasceram em Canaã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 6, 'Esaú tomou suas mulheres, seus filhos e filhas e todos os de sua casa, assim como os seus rebanhos, todos os outros animais e todos os bens que havia adquirido em Canaã, e foi para outra região, para longe do seu irmão Jacó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 7, 'Os seus bens eram tantos que eles já não podiam morar juntos; a terra onde estavam vivendo não podia sustentá-los, por causa dos seus rebanhos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 8, 'Por isso Esaú, que é Edom, fixou-se nos montes de Seir.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 9, 'Este é o registro da descendência de Esaú, pai dos edomitas, nos montes de Seir.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 10, 'Estes são os nomes dos filhos de Esaú: Elifaz, filho de Ada, mulher de Esaú; e Reuel, filho de Basemate, mulher de Esaú.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 11, 'Estes foram os filhos de Elifaz: Temã, Omar, Zefô, Gaetã e Quenaz.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 12, 'Elifaz, filho de Esaú, tinha uma concubina chamada Timna, que lhe deu um filho chamado Amaleque. Foram esses os netos de Ada, mulher de Esaú.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 13, 'Estes foram os filhos de Reuel: Naate, Zerá, Samá e Mizá. Foram esses os netos de Basemate, mulher de Esaú.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 14, 'Estes foram os filhos de Oolibama, mulher de Esaú, filha de Aná e neta de Zibeão, os quais ela deu a Esaú: Jeús, Jalão e Corá.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 15, 'Foram estes os chefes dentre os descendentes de Esaú: Os filhos de Elifaz, filho mais velho de Esaú: Temã, Omar, Zefô, Quenaz,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 16, 'Corá, Gae­tã e Amaleque. Foram esses os chefes descendentes de Elifaz em Edom; eram netos de Ada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 17, 'Foram estes os filhos de Reuel, filho de Esaú: Os chefes Naate, Zerá, Samá e Mizá. Foram esses os chefes descendentes de Reuel em Edom; netos de Basemate, mulher de Esaú.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 18, 'Foram estes os filhos de Oolibama, mulher de Esaú: Os chefes Jeús, Jalão e Corá. Foram esses os chefes descendentes de Oolibama, mulher de Esaú, filha de Aná.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 19, 'Foram esses os filhos de Esaú, que é Edom, e esses foram os seus chefes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 20, 'Estes foram os filhos de Seir, o horeu, que estavam habitando aquela região: Lotã, So­bal, Zibeão e Aná,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 21, 'Disom, Ézer e Disã. Esses filhos de Seir foram chefes dos horeus no território de Edom.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 22, 'Estes foram os filhos de Lotã: Hori e Hemã. Timna era irmã de Lotã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 23, 'Estes foram os filhos de Sobal: Alvã, Manaate, Ebal, Sefô e Onã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 24, 'Estes foram os filhos de Zibeão: Aiá e Aná. Foi este Aná que descobriu as fontes de águas quentes no deserto, quando levava para pastar os jumentos de Zibeão, seu pai.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 25, 'Estes foram os filhos de Aná: Disom e Oolibama, a filha de Aná.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 26, 'Estes foram os filhos de Disom: Hendã, Esbã, Itrã e Querã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 27, 'Estes foram os filhos de Ézer: Bilã, Zaavã e Acã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 28, 'Estes foram os filhos de Disã: Uz e Arã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 29, 'Estes foram os chefes dos horeus: Lotã, Sobal, Zibeão, Aná,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 30, 'Disom, Ézer e Disã. Esses foram os chefes dos horeus, de acordo com as suas divisões tribais na região de Seir.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 31, 'Estes foram os reis que reinaram no território de Edom antes de haver rei entre os israelitas:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 32, 'Belá, filho de Beor, reinou em Edom. Sua cidade chamava-se Dinabá.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 33, 'Quando Belá morreu, foi sucedido por Jobabe, filho de Zerá, de Bozra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 34, 'Jobabe morreu, e Husã, da terra dos temanitas, foi o seu sucessor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 35, 'Husã morreu, e Hadade, filho de Bedade, que tinha derrotado os midianitas na terra de Moabe, foi o seu sucessor. Sua cidade chamava-se Avite.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 36, 'Hadade morreu, e Samlá de Masreca foi o seu sucessor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 37, 'Samlá morreu, e Saul, de Reobote, próxima ao Eufrates, foi o seu sucessor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 38, 'Saul morreu, e Baal-Hanã, filho de Acbor, foi o seu sucessor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 39, 'Baal-Hanã, filho de Acbor, morreu, e Hadade foi o seu sucessor. Sua cidade chamava-se Paú, e o nome de sua mulher era Meetabel, filha de Matrede, neta de Mezaabe.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 40, 'Estes foram os chefes descendentes de Esaú, conforme os seus nomes, clãs e regiões: Timna, Alva, Jetete,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 41, 'Oolibama, Elá, Pi­nom,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 42, 'Quenaz, Temã, Mibzar,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 36, 43, 'Mag­diel e Irã. Foram esses os chefes de Edom; cada um deles fixou-se numa região da terra que ocuparam. Os edomitas eram descendentes de Esaú.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 1, 'Jacó habitou na terra de Canaã, onde seu pai tinha vivido como estrangeiro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 2, 'Esta é a história da família de Jacó: Quando José tinha dezessete anos, pastoreava os rebanhos com os seus irmãos. Ajudava os filhos de Bila e os filhos de Zilpa, mulheres de seu pai; e contava ao pai a má fama deles.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 3, 'Ora, Israel gostava mais de José do que de qualquer outro filho, porque lhe havia nascido em sua velhice; por isso mandou fazer para ele uma túnica longa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 4, 'Quando os seus irmãos viram que o pai gostava mais dele do que de qualquer outro filho, odiaram-no e não conseguiam falar com ele amigavelmente.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 5, 'Certa vez, José teve um sonho e, quando o contou a seus irmãos, eles passaram a odiá-lo ainda mais.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 6, '“Ouçam o sonho que tive”, disse-lhes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 7, '“Estávamos amarrando os feixes de trigo no campo, quando o meu feixe se levantou e ficou em pé, e os seus feixes se ajuntaram ao redor do meu e se curvaram diante dele.”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 8, 'Seus irmãos lhe disseram: “Então você vai reinar sobre nós? Quer dizer que você vai nos governar?” E o odiaram ainda mais, por causa do sonho e do que tinha dito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 9, 'Depois teve outro sonho e o contou aos seus irmãos: “Tive outro sonho, e desta vez o sol, a lua e onze estrelas se curvavam diante de mim”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 10, 'Quando o contou ao pai e aos irmãos, o pai o repreendeu e lhe disse: “Que sonho foi esse que você teve? Será que eu, sua mãe, e seus irmãos viremos a nos curvar até o chão diante de você?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 11, 'Assim seus irmãos tiveram ciúmes dele; o pai, no entanto, refletia naquilo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 12, 'Os irmãos de José tinham ido cuidar dos rebanhos do pai, perto de Siquém,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 13, 'e Israel disse a José: “Como você sabe, seus irmãos estão apascentando os rebanhos perto de Siquém. Quero que você vá até lá”. “Sim, senhor”, respondeu ele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 14, 'Disse-lhe o pai: “Vá ver se está tudo bem com os seus irmãos e com os rebanhos, e traga-me notícias”. Jacó o enviou quando estava no vale de Hebrom. Mas José se perdeu quando se aproximava de Siquém;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 15, 'um homem o encontrou vagueando pelos campos e lhe perguntou: “Que é que você está procurando?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 16, 'Ele respondeu: “Procuro meus irmãos. Pode me dizer onde eles estão apascentando os rebanhos?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 17, 'Respondeu o homem: “Eles já partiram daqui. Eu os ouvi dizer: “Vamos para Dotã””. Assim José foi em busca dos seus irmãos e os encontrou perto de Dotã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 18, 'Mas eles o viram de longe e, antes que chegasse, planejaram matá-lo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 19, '“Lá vem aquele sonhador!”, diziam uns aos outros.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 20, '“É agora! Vamos matá-lo e jogá-lo num destes poços, e diremos que um animal selvagem o devorou. Veremos então o que será dos seus sonhos.”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 21, 'Quando Rúben ouviu isso, tentou livrá-lo das mãos deles, dizendo: “Não lhe tiremos a vida!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 22, 'E acrescentou: “Não derramem sangue. Joguem-no naquele poço no deserto, mas não toquem nele”. Rúben propôs isso com a intenção de livrá-lo e levá-lo de volta ao pai.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 23, 'Chegando José, seus irmãos lhe arranca­ram a túnica longa,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 24, 'agarraram-no e o jogaram no poço, que estava vazio e sem água.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 25, 'Ao se assentarem para comer, viram ao longe uma caravana de ismaelitas que vinha de Gileade. Seus camelos estavam carregados de especiarias, bálsamo e mirra, que eles levavam para o Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 26, 'Judá disse então a seus irmãos: Que ganharemos se matarmos o nosso irmão e escondermos o seu sangue?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 27, 'Vamos vendê-lo aos ismaelitas. Não tocaremos nele, afinal é nosso irmão, é nosso próprio sangue. E seus irmãos concordaram.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 28, 'Quando os mercadores ismaelitas de Midiã se aproximaram, seus irmãos tiraram José do poço e o venderam por vinte peças de prata aos ismaelitas, que o levaram para o Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 29, 'Quando Rúben voltou ao poço e viu que José não estava lá, rasgou suas vestes');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 30, 'e, voltando a seus irmãos, disse: “O jovem não está lá! Para onde irei agora?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 31, 'Então eles mataram um bode, mergulharam no sangue a túnica de José');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 32, 'e a mandaram ao pai com este recado: “Achamos isto. Veja se é a túnica de teu filho”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 33, 'Ele a reconheceu e disse: “É a túnica de meu filho! Um animal selvagem o devorou! José foi despedaçado!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 34, 'Então Jacó rasgou suas vestes, vestiu-se de pano de saco e chorou muitos dias por seu filho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 35, 'Todos os seus filhos e filhas vieram consolá-lo, mas ele recusou ser consolado, dizendo: “Não! Chorando descerei à sepultura para junto de meu filho”. E continuou a chorar por ele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 37, 36, 'Nesse meio tempo, no Egito, os midianitas venderam José a Potifar, oficial do faraó e capitão da guarda.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 1, 'Por essa época, Judá deixou seus ir­mãos e passou a viver na casa de um homem de Adulão, chamado Hira.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 2, 'Ali Judá encontrou a filha de um cananeu chamado Suá, e casou-se com ela. Ele a possuiu,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 3, 'ela engravidou e deu à luz um filho, ao qual ele deu o nome de Er.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 4, 'Tornou a engravidar, teve um filho e deu-lhe o nome de Onã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 5, 'Quando estava em Quezibe, ela teve ainda outro filho e chamou-o Selá.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 6, 'Judá escolheu uma mulher chamada Tamar para Er, seu filho mais velho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 7, 'Mas o Senhor reprovou a conduta perversa de Er, filho mais velho de Judá, e por isso o matou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 8, 'Então Judá disse a Onã: “Case-se com a mulher do seu irmão, cumpra as suas obrigações de cunhado para com ela e dê uma descendência a seu irmão”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 9, 'Mas Onã sabia que a descendência não seria sua; assim, toda vez que possuía a mulher do seu irmão, derramava o sêmen no chão para evitar que seu irmão tivesse descendência.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 10, 'O Senhor reprovou o que ele fazia, e por isso o matou também.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 11, 'Disse então Judá à sua nora Tamar: “More como viúva na casa de seu pai até que o meu filho Selá cresça”, porque temia que ele viesse a morrer, como os seus irmãos. Assim Tamar foi morar na casa do pai.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 12, 'Tempos depois morreu a mulher de Judá, filha de Suá. Passado o luto, Judá foi ver os tosquiadores do seu rebanho em Timna com o seu amigo Hira, o adulamita.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 13, 'Quando foi dito a Tamar: “Seu sogro está a caminho de Timna para tosquiar suas ovelhas”,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 14, 'ela trocou suas roupas de viúva, cobriu-se com um véu para se disfarçar e foi sentar-se à entrada de Enaim, que fica no caminho de Tim­na. Ela fez isso porque viu que, embora Selá já fosse crescido, ela não lhe tinha sido dada em casamento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 15, 'Quando a viu, Judá pensou que fosse uma prostituta, porque ela havia encoberto o rosto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 16, 'Não sabendo que era a sua nora, dirigiu-se a ela, à beira da estrada, e disse: “Venha cá, quero deitar-me com você”. Ela lhe perguntou: “O que você me dará para deitar-se comigo?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 17, 'Disse ele: “Eu lhe mandarei um cabritinho do meu rebanho”. E ela perguntou: “Você me deixará alguma coisa como garantia até que o mande?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 18, 'Disse Judá: “Que garantia devo dar-lhe?” Respondeu ela: “O seu selo com o cordão, e o cajado que você tem na mão”. Ele os entre­gou e a possuiu, e Tamar engravidou dele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 19, 'Ela se foi, tirou o véu e tornou a vestir as roupas de viúva.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 20, 'Judá mandou o cabritinho por meio de seu amigo adulamita, a fim de reaver da mulher sua garantia, mas ele não a encontrou,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 21, 'e perguntou aos homens do lugar: “Onde está a prostituta cultual que costuma ficar à beira do caminho de Enaim?” Eles responderam: “Aqui não há nenhuma prostituta cultual”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 22, 'Assim ele voltou a Judá e disse: “Não a encontrei. Além disso, os homens do lugar disseram que lá não há nenhuma prostituta cultual”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 23, 'Disse Judá: “Fique ela com o que lhe dei. Não quero que nos tornemos objeto de zombaria. Afinal de contas, mandei a ela este cabritinho, mas você não a encontrou”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 24, 'Cerca de três meses mais tarde, disseram a Judá: “Sua nora Tamar prostituiu-se, e na sua prostituição ficou grávida”. Disse Judá: “Tragam-na para fora e queimem-na viva!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 25, 'Quando ela estava sendo levada para fora, mandou o seguinte recado ao sogro: “Estou grávida do homem que é dono destas coisas”. E acrescentou: “Veja se o senhor reconhece a quem pertencem este selo, este cordão e este cajado”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 26, 'Judá os reconheceu e disse: “Ela é mais justa do que eu, pois eu devia tê-la entregue a meu filho Selá”. E não voltou a ter relações com ela.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 27, 'Quando lhe chegou a época de dar à luz, havia gêmeos em seu ventre.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 28, 'Enquanto ela dava à luz, um deles pôs a mão para fora; então a parteira pegou um fio vermelho e amarrou o pulso do menino, dizendo: “Este saiu primeiro”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 29, 'Mas quando ele recolheu a mão, seu irmão saiu e ela disse: “Então você conseguiu uma brecha para sair!” E deu-lhe o nome de Perez.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 38, 30, 'Depois saiu seu irmão que estava com o fio vermelho no pulso, e foi-lhe dado o nome de Zerá.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 39, 1, 'José havia sido levado para o Egito, onde o egípcio Potifar, oficial do faraó e capitão da guarda, comprou-o dos ismaelitas que o tinham levado para lá.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 39, 2, 'O Senhor estava com José, de modo que este prosperou e passou a morar na casa do seu senhor egípcio.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 39, 3, 'Quando este percebeu que o Senhor estava com ele e que o fazia prosperar em tudo o que realizava,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 39, 4, 'agradou-se de José e tornou-o administrador de seus bens. Potifar deixou a seu cuidado a sua casa e lhe confiou tudo o que possuía.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 39, 5, 'Desde que o deixou cuidando de sua casa e de todos os seus bens, o Senhor abençoou a casa do egípcio por causa de José. A bênção do Senhor estava sobre tudo o que Potifar possuía, tanto em casa como no campo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 39, 6, 'Assim, deixou ele aos cuidados de José tudo o que tinha, e não se preocupava com coisa alguma, exceto com sua própria comida. José era atraente e de boa aparência,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 39, 7, 'e, depois de certo tempo, a mulher do seu senhor começou a cobiçá-lo e o convidou: “Venha, deite-se comigo!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 39, 8, 'Mas ele se recusou e lhe disse: Meu senhor não se preocupa com coisa alguma de sua casa, e tudo o que tem deixou aos meus cuidados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 39, 9, 'Ninguém desta casa está acima de mim. Ele nada me negou, a não ser a senhora, porque é a mulher dele. Como poderia eu, então, cometer algo tão perverso e pecar contra Deus?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 39, 10, 'Assim, embora ela insistisse com José dia após dia, ele se recusava a deitar-se com ela e evitava ficar perto dela.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 39, 11, 'Um dia ele entrou na casa para fazer suas tarefas, e nenhum dos empregados ali se encontrava.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 39, 12, 'Ela o agarrou pelo manto e voltou a convidá-lo: “Vamos, deite-se comigo!” Mas ele fugiu da casa, deixando o manto na mão dela.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 39, 13, 'Quando ela viu que, ao fugir, ele tinha deixado o manto em sua mão,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 39, 14, 'chamou os empregados e lhes disse: Vejam, este hebreu nos foi trazido para nos insultar! Ele entrou aqui e tentou abusar de mim, mas eu gritei.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 39, 15, 'Quando me ouviu gritar por socorro, largou seu manto ao meu lado e fugiu da casa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 39, 16, 'Ela conservou o manto consigo até que o senhor de José chegasse à casa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 39, 17, 'Então repetiu-lhe a história: Aquele escravo hebreu que você nos trouxe aproximou-se de mim para me insultar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 39, 18, 'Mas, quando gritei por socorro, ele largou seu manto ao meu lado e fugiu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 39, 19, 'Quando o seu senhor ouviu o que a sua mulher lhe disse: “Foi assim que o seu escravo me tratou”, ficou indignado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 39, 20, 'Mandou buscar José e lançou-o na prisão em que eram postos os prisioneiros do rei. José ficou na prisão,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 39, 21, 'mas o Senhor estava com ele e o tratou com bondade, concedendo-lhe a simpatia do carcereiro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 39, 22, 'Por isso o carcereiro encarregou José de todos os que estavam na prisão, e ele se tornou responsável por tudo o que lá sucedia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 39, 23, 'O carcereiro não se preocupava com nada do que estava a cargo de José, porque o Senhor estava com José e lhe concedia bom êxito em tudo o que realizava.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 40, 1, 'Algum tempo depois, o copeiro e o padeiro do rei do Egito fizeram uma ofensa ao seu senhor, o rei do Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 40, 2, 'O faraó irou-se com os dois oficiais, o chefe dos copeiros e o chefe dos padeiros,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 40, 3, 'e mandou prendê-los na casa do capitão da guarda, na prisão em que José estava.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 40, 4, 'O capitão da guarda os deixou aos cuidados de José, que os servia. Depois de certo tempo,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 40, 5, 'o copeiro e o padeiro do rei do Egito, que estavam na prisão, sonharam. Cada um teve um sonho, ambos na mesma noite, e cada sonho tinha a sua própria interpretação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 40, 6, 'Quando José foi vê-los na manhã seguinte, notou que estavam abatidos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 40, 7, 'Por isso perguntou aos oficiais do faraó, que também estavam presos na casa do seu senhor: “Por que hoje vocês estão com o semblante triste?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 40, 8, 'Eles responderam: “Tivemos sonhos, mas não há quem os interprete”. Disse-lhes José: “Não são de Deus as interpretações? Contem-me os sonhos”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 40, 9, 'Então o chefe dos copeiros contou o seu sonho a José: Em meu sonho vi diante de mim uma videira,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 40, 10, 'com três ramos. Ela brotou, floresceu e deu uvas que amadureciam em cachos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 40, 11, 'A taça do faraó estava em minha mão. Peguei as uvas, e as espremi na taça do faraó, e a entreguei em sua mão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 40, 12, 'Disse-lhe José: Esta é a interpretação: os três ramos são três dias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 40, 13, 'Dentro de três dias o faraó vai exaltá-lo e restaurá-lo à sua posição, e você servirá a taça na mão dele, como costumava fazer quando era seu copeiro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 40, 14, 'Quando tudo estiver indo bem com você, lembre-se de mim e seja bondoso comigo; fale de mim ao faraó e tire-me desta prisão,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 40, 15, 'pois fui trazido à força da terra dos hebreus, e também aqui nada fiz para ser jogado neste calabouço.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 40, 16, 'Ouvindo o chefe dos padeiros essa interpretação favorável, disse a José: Eu também tive um sonho: sobre a minha cabeça havia três cestas de pão branco.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 40, 17, 'Na cesta de cima havia todo tipo de pães e doces que o faraó aprecia, mas as aves vinham comer da cesta que eu trazia na cabeça.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 40, 18, 'E disse José: Esta é a interpretação: as três cestas são três dias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 40, 19, 'Dentro de três dias o faraó vai decapitá-lo e pendurá-lo numa árvore. E as aves comerão a sua carne.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 40, 20, 'Três dias depois era o aniversário do faraó, e ele ofereceu um banquete a todos os seus conselheiros. Na presença deles reapresentou o chefe dos copeiros e o chefe dos padeiros;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 40, 21, 'restaurou à sua posição o chefe dos copeiros, de modo que ele voltou a ser aquele que servia a taça do faraó,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 40, 22, 'mas ao chefe dos padeiros mandou enforcar, como José lhes dissera em sua interpretação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 40, 23, 'O chefe dos copeiros, porém, não se lembrou de José; ao contrário, esqueceu-se dele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 1, 'Ao final de dois anos, o faraó teve um sonho. Ele estava em pé junto ao rio Nilo,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 2, 'quando saíram do rio sete vacas belas e gordas, que começaram a pastar entre os juncos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 3, 'Depois saíram do rio mais sete vacas, feias e magras, que foram para junto das outras, à beira do Nilo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 4, 'Então as vacas feias e magras comeram as sete vacas belas e gordas. Nisso o faraó acordou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 5, 'Tornou a adormecer e teve outro sonho. Sete espigas de trigo, graúdas e boas, cresciam no mesmo pé.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 6, 'Depois brotaram outras sete espigas, mirradas e ressequidas pelo vento leste.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 7, 'As espigas mirradas engoliram as sete espigas graúdas e cheias. Então o faraó acordou; era um sonho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 8, 'Pela manhã, perturbado, mandou chamar todos os magos e sábios do Egito e lhes contou os sonhos, mas ninguém foi capaz de interpretá-los.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 9, 'Então o chefe dos copeiros disse ao fa­raó: Hoje me lembro de minhas faltas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 10, 'Certa vez o faraó ficou irado com os seus dois servos e mandou prender-me junto com o chefe dos padeiros, na casa do capitão da guarda.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 11, 'Certa noite cada um de nós teve um sonho, e cada sonho tinha uma interpretação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 12, 'Pois bem, havia lá conosco um jovem hebreu, servo do capitão da guarda. Contamos a ele os nossos sonhos, e ele os interpretou, dando a cada um de nós a interpretação do seu próprio sonho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 13, 'E tudo aconteceu conforme ele nos dissera: eu fui restaurado à minha posição e o outro foi enforcado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 14, 'O faraó mandou chamar José, que foi trazido depressa do calabouço. Depois de se barbear e trocar de roupa, apresentou-se ao faraó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 15, 'O faraó disse a José: “Tive um sonho que ninguém consegue interpretar. Mas ouvi falar que você, ao ouvir um sonho, é capaz de interpretá-lo”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 16, 'Respondeu-lhe José: “Isso não depende de mim, mas Deus dará ao faraó uma resposta favorável”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 17, 'Então o faraó contou o sonho a José: Sonhei que estava em pé, à beira do Nilo,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 18, 'quando saíram do rio sete vacas, belas e gordas, que começaram a pastar entre os juncos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 19, 'Depois saíram outras sete, raquíticas, muito feias e magras. Nunca vi vacas tão feias em toda a terra do Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 20, 'As vacas magras e feias comeram as sete vacas gordas que tinham aparecido primeiro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 21, 'Mesmo depois de havê-las comido, não parecia que o tivessem feito, pois continuavam tão magras como antes. Então acordei.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 22, 'Depois tive outro sonho. Vi sete espigas de cereal, cheias e boas, que cresciam num mesmo pé.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 23, 'Depois delas, brotaram outras sete, murchas e mirradas, ressequidas pelo vento leste.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 24, 'As espigas magras engoliram as sete espigas boas. Contei isso aos magos, mas ninguém foi capaz de explicá-lo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 25, '“O faraó teve um único sonho”, disse-lhe José. “Deus revelou ao faraó o que ele está para fazer.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 26, 'As sete vacas boas são sete anos, e as sete espigas boas são também sete anos; trata-se de um único sonho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 27, 'As sete vacas magras e feias que surgiram depois das outras, e as sete espigas mirradas, queimadas pelo vento leste, são sete anos. Serão sete anos de fome.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 28, 'É exatamente como eu disse ao faraó: Deus mostrou ao faraó aquilo que ele vai fazer.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 29, 'Sete anos de muita fartura estão para vir sobre toda a terra do Egito,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 30, 'mas depois virão sete anos de fome. Então todo o tempo de fartura será esquecido, pois a fome arruinará a terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 31, 'A fome que virá depois será tão rigorosa que o tempo de fartura não será mais lembrado na terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 32, 'O sonho veio ao faraó duas vezes porque a questão já foi decidida por Deus, que se apressa em realizá-la.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 33, 'Procure agora o faraó um homem criterioso e sábio e coloque-o no comando da terra do Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 34, 'O faraó também deve estabelecer supervisores para recolher um quinto da colheita do Egito durante os sete anos de fartura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 35, 'Eles deverão recolher o que puderem nos anos bons que virão e fazer estoques de trigo que, sob o controle do faraó, serão armazenados nas cidades.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 36, 'Esse estoque servirá de reserva para os sete anos de fome que virão sobre o Egito, para que a terra não seja arrasada pela fome.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 37, 'O plano pareceu bom ao faraó e a todos os seus conselheiros.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 38, 'Por isso o faraó lhes perguntou: “Será que vamos achar alguém como este homem, em quem está o espírito divino?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 39, 'Disse, pois, o faraó a José: Uma vez que Deus lhe revelou todas essas coisas, não há ninguém tão criterioso e sábio como você.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 40, 'Você terá o comando de meu palácio, e todo o meu povo se sujeitará às suas ordens. Somente em relação ao trono serei maior que você.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 41, 'E o faraó prosseguiu: “Entrego a você agora o comando de toda a terra do Egito”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 42, 'Em seguida o faraó tirou do dedo o seu anel-selo e o colocou no dedo de José. Mandou-o vestir linho fino e colocou uma corrente de ouro em seu pescoço.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 43, 'Também o fez subir em sua segunda carruagem real, e à frente os arautos iam gritando: “Abram caminho!”  Assim José foi colocado no comando de toda a terra do Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 44, 'Disse ainda o faraó a José: “Eu sou o faraó, mas sem a sua palavra ninguém poderá levantar a mão nem o pé em todo o Egito”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 45, 'O faraó deu a José o nome de Zafenate-Panéia e lhe deu por mulher Azenate, filha de Potífera, sacerdote de Om. Depois José foi inspecionar toda a terra do Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 46, 'José tinha trinta anos de idade quando começou a servir ao faraó, rei do Egito. Ele se ausentou da presença do faraó e foi percorrer todo o Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 47, 'Durante os sete anos de fartura a terra teve grande produção.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 48, 'José recolheu todo o excedente dos sete anos de fartura no Egito e o armazenou nas cidades. Em cada cidade ele armazenava o trigo colhido nas lavouras das redondezas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 49, 'Assim José estocou muito trigo, como a areia do mar. Tal era a quantidade que ele parou de anotar, porque ia além de toda medida.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 50, 'Antes dos anos de fome, Azenate, filha de Potífera, sacerdote de Om, deu a José dois filhos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 51, 'Ao primeiro, José deu o nome de Manassés, dizendo: “Deus me fez esquecer todo o meu sofrimento e toda a casa de meu pai”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 52, 'Ao segundo filho chamou Efraim, dizen­do: “Deus me fez prosperar na terra onde tenho sofrido”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 53, 'Assim chegaram ao fim os sete anos de fartura no Egito,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 54, 'e começaram os sete anos de fome, como José tinha predito. Houve fome em todas as terras, mas em todo o Egito havia alimento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 55, 'Quando todo o Egito começou a sofrer com a fome, o povo clamou ao faraó por comida, e este respondeu a todos os egípcios: “Dirijam-se a José e façam o que ele disser”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 56, 'Quando a fome já se havia espalhado por toda a terra, José mandou abrir os locais de armazenamento e começou a vender trigo aos egípcios, pois a fome se agravava em todo o Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 41, 57, 'E de toda a terra vinha gente ao Egito para comprar trigo de José, porquanto a fome se agravava em toda parte.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 1, 'Quando Jacó soube que no Egito havia trigo, disse a seus filhos: “Por que estão aí olhando uns para os outros?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 2, 'Disse ainda: “Ouvi dizer que há trigo no Egito. Desçam até lá e comprem trigo para nós, para que possamos continuar vivos e não morramos de fome”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 3, 'Assim dez dos irmãos de José desceram ao Egito para comprar trigo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 4, 'Jacó não deixou que Benjamim, irmão de José, fosse com eles, temendo que algum mal lhe acontecesse.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 5, 'Os filhos de Israel estavam entre outros que também foram comprar trigo, por causa da fome na terra de Canaã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 6, 'José era o governador do Egito e era ele que vendia trigo a todo o povo da terra. Por isso, quando os irmãos de José chegaram, curvaram-se diante dele com o rosto em terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 7, 'José reconheceu os seus irmãos logo que os viu, mas agiu como se não os conhecesse, e lhes falou asperamente: “De onde vocês vêm?” Responderam eles: “Da terra de Canaã, para comprar comida”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 8, 'José reconheceu os seus irmãos, mas eles não o reconheceram.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 9, 'Lembrou-se então dos sonhos que tivera a respeito deles e lhes disse: “Vocês são espiões! Vieram para ver onde a nossa terra está desprotegida”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 10, 'Eles responderam: Não, meu senhor. Teus servos vieram comprar comida.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 11, 'Todos nós somos filhos do mesmo pai. Teus servos são homens honestos, e não espiões.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 12, 'Mas José insistiu: “Não! Vocês vieram ver onde a nossa terra está desprotegida”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 13, 'E eles disseram: “Teus servos eram doze irmãos, todos filhos do mesmo pai, na terra de Canaã. O caçula está agora em casa com o pai, e o outro já morreu”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 14, 'José tornou a afirmar: É como lhes falei: Vocês são espiões!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 15, 'Vocês serão postos à prova. Juro pela vida do faraó que vocês não sairão daqui, enquanto o seu irmão caçula não vier para cá.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 16, 'Mandem algum de vocês buscar o seu irmão enquanto os demais aguardam presos. Assim ficará provado se as suas palavras são verdadeiras ou não. Se não forem, juro pela vida do faraó que ficará confirmado que vocês são espiões!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 17, 'E os deixou presos três dias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 18, 'No terceiro dia, José lhes disse: Eu tenho temor de Deus. Se querem salvar sua vida, façam o seguinte:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 19, 'se vocês são homens honestos, deixem um dos seus irmãos aqui na prisão, enquanto os demais voltam, levando trigo para matar a fome das suas famílias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 20, 'Tragam-me, porém, o seu irmão caçula, para que se comprovem as suas palavras e vocês não tenham que morrer.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 21, 'Eles se prontificaram a fazer isso e disseram uns aos outros: “Certamente estamos sendo punidos pelo que fizemos a nosso irmão. Vimos como ele estava angustiado, quando nos implorava por sua vida, mas não lhe demos ouvidos; por isso nos sobreveio esta angústia”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 22, 'Rúben respondeu: “Eu não lhes disse que não maltratassem o menino? Mas vocês não quiseram me ouvir! Agora teremos que prestar contas do seu sangue”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 23, 'Eles, porém, não sabiam que José podia compreendê-los, pois ele lhes falava por meio de um intérprete.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 24, 'Nisso José retirou-se e começou a chorar, mas logo depois voltou e conversou de novo com eles. Então escolheu Simeão e mandou acorrentá-lo diante deles.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 25, 'Em seguida, José deu ordem para que enchessem de trigo suas bagagens, devolvessem a prata de cada um deles, colocando-a nas bagagens, e lhes dessem mantimentos para a viagem. E assim foi feito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 26, 'Eles puseram a carga de trigo sobre os seus jumentos e partiram.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 27, 'No lugar onde pararam para pernoitar, um deles abriu a bagagem para pegar forragem para o seu jumento e viu a prata na boca da bagagem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 28, 'E disse a seus irmãos: “Devolveram a minha prata. Está aqui em minha bagagem”. Tomados de pavor em seu coração e tremendo, disseram uns aos outros: “Que é isto que Deus fez conosco?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 29, 'Ao chegarem à casa de seu pai Jacó, na terra de Canaã, relataram-lhe tudo o que lhes acontecera, dizendo:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 30, 'O homem que governa aquele país falou asperamente conosco e nos tratou como espiões.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 31, 'Mas nós lhe asseguramos que somos homens honestos e não espiões.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 32, 'Dissemos também que éramos doze irmãos, filhos do mesmo pai, e que um já havia morrido e que o caçula estava com o nosso pai, em Canaã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 33, 'Então o homem que governa aquele país nos disse: “Vejamos se vocês são honestos: um dos seus irmãos ficará aqui comigo, e os outros poderão voltar e levar mantimentos para matar a fome das suas famílias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 34, 'Tragam-me, porém, o seu irmão caçula, para que eu comprove que vocês não são espiões, mas sim, homens honestos. Então lhes devolverei o irmão e os autorizarei a fazer negócios nesta terra”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 35, 'Ao esvaziarem as bagagens, dentro da bagagem de cada um estava a sua bolsa cheia de prata. Quando eles e seu pai viram as bolsas cheias de prata, ficaram com medo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 36, 'E disse-lhes seu pai Jacó: “Vocês estão tirando meus filhos de mim! Já fiquei sem José, agora sem Simeão e ainda querem levar Benjamim. Tudo está contra mim!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 37, 'Então Rúben disse ao pai: “Podes matar meus dois filhos se eu não o trouxer de volta. Deixa-o aos meus cuidados, e eu o trarei”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 42, 38, 'Mas o pai respondeu: “Meu filho não descerá com vocês; seu irmão está morto, e ele é o único que resta. Se qualquer mal lhe acontecer na viagem que estão por fazer, vocês farão estes meus cabelos brancos descerem à sepultura com tristeza”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 1, 'A fome continuava rigorosa na terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 2, 'Assim, quando acabou todo o trigo que os filhos de Jacó tinham trazido do Egito, seu pai lhes disse: “Voltem e comprem um pouco mais de comida para nós”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 3, 'Mas Judá lhe disse: O homem nos advertiu severamente: “Não voltem à minha presença, a não ser que tragam o seu irmão”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 4, 'Se enviares o nosso irmão conosco, desceremos e compraremos comida para ti.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 5, 'Mas se não o enviares conosco, não iremos, porque foi assim que o homem falou: “Não voltem à minha presença, a não ser que tragam o seu irmão”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 6, 'Israel perguntou: “Por que me causaram esse mal, contando àquele homem que tinham outro irmão?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 7, 'E lhe responderam: “Ele nos interrogou sobre nós e sobre nossa família. E também nos perguntou: “O pai de vocês ainda está vivo? Vocês têm outro irmão?” Nós simplesmente respondemos ao que ele nos perguntou. Como poderíamos saber que ele exigiria que levássemos o nosso irmão?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 8, 'Então disse Judá a Israel, seu pai: Deixa o jovem ir comigo e partiremos imediatamente, a fim de que tu, nós e nossas crianças sobrevivamos e não venhamos a morrer.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 9, 'Eu me comprometo pessoalmente pela segurança dele; podes me considerar responsável por ele. Se eu não o trouxer de volta e não o colocar bem aqui na tua presença, serei culpado diante de ti pelo resto da minha vida.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 10, 'Como se vê, se não tivéssemos demorado tanto, já teríamos ido e voltado duas vezes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 11, 'Então Israel, seu pai, lhes disse: Se tem que ser assim, que seja! Coloquem alguns dos melhores produtos da nossa terra na bagagem e levem-nos como presente ao tal homem: um pouco de bálsamo, um pouco de mel, algumas especiarias e mirra, algumas nozes de pistache e amêndoas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 12, 'Levem prata em dobro, e devolvam a prata que foi colocada de volta na boca da bagagem de vocês. Talvez isso tenha acontecido por engano.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 13, 'Peguem também o seu irmão e voltem àquele homem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 14, 'Que o Deus todo-poderoso lhes conceda misericórdia diante daquele homem, para que ele permita que o seu outro irmão e Benjamim voltem com vocês. Quanto a mim, se ficar sem filhos, sem filhos ficarei.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 15, 'Então os homens desceram ao Egito, levando o presente, prata em dobro e Benjamim, e foram à presença de José.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 16, 'Quando José viu Benjamim com eles, disse ao administrador de sua casa: “Leve estes homens à minha casa, mate um animal e prepare-o; eles almoçarão comigo ao meio-dia”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 17, 'Ele fez o que lhe fora ordenado e levou-os à casa de José.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 18, 'Eles ficaram com medo quando foram levados à casa de José, e pensaram: “Trouxeram-nos aqui por causa da prata que foi devolvida às nossas bagagens na primeira vez. Ele quer atacar-nos, subjugar-nos, tornar-nos escravos e tomar de nós os nossos jumentos”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 19, 'Por isso, dirigiram-se ao administrador da casa de José e lhe disseram à entrada da casa:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 20, 'Ouça, senhor! A primeira vez que viemos aqui foi realmente para comprar comida.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 21, 'Mas no lugar em que paramos para pernoitar, abrimos nossas bagagens e cada um de nós encontrou a prata que tinha trazido, na quantia exata. Por isso a trouxemos de volta conosco,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 22, 'além de mais prata, para comprar comida. Não sabemos quem pôs a prata em nossa bagagem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 23, '“Fiquem tranqüilos”, disse o administrador. “Não tenham medo. O seu Deus, o Deus de seu pai, foi quem lhes deu um tesouro em suas bagagens, porque a prata de vocês eu recebi.” Então soltou Simeão e o levou à presença deles.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 24, 'Em seguida os levou à casa de José, deu-lhes água para lavarem os pés e forragem para os seus jumentos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 25, 'Eles então prepararam o presente para a chegada de José ao meio-dia, porque ficaram sabendo que iriam almoçar ali.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 26, 'Quando José chegou, eles o presentearam com o que tinham trazido e curvaram-se diante dele até o chão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 27, 'Ele então lhes perguntou como passavam e disse em seguida: “Como vai o pai de vocês, o homem idoso de quem me falaram? Ainda está vivo?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 28, 'Eles responderam: “Teu servo, nosso pai, ainda vive e passa bem”. E se curvaram para prestar-lhe honra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 29, 'Olhando ao redor e vendo seu irmão Benjamim, filho de sua mãe, José perguntou: “É este o irmão caçula de quem me falaram?” E acrescentou: “Deus lhe conceda graça, meu filho”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 30, 'Profundamente emocionado por causa de seu irmão, José apressou-se em sair à procura de um lugar para chorar, e entrando em seu quarto, chorou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 31, 'Depois de lavar o rosto, saiu e, controlando-se, disse: “Sirvam a comida”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 32, 'Serviram a ele em separado dos seus irmãos e também dos egípcios que comiam com ele, porque os egípcios não podiam comer com os hebreus, pois isso era sacrilégio para eles.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 33, 'Seus irmãos foram colocados à mesa perante ele por ordem de idade, do mais velho ao mais moço, e olhavam perplexos uns para os outros.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 43, 34, 'Então ­lhes serviram da comida da mesa de José, e a porção de Benjamim era cinco vezes maior que a dos outros. E eles festejaram e beberam à vontade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 1, 'José deu as seguintes ordens ao administrador de sua casa: Encha as bagagens desses homens com todo o mantimento que puderem carregar e coloque a prata de cada um na boca de sua bagagem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 2, 'Depois coloque a minha taça, a taça de prata, na boca da bagagem do caçula, junto com a prata paga pelo trigo. E ele fez tudo conforme as ordens de José.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 3, 'Assim que despontou a manhã, despediram os homens com os seus jumentos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 4, 'Ainda não tinham se afastado da cidade, quando José disse ao administrador de sua casa: Vá atrás daqueles homens e, quando os alcançar, diga-lhes: Por que retribuíram o bem com o mal?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 5, 'Não é esta a taça que o meu senhor usa para beber e para fazer adivinhações? Vocês cometeram grande maldade!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 6, 'Quando ele os alcançou, repetiu-lhes essas palavras.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 7, 'Mas eles lhe responderam: Por que o meu senhor diz isso? Longe dos seus servos fazer tal coisa!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 8, 'Nós lhe trouxemos de volta, da terra de Canaã, a prata que encontramos na boca de nossa bagagem. Como roubaríamos prata ou ouro da casa do seu senhor?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 9, 'Se algum dos seus servos for encontrado com ela, morrerá; e nós, os demais, seremos escravos do meu senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 10, 'E disse ele: “Concordo. Somente quem for encontrado com ela será meu escravo; os demais estarão livres”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 11, 'Cada um deles descarregou depressa a sua bagagem e abriu-a.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 12, 'O administrador começou então a busca, desde a bagagem do mais velho até a do mais novo. E a taça foi encontrada na bagagem de Benjamim.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 13, 'Diante disso, eles rasgaram as suas vestes. Em seguida, todos puseram a carga de novo em seus jumentos e retornaram à cidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 14, 'Quando Judá e seus irmãos chegaram à casa de José, ele ainda estava lá. Então eles se lançaram ao chão perante ele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 15, 'E José lhes per­guntou: “Que foi que vocês fizeram? Vocês não sabem que um homem como eu tem poder para adivinhar?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 16, 'Respondeu Judá: “O que diremos a meu senhor? Que podemos falar? Como podemos provar nossa inocência? Deus trouxe à luz a culpa dos teus servos. Agora somos escravos do meu senhor, como também aquele que foi encontrado com a taça”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 17, 'Disse, porém, José: “Longe de mim fazer tal coisa! Somente aquele que foi encontrado com a taça será meu escravo. Os demais podem voltar em paz para a casa do seu pai”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 18, 'Então Judá dirigiu-se a ele, dizendo: Por favor, meu senhor, permite-me dizer-te uma palavra. Não se acenda a tua ira contra o teu servo, embora sejas igual ao próprio faraó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 19, 'Meu senhor perguntou a estes seus servos se ainda tínhamos pai e algum outro irmão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 20, 'E nós respondemos: Temos um pai já idoso, cujo filho caçula nasceu-lhe em sua velhice. O irmão deste já morreu, e ele é o único filho da mesma mãe que restou, e seu pai o ama muito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 21, 'Então disseste a teus servos que o trouxessem a ti para que os teus olhos pudessem vê-lo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 22, 'E nós respondemos a meu senhor que o jovem não poderia deixar seu pai, pois, caso o fizesse, seu pai morreria.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 23, 'Todavia disseste a teus servos que se o nosso irmão caçula não viesse conosco, nunca mais veríamos a tua face.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 24, 'Quando voltamos a teu servo, a meu pai, contamos-lhe o que o meu senhor tinha dito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 25, 'Quando o nosso pai nos mandou voltar para comprar um pouco mais de comida,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 26, 'nós lhe dissemos: Só poderemos voltar para lá, se o nosso irmão caçula for conosco. Pois não pode­remos ver a face daquele homem, a não ser que o nosso irmão caçula esteja conosco.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 27, 'Teu servo, meu pai, nos disse então: “Vocês sabem que minha mulher me deu apenas dois filhos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 28, 'Um deles se foi, e eu disse: Com certeza foi despedaçado. E até hoje, nunca mais o vi.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 29, 'Se agora vocês também levarem este de mim, e algum mal lhe acontecer, a tristeza que me causarão fará com que os meus cabelos brancos desçam à sepultura”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 30, 'Agora, pois, se eu voltar a teu servo, a meu pai, sem levar o jovem conosco, logo que meu pai, que é tão apegado a ele,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 31, 'perceber que o jovem não está conosco, morrerá. Teus servos farão seu velho pai descer seus cabelos brancos à sepultura com tristeza.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 32, 'Além disso, teu servo garantiu a segurança do jovem a seu pai, dizendo-lhe: Se eu não o trouxer de volta, suportarei essa culpa diante de ti pelo resto da minha vida!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 33, 'Por isso agora te peço, por favor, deixa o teu servo ficar como escravo do meu senhor no lugar do jovem e permite que ele volte com os seus irmãos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 44, 34, 'Como poderei eu voltar a meu pai sem levar o jovem comigo? Não! Não posso ver o mal que sobreviria a meu pai.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 1, 'A essa altura, José já não podia mais conter-se diante de todos os que ali estavam, e gritou: “Façam sair a todos!” Assim, ninguém mais estava presente quando José se revelou a seus irmãos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 2, 'E ele se pôs a chorar tão alto que os egípcios o ouviram, e a notícia chegou ao palácio do faraó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 3, 'Então disse José a seus irmãos: “Eu sou José! Meu pai ainda está vivo?” Mas os seus irmãos ficaram tão pasmados diante dele que não conseguiam responder-lhe.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 4, '“Cheguem mais perto”, disse José a seus irmãos. Quando eles se aproximaram, disse-lhes: “Eu sou José, seu irmão, aquele que vocês venderam ao Egito!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 5, 'Agora, não se aflijam nem se recriminem por terem me vendido para cá, pois foi para salvar vidas que Deus me enviou adiante de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 6, 'Já houve dois anos de fome na terra, e nos próximos cinco anos não haverá cultivo nem colheita.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 7, 'Mas Deus me enviou à frente de vocês para lhes preservar um remanescente nesta terra e para salvar-lhes a vida com grande livramento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 8, 'Assim, não foram vocês que me mandaram para cá, mas sim o próprio Deus. Ele me tornou ministro do faraó, e me fez administrador de todo o palácio e governador de todo o Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 9, 'Voltem depressa a meu pai e digam-lhe: Assim diz o seu filho José: Deus me fez senhor de todo o Egito. Vem para cá, não te demores.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 10, 'Tu viverás na região de Gósen e ficarás perto de mim — tu, os teus filhos, os teus netos, as tuas ovelhas, os teus bois e todos os teus bens.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 11, 'Eu te sustentarei ali, porque ainda haverá cinco anos de fome. Do contrário, tu, a tua família e todos os teus rebanhos acabarão na miséria.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 12, 'Vocês estão vendo com os seus próprios olhos, e meu irmão Benjamim também, que realmente sou eu que estou falando com vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 13, 'Contem a meu pai quanta honra me prestam no Egito e tudo o que vocês mesmos testemunharam. E tragam meu pai para cá depressa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 14, 'Então ele se lançou chorando sobre o seu irmão Benjamim e o abraçou, e Benjamim também o abraçou, chorando.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 15, 'Em seguida beijou todos os seus irmãos e chorou com eles. E só depois os seus irmãos conseguiram conversar com ele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 16, 'Quando se ouviu no palácio do faraó que os irmãos de José haviam chegado, o faraó e todos os seus conselheiros se alegraram.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 17, 'Disse então o faraó a José: Diga a seus irmãos que ponham as cargas nos seus animais, voltem para a terra de Canaã');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 18, 'e retornem para cá, trazendo seu pai e suas famílias. Eu lhes darei o melhor da terra do Egito e vocês poderão desfrutar a fartura desta terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 19, 'Mande-os também levar carruagens do Egito para trazerem as suas mulheres, os seus filhos e seu pai.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 20, 'Não se preocupem com os seus bens, pois o melhor de todo o Egito será de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 21, 'Assim fizeram os filhos de Israel. José lhes providenciou carruagens, como o faraó tinha ordenado, e também mantimentos para a viagem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 22, 'A cada um deu uma muda de roupa nova, mas a Benjamim deu trezentas peças de prata e cinco mudas de roupa nova.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 23, 'E a seu pai enviou dez jumentos carregados com o melhor do que havia no Egito e dez jumentas carregadas de trigo, pão e outras provisões para a viagem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 24, 'Depois despediu-se dos seus irmãos e, ao partirem, disse-lhes: “Não briguem pelo caminho!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 25, 'Assim partiram do Egito e voltaram a seu pai Jacó, na terra de Canaã,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 26, 'e lhe deram a notícia: “José ainda está vivo! Na verdade ele é o governador de todo o Egito”. O coração de Jacó quase parou! Não podia acreditar neles.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 27, 'Mas, quando lhe relataram tudo o que José lhes dissera, e vendo Jacó, seu pai, as carruagens que José enviara para buscá-lo, seu espírito reviveu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 45, 28, 'E Israel disse: “Basta! Meu filho José ainda está vivo. Irei vê-lo antes que eu morra”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 1, 'Israel partiu com tudo o que lhe pertencia. Ao chegar a Berseba, ofereceu sacrifícios ao Deus de Isaque, seu pai.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 2, 'E Deus falou a Israel por meio de uma visão noturna: “Jacó! Jacó!” “Eis-me aqui”, respondeu ele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 3, '“Eu sou Deus, o Deus de seu pai”, disse ele. “Não tenha medo de descer ao Egito, porque lá farei de você uma grande nação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 4, 'Eu mesmo descerei ao Egito com você e certamente o trarei de volta. E a mão de José fechará os seus olhos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 5, 'Então Jacó partiu de Berseba. Os filhos de Israel levaram seu pai Jacó, seus filhos e as suas mulheres nas carruagens que o faraó tinha enviado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 6, 'Também levaram os seus rebanhos e os bens que tinham adquirido em Canaã. Assim Jacó foi para o Egito com toda a sua descendência.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 7, 'Levou consigo para o Egito seus filhos, seus netos, suas filhas e suas netas, isto é, todos os seus descendentes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 8, 'Estes são os nomes dos israelitas, Jacó e seus descendentes, que foram para o Egito: Rúben, o filho mais velho de Jacó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 9, 'Estes foram os filhos de Rúben: Enoque, Palu, Hezrom e Carmi.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 10, 'Estes foram os filhos de Simeão: Jemuel, Jamim, Oade, Jaquim, Zoare Saul, filho de uma cananéia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 11, 'Estes foram os filhos de Levi: Gérson, Coate e Merari.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 12, 'Estes foram os filhos de Judá: Er, Onã, Selá, Perez e Zerá. Er e Onã morreram na terra de Canaã. Estes foram os filhos de Perez: Hezrom e Hamul.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 13, 'Estes foram os filhos de Issacar: Tolá, Puá, Jasube e Sinrom.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 14, 'Estes foram os filhos de Zebulom: Serede, Elom e Jaleel.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 15, 'Foram esses os filhos que Lia deu a Jacó em Padã-Arã, além de sua filha Diná. Seus descendentes eram ao todo trinta e três.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 16, 'Estes foram os filhos de Gade: Zefom, Hagi, Suni, Esbom, Eri, Arodi e Areli.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 17, 'Estes foram os filhos de Aser: Imna, Isvá, Isvi e Berias, e a irmã deles, Sera. Estes foram os filhos de Berias: Héber e Malquiel.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 18, 'Foram esses os dezesseis descendentes que Zilpa, serva que Labão tinha dado à sua filha Lia, deu a Jacó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 19, 'Estes foram os filhos de Raquel, mulher de Jacó: José e Benjamim.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 20, 'Azenate, filha de Potífera, sacerdote de Om, deu dois filhos a José no Egito: Manassés e Efraim.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 21, 'Estes foram os filhos de Benjamim: Belá, Bequer, Asbel, Gera, Naamã, Eí, Rôs, Mupim, Hupim e Arde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 22, 'Foram esses os catorze descendentes que Raquel deu a Jacó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 23, 'O filho de Dã foi Husim.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 24, 'Estes foram os filhos de Naftali: Jazeel, Guni, Jezer e Silém.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 25, 'Foram esses os sete descendentes que Bila, serva que Labão tinha dado à sua filha Raquel, deu a Jacó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 26, 'Todos os que foram para o Egito com Jacó, todos os seus descendentes, sem contar as mulheres de seus filhos, totalizaram sessenta e seis pessoas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 27, 'Com mais os dois filhos que nas­ceram a José no Egito, os membros da família de Jacó que foram para o Egito chegaram a setenta.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 28, 'Ora, Jacó enviou Judá à sua frente a José, para saber como ir a Gósen. Quando lá chegaram,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 29, 'José, de carruagem pronta, partiu para Gósen para encontrar-se com seu pai Israel. Assim que o viu, correu para abraçá-lo e, abraçado a ele, chorou longamente.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 30, 'Israel disse a José: “Agora já posso morrer, pois vi o seu rosto e sei que você ainda está vivo”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 31, 'Então José disse aos seus irmãos e a toda a família de seu pai: Vou partir e informar ao faraó que os meus irmãos e toda a família de meu pai, que viviam em Canaã, vieram para cá.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 32, 'Direi que os homens são pastores, cuidam de rebanhos, e trouxeram consigo suas ovelhas, seus bois e tudo quanto lhes pertence.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 33, 'Quando o faraó mandar chamá-los e perguntar: “Em que vocês trabalham?”,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 46, 34, 'respondam-lhe assim: “Teus servos criam rebanhos desde pequenos, como o fizeram nossos antepassados”. Assim lhes será permitido habitar na região de Gósen, pois todos os pastores são desprezados pelos egípcios.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 1, 'José foi dar as notícias ao faraó: “Meu pai e meus irmãos chegaram de Canaã com suas ovelhas, seus bois e tudo o que lhes pertence, e estão agora em Gósen”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 2, 'Depois escolheu cinco de seus irmãos e os apresentou ao faraó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 3, 'Perguntou-lhes o faraó: “Em que vocês trabalham?” Eles lhe responderam: “Teus servos são pastores, como os nossos antepassados”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 4, 'Disseram-lhe ainda: “Viemos morar aqui por uns tempos, porque a fome é rigorosa em Canaã, e os rebanhos de teus servos não têm pastagem. Agora, por favor, permite que teus servos se estabeleçam em Gósen”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 5, 'Então o faraó disse a José: Seu pai e seus irmãos vieram a você,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 6, 'e a terra do Egito está a sua disposição; faça com que seu pai e seus irmãos habitem na melhor parte da terra. Deixe-os morar em Gósen. E se você vê que alguns deles são competentes, coloque-os como responsáveis por meu rebanho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 7, 'Então José levou seu pai Jacó ao faraó e o apresentou a ele. Depois Jacó abençoou o faraó,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 8, 'e este lhe perguntou: “Quantos anos o senhor tem?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 9, 'Jacó respondeu ao faraó: “São cento e trinta os anos da minha peregrinação. Foram poucos e difíceis e não chegam aos anos da peregrinação dos meus antepassados”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 10, 'Então, Jacó abençoou o faraó e retirou-se.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 11, 'José instalou seu pai e seus irmãos e deu-lhes propriedade na melhor parte das terras do Egito, na região de Ramessés, conforme a ordem do faraó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 12, 'Providenciou também sustento para seu pai, para seus irmãos e para toda a sua família, de acordo com o número de filhos de cada um.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 13, 'Não havia mantimento em toda a região, pois a fome era rigorosa; tanto o Egito como Canaã desfaleciam por causa da fome.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 14, 'José recolheu toda a prata que circulava no Egito e em Canaã, dada como pagamento do trigo que o povo comprava, e levou-a ao palácio do faraó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 15, 'Quando toda a prata do Egito e de Canaã se esgotou, todos os egípcios foram suplicar a José: “Dá-nos comida! Não nos deixes morrer só porque a nossa prata acabou”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 16, 'E José lhes disse: “Tragam então os seus rebanhos, e em troca lhes darei trigo, uma vez que a prata de vocês acabou”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 17, 'E trouxeram a José os rebanhos, e ele deu-lhes trigo em troca de cavalos, ovelhas, bois e jumentos. Durante aquele ano inteiro ele os sustentou em troca de todos os seus rebanhos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 18, 'O ano passou, e no ano seguinte voltaram a José, dizendo: Não temos como esconder de ti, meu senhor, que uma vez que a nossa prata acabou e os nossos rebanhos lhe pertencem, nada mais nos resta para oferecer, a não ser os nossos próprios corpos e as nossas terras.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 19, 'Não deixes que morramos e que as nossas terras pereçam diante dos teus olhos! Compra-nos, e compra as nossas terras, em troca de trigo, e nós, com as nossas terras, seremos escravos do faraó. Dá-nos sementes para que sobrevivamos e não morramos de fome, a fim de que a terra não fique desolada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 20, 'Assim, José comprou todas as terras do Egito para o faraó. Todos os egípcios tiveram que vender os seus campos, pois a fome os obrigou a isso. A terra tornou-se propriedade do faraó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 21, 'Quanto ao povo, José o reduziu à servidão, de uma à outra extremidade do Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 22, 'Somente as terras dos sacerdotes não foram compradas, porque, por lei, esses recebiam sustento regular do faraó, e disso viviam. Por isso não tiveram que vender as suas terras.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 23, 'Então José disse ao povo: Ouçam! Hoje comprei vocês e suas terras para o faraó; aqui estão as sementes para que cultivem a terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 24, 'Mas vocês darão a quinta parte das suas colheitas ao faraó. Os outros quatro quintos ficarão para vocês como sementes para os campos e como alimento para vocês, seus filhos e os que vivem em suas casas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 25, 'Eles disseram: “Meu senhor, tu nos salvaste a vida. Visto que nos favoreceste, seremos escravos do faraó”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 26, 'Assim, quanto à terra, José estabeleceu o seguinte decreto no Egito, que permanece até hoje: um quinto da produção pertence ao faraó. Somente as terras dos sacerdotes não se tornaram propriedade do faraó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 27, 'Os israelitas se estabeleceram no Egito, na região de Gósen. Lá adquiriram propriedades, foram prolíferos e multiplicaram-se muito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 28, 'Jacó viveu dezessete anos no Egito, e os anos da sua vida chegaram a cento e quarenta e sete.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 29, 'Aproximando-se a hora da sua morte, Israel chamou seu filho José e lhe disse: Se quer agradar-me, ponha a mão debaixo da minha coxa e prometa que será bondoso e fiel comigo: Não me sepulte no Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 30, 'Quando eu descansar com meus pais, leve-me daqui do Egito e sepulte-me junto a eles”. José respondeu: “Farei como o senhor me pede”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 47, 31, 'Mas Jacó insistiu: “Jure-me”. E José lhe jurou, e Israel curvou-se apoiado em seu bordão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 48, 1, 'Algum tempo depois, disseram a José: “Seu pai está doente”; e ele foi vê-lo, levando consigo seus dois filhos, Manassés e Efraim.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 48, 2, 'E anunciaram a Jacó: “Seu filho José veio vê-lo”. Israel reuniu suas forças e assentou-se na cama.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 48, 3, 'Então disse Jacó a José: O Deus todo-poderoso apareceu-me em Luz, na terra de Canaã, e ali me abençoou,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 48, 4, 'dizendo: “Eu o farei prolífero e o multiplicarei. Farei de você uma comunidade de povos e darei esta terra por propriedade perpétua aos seus descendentes”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 48, 5, 'Agora, pois, os seus dois filhos que lhe nasceram no Egito, antes da minha vinda para cá, serão reconhecidos como meus; Efraim e Manassés serão meus, como são meus Rúben e Simeão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 48, 6, 'Os filhos que lhe nascerem depois deles serão seus; serão convocados sob o nome dos seus irmãos para receberem sua herança.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 48, 7, 'Quando eu voltava de Padã, para minha tristeza Raquel morreu em Canaã, quando ainda estávamos a caminho, a pouca distância de Efrata. Eu a sepultei ali, ao lado do caminho para Efrata, que é Belém.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 48, 8, 'Quando Israel viu os filhos de José, perguntou: “Quem são estes?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 48, 9, 'Respondeu José a seu pai: “São os filhos que Deus me deu aqui”. Então Israel disse: “Traga-os aqui para que eu os abençoe”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 48, 10, 'Os olhos de Israel já estavam enfraquecidos por causa da idade avançada, e ele mal po­dia enxergar. Por isso José levou seus filhos para perto dele, e seu pai os beijou e os abraçou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 48, 11, 'E Israel disse a José: “Nunca pensei que veria a sua face novamente, e agora Deus me concede ver também os seus filhos!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 48, 12, 'Em seguida, José os tirou do colo de Israel e curvou-se com o rosto em terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 48, 13, 'E José tomou os dois, Efraim à sua direita, perto da mão esquerda de Israel, e Manassés à sua esquerda, perto da mão direita de Israel, e os aproximou dele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 48, 14, 'Israel, porém, estendeu a mão direita e a pôs sobre a cabeça de Efraim, embora este fosse o mais novo e, cruzando os braços, pôs a mão esquerda sobre a cabeça de Manassés, embora Manassés fosse o filho mais velho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 48, 15, 'E abençoou a José, dizendo: Que o Deus, a quem serviram meus pais Abraão e Isaque, o Deus que tem sido o meu pastorem toda a minha vida até o dia de hoje,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 48, 16, 'o Anjo que me redimiu de todo o mal, abençoe estes meninos. Sejam eles chamados pelo meu nome e pelos nomes de meus pais Abraão e Isaque, e cresçam muito na terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 48, 17, 'Quando José viu seu pai colocar a mão direita sobre a cabeça de Efraim, não gostou; por isso pegou a mão do pai, a fim de mudá-la da cabeça de Efraim para a de Manassés,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 48, 18, 'e lhe disse: “Não, meu pai, este aqui é o mais velho; ponha a mão direita sobre a cabeça dele”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 48, 19, 'Mas seu pai recusou-se e respondeu: “Eu sei, meu filho, eu sei. Ele também se tornará um povo, também será grande. Apesar disso, seu irmão mais novo será maior do que ele, e seus descendentes se tornarão muitos povos”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 48, 20, 'Assim, Jacó os abençoou naquele dia, dizendo: “O povo de Israel usará os seus nomes para abençoar uns aos outros com esta expressão: Que Deus faça a você como fez a Efraim e a Manassés!” E colocou Efraim à frente de Manassés.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 48, 21, 'A seguir, Israel disse a José: Estou para morrer, mas Deus estará com vocês e os levará de volta à terra de seus antepassados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 48, 22, 'E a você, como alguém que está acima de seus irmãos, dou a região montanhosa que tomei dos amorreus com a minha espada e com o meu arco.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 1, 'Então Jacó chamou seus filhos e disse: Ajuntem-se a meu lado para que eu lhes diga o que lhes acontecerá nos dias que virão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 2, 'Reúnam-se para ouvir, filhos de Jacó; ouçam o que diz seu pai Israel.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 3, 'Rúben, você é meu primogênito, minha força, o primeiro sinal do meu vigor, superior em honra, superior em poder.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 4, 'Turbulento como as águas, já não será superior, porque você subiu à cama de seu pai, ao meu leito, e o desonrou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 5, 'Simeão e Levi são irmãos; suas espadas são armas de violência.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 6, 'Que eu não entre no conselho deles, nem participe da sua assembléia, porque em sua ira mataram homens e a seu bel-prazer aleijaram bois, cortando-lhes o tendão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 7, 'Maldita seja a sua ira, tão tremenda, e a sua fúria, tão cruel! Eu os dividirei pelas terras de Jacó e os dispersarei em Israel.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 8, 'Judá, seus irmãos o louvarão, sua mão estará sobre o pescoço dos seus inimigos; os filhos de seu pai se curvarão diante de você.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 9, 'Judá é um leão novo. Você vem subindo, filho meu, depois de matar a presa. Como um leão, ele se assenta; e deita-se como uma leoa; quem tem coragem de acordá-lo?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 10, 'O cetro não se apartará de Judá, nem o bastão de comando de seus descendentes, até que venha aquele a quem ele pertence, e a ele as nações obedecerão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 11, 'Ele amarrará seu jumento a uma videira e o seu jumentinho, ao ramo mais seleto; lavará no vinho as suas roupas, no sangue das uvas, as suas vestimentas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 12, 'Seus olhos serão mais escuros que o vinho; seus dentes, mais brancos que o leite.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 13, 'Zebulom morará à beira-mar e se tornará um porto para os navios; suas fronteiras se estenderão até Sidom.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 14, 'Issacar é um jumento forte, deitado entre as suas cargas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 15, 'Quando ele perceber como é bom o seu lugar de repouso e como é aprazível a sua terra, curvará seus ombros ao fardo e se submeterá a trabalhos forçados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 16, 'Dã defenderá o direito do seu povo como qualquer das tribos de Israel.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 17, 'Dã será uma serpente à beira da estrada, uma víbora à margem do caminho, que morde o calcanhar do cavaloe faz cair de costas o seu cavaleiro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 18, 'Ó Senhor, eu espero a tua libertação!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 19, 'Gade será atacado por um bando, mas é ele que o atacará e o perseguirá.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 20, 'A mesa de Aser será farta; ele oferecerá manjares de rei.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 21, 'Naftali é uma gazela solta, que por isso faz festa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 22, 'José é uma árvore frutífera, árvore frutífera à beira de uma fonte, cujos galhos passam por cima do muro. ');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 23, 'Com rancor arqueiros o atacaram, atirando-lhe flechas com hostilidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 24, 'Mas o seu arco permaneceu firme, os seus braços fortes, ágeis para atirar, pela mão do Poderoso de Jacó, pelo nome do Pastor, a Rocha de Israel,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 25, 'pelo Deus de seu pai, que ajuda você, o Todo-poderoso, que o abençoa com bênçãos dos altos céus, bênçãos das profundezas, bênçãos da fertilidade e da fartura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 26, 'As bênçãos de seu pai são superiores às bênçãos dos montes antigos, às delícias das colinas eternas. Que todas essas bênçãos repousem sobre a cabeça de José, sobre a fronte daquele que foi separado de entre os seus irmãos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 27, 'Benjamim é um lobo predador; pela manhã devora a presa e à tarde divide o despojo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 28, 'São esses os que formaram as doze tribos de Israel, e foi isso que seu pai lhes disse, ao abençoá-los, dando a cada um a bênção que lhe pertencia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 29, 'A seguir, Jacó deu-lhes estas instruções: Estou para ser reunido aos meus antepassados. Sepultem-me junto aos meus pais na caverna do campo de Efrom, o hitita,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 30, 'na caverna do campo de Macpela, perto de Manre, em Canaã, campo que Abraão comprou de Efrom, o hitita, como propriedade para sepultura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 31, 'Ali foram sepultados Abraão e Sara, sua mulher, e Isaque e Rebeca, sua mulher; ali também sepultei Lia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 32, '“Tanto o campo como a caverna que nele está foram comprados dos hititas”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 49, 33, 'Ao acabar de dar essas instruções a seus filhos, Jacó deitou-se, expirou e foi reunido aos seus antepassados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 1, 'José atirou-se sobre seu pai, chorou sobre ele e o beijou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 2, 'Em seguida deu ordens aos médicos, que estavam ao seu serviço, que embalsamassem seu pai Israel. E eles o embalsamaram.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 3, 'Levaram quarenta dias completos, pois esse era o tempo para o embalsamamento. E os egípcios choraram sua morte setenta dias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 4, 'Passados os dias de luto, José disse à corte do faraó: Se posso contar com a bondade de vocês, falem com o faraó em meu favor. Digam-lhe que');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 5, 'meu pai fez-me prestar-lhe o seguinte juramento: “Estou à beira da morte; sepulte-me no túmulo que preparei para mim na terra de Canaã”. Agora, pois, peçam-lhe que me permita partir e sepultar meu pai; logo depois voltarei.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 6, 'Respondeu o faraó: “Vá e faça o sepultamento de seu pai como este o fez jurar”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 7, 'Então José partiu para sepultar seu pai. Com ele foram todos os conselheiros do faraó, as autoridades da sua corte e todas as autoridades do Egito,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 8, 'e, além deles, todos os da família de José, os seus irmãos e todos os da casa de seu pai. Somente as crianças, as ovelhas ­e os bois foram deixados em Gósen.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 9, 'Carruagens e cavaleiros também o acompanharam. A comitiva era imensa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 10, 'Chegando à eira de Atade, perto do Jordão, lamentaram-se em alta voz, com grande amargura; e ali José guardou sete dias de pranto pela morte do seu pai.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 11, 'Quando os cananeus que lá habitavam viram aquele pranto na eira de Atade, disseram: “Os egípcios estão celebrando uma cerimônia de luto solene”. Por essa razão, aquele lugar, próximo ao Jordão, foi chamado Abel-Mizraim.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 12, 'Assim fizeram os filhos de Jacó o que este lhes havia ordenado:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 13, 'Levaram-no à terra de Canaã e o sepultaram na caverna do campo de Macpela, perto de Manre, que, com o campo, Abraão tinha comprado de Efrom, o hitita, para que lhe servisse de propriedade para sepultura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 14, 'Depois de sepultar seu pai, José voltou ao Egito, com os seus irmãos e com todos os demais que o tinham acompanhado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 15, 'Vendo os irmãos de José que seu pai havia morrido, disseram: “E se José tiver rancor contra nós e resolver retribuir todo o mal que lhe causamos?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 16, 'Então mandaram um recado a José, dizendo: Antes de morrer, teu pai nos ordenou');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 17, 'que te disséssemos o seguinte: “Peço-lhe que perdoe os erros e pecados de seus irmãos que o trataram com tanta maldade!” Agora, pois, perdoa os pecados dos servos do Deus do teu pai. Quando recebeu o recado, José chorou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 18, 'Depois vieram seus irmãos, prostraram-se diante dele e disseram: “Aqui estamos. Somos teus escravos!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 19, 'José, porém, lhes disse: Não tenham medo. Estaria eu no lugar de Deus?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 20, 'Vocês planejaram o mal contra mim, mas Deus o tornou em bem, para que hoje fosse preservada a vida de muitos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 21, 'Por isso, não tenham medo. Eu sustentarei vocês e seus filhos. E assim os tranqüilizou e lhes falou amavelmente.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 22, 'José permaneceu no Egito, com toda a família de seu pai. Viveu cento e dez anos');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 23, 'e viu a terceira geração dos filhos de Efraim. Além disso, recebeu como seus os filhos de Maquir, filho de Manassés.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 24, 'Antes de morrer José disse a seus irmãos: “Estou à beira da morte. Mas Deus certamente virá em auxílio de vocês e os tirará desta terra, levando-os para a terra que prometeu com juramento a Abraão, a Isaque e a Jacó”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 25, 'E José fez que os filhos de Israel lhe prestassem um juramento, dizendo-lhes: “Quando Deus intervier em favor de vocês, levem os meus ossos daqui”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 1, 50, 26, 'Morreu José com a idade de cento e dez anos. E, depois de embalsamado, foi colocado num sarcófago no Egito.');
INSERT INTO BIBLIA_BOOK (name) VALUES ('Êxodo');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 1, 1, 'São estes, pois, os nomes dos filhos de Israel que entraram com Jacó no Egito, cada um com a sua respectiva família:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 1, 2, 'Rúben, Simeão, Levi e Judá;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 1, 3, 'Issacar, Zebulom e Benja­mim;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 1, 4, 'Dã, Naftali, Gade e Aser.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 1, 5, 'Ao todo, os descendentes de Jacó eram setenta; José, po­rém, já estava no Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 1, 6, 'Ora, morreram José, todos os seus irmãos e toda aquela geração.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 1, 7, 'Os israelitas, po­rém, eram férteis, proliferaram, tornaram-se numerosos e fortaleceram-se muito, tanto que encheram o país.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 1, 8, 'Então subiu ao trono do Egito um novo rei, que nada sabia sobre José.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 1, 9, 'Disse ele ao seu povo: Vejam! O povo israelita é agora numero­so e mais forte que nós.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 1, 10, 'Temos que agir com astúcia, para que não se tornem ainda mais numerosos e, no caso de guerra, aliem-se aos nossos inimigos, lutem contra nós e fujam do país.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 1, 11, 'Estabeleceram, pois, sobre eles chefes de trabalhos forçados, para os oprimir com tare­fas pesadas. E assim os israelitas construíram para o faraó as cidades-celeiros de Pitom e Ramessés.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 1, 12, 'Todavia, quanto mais eram oprimi­dos, mais numerosos se tornavam e mais se espalhavam. Por isso os egípcios passaram a temer os israelitas,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 1, 13, 'e os sujeitaram a cruel escravidão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 1, 14, 'Tornaram-lhes a vida amarga, impondo-lhes a árdua tarefa de preparar o barro e fazer tijolos, e executar todo tipo de trabalho agrícola; em tudo os egípcios os sujeitavam a cruel escravidão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 1, 15, 'O rei do Egito ordenou às parteiras dos hebreus, que se chamavam Sifrá e Puá:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 1, 16, '“Quan­do vocês ajudarem as hebréias a dar à luz, verifiquem se é menino. Se for, matem-no; se for menina, deixem-na viver”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 1, 17, 'Todavia, as parteiras temeram a Deus e não obedeceram às ordens do rei do Egito; deixaram viver os meni­nos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 1, 18, 'En­tão o rei do Egito convocou as partei­ras e lhes perguntou: “Por que vocês fizeram isso? Por que deixaram viver os meninos?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 1, 19, 'Responderam as parteiras ao faraó: “As mulheres hebréias não são como as egípcias. São cheias de vigor e dão à luz antes de chegarem as parteiras”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 1, 20, 'Deus foi bondoso com as parteiras; e o povo ia se tornando ainda mais numeroso, cada vez mais forte.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 1, 21, 'Visto que as parteiras temeram a Deus, ele concedeu-lhes que tivessem suas próprias famílias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 1, 22, 'Por isso o faraó ordenou a todo o seu povo: “Lancem ao Nilo todo menino recém-nascido, mas deixem viver as meninas”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 1, 'Um homem da tribo de Levi casou-se com uma mulher da mesma tribo,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 2, 'e ela engravidou e deu à luz um filho. Vendo que era boni­to, ela o escondeu por três meses.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 3, 'Quan­do já não podia mais escondê-lo, pegou um cesto feito de junco e o vedou com piche e betume. Colocou nele o menino e deixou o cesto entre os juncos, à margem do Nilo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 4, 'A irmã do meni­no ficou observando de longe para ver o que lhe aconteceria.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 5, 'A filha do faraó descera ao Nilo para tomar banho. Enquanto isso, as suas servas andavam pela margem do rio. Nisso viu o cesto entre os juncos e mandou sua criada apanhá-lo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 6, 'Ao abri-lo, viu um bebê chorando. Ficou com pena dele e disse: “Este menino é dos hebreus”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 7, 'Então a irmã do menino aproximou-se e perguntou à filha do faraó: “A senhora quer que eu vá chamar uma mulher dos hebreus para amamentar e criar o menino?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 8, '“Quero”, respondeu ela. E a moça foi chamar a mãe do menino.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 9, 'Então a filha do faraó disse à mulher: “Leve este menino e amamente-o para mim, e eu lhe pagarei por is­so”. A mulher levou o menino e o amamentou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 10, 'Tendo o menino crescido, ela o levou à filha do faraó, que o adotou e lhe deu o nome de Moisés, dizendo: “Porque eu o tirei das águas”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 11, 'Certo dia, sendo Moisés já adulto, foi ao lugar onde estavam os seus irmãos e descobriu como era pesado o trabalho que realizavam. Viu também um egípcio espancar um dos hebreus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 12, 'Correu o olhar por todos os lados e, não vendo ninguém, matou o egípcio e o escondeu na areia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 13, 'No dia seguinte saiu e viu dois hebreus brigando. Então perguntou ao agressor: “Por que você está espancando o seu companheiro?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 14, 'O homem respondeu: “Quem o nomeou líder e juiz sobre nós? Quer matar-me como matou o egípcio?” Moisés teve medo e pensou: “Com certeza tudo já foi descoberto!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 15, 'Quando o faraó soube disso, procurou matar Moisés, mas este fugiu e foi morar na terra de Midiã. Ali assentou-se à beira de um poço.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 16, 'Ora, o sacerdote de Midiã tinha sete filhas. Elas foram buscar água para encher os bebedouros e dar de beber ao rebanho de seu pai.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 17, 'Alguns pastores se aproximaram e come­çaram a expulsá-las dali; Moisés, porém, veio em auxílio delas e deu água ao rebanho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 18, 'Quando as moças voltaram a seu pai Reuel, este lhes perguntou: “Por que voltaram tão cedo hoje?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 19, 'Elas responderam: “Um egípcio defendeu-nos dos pastores e ainda tirou água do poço para nós e deu de beber ao rebanho”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 20, '“Onde está ele?”, perguntou o pai a elas. “Por que o deixaram lá? Convidem-no para comer conosco.”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 21, 'Moisés aceitou e concordou também em morar na casa daquele homem; este lhe deu por mulher sua filha Zípora.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 22, 'Ela deu à luz um menino, a quem Moisés deu o nome de Gérson, dizendo: “Sou imigrante em terra estra­ngeira”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 23, 'Muito tempo depois, morreu o rei do Egito. Os israelitas gemiam e clamavam debaixo da escravidão; e o seu clamor subiu até Deus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 24, 'Ouviu Deus o lamento deles e lembrou-se da aliança que fizera com Abraão, Isaque e Jacó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 2, 25, 'Deus olhou para os israelitas e viu a situação deles.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 3, 1, 'Moisés pastoreava o rebanho de seu sogro Jetro, que era sacerdote de Midiã. Um dia levou o rebanho para o outro lado do deserto e chegou a Horebe, o monte de Deus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 3, 2, 'Ali o Anjo do Senhor lhe apareceu numa chama de fogo que saía do meio de uma sarça. Moisés viu que, embora a sarça estivesse em chamas, não era consumida pelo fogo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 3, 3, '“Que impressionante!”, pensou. “Por que a sarça não se queima? Vou ver isso de perto.”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 3, 4, 'O Senhor viu que ele se aproximava para observar. E então, do meio da sarça Deus o chamou: “Moisés, Moisés!” “Eis-me aqui”, respondeu ele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 3, 5, 'Então disse Deus: “Não se aproxime. Tire as sandálias dos pés, pois o lugar em que você está é terra santa”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 3, 6, 'Disse ainda: “Eu sou o Deus de seu pai, o Deus de Abraão, o Deus de Isaque, o Deus de Jacó”. Então Moisés cobriu o rosto, pois teve medo de olhar para Deus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 3, 7, 'Disse o Senhor: De fato tenho visto a opressão sobre o meu povo no Egito, tenho escutado o seu clamor, por causa dos seus feitores, e sei quanto eles estão sofrendo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 3, 8, 'Por isso desci para livrá-los das mãos dos egíp­cios e tirá-los daqui para uma terra boa e vasta, onde há leite e mel com fartura: a terra dos cananeus, dos hititas, dos amorreus, dos ferezeus, dos heveus e dos jebuseus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 3, 9, 'Pois agora o clamor dos israelitas chegou a mim, e tenho visto como os egípcios os oprimem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 3, 10, 'Vá, pois, agora; eu o envio ao faraó para tirar do Egito o meu povo, os israelitas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 3, 11, 'Moisés, porém, respondeu a Deus: “Quem sou eu para apresentar-me ao faraó e tirar os israelitas do Egito?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 3, 12, 'Deus afirmou: “Eu estarei com você. Esta é a prova de que sou eu quem o envia: quan­do você tirar o povo do Egito, vocês pres­tarão culto a Deus neste monte”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 3, 13, 'Moisés perguntou: “Quando eu chegar diante dos israelitas e lhes disser: O Deus dos seus antepassados me enviou a vocês, e eles me perguntarem: “Qual é o nome dele?” Que lhes direi?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 3, 14, 'Disse Deus a Moisés: “Eu Sou o que Sou. É isto que você dirá aos israelitas: Eu Sou me enviou a vocês”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 3, 15, 'Disse também Deus a Moisés: Diga aos israelitas: O Senhor , o Deus dos seus ante­passados, o Deus de Abraão, o Deus de Isaque, o Deus de Jacó, enviou-me a vocês. Esse é o meu nome para sempre, nome pelo qual serei lembrado de geração em geração.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 3, 16, 'Vá, reúna as autoridades de Israel e diga-lhes: O Senhor, o Deus dos seus antepas­sados, o Deus de Abraão, de Isaque e de Jacó, apareceu a mim e disse: Eu virei em auxílio de vocês; pois vi o que lhes tem sido feito no Egi­to.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 3, 17, 'Prometi tirá-los da opressão do Egito para a terra dos cananeus, dos hititas, dos amorreus, dos ferezeus, dos heveus e dos jebuseus, terra onde há leite e mel com fartura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 3, 18, 'As autoridades de Israel o atenderão. Depois você irá com elas ao rei do Egito e lhe dirá: O Senhor, o Deus dos hebreus, veio ao nosso encontro. Agora, deixe-nos fazer uma caminhada de três dias, adentrando o deserto, para oferecermos sacrifícios ao Senhor, o nosso Deus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 3, 19, 'Eu sei que o rei do Egito não os deixará sair, a não ser que uma poderosa mão o force.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 3, 20, 'Por isso estenderei a minha mão e ferirei os egípcios com todas as maravilhas que realizarei no meio deles. Depois disso ele os deixará sair.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 3, 21, 'E farei que os egípcios tenham boa vontade para com o povo, de modo que, quan­do vocês saírem, não sairão de mãos vazias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 3, 22, 'To­das as israelitas pedirão às suas vizinhas, e às mulheres que estiverem hospedando em casa, objetos de prata e de ouro, e roupas, que vocês porão em seus filhos e em suas filhas. Assim vocês despojarão os egíp­cios.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 1, 'Moisés respondeu: “E se eles não acreditarem em mim nem quiserem me ouvir e disserem: “O Senhor não lhe apareceu”?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 2, 'Então o Senhor lhe perguntou: “Que é isso em sua mão?” “Uma vara”, respondeu ele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 3, 'Disse o Senhor: “Jogue-a ao chão”. Moisés jogou-a, e ela se transformou numa serpente. Moisés fugiu dela,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 4, 'mas o Senhor lhe disse: “Estenda a mão e pegue-a pela cauda”. Moisés estendeu a mão, pegou a ser­pente e esta se transformou numa vara em sua mão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 5, 'E disse o Senhor: “Isso é para que eles acreditem que o Deus dos seus antepassados, o Deus de Abraão, o Deus de Isaque, o Deus de Jacó, apareceu a você”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 6, 'Disse-lhe mais o Senhor: “Coloque a mão no peito”. Moisés obedeceu e, quando a retirou, ela estava leprosa; parecia neve.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 7, 'Ordenou-lhe depois: “Agora, coloque de novo a mão no peito”. Moisés tornou a pôr a mão no peito e, quando a tirou, ela estava nova­mente como o restante da sua pele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 8, 'Prosseguiu o Senhor: Se eles não acreditarem em você nem derem atenção ao primeiro sinal milagroso, acreditarão no se­gundo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 9, 'E se ainda assim não acreditarem nestes dois sinais nem lhe derem ouvidos, tire um pou­co de água do Nilo e derrame-a em terra seca. Quando você derramar essa água em terra seca ela se transformará em sangue.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 10, 'Disse, porém, Moisés ao Senhor: “Ó Senhor! Nunca tive facilidade para falar, nem no passado nem agora que falaste a teu servo. Não consigo falar bem!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 11, 'Disse-lhe o Senhor: Quem deu boca ao homem? Quem o fez surdo ou mudo? Quem lhe concede vista ou o torna cego? Não sou eu, o Senhor?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 12, 'Agora, pois, vá; eu estarei com você, ensinando-lhe o que dizer.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 13, 'Respondeu-lhe, porém, Moisés: “Ah, Senhor! Peço-te que envies outra pessoa”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 14, 'Então o Senhor se irou com Moisés e lhe disse: Você não tem o seu irmão Arão, o levita? Eu sei que ele fala bem. Ele já está vin­do ao seu encontro e se alegrará ao vê-lo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 15, 'Você falará com ele e lhe dirá o que ele deve dizer; eu estarei com vocês quando falarem, e lhes direi o que fazer.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 16, 'Assim como Deus fala ao profeta, você falará a seu irmão, e ele será o seu porta-voz diante do povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 17, 'E leve na mão esta vara; com ela você fará os sinais milagrosos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 18, 'Depois Moisés voltou a Jetro, seu sogro, e lhe disse: “Preciso voltar ao Egito para ver se meus parentes ainda vivem”. Jetro lhe respondeu: “Vá em paz!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 19, 'Ora, o Senhor tinha dito a Moisés, em Midiã: “Volte ao Egito, pois já morreram todos os que procuravam matá-lo”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 20, 'Então Moi­sés levou sua mulher e seus filhos montados num jumento e partiu de volta ao Egito. Levava na mão a vara de Deus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 21, 'Disse mais o Senhor a Moisés: Quan­do você voltar ao Egito, tenha o cuidado de fazer diante do faraó todas as maravilhas que concedi a você o poder de realizar. Mas eu vou endurecer o coração dele, para não deixar o povo ir.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 22, 'Depois diga ao faraó que assim diz o Senhor: Israel é o meu primeiro filho,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 23, 'e eu já lhe disse que deixe o meu filho ir para prestar-me culto. Mas você não quis deixá-lo ir; por isso matarei o seu primeiro filho!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 24, 'Numa hospedaria ao longo do cami­nho, o Senhor foi ao encontro de Moisés e procurou matá-lo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 25, 'Mas Zípora pegou uma pedra afiada, cortou o prepúcio de seu filho e tocou os pés de Moisés. E disse: “Você é para mim um marido de sangue!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 26, 'Ela disse “mari­do de sangue”, referindo-se à circuncisão. Nes­sa ocasião o Senhor o deixou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 27, 'Então o Senhor disse a Arão: “Vá ao deserto encontrar-se com Moisés”. Ele foi, encontrou-se com Moisés no monte de Deus, e o saudou com um beijo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 28, 'Moisés contou a Arão tudo o que o Senhor lhe tinha mandado dizer, e também falou-lhe de todos os sinais milagrosos que lhe havia ordenado realizar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 29, 'Assim Moisés e Arão foram e reuni­ram todas as autoridades dos israelitas,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 30, 'e Arão lhes contou tudo o que o Senhor dissera a Moi­sés. Em seguida Moisés também realizou os sinais diante do povo,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 4, 31, 'e eles creram. Quando o povo soube que o ­Senhordecidira vir em seu auxílio, tendo visto a sua opressão, curvou-se em adoração.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 5, 1, 'Depois disso Moisés e Arão foram falar com o faraó e disseram: “Assim diz o Senhor, o Deus de Israel: “Deixe o meu povo ir para celebrar-me uma festa no deserto””.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 5, 2, 'O faraó respondeu: “Quem é o Senhor, para que eu lhe obedeça e deixe Israel sair? Não conheço o Senhor, e não deixarei Israel sair”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 5, 3, 'Eles insistiram: “O Deus dos hebreus veio ao nosso encontro. Agora, permite-nos caminhar três dias no deserto, para oferecer sacri­fícios ao Senhor, o nosso Deus; caso contrário, ele nos atingirá com pragas ou com a espada”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 5, 4, 'Mas o rei do Egito respondeu: “Moisés e Arão, por que vocês estão fazendo o povo interromper suas tarefas? Voltem ao trabalho!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 5, 5, 'E acrescentou: “Essa gente já é tão numerosa, e vocês ainda os fazem parar de trabalhar!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 5, 6, 'No mesmo dia o faraó deu a seguinte ordem aos feitores e capatazes responsáveis pelo povo:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 5, 7, 'Não forneçam mais palha ao povo para fazer tijolos, como faziam antes. Eles que tratem de ajuntar palha!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 5, 8, 'Mas exijam que conti­nuem a fazer a mesma quantidade de tijolos; não reduzam a cota. São preguiçosos, e por isso estão clamando: “Iremos oferecer sacrifícios ao nosso Deus”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 5, 9, 'Aumentem a carga de trabalho dessa gente para que cumpram suas tarefas e não dêem atenção a mentiras.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 5, 10, 'Os feitores e os capatazes foram dizer ao povo: Assim diz o faraó: “Já não lhes darei palha.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 5, 11, 'Saiam e recolham-na onde puderem achá-la, pois o trabalho de vocês em nada será reduzido”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 5, 12, 'O povo, então, espalhou-se por todo o Egito, a fim de ajuntar restolho em lugar da palha.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 5, 13, 'Enquanto isso, os feitores os pressionavam, dizendo: “Completem a mesma tarefa diária que lhes foi exigida quando tinham pa­lha”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 5, 14, 'Os capatazes israelitas indicados pelos feitores do faraó eram espancados e interroga­dos: “Por que não completaram ontem e hoje a mesma cota de tijolos dos dias anteriores?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 5, 15, 'Então os capatazes israelitas foram apelar para o faraó: Por que tratas os teus ser­vos dessa maneira?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 5, 16, 'Nós, teus servos, não recebemos palha, e, contudo, nos dizem: “Façam tijolos!” Os teus servos têm sido espancados, mas a culpa é do teu próprio povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 5, 17, 'Respondeu o faraó: Preguiçosos é o que vocês são! Preguiçosos! Por isso andam dizendo: “Iremos oferecer sacrifícios ao Senhor”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 5, 18, 'Agora, voltem ao trabalho. Vocês não receberão palha alguma! Continuem a produzir a cota integral de tijolos!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 5, 19, 'Os capatazes israelitas se viram em dificuldade quando lhes disseram que não poderiam reduzir a quantidade de tijolos exigida a cada dia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 5, 20, 'Ao saírem da presença do faraó, encontraram-se com Moisés e Arão, que estavam à espera deles,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 5, 21, 'e lhes disseram: “O Senhor os examine e os julgue! Vocês atraíram o ódio do faraó e dos seus conselheiros sobre nós, e lhes puseram nas mãos uma espada para que nos matem”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 5, 22, 'Moisés voltou-se para o Senhor e perguntou: Senhor, por que maltrataste este povo? Afinal, por que me enviaste?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 5, 23, 'Desde que me dirigi ao faraó para falar em teu nome, ele tem maltratado este povo, e tu de modo algum libertaste o teu povo!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 1, 'Então o Senhor disse a Moisés: “Agora você verá o que farei ao faraó: Por minha mão poderosa, ele os deixará ir; por minha mão poderosa, ele os expulsará do seu país”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 2, 'Disse Deus ainda a Moisés: Eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 3, 'Apareci a Abraão, a Isaque e a Jacó como o Deus todo-poderoso, mas pelo meu nome, o Senhor , não me revelei a eles.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 4, 'Depois estabeleci com eles a minha aliança para dar-lhes a terra de Canaã, terra onde viveram como estrangeiros.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 5, 'E agora ouvi o lamento dos israelitas, a quem os egípcios mantêm escravos, e lembrei-me da minha aliança.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 6, 'Por isso, diga aos israelitas: Eu sou o Senhor. Eu os livrarei do trabalho imposto pelos egípcios. Eu os libertarei da escravidão e os resgatarei com braço forte e com poderosos atos de juízo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 7, 'Eu os farei meu povo e serei o Deus de vocês. Então vocês saberão que eu sou o Senhor, o seu Deus, que os livra do trabalho imposto pelos egípcios.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 8, 'E os farei entrar na terra que, com mão levantada, jurei que daria a Abraão, a Isaque e a Jacó. Eu a darei a vocês como propriedade. Eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 9, 'Moisés declarou isso aos israelitas, mas eles não lhe deram ouvidos, por causa da angústia e da cruel escravidão que sofriam.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 10, 'Então o Senhor ordenou a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 11, '“Vá dizer ao faraó, rei do Egito, que deixe os israelitas saírem do país”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 12, 'Moisés, porém, disse na presença do Senhor: “Se os israelitas não me dão ouvidos, como me ouvirá o faraó? Ainda mais que não tenho facilidade para falar!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 13, 'Mas o Senhor ordenou a Moisés e a Arão que dissessem aos israelitas e ao faraó, rei do Egito, que tinham ordem para tirar do Egito os israelitas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 14, 'Estes foram os chefes das famílias israelitas: Os filhos de Rúben, filho mais velho de Israel, foram: Enoque, Palu, Hezrom e Carmi. Esses foram os clãs de Rúben.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 15, 'Os filhos de Simeão foram: Jemuel, Jamim, Oade, Jaquim, Zoar e Saul, filho de uma cananéia. Esses foram os clãs de Simeão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 16, 'Estes são os nomes dos filhos de Levi, por ordem de nascimento: Gérson, Coate e Merari. Levi viveu cento e trinta e sete anos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 17, 'Os filhos de Gérson, conforme seus clãs, foram Libni e Simei.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 18, 'Os filhos de Coate foram Anrão, Isar, Hebrom e Uziel. Coate viveu cento e trinta e três anos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 19, 'Os filhos de Merari foram Mali e Musi. Esses foram os clãs de Levi, por ordem de nascimento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 20, 'Anrão tomou por mulher sua tia Joque­bede, que lhe deu à luz Arão e Moisés. Anrão viveu cento e trinta e sete anos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 21, 'Os filhos de Isar foram Corá, Nefegue e Zicri.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 22, 'Os filhos de Uziel foram Misael, Elzafã e Sitri.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 23, 'Arão tomou por mulher a Eliseba, filha de Aminadabe, irmã de Naassom, e ela lhe deu à luz Nadabe, Abiú, Eleazar e Itamar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 24, 'Os filhos de Corá foram Assir, Elcana e Abiasafe. Esses foram os clãs dos coraítas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 25, 'Eleazar, filho de Arão, tomou por mulher uma das filhas de Futiel, e ela lhe deu à luz Finéias. Esses foram os chefes das famílias dos levitas, conforme seus clãs.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 26, 'Foi a este Arão e a este Moisés que o Senhor disse: “Tirem os israelitas do Egito, organizados segundo as suas divisões”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 27, 'Fo­ram eles, Moisés e Arão, que falaram ao faraó, rei do Egito, a fim de tirarem os israelitas do Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 28, 'Ora, quando o Senhor falou com Moisés no Egito,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 29, 'disse-lhe: “Eu sou o Senhor. Diga ao faraó, rei do Egito, tudo o que eu lhe disser”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 6, 30, 'Moisés, porém, perguntou ao Senhor: “Como o faraó me dará ouvidos, se não tenho facilidade para falar?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 1, 'O Senhor lhe respondeu: Dou-lhe a minha autoridade perante o faraó, e seu irmão Arão será seu porta-voz.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 2, 'Você falará tudo o que eu lhe ordenar, e o seu irmão Arão dirá ao faraó que deixe os israelitas saírem do país.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 3, 'Eu, porém, farei o coração do faraó resistir; e, embora multiplique meus sinais e maravilhas no Egito,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 4, 'ele não os ouvirá. Então porei a minha mão sobre o Egito, e com poderosos atos de juízo tirarei do Egito os meus exércitos, o meu povo, os israelitas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 5, 'E os egípcios saberão que eu sou o Senhor, quando eu estender a minha mão contra o Egito e tirar de lá os israelitas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 6, 'Moisés e Arão fizeram como o Senhor lhes havia ordenado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 7, 'Moisés tinha oitenta anos de idade e Arão oitenta e três, quando falaram com o faraó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 8, 'Disse o Senhor a Moisés e a Arão:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 9, '“Quando o faraó lhes pedir que façam algum milagre, diga a Arão que tome a sua vara e jogue-a diante do faraó; e ela se transformará numa serpente”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 10, 'Moisés e Arão dirigiram-se ao faraó e fizeram como o Senhor tinha ordenado. Arão jogou a vara diante do faraó e seus conselhei­ros, e ela se transformou em serpente.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 11, 'O faraó, porém, mandou chamar os sábios e feiticeiros; e também os magos do Egito fizeram a mesma coisa por meio das suas ciências ocultas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 12, 'Cada um deles jogou ao chão uma vara, e estas se transformaram em serpentes. Mas a vara de Arão engoliu as varas deles.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 13, 'Contudo, o coração do faraó se endureceu e ele não quis dar ouvidos a Moisés e a Arão, como o ­Senhortinha dito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 14, 'Disse o Senhor a Moisés: O coração do faraó está obstinado; ele não quer deixar o povo ir.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 15, 'Vá ao faraó de manhã, quando ele estiver indo às águas. Espere-o na margem do rio para encontrá-lo e leve também a vara que se transformou em serpente.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 16, 'Diga-lhe: O Senhor, o Deus dos hebreus, mandou-me dizer-lhe: Deixe ir o meu povo, para prestar-me culto no deserto. Mas até agora você não me atendeu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 17, 'Assim diz o Senhor: Nisto você saberá que eu sou o Senhor: com a vara que trago na mão ferirei as águas do Nilo, e elas se transformarão em sangue.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 18, 'Os peixes do Nilo morrerão, o rio ficará cheirando mal, e os egípcios não suporta­rão beber das suas águas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 19, 'Disse o Senhor a Moisés: “Diga a Arão que tome a sua vara e estenda a mão sobre as águas do Egito, dos rios, dos canais, dos açudes e de todos os reservatórios, e elas se transformarão em sangue. Haverá sangue por toda a terra do Egito, até nas vasilhas de madeira e nas vasilhas de pedra”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 20, 'Moisés e Arão fizeram como o Senhor tinha ordenado. Arão levantou a vara e feriu as águas do Nilo na presença do faraó e dos seus conselheiros; e toda a água do rio transformou-se em sangue.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 21, 'Os peixes morre­ram e o rio cheirava tão mal que os egípcios não conseguiam beber das suas águas. Havia sangue por toda a terra do Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 22, 'Mas os magos do Egito fizeram a mesma coisa por meio de suas ciências ocultas. O coração do faraó se endureceu, e ele não deu ouvidos a Moisés e a Arão, como o Senhor tinha dito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 23, 'Ao contrário, deu-lhes as costas e voltou para o seu palácio. Nem assim o faraó levou isso a sério.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 24, 'Todos os egípcios cavaram buracos às margens do Nilo para encontrar água potável, pois da água do rio não podiam mais beber.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 7, 25, 'Passaram-se sete dias depois que o Senhor feriu o Nilo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 1, 'O Senhor falou a Moisés: Vá ao faraó e diga-lhe que assim diz o Senhor: Deixe o meu povo ir para que me preste culto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 2, 'Se você não quiser deixá-lo ir, mandarei sobre todo o seu território uma praga de rãs.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 3, 'O Nilo ficará infestado de rãs. Elas subirão e entrarão em seu palácio, em seu quarto, e até em sua cama; estarão também nas casas dos seus conselheiros e do seu povo, dentro dos seus fornos e nas suas amassadeiras.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 4, 'As rãs subirão em você, em seus conselheiros e em seu povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 5, 'Depois o Senhor disse a Moisés: “Diga a Arão que estenda a mão com a vara sobre os rios, sobre os canais e sobre os açudes, e faça subir deles rãs sobre a terra do Egito”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 6, 'Assim Arão estendeu a mão sobre as águas do Egito, e as rãs subiram e cobriram a terra do Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 7, 'Mas os magos fizeram a mesma coisa por meio das suas ciências ocultas: fizeram subir rãs sobre a terra do Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 8, 'O faraó mandou chamar Moisés e Arão e disse: “Orem ao Senhor para que ele tire estas rãs de mim e do meu povo; então deixarei o povo ir e oferecer sacrifícios ao Senhor”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 9, 'Moisés disse ao faraó: “Tua é a honra de dizer-me quando devo orar por ti, por teus conselheiros e por teu povo, para que tu e tuas casas fiquem livres das rãs e sobrem apenas as que estão no rio”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 10, '“Amanhã”, disse o faraó. Moisés respondeu: “Será como tu dizes, para que saibas que não há ninguém como o Senhor, o nosso Deus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 11, 'As rãs deixarão a ti, a tuas casas, a teus conselheiros e a teu povo; sobrarão apenas as que estão no rio.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 12, 'Depois que Moisés e Arão saíram da presença do faraó, Moisés clamou ao Senhor por causa das rãs que enviara sobre o faraó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 13, 'E o Senhor atendeu o pedido de Moisés; morreram as rãs que estavam nas casas, nos pátios e nos campos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 14, 'Foram ajuntadas em montões e, por isso, a terra cheirou mal.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 15, 'Mas quando o faraó percebeu que houve alívio, obstinou-se em seu coração e não deu mais ouvidos a Moisés e a Arão, conforme o Senhor tinha dito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 16, 'Então o Senhor disse a Moisés: “Diga a Arão que estenda a sua vara e fira o pó da terra, e o pó se transformará em piolhos por toda a terra do Egito”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 17, 'Assim fizeram e, quando Arão estendeu a mão e com a vara feriu o pó da terra, surgiram piolhos nos homens e nos animais. Todo o pó de toda a terra do Egito transformou-se em piolhos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 18, 'Mas, quando os magos tentaram fazer surgir piolhos por meio das suas ciências ocultas, não conseguiram. E os piolhos infestavam os homens e os animais.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 19, 'Os magos disseram ao faraó: “Isso é o dedo de Deus”. Mas o coração do faraó permaneceu endurecido, e ele não quis ouvi-los, conforme o Senhor tinha dito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 20, 'Depois o Senhor disse a Moisés: Levante-se bem cedo e apresente-se ao faraó, quando ele estiver indo às águas. Diga-lhe que assim diz o Senhor: Deixe o meu povo ir para que me preste culto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 21, 'Se você não deixar meu povo ir, enviarei enxames de moscas para atacar você, os seus conselheiros, o seu povo e as suas casas. As casas dos egípcios e o chão em que pisam se encherão de moscas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 22, 'Mas naquele dia tratarei de maneira diferente a terra de Gósen, onde habita o meu povo; nenhum enxame de moscas se achará ali, para que você saiba que eu, o Senhor, estou nessa terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 23, 'Farei distinção entre o meu povo e o seu. Este sinal milagroso acontecerá amanhã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 24, 'E assim fez o Senhor. Grandes enxa­mes de moscas invadiram o palácio do faraó e as casas de seus conselheiros, e em todo o Egito a terra foi arruinada pelas moscas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 25, 'Então o faraó mandou chamar Moisés e Arão e disse: “Vão oferecer sacrifícios ao seu Deus, mas não saiam do país”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 26, '“Isso não seria sensato”, respondeu Moisés; “os sacrifícios que oferecemos ao Senhor, o nosso Deus, são um sacrilégio para os egípcios. Se oferecermos sacrifícios que lhes pareçam sacri­légio, isso não os levará a nos apedrejar?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 27, 'Fare­mos três dias de viagem no deserto, e oferecere­mos sacrifícios ao Senhor, o nosso Deus, como ele nos ordena.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 28, 'Disse o faraó: “Eu os deixarei ir e oferecer sacrifícios ao Senhor, o seu Deus, no deserto, mas não se afastem muito e orem por mim também”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 29, 'Moisés respondeu: “Assim que sair da tua presença, orarei ao Senhor, e amanhã os enxames de moscas deixarão o faraó, teus conselheiros e teu povo. Mas que o faraó não volte a agir com falsidade, impedindo que o povo vá oferecer sacrifícios ao Senhor”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 30, 'Então Moisés saiu da presença do faraó e orou ao Senhor,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 31, 'e o Senhor atendeu o seu pedido: as moscas deixaram o faraó, seus conselheiros e seu povo; não restou uma só mosca.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 8, 32, 'Mas também dessa vez o faraó obstinou-se em seu coração e não deixou que o povo saísse.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 1, 'Depois o Senhor disse a Moisés: Vá ao faraó e diga-lhe que assim diz o Senhor, o Deus dos hebreus: Deixe o meu povo ir para que me preste culto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 2, 'Se você ainda não quiser deixá-lo ir e continuar a impedi-lo,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 3, 'saiba que a mão do Senhor trará uma praga terrível sobre os rebanhos do faraó que estão nos campos: os cavalos, os jumentos, os camelos, os bois e as ovelhas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 4, 'Mas o Senhor fará distinção entre os rebanhos de Israel e os do Egito. Nenhum animal dos israelitas morrerá.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 5, 'O Senhor estabeleceu um prazo: “Amanhã o Senhor fará o que prometeu nesta terra”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 6, 'No dia seguinte o Senhor o fez. Todos os rebanhos dos egípcios morreram, mas nenhum rebanho dos israelitas morreu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 7, 'O faraó mandou verificar e constatou que nenhum ani­mal dos israelitas havia morrido. Mesmo assim, seu coração continuou obstinado e não deixou o povo ir.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 8, 'Disse mais o Senhor a Moisés e a Arão: Tirem um punhado de cinza de uma fornalha, e Moisés a espalhará no ar, diante do faraó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 9, 'Ela se tornará como um pó fino sobre toda a terra do Egito, e feridas purulentas surgi­rão nos homens e nos animais em todo o Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 10, 'Eles tiraram cinza duma fornalha e se puseram diante do faraó. Moisés a espalhou pelo ar, e feridas purulentas começaram a es­tourar nos homens e nos animais.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 11, 'Nem os magos podiam manter-se diante de Moisés, porque ficaram cobertos de feridas, como os demais egípcios.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 12, 'Mas o Senhor endureceu o coração do faraó, e ele se recusou a atender Moisés e Arão, conforme o Senhor tinha dito a Moisés.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 13, 'Disse o Senhor a Moisés: Levante-se logo cedo, apresente-se ao faraó e diga-lhe que assim diz o Senhor, o Deus dos hebreus: Deixe o meu povo ir para que me preste culto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 14, 'Caso contrário, mandarei desta vez todas as minhas pragas contra você, contra os seus conselheiros e contra o seu povo, para que você saiba que em toda a terra não há ninguém como eu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 15, 'Porque eu já poderia ter estendido a mão, ferindo você e o seu povo com uma praga que teria eliminado você da terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 16, 'Mas eu o mantive em pé exatamente com este propósito: mostrar-lhe o meu poder e fazer que o meu nome seja proclamado em toda a terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 17, 'Contudo você ainda insiste em colocar-se contra o meu povo e não o deixa ir.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 18, 'Amanhã, a esta hora, enviarei a pior tempestade de granizo que já caiu sobre o Egito, desde o dia da sua fundação até hoje.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 19, 'Agora, mande recolher os seus rebanhos e tudo o que você tem nos campos. Todos os homens e animais que estiverem nos campos, que não tiverem sido abrigados, serão atingidos pelo granizo e morrerão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 20, 'Os conselheiros do faraó que temiam a palavra do Senhor apressaram-se em recolher aos abrigos os seus rebanhos e os seus es­cravos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 21, 'Mas os que não se importaram com a palavra do Senhor deixaram os seus escravos e os seus rebanhos no campo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 22, 'Então o Senhor disse a Moisés: “Estenda a mão para o céu, e cairá granizo sobre toda a terra do Egito: sobre homens, sobre animais e sobre toda a vegetação do Egito”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 23, 'Quando Moisés estendeu a vara para o céu, o Senhor fez vir trovões e granizo, e raios caíam sobre a terra. Assim o Senhor fez chover granizo sobre a terra do Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 24, 'Caiu granizo, e raios cortavam o céu em todas as direções. Nunca houve uma tempestade de granizo como aquela em todo o Egito, desde que este se tornou uma nação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 25, 'Em todo o Egito o granizo atingiu tudo o que havia nos campos, tanto homens como animais; destruiu toda a vegetação, além de quebrar todas as árvores.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 26, 'Somente na terra de Gósen, onde estavam os israelitas, não caiu granizo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 27, 'Então o faraó mandou chamar Moisés e Arão e disse-lhes: Desta vez eu pequei. O Senhor é justo; eu e o meu povo é que somos culpados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 28, 'Orem ao Senhor! Os trovões de Deus e o granizo já são demais. Eu os deixarei ir; não precisam mais ficar aqui.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 29, 'Moisés respondeu: Assim que eu tiver saído da cidade, erguerei as mãos em oração ao Senhor. Os trovões cessarão e não cairá mais granizo, para que saibas que a terra pertence ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 30, 'Mas eu bem sei que tu e os teus con­selheiros ainda não sabem o que é tremer diante do Senhor Deus!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 31, '(O linho e a cevada foram destruídos, pois a cevada já havia amadurecido e o linho estava em flor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 32, 'Todavia, o trigo e o centeio nada sofreram, pois só amadurecem mais tarde. )');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 33, 'Assim Moisés deixou o faraó, saiu da cidade, e ergueu as mãos ao Senhor. Os trovões e o granizo cessaram, e a chuva parou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 34, 'Quando o faraó viu que a chuva, o granizo e os trovões haviam cessado, pecou novamente e obstinou-se em seu coração, ele e os seus conse­lheiros.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 9, 35, 'O coração do faraó continuou endurecido, e ele não deixou que os israelitas saíssem, como o Senhor tinha dito por meio de Moisés.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 1, 'O Senhor disse a Moisés: Vá ao faraó, pois tornei obstinado o coração dele e o de seus conselheiros, a fim de realizar estes meus prodígios entre eles,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 2, 'para que você possa contar a seus filhos e netos como zombei dos egípcios e como realizei meus milagres entre eles. Assim vocês saberão que eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 3, 'Dirigiram-se, pois, Moisés e Arão ao faraó e lhe disseram: Assim diz o Senhor, o Deus dos hebreus: “Até quando você se recusará a humilhar-se perante mim? Deixe ir o meu povo, para que me preste culto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 4, 'Se você não quiser deixá-lo ir, farei vir gafanhotos sobre o seu território amanhã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 5, 'Eles cobrirão a face da terra até não se poder enxergar o solo. Devorarão o pouco que ainda lhes restou da tempestade de granizo e todas as árvores que estiverem brotando nos campos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 6, 'Encherão os seus palácios e as casas de todos os seus conse­lheiros e de todos os egípcios: algo que os seus pais e os seus antepassados jamais viram, desde o dia em que se fixaram nesta terra até o dia de hoje”. A seguir Moisés virou as costas e saiu da presença do faraó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 7, 'Os conselheiros do faraó lhe disseram: “Até quando este homem será uma ameaça para nós? Deixa os homens irem prestar culto ao Senhor, o Deus deles. Não percebes que o Egito está arruinado?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 8, 'Então Moisés e Arão foram trazidos de volta à presença do faraó, que lhes disse: “Vão e prestem culto ao Senhor, o seu Deus. Mas, digam-me, quem irá?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 9, 'Moisés respondeu: “Temos que levar todos: os jovens e os velhos, os nossos filhos e as nossas filhas, as nossas ovelhas e os nossos bois, porque vamos celebrar uma festa ao Senhor”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 10, 'Disse-lhes o faraó: Vocês vão mesmo precisar do Senhor quando eu deixá-los ir com as mulheres e crianças! É claro que vocês estão com más intenções.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 11, 'De forma alguma! Só os homens podem ir prestar culto ao Senhor, como vocês têm pedido. E Moisés e Arão foram expulsos da presença do faraó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 12, 'Mas o Senhor disse a Moisés: “Estenda a mão sobre o Egito para que os gafanhotos venham sobre a terra e devorem toda a vegetação, tudo o que foi deixado pelo granizo”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 13, 'Moisés estendeu a vara sobre o Egito, e o Senhor fez soprar sobre a terra um vento oriental durante todo aquele dia e toda aquela noite. Pela manhã, o vento havia trazido os gafanhotos,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 14, 'os quais invadiram todo o Egito e desceram em grande número sobre toda a sua extensão. Nunca antes houve tantos gafanhotos, nem jamais haverá.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 15, 'Eles cobriram toda a face da terra de tal forma que ela escureceu. Devoraram tudo o que o granizo tinha deixado: toda a vegetação e todos os frutos das árvores. Não restou nada verde nas árvores nem nas plantas do campo, em toda a terra do Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 16, 'O faraó mandou chamar Moisés e Arão imediatamente e disse-lhes: Pequei contra o Senhor, o seu Deus, e contra vocês!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 17, 'Agora perdoem ainda esta vez o meu pecado e orem ao Senhor, o seu Deus, para que leve esta praga mortal para longe de mim.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 18, 'Moisés saiu da presença do faraó e orou ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 19, 'E o Senhor fez soprar com muito mais força o vento ocidental, e este envolveu os gafanhotos e os lançou no mar Vermelho. Não restou um gafanhoto sequer em toda a extensão do Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 20, 'Mas o Senhor endureceu o coração do faraó, e ele não deixou que os israelitas saíssem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 21, 'O Senhor disse a Moisés: “Estenda a mão para o céu, e trevas cobrirão o Egito, trevas tais que poderão ser apalpadas”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 22, 'Moisés estendeu a mão para o céu, e por três dias houve densas trevas em todo o Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 23, 'Ninguém pôde ver ninguém, nem sair do seu lugar durante três dias. Todavia, todos os israelitas tinham luz nos locais em que habitavam.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 24, 'Então o faraó mandou chamar Moisés e disse: “Vão e prestem culto ao Senhor. Deixem somente as ovelhas e os bois; as mulheres e as crianças podem ir”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 25, 'Mas Moisés contestou: Tu mesmo nos darás os animais para os nossos sacrifícios e holocaustos que ofereceremos ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 26, 'Além disso, os nossos rebanhos também irão conosco; nem um casco de animal será deixado. Temos que escolher alguns deles para pres­tar culto ao Senhor, o nosso Deus, e, enquanto não chegarmos ao local, não saberemos quais ani­mais sacrificaremos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 27, 'Mas o Senhor endureceu o coração do faraó, e ele se recusou a deixá-los ir.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 28, 'Disse o faraó a Moisés: “Saia da minha presença! Trate de não aparecer nunca mais diante de mim! No dia em que vir a minha face, você mor­rerá”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 10, 29, 'Respondeu Moisés: “Será como disses­te; nunca mais verei a tua face”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 11, 1, 'Disse então o Senhor a Moisés: Enviarei ainda mais uma praga sobre o faraó e sobre o Egito. Somente depois desta ele os deixará sair daqui e até os expulsará totalmente.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 11, 2, 'Diga ao povo, tanto aos homens como às mulheres, que peça aos seus vizinhos objetos de prata e de ouro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 11, 3, 'O Senhor tornou os egípcios favoráveis ao povo, e o próprio Moisés era tido em alta estima no Egito pelos conselheiros do faraó e pelo povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 11, 4, 'Disse, pois, Moisés ao faraó: Assim diz o Senhor: “Por volta da meia-noite, passarei por todo o Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 11, 5, 'Todos os primogênitos do Egito morrerão, desde o filho mais velho do faraó, herdeiro do trono, até o filho mais velho da escrava que trabalha no moinho, e também todas as primeiras crias do gado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 11, 6, 'Haverá grande pranto em todo o Egito, como nunca houve antes nem jamais haverá.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 11, 7, 'Entre os israelitas, porém, nem sequer um cão latirá contra homem ou animal”. Então vocês saberão que o Senhor faz distinção entre o Egito e Israel!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 11, 8, 'Todos esses seus conselheiros virão a mim e se ajoelharão diante de mim, suplicando: “Saiam você e todo o povo que o segue!” Só então eu sairei. E, com grande ira, Moisés saiu da presença do faraó.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 11, 9, 'O Senhor tinha dito a Moisés: “O faraó não lhes dará ouvidos, a fim de que os meus prodígios se multipliquem no Egito”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 11, 10, 'Moisés e Arão realizaram todos esses prodígi­os diante do faraó, mas o Senhor lhe endureceu o coração, e ele não quis deixar os israelitas saírem do país.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 1, 'O Senhor disse a Moisés e a Arão, no Egito:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 2, 'Este deverá ser o primeiro mês do ano para vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 3, 'Digam a toda a comunidade de Israel que no décimo dia deste mês todo homem deverá separar um cordeiro ou um cabrito, para a sua família, um para cada casa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 4, 'Se uma família for pequena demais para um animal inteiro, deve dividi-lo com seu vizinho mais próximo, conforme o número de pessoas e conforme o que cada um puder comer.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 5, 'O animal escolhido será macho de um ano, sem defeito, e pode ser cordeiro ou cabrito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 6, 'Guardem-no até o décimo quarto dia do mês, quando toda a comunidade de Israel irá sacrificá-lo, ao pôr-do-sol.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 7, 'Passem, então, um pouco do sangue nas laterais e nas vigas superiores das portas das casas nas quais vocês comerão o animal.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 8, 'Naquela mesma noite comerão a carne assada no fogo, com ervas amargas e pão sem fermento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 9, 'Não comam a carne crua, nem cozida em água, mas assada no fogo: cabeça, pernas e vísceras.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 10, 'Não deixem sobrar nada até pela manhã; caso isso aconteça, queimem o que restar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 11, 'Ao comerem, estejam prontos para sair: cinto no lugar, sandálias nos pés e cajado na mão. Comam apressadamente. Esta é a Páscoa do Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 12, 'Naquela mesma noite passarei pelo Egito e matarei todos os primogênitos, tanto dos homens como dos animais, e executarei juízo sobre todos os deuses do Egito. Eu sou o Senhor!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 13, 'O sangue será um sinal para indicar as casas em que vocês estiverem; quando eu vir o sangue, passarei adiante. A praga de destruição não os atingirá quando eu ferir o Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 14, 'Este dia será um memorial que vocês e todos os seus descendentes celebrarão como festa ao Senhor. Celebrem-no como decreto perpétuo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 15, 'Durante sete dias comam pão sem fermento. No primeiro dia tirem de casa o fermento, porque quem comer qualquer coisa fermentada, do primeiro ao sétimo dia, será eliminado de Israel.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 16, 'Convoquem uma reunião santa no primeiro dia e outra no sétimo. Não façam nenhum trabalho nesses dias, exceto o da preparação da comida para todos. É só o que poderão fazer.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 17, 'Celebrem a festa dos pães sem fermento, porque foi nesse mesmo dia que eu tirei os exércitos de vocês do Egito. Celebrem esse dia como decreto perpétuo por todas as suas gerações.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 18, 'No primeiro mês comam pão sem fermento, desde o entardecer do décimo quarto dia até o entardecer do vigésimo primeiro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 19, 'Durante sete dias vocês não deverão ter fermento em casa. Quem comer qualquer coisa fermentada será eliminado da comunidade de Israel, seja estrangeiro, seja natural da terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 20, 'Não comam nada fermentado. Onde quer que morarem, comam apenas pão sem fermento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 21, 'Então Moisés convocou todas as autoridades de Israel e lhes disse: Escolham um cordeiro ou um cabrito para cada família. Sacrifiquem-no para celebrar a Páscoa!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 22, 'Molhem um feixe de hissopo no sangue que estiver na bacia e passem o sangue na viga superior e nas laterais das portas. Nenhum de vocês poderá sair de casa até o amanhecer.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 23, 'Quando o Senhor passar pela terra para matar os egípcios, verá o sangue na viga superior e nas laterais da porta e passará sobre aquela porta, e não permitirá que o destruidor entre na casa de vocês para matá-los.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 24, 'Obedeçam a estas instruções como decreto perpétuo para vocês e para os seus descendentes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 25, 'Quando entrarem na terra que o Senhor prometeu lhes dar, celebrem essa cerimônia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 26, 'Quando os seus filhos lhes perguntarem: “O que significa esta cerimônia?”,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 27, 'respondam-lhes: É o sacrifício da Páscoa ao Senhor, que passou sobre as casas dos israelitas no Egito e poupou nossas casas quando matou os egípcios. Então o povo curvou-se em adoração.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 28, 'Depois os israelitas se retiraram e fizeram conforme o Senhor tinha ordenado a Moisés e a Arão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 29, 'Então, à meia-noite, o Senhor matou todos os primogênitos do Egito, desde o filho mais velho do faraó, herdeiro do trono, até o filho mais velho do prisioneiro que estava no calabouço, e também todas as primeiras crias do gado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 30, 'No meio da noite o faraó, todos os seus conselheiros e todos os egípcios se levantaram. E houve grande pranto no Egito, pois não havia casa que não tivesse um morto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 31, 'Naquela mesma noite o faraó mandou chamar Moisés e Arão e lhes disse: Saiam imediatamente do meio do meu povo, vocês e os israelitas! Vão prestar culto ao Senhor, como vocês pediram.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 32, 'Levem os seus rebanhos, como tinham dito, e abençoem a mim também.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 33, 'Os egípcios pressionavam o povo para que se apressasse em sair do país, dizendo: “Todos nós morreremos!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 34, 'Então o povo tomou a massa de pão ainda sem fermento e a carregou nos ombros, nas amassadeiras embrulhadas em suas roupas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 35, 'Os israelitas obedeceram à ordem de Moisés e pediram aos egípcios objetos de prata e de ouro, bem como roupas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 36, 'O Senhor concedeu ao povo uma disposição favorável da parte dos egípcios, de modo que lhes davam o que pediam; assim eles despojaram os egípcios.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 37, 'Os israelitas foram de Ramessés até Sucote. Havia cerca de seiscentos mil homens a pé, além de mulheres e crianças.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 38, 'Grande multidão de estrangeiros de todo tipo seguiu com eles, além de grandes rebanhos, tanto de bois como de ovelhas e cabras.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 39, 'Com a massa que haviam trazido do Egito, fizeram pães sem fermento. A massa não tinha fermentado, pois eles foram expulsos do Egito e não tiveram tempo de preparar comida.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 40, 'Ora, o período que os israelitas viveram no Egito foi de quatrocentos e trinta anos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 41, 'No dia em que se completaram os quatrocentos e trinta anos, todos os exércitos do Senhor saíram do Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 42, 'Assim como o Senhor pas­sou em vigília aquela noite para tirar do Egito os israelitas, estes também devem passar em vigília essa mesma noite, para honrar o Senhor, por todas as suas gerações.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 43, 'Disse o Senhor a Moisés e a Arão: Estas são as leis da Páscoa: Nenhum estrangei­ro poderá comê-la.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 44, 'O escravo comprado poderá comer da Páscoa, depois de circuncidado,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 45, 'mas o residente temporário e o trabalhador contratado dela não comerão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 46, 'Vocês a comerão numa só casa; não levem nenhum pedaço de carne para fora da casa, nem quebrem nenhum dos ossos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 47, 'Toda a comunidade de Israel terá que celebrar a Páscoa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 48, 'Qualquer estrangeiro residente entre vocês que quiser celebrar a Páscoa do Senhor terá que circuncidar todos os do sexo masculino da sua família; então poderá participar como o natural da terra. Nenhum incircunciso poderá participar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 49, 'A mesma lei se aplicará ao natural da terra e ao estrangeiro residente.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 50, 'Todos os israelitas fizeram como o Senhor tinha ordenado a Moisés e a Arão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 12, 51, 'No mesmo dia o Senhor tirou os israelitas do Egito, organizados segundo as suas divisões.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 13, 1, 'E disse o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 13, 2, '“Consagre a mim todos os primogênitos. O primeiro filho israelita me pertence, não somente entre os homens, mas também entre os animais”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 13, 3, 'Então disse Moisés ao povo: Comemorem esse dia em que vocês saíram do Egito, da terra da escravidão, porque o Senhor os tirou dali com mão poderosa. Não comam nada fermentado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 13, 4, 'Neste dia do mês de abibe vocês estão saindo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 13, 5, 'Quando o Senhor os fizer entrar na terra dos cananeus, dos hititas, dos amorreus, dos heveus e dos jebuseus — terra que ele jurou aos seus antepassados que daria a vocês, terra onde há leite e mel com fartura — vocês deverão cele­brar esta cerimônia neste mesmo mês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 13, 6, 'Durante sete dias comam pão sem fermento e, no sétimo dia, façam uma festa dedicada ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 13, 7, 'Comam pão sem fermento durante os sete dias; não haja nada fermentado entre vocês, nem fermento algum dentro do seu território.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 13, 8, 'Nesse dia cada um dirá a seu filho: Assim faço pelo que o Senhor fez por mim quando saí do Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 13, 9, 'Isto lhe será como sinal em sua mão e memorial em sua testa, para que a lei do Senhor esteja em seus lábios, porque o Senhor o tirou do Egito com mão poderosa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 13, 10, 'Cumpra esta determinação na época certa, de ano em ano.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 13, 11, 'Depois que o Senhor os fizer entrar na terra dos cananeus e entregá-la a vocês, como jurou a vocês e aos seus antepassados,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 13, 12, 'separem para o Senhor o primeiro nascido de todo ventre. Todos os primeiros machos dos seus rebanhos pertencem ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 13, 13, 'Resgatem com um cordeiro toda primeira cria dos jumentos, mas se não quiserem resgatá-la, quebrem-lhe o pescoço. Resgatem também todo primogênito entre os seus filhos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 13, 14, 'No futuro, quando os seus filhos lhes perguntarem: “Que significa isto?”, digam-lhes: Com mão poderosa o Senhor nos tirou do Egito, da terra da escravidão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 13, 15, 'Quando o faraó resistiu e recusou deixar-nos sair, o Senhor matou todos os primogênitos do Egito, tanto os de homens como os de animais. Por isso sacrificamos ao Senhor os primeiros machos de todo ventre e resgatamos os nossos primogênitos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 13, 16, '“Isto será como sinal em sua mão e símbolo em sua testa de que o Senhor nos tirou do Egito com mão poderosa”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 13, 17, 'Quando o faraó deixou sair o povo, Deus não o guiou pela rota da terra dos filisteus, embora este fosse o caminho mais curto, pois disse: “Se eles se defrontarem com a guerra, talvez se arrependam e voltem para o Egito”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 13, 18, 'Assim, Deus fez o povo dar a volta pelo de­serto, seguindo o caminho que leva ao mar Vermelho. Os israelitas saíram do Egito preparados para lutar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 13, 19, 'Moisés levou os ossos de José, porque José havia feito os filhos de Israel prestarem um juramento, quando disse: “Deus certamente virá em auxílio de vocês; levem então os meus ossos daqui”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 13, 20, 'Os israelitas partiram de Sucote e acam­param em Etã, junto ao deserto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 13, 21, 'Durante o dia o Senhor ia adiante deles, numa coluna de nuvem, para guiá-los no caminho, e de noite, numa coluna de fogo, para iluminá-los, e assim podiam caminhar de dia e de noite.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 13, 22, 'A coluna de nuvem não se afastava do povo de dia, nem a coluna de fogo, de noite.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 1, 'Disse o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 2, 'Diga aos israelitas que mudem o rumo e acampem perto de Pi-Hairote, entre Migdol e o mar. Acampem à beira-mar, defronte de Baal-Zefom.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 3, 'O faraó pensará que os israelitas estão vagando confusos, cercados pelo deserto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 4, 'Então endure­cerei o coração do faraó, e ele os perseguirá. Todavia, eu serei glorificado por meio do faraó e de todo o seu exército; e os egípcios saberão que eu sou o Senhor. E assim fizeram os israelitas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 5, 'Contaram ao rei do Egito que o povo havia fugido. Então o faraó e os seus conselheiros mudaram de idéia e disseram: “O que foi que fizemos? Deixamos os israelitas saírem e perdemos os nossos escravos!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 6, 'Então o faraó mandou aprontar a sua carruagem e levou consigo o seu exército.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 7, 'Levou todos os carros de guerra do Egito, inclusive seiscentos dos melhores desses carros, cada um com um oficial no comando.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 8, 'O Senhor endureceu o coração do faraó, rei do Egito, e este perseguiu os israelitas, que marchavam triunfantemente.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 9, 'Os egípcios, com todos os cavalos e carros de guerra do faraó, os cavaleiros e a infantaria, saíram em perseguição aos israelitas e os alcançaram quando estavam acampados à beira-mar, perto de Pi-Hairote, defronte de Baal-Zefom.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 10, 'Ao aproximar-se o faraó, os israelitas olharam e avistaram os egípcios que marchavam na direção deles. E, aterrorizados, clamaram ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 11, 'Disseram a Moisés: Foi por falta de túmulos no Egito que você nos trouxe para morrermos no deserto? O que você fez conosco, tirando-nos de lá?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 12, 'Já lhe tínhamos dito no Egito: Deixe-nos em paz! Seremos escravos dos egípcios! Antes ser escravos dos egípcios do que morrer no deserto!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 13, 'Moisés respondeu ao povo: Não tenham medo. Fiquem firmes e vejam o livramento que o Senhor lhes trará hoje, porque vocês nunca mais verão os egípcios que hoje vêem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 14, 'O Senhor lutará por vocês; tão somente acalmem-se.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 15, 'Disse então o Senhor a Moisés: Por que você está clamando a mim? Diga aos israelitas que sigam avante.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 16, 'Erga a sua vara e es­tenda a mão sobre o mar, e as águas se dividirão para que os israelitas atravessem o mar em terra seca.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 17, 'Eu, porém, endurecerei o coração dos egípcios e eles os perseguirão. E serei glorificado com a derrota do faraó e de todo o seu exército, com seus carros de guerra e seus cavaleiros.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 18, 'Os egípcios saberão que eu sou o Senhor quando eu for glorificado com a derrota do faraó, com seus carros de guerra e seus cavaleiros.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 19, 'A seguir o anjo de Deus que ia à frente dos exércitos de Israel retirou-se, colocando-se atrás deles. A coluna de nuvem também saiu da frente deles e se pôs atrás,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 20, 'entre os egípcios e os israelitas. A nuvem trouxe trevas para um e luz para o outro, de modo que os egípcios não puderam aproximar-se dos israelitas durante toda a noite.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 21, 'Então Moisés estendeu a mão sobre o mar, e o Senhor afastou o mar e o tornou em terra seca, com um forte vento oriental que soprou toda aquela noite. As águas se dividiram,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 22, 'e os israelitas atravessaram pelo meio do mar em terra seca, tendo uma parede de água à direita e outra à esquerda.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 23, 'Os egípcios os perseguiram, e todos os cavalos, carros de guerra e cavaleiros do faraó foram atrás deles até o meio do mar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 24, 'No fim da madrugada, do alto da coluna de fogo e de nuvem, o Senhor viu o exército dos egípcios e o pôs em confusão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 25, 'Fez que as rodas dos seus carros começassem a soltar-se, de forma que tinham dificuldade em conduzi-los. E os egípcios gritaram: “Vamos fugir dos israelitas! O Senhor está lutando por eles contra o Egito”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 26, 'Mas o Senhor disse a Moisés: “Estenda a mão sobre o mar para que as águas voltem sobre os egípcios, sobre os seus carros de guerra e sobre os seus cavaleiros”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 27, 'Moisés estendeu a mão sobre o mar, e ao raiar do dia o mar voltou ao seu lugar. Quando os egípcios estavam fugindo, foram de encontro às águas, e o Senhor os lançou ao mar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 28, 'As águas volta­ram e encobriram os seus carros de guerra e os seus cavaleiros, todo o exército do faraó que havia perseguido os israelitas mar adentro. Ninguém sobreviveu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 29, 'Mas os israelitas atravessaram o mar pisando em terra seca, tendo uma parede de água à direita e outra à esquerda.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 30, 'Naquele dia o Senhor salvou Israel das mãos dos egípcios, e os israelitas viram os egípcios mortos na praia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 14, 31, 'Israel viu o grande poder do Senhor contra os egípcios, temeu o Senhor e pôs nele a sua confiança, como também em Moisés, seu servo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 1, 'Então Moisés e os israelitas entoa­ram este cântico ao Senhor: Cantarei ao Senhor, pois triunfou gloriosamente. Lançou ao mar o cavaloe o seu cavaleiro!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 2, 'O Senhor é a minha força e a minha canção; ele é a minha salvação! Ele é o meu Deus e eu o louvarei, é o Deus de meu pai, e eu o exaltarei!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 3, 'O Senhor é guerreiro, o seu nome é Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 4, 'Ele lançou ao maros carros de guerra e o exército do faraó. Os seus melhores oficiais afogaram-se no mar Vermelho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 5, 'Águas profundas os encobriram; como pedra desceram ao fundo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 6, 'Senhor, a tua mão direita foi majestosa em poder. Senhor, a tua mão direita despedaçou o inimigo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 7, 'Em teu triunfo grandioso, derrubaste os teus adversários. Enviaste o teu furor flamejante, que os consumiu como palha.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 8, 'Pelo forte sopro das tuas narinas as águas se amontoaram. As águas turbulentas firmaram-se como muralha; as águas profundas congelaram-se no coração do mar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 9, 'O inimigo se gloriava: “Eu os perseguirei e os alcançarei, dividirei o despojo e os devorarei. Com a espada na mão, eu os destruirei”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 10, 'Mas enviaste o teu sopro, e o mar os encobriu. Afundaram como chumbo nas águas volumosas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 11, 'Quem entre os deuses é semelhante a ti, Senhor? Quem é semelhante a ti? Majestoso em santidade, terrível em feitos gloriosos, autor de maravilhas?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 12, 'Estendes a tua mão direita e a terra os engole.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 13, 'Com o teu amor conduzes o povo que resgataste; com a tua força tu o levas à tua santa habitação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 14, 'As nações ouvem e estremecem; angústia se apoderado povo da Filístia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 15, 'Os chefes de Edomficam aterrorizados, os poderosos de Moabe são tomados de tremor, o povo de Canaã esmorece;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 16, 'terror e medo caem sobre eles; pelo poder do teu braço ficam paralisados como pedra, até que passe o teu povo, ó Senhor, até que passe o povo que tu compraste.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 17, 'Tu o farás entrar e o plantarás no monte da tua herança, no lugar, ó Senhor, que fizeste para a tua habitação, no santuário, ó Senhor, que as tuas mãos estabeleceram.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 18, 'O Senhor reinará eternamente.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 19, 'Quando os cavalos, os carros de guerra e os cavaleiros do faraó entraram no mar, o Senhor fez que as águas do mar se voltassem sobre eles, mas os israelitas atravessaram o mar pisando em terra seca.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 20, 'Então Miriã, a profetisa, irmã de Arão, pegou um tamborim e todas as mulheres a seguiram, tocando tamborins e dançando.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 21, 'E Miriã lhes respondia, cantando: “Cantem ao Senhor, pois triunfou gloriosamente. Lançou ao mar o cavalo e o seu cavaleiro”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 22, 'Depois Moisés conduziu Israel desde o mar Vermelho até o deserto de Sur. Durante três dias caminharam no deserto sem encontrar água.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 23, 'Então chegaram a Mara, mas não pude­ram beber das águas de lá porque eram amargas. Esta é a razão porque o lugar chama-se Mara.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 24, 'E o povo começou a reclamar a Moisés, dizendo: “Que beberemos?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 25, 'Moisés clamou ao Senhor, e este lhe indicou um arbusto. Ele o lançou na água, e esta se tornou boa. Em Mara o Senhor lhes deu leis e ordenanças, e os colocou à prova,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 26, 'dizendo-lhes: “Se vocês derem atenção ao Senhor, o seu Deus, e fizerem o que ele aprova, se derem ouvidos aos seus mandamentos e obedecerem a todos os seus decretos, não trarei sobre vocês nenhuma das doenças que eu trouxe sobre os egípcios, pois eu sou o Senhor que os cura”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 15, 27, 'Depois chegaram a Elim, onde havia doze fontes de água e setenta palmeiras; e acamparam junto àquelas águas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 1, 'Toda a comunidade de Israel partiu de Elim e chegou ao deserto de Sim, que fica entre Elim e o Sinai. Foi no décimo quinto dia do segundo mês, depois que saíram do Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 2, 'No deserto, toda a comunidade de Israel recla­mou a Moisés e Arão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 3, 'Disseram-lhes os israelitas: “Quem dera a mão do Senhor nos tivesse matado no Egito! Lá nos sentávamos ao redor das panelas de carne e comíamos pão à vontade, mas vocês nos trouxeram a este deserto para fazer morrer de fome toda esta multidão!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 4, 'Disse, porém, o Senhor a Moisés: Eu lhes farei chover pão do céu. O povo sairá e recolherá diariamente a porção necessária para aquele dia. Com isso os porei à prova para ver se seguem ou não as minhas instruções.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 5, 'No sexto dia trarão para ser preparado o dobro do que recolhem nos outros dias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 6, 'Assim Moisés e Arão disseram a todos os israelitas: Ao entardecer, vocês saberão que foi o Senhor quem os tirou do Egito,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 7, 'e ama­nhã cedo verão a glória do Senhor, porque o Senhor ouviu a queixa de vocês contra ele. Quem somos nós para que vocês reclamem a nós?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 8, 'Disse ainda Moisés: “O Senhor lhes dará carne para comer ao entardecer e pão à vontade pela manhã, porque ele ouviu as suas queixas contra ele. Quem somos nós? Vocês não estão reclamando de nós, mas do Senhor”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 9, 'Disse Moisés a Arão: “Diga a toda a comunidade de Israel que se apresente ao Senhor, pois ele ouviu as suas queixas”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 10, 'Enquanto Arão falava a toda a comunidade, todos olharam em direção ao deserto, e a glória do Senhor apareceu na nuvem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 11, 'E o Senhor disse a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 12, '“Ouvi as queixas dos israelitas. Responda-lhes que ao pôr-do-sol vocês comerão carne, e ao amanhecer se fartarão de pão. Assim saberão que eu sou o Senhor, o seu Deus”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 13, 'No final da tarde, apareceram codornizes que cobriram o lugar onde estavam acampados; ao amanhecer havia uma camada de orvalho ao redor do acampamento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 14, 'Depois que o orvalho secou, flocos finos semelhantes a geada estavam sobre a superfície do deserto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 15, 'Quan­do os israelitas viram aquilo, começaram a pergun­tar uns aos outros: “Que é isso?”, pois não sabiam do que se tratava. Disse-lhes Moisés: “Este é o pão que o Senhor lhes deu para comer.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 16, 'Assim ordenou o Senhor: “Cada chefe de família recolha quanto precisar: um jarro para cada pessoa da sua tenda”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 17, 'Os israelitas fizeram como lhes fora dito; alguns recolheram mais, outros menos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 18, 'Quando mediram com o jarro, quem tinha recolhido muito não teve demais, e não faltou a quem tinha recolhido pouco. Cada um recolheu quanto precisava.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 19, '“Ninguém deve guardar nada para a manhã seguinte”, ordenou-lhes Moisés.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 20, 'Todavia, alguns deles não deram atenção a Moisés e guardaram um pouco até a manhã seguinte, mas aquilo criou bicho e começou a cheirar mal. Por isso Moisés irou-se contra eles.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 21, 'Cada manhã todos recolhiam quanto precisavam, pois, quando o sol esquentava, aquilo se derretia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 22, 'No sexto dia recolheram o dobro: dois jarros para cada pessoa; e os líderes da comunidade foram contar isso a Moisés,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 23, 'que lhes explicou: “Foi isto que o Senhor ordenou: “Ama­nhã será dia de descanso, sábado consa­grado ao Senhor. Assem e cozinhem o que quiserem. Guardem o que sobrar até a manhã seguinte””.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 24, 'E eles o guardaram até a manhã seguinte, como Moisés tinha ordenado, e não cheirou mal nem criou bicho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 25, '“Comam-no hoje”, disse Moisés, “pois hoje é o sábado do Senhor. Hoje, vocês não o encontrarão no ter­reno.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 26, 'Durante seis dias vocês podem recolhê-lo, mas, no sétimo dia, o sábado, nada acharão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 27, 'Apesar disso, alguns deles saíram no sétimo dia para recolhê-lo, mas não encontraram nada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 28, 'Então o Senhor disse a Moisés: Até quando vocês se recusarão a obedecer aos meus mandamentos e às minhas instruções?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 29, 'Vejam que o Senhor lhes deu o sábado; e por isso, no sexto dia, ele lhes dá pão para dois dias. No sétimo dia, fiquem todos onde estiverem; ninguém deve sair.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 30, 'Então o povo descansou no sétimo dia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 31, 'O povo de Israel chamou maná àquele pão. Era branco como semente de coentro e tinha gosto de bolo de mel.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 32, 'Disse Moisés: “O Senhor ordenou-lhes que recolham um jarro de maná e que o guardem para as futuras gerações, para que vejam o pão que lhes dei no deserto, quando os tirei do Egito”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 33, 'Então Moisés disse a Arão: “Ponha numa vasilha a medida de um jarro de maná, e coloque-a diante do Senhor, para que seja conservado para as futuras gerações”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 34, 'Em obediência ao que o Senhor tinha ordenado a Moisés, Arão colocou o maná junto às tábuas da aliança, para ali ser guardado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 35, 'Os israelitas comeram maná durante quarenta anos, até chegarem a uma terra habitável; comeram maná até chegarem às fronteiras de Canaã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 16, 36, '(O jarro é a décima parte de uma arroba. )');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 17, 1, 'Toda a comunidade de Israel partiu do deserto de Sim, andando de um lugar para outro, conforme a ordem do Senhor. Acamparam em Refidim, mas lá não havia água para beber.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 17, 2, 'Por essa razão queixaram-se a Moisés e exigiram: “Dê-nos água para beber”. Ele respondeu: “Por que se queixam a mim? Por que colocam o Senhor à prova?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 17, 3, 'Mas o povo estava sedento e reclamou a Moisés: “Por que você nos tirou do Egito? Foi para matar de sede a nós, aos nossos filhos e aos nossos rebanhos?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 17, 4, 'Então Moisés clamou ao Senhor: “Que farei com este povo? Estão a ponto de apedrejar-me!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 17, 5, 'Respondeu-lhe o Senhor: Passe à frente do povo. Leve com você algumas das autoridades de Israel, tenha na mão a vara com a qual você feriu o Nilo e vá adiante.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 17, 6, 'Eu estarei à sua espera no alto da rocha do monte Horebe. Bata na rocha, e dela sairá água para o povo beber. Assim fez Moisés, à vista das autoridades de Israel.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 17, 7, 'E chamou aquele lugar Massá e Meribá, porque ali os israelitas reclamaram e puseram o Senhor à prova, dizendo: “O Senhor está entre nós, ou não?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 17, 8, 'Sucedeu que os amalequitas vieram atacar os israelitas em Refidim.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 17, 9, 'Então Moisés disse a Josué: “Escolha alguns dos nossos homens e lute contra os amalequitas. Amanhã tomarei posição no alto da colina, com a vara de Deus em minhas mãos”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 17, 10, 'foi então lutar contra os amale­quitas, conforme Moisés tinha ordenado. Moisés, Arão e Hur, porém, subiram ao alto da colina.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 17, 11, 'Enquanto Moisés mantinha as mãos erguidas, os israelitas venciam; quando, porém, as abaixava, os amalequitas venciam.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 17, 12, 'Quando as mãos de Moisés já estavam cansadas, eles pegaram uma pedra e a colocaram debaixo dele, para que nela se assentasse. Arão e Hur mantiveram erguidas as mãos de Moisés, um de cada lado, de modo que as mãos permaneceram firmes até o pôr-do-sol.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 17, 13, 'E Josué derrotou o exército amalequita ao fio da espada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 17, 14, 'Depois o Senhor disse a Moisés: “Escreva isto num rolo, como memorial, e declare a Josué que farei que os amalequitas sejam esquecidos para sempre debaixo do céu”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 17, 15, 'Moisés construiu um altar e chamou-lhe “o Senhor é minha bandeira”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 17, 16, 'E jurou: “Pelo trono do Senhor!  O Senhor fará guerra contra os amalequitas de geração em geração”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 1, 'Jetro, sacerdote de Midiã e sogro de Moisés, soube de tudo o que Deus tinha feito por Moisés e pelo povo de Israel, como o Senhor havia tirado Israel do Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 2, 'Moisés tinha mandado Zípora, sua mulher, para a casa de seu sogro Jetro, que a recebeu');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 3, 'com os seus dois filhos. Um deles chamava-se Gérson, pois Moisés dissera: “Tornei-me imigrante em terra estrangeira”;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 4, 'e o outro chamava-se Eliézer, pois dissera: “O Deus de meu pai foi o meu ajudador; livrou-me da espada do faraó”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 5, 'Jetro, sogro de Moisés, veio com os filhos e a mulher de Moisés encontrá-lo no de­serto, onde estava acampado, perto do monte de Deus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 6, 'E Jetro mandou dizer-lhe: “Eu, seu sogro Jetro, estou indo encontrá-lo, e comigo vão sua mulher e seus dois filhos”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 7, 'Então Moisés saiu ao encontro do so­gro, curvou-se e beijou-o; trocaram saudações e depois entraram na tenda.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 8, 'Então Moisés contou ao sogro tudo quanto o Senhor tinha feito ao faraó e aos egípcios por amor a Israel e também todas as dificuldades que tinham enfrentado pelo caminho e como o Senhor os livrara.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 9, 'Jetro alegrou-se ao ouvir todas as coisas boas que o Senhor tinha feito a Israel, libertando-o das mãos dos egípcios.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 10, 'Disse ele: Bendito seja o Senhor que libertou vocês das mãos dos egípcios e do faraó; que livrou o povo das mãos dos egípcios!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 11, 'Agora sei que o Senhoré maior do que todos os outros deuses, pois ele os superou exatamente naquilo de que se vangloriavam.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 12, 'Então Jetro, sogro de Moisés, ofereceu um holocausto e sacrifícios a De­us, e Arão veio com todas as autoridades de Israel para comerem com o sogro de Moisés na presença de Deus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 13, 'No dia seguinte Moisés assentou-se para julgar as questões do povo, e este permaneceu em pé diante dele, desde a manhã até o cair da tarde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 14, 'Quando o seu sogro viu tudo o que ele estava fazendo pelo povo, disse: “Que é que você está fazendo? Por que só você se assenta para julgar, e todo este povo o espera em pé, desde a manhã até o cair da tarde?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 15, 'Moisés lhe respondeu: O povo me procura para que eu consulte a Deus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 16, 'Toda vez que alguém tem uma questão, esta me é trazida, e eu decido entre as partes, e ensino-lhes os decretos e leis de Deus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 17, 'Respondeu o sogro de Moisés: O que você está fazendo não é bom.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 18, 'Você e o seu povo ficarão esgotados, pois essa tarefa lhe é pesada demais. Você não pode executá-la sozinho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 19, 'Agora, ouça-me! Eu lhe darei um conselho, e que Deus esteja com você! Seja você o representante do povo diante de Deus e leve a Deus as suas questões.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 20, 'Oriente-os quanto aos decretos e leis, mostrando-lhes como devem viver e o que devem fazer.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 21, 'Mas escolha dentre todo o povo homens capazes, tementes a Deus, dignos de confiança e inimigos de ganho desonesto. Estabeleça-os como chefes de mil, de cem, de cinqüenta e de dez.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 22, 'Eles estarão sempre à disposição do povo para julgar as questões. Trarão a você apenas as questões difíceis; as mais simples decidirão sozinhos. Isso tornará mais leve o seu fardo, porque eles o dividirão com você.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 23, 'Se você assim fizer, e se assim Deus ordenar, você será capaz de suportar as dificuldades, e todo este povo voltará para casa satisfeito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 24, 'Moisés aceitou o conselho do sogro e fez tudo como ele tinha sugerido.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 25, 'Escolheu homens capazes de todo o Israel e colocou-os como líderes do povo: chefes de mil, de cem, de cinqüenta e de dez.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 26, 'Estes ficaram como juízes permanentes do povo. As questões difíceis levavam a Moisés; as mais simples, porém, eles mesmos resolviam.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 18, 27, 'Então Moisés e seu sogro se despediram, e este voltou para a sua terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 1, 'No dia em que se completaram três meses que os israelitas haviam saído do Egito, chegaram ao deserto do Sinai.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 2, 'Depois de saí­rem de Refidim, entraram no deserto do Sinai, e Israel acampou ali, diante do monte.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 3, 'Logo Moisés subiu o monte para encontrar-se com Deus. E o ­Senhoro chamou do monte, dizendo: Diga o seguinte aos des­cendentes de Jacó e declare aos israelitas:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 4, 'Vocês viram o que fiz ao Egito e como os transportei sobre asas de águias e os trouxe para junto de mim.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 5, 'Agora, se me obedecerem fielmente e guardarem a minha aliança, vocês serão o meu tesouro pessoal dentre todas as nações. Embora toda a terra seja minha,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 6, 'vocês serão para mim um reino de sacerdotes e uma nação santa. Essas são as palavras que você dirá aos israelitas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 7, 'Moisés voltou, convocou as autoridades do povo e lhes expôs tudo o que o Senhor havia-lhe mandado falar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 8, 'O povo todo respondeu unânime: “Faremos tudo o que o Senhor ordenou”. E Moisés levou ao ­Senhora resposta do povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 9, 'Disse o Senhor a Moisés: “Virei a você numa densa nuvem, a fim de que o povo, ouvindo-me falar-lhe, passe a confiar sempre em você”. Então Moisés relatou ao Senhor o que o povo lhe dissera.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 10, 'E o Senhor disse a Moisés: Vá ao povo e consagre-o hoje e amanhã. Eles deverão lavar as suas vestes');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 11, 'e estar prontos no terceiro dia, porque nesse dia o Senhor descerá sobre o monte Sinai, à vista de todo o povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 12, 'Esta­beleça limites em torno do monte e diga ao povo: Tenham o cuidado de não subir ao monte e de não tocar na sua base. Quem tocar no monte certamente será morto;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 13, 'será apedrejado ou morto a flechadas. Ninguém deverá tocá-lo com a mão. Seja homem, seja animal, não viverá. Somente quando a corneta soar um toque longo eles poderão subir ao monte.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 14, 'Tendo Moisés descido do monte, consagrou o povo; e eles lavaram as suas vestes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 15, 'Disse ele então ao povo: “Preparem-se para o terceiro dia, e até lá não se acheguem a mulher”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 16, 'Ao amanhecer do terceiro dia houve trovões e raios, uma densa nuvem cobriu o mon­te, e uma trombeta ressoou fortemente. Todos no acampamento tremeram de medo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 17, 'Moisés levou o povo para fora do acampamento, para encontrar-se com Deus, e eles ficaram ao pé do monte.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 18, 'O monte Sinai estava coberto de fumaça, pois o Senhor tinha desci­do sobre ele em chamas de fogo. Dele subia fumaça como que de uma fornalha; todo o monte tremia violentamente,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 19, 'e o som da trombeta era cada vez mais forte. Então Moisés falou, e a voz de Deus lhe respondeu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 20, 'O Senhor desceu ao topo do monte Sinai e chamou Moisés para o alto do monte. Moisés subiu');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 21, 'e o Senhor lhe disse: Desça e alerte o povo que não ultrapasse os limites, para ver o Senhor, e muitos deles pereçam.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 22, 'Mesmo os sacerdotes que se aproximarem do Senhor devem consagrar-se; senão o Senhor os fulminará.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 23, 'Moisés disse ao Senhor: “O povo não pode subir ao monte Sinai, pois tu mesmo nos avisaste: “Estabeleça um limite em torno do monte e declare-o santo””.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 24, 'O Senhor respondeu: “Desça e depois torne a subir, acompanhado de Arão. Quanto aos sacerdotes e ao povo, não devem ultrapassar o limite para subir ao Senhor; senão, o Senhor os fulminará”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 19, 25, 'Então Moisés desceu e avisou o povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 1, 'E Deus falou todas estas palavras:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 2, 'Eu sou o Senhor, o teu Deus, que te tirou do Egito, da terra da escravidão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 3, 'Não terás outros deuses além de mim.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 4, 'Não farás para ti nenhum ídolo, nenhuma imagem de qualquer coisa no céu, na terra, ou nas águas debaixo da terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 5, 'Não te prostrarás diante deles nem lhes prestarás culto, porque eu, o Senhor, o teu Deus, sou Deus zeloso, que castigo os filhos pelos pecados de seus pais até a terceira e quarta geração daqueles que me desprezam,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 6, 'mas trato com bondade até mil gerações aos que me amam e obedecem aos meus mandamentos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 7, 'Não tomarás em vão o nome do Senhor, o teu Deus, pois o Senhor não deixará impune quem tomar o seu nome em vão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 8, 'Lembra-te do dia de sábado, para santificá-lo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 9, 'Trabalharás seis dias e neles farás todos os teus trabalhos,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 10, 'mas o sétimo dia é o sábado dedicado ao Senhor, o teu Deus. Nesse dia não farás trabalho algum, nem tu, nem teus filhos ou filhas, nem teus servos ou servas, nem teus animais, nem os estrangeiros que morarem em tuas cidades.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 11, 'Pois em seis dias o Senhor fez os céus e a terra, o mar e tudo o que neles existe, mas no sétimo dia descansou. Portanto, o Senhor abençoou o sétimo dia e o santificou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 12, 'Honra teu pai e tua mãe, a fim de que tenhas vida longa na terra que o Senhor, o teu Deus, te dá.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 13, 'Não matarás.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 14, 'Não adulterarás.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 15, 'Não furtarás.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 16, 'Não darás falso testemunho contra o teu próximo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 17, '“Não cobiçarás a casa do teu próximo. Não cobiçarás a mulher do teu próximo, nem seus servos ou servas, nem seu boi ou jumento, nem coisa alguma que lhe pertença”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 18, 'Vendo-se o povo diante dos trovões e dos relâmpagos, e do som da trombeta e do monte fumegando, todos tremeram assustados. Ficaram a distância');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 19, 'e disseram a Moisés: “Fa­la tu mesmo conosco, e ouviremos. Mas que Deus não fale conosco, para que não morra­mos”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 20, 'Moisés disse ao povo: “Não tenham medo! Deus veio prová-los, para que o temor de Deus esteja em vocês e os livre de pecar”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 21, 'Mas o povo permaneceu a distância, ao passo que Moisés aproximou-se da nuvem escura em que Deus se encontrava.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 22, 'O Senhor disse a Moisés: Diga o seguinte aos israelitas: Vocês viram por si mes­mos que do céu lhes falei:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 23, 'não façam ídolos de prata nem de ouro para me representarem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 24, 'Façam-me um altar de terra e nele sacrifiquem-me os seus holocaustos e as suas ofertas de comunhão, as suas ovelhas e os seus bois. Onde quer que eu faça celebrar o meu nome, virei a vocês e os abençoarei.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 25, 'Se me fizerem um altar de pedras, não o façam com pedras lavradas, porque o uso de ferramentas o profanaria.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 20, 26, 'Não subam por degraus ao meu altar, para que nele não seja exposta a sua nu­dez.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 1, 'São estas as leis que você procla­mará ao povo:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 2, 'Se você comprar um escravo hebreu, ele o servirá por seis anos. Mas no sétimo ano será liberto, sem precisar pagar nada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 3, 'Se che­gou solteiro, solteiro receberá liberdade; mas se chegou casado, sua mulher irá com ele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 4, 'Se o seu senhor lhe tiver dado uma mulher, e esta lhe tiver dado filhos ou filhas, a mulher e os filhos pertencerão ao senhor; somente o homem sairá livre.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 5, 'Se, porém, o escravo declarar: “Eu amo o meu senhor, a minha mulher e os meus filhos, e não quero sair livre”,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 6, 'o seu senhor o levará perante os juízes. Terá que levá-lo à porta ou à lateral da porta e furar a sua orelha. Assim, ele será seu escravo por toda a vida.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 7, 'Se um homem vender sua filha como escrava, ela não será liberta como os escravos homens.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 8, 'Se ela não agradar ao seu senhor que a escolheu, ele deverá permitir que ela seja res­gatada. Não poderá vendê-la a estrangeiros, pois isso seria deslealdade para com ela.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 9, 'Se o seu senhor a escolher para seu filho, lhe dará os direitos de uma filha.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 10, 'Se o senhor tomar uma segunda mulher para si, não poderá privar a primeira de alimento, de roupas e dos direitos conjugais.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 11, 'Se não lhe garantir essas três coisas, ela pode­rá ir embora sem precisar pagar nada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 12, 'Quem ferir um homem e o matar terá que ser executado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 13, 'Todavia, se não o fez intencionalmente, mas Deus o permitiu, designei um lugar para onde poderá fugir.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 14, 'Mas se alguém tiver planejado matar outro deliberadamente, tire-o até mesmo do meu altar e mate-o.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 15, 'Quem agredir o próprio pai ou a própria mãe terá que ser executado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 16, 'Aquele que seqüestrar alguém e vendê-lo ou for apanhado com ele em seu poder, terá que ser executado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 17, 'Quem amaldiçoar seu pai ou sua mãe terá que ser executado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 18, 'Se dois homens brigarem e um deles ferir o outro com uma pedra ou com o punho e o outro não morrer, mas cair de cama,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 19, 'aquele que o feriu será absolvido, se o outro se levantar e caminhar com o auxílio de uma bengala; todavia, ele terá que indenizar o homem ferido pelo tempo que este perdeu e responsabilizar-se por sua completa recuperação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 20, 'Se alguém ferir seu escravo ou escrava com um pedaço de pau, e como resultado o escravo morrer, será punido;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 21, 'mas se o escravo sobreviver um ou dois dias, não será punido, visto que é sua propriedade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 22, 'Se homens brigarem e ferirem uma mulher grávida, e ela der à luz prematuramen­te, não havendo, porém, nenhum dano sério, o ofensor pagará a indenização que o marido daquela mulher exigir, conforme a determinação dos juízes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 23, 'Mas, se houver danos graves, a pena será vida por vida,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 24, 'olho por olho, dente por dente, mão por mão, pé por pé,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 25, 'queimadura por queimadura, ferida por ferida, contusão por contusão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 26, 'Se alguém ferir o seu escravo ou sua escrava no olho e o cegar, terá que libertar o escravo como compensação pelo olho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 27, 'Se quebrar um dente de um escravo ou de uma escra­va, terá que libertar o escravo como compensação pelo dente.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 28, 'Se um boi chifrar um homem ou uma mulher, causando-lhe a morte, o boi terá que ser apedrejado até a morte, e a sua carne não pode­rá ser comida. Mas o dono do boi será absolvi­do.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 29, 'Se, todavia, o boi costumava chifrar e o dono, ainda que alertado, não o manteve preso, e o boi matar um homem ou uma mulher, o boi será apedrejado e o dono também terá que ser morto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 30, 'Caso, porém, lhe peçam um pagamento, poderá resgatar a sua vida pagando o que for exigido.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 31, 'Esta sentença também se aplica no caso de um boi chifrar um menino ou uma menina.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 32, 'Se o boi chifrar um escravo ou escrava, o dono do animal terá que pagar tre­zentos e sessenta gramas de prata ao dono do escravo, e o boi será apedrejado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 33, 'Se alguém abrir ou deixar aberta uma cisterna, não tendo o cuidado de tampá-la, e um jumento ou um boi nela cair,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 34, 'o dono da cister­na terá que pagar o prejuízo, indenizando o dono do animal, e ficará com o animal morto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 35, 'Se o boi de alguém ferir o boi de outro e o matar, venderão o boi vivo e dividirão em partes iguais, tanto o valor do boi vivo como o animal morto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 21, 36, 'Contudo, se o boi costumava chifrar, e o dono não o manteve preso, este terá que pagar boi por boi, e ficará com o que morreu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 1, 'Se alguém roubar um boi ou uma ovelha e abatê-lo ou vendê-lo, terá que restituir cinco bois pelo boi e quatro ovelhas pela ove­lha.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 2, 'Se o ladrão que for pego arrombando for ferido e morrer, quem o feriu não será cul­pado de homicídio,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 3, 'mas se isso acontecer de­pois do nascer do sol, será culpado de homicídio. O ladrão terá que restituir o que rou­bou, mas, se não tiver nada, será vendido para pagar o roubo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 4, 'Se o que foi roubado for encontrado vivo em seu poder, seja boi, seja jumento, seja ovelha, ele deverá restituí-lo em dobro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 5, 'Se alguém levar seu rebanho para pastar num campo ou numa vinha e soltá-lo de modo que venha a pastar no campo de outro homem, fará restituição com o melhor do seu campo ou da sua vinha.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 6, 'Se um fogo se espalhar e alcançar os espinheiros, e queimar os feixes colhidos ou o trigo plantado ou até a lavoura toda, aquele que iniciou o incêndio restituirá o prejuízo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 7, 'Se alguém entregar ao seu próximo prata ou bens para serem guardados e estes forem roubados da casa deste, o ladrão, se for encontrado, terá que restituí-los em dobro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 8, 'Mas se o ladrão não for encontrado, o dono da casa terá que comparecer perante os juízes para que se determine se ele não lançou mão dos bens do outro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 9, 'Sempre que alguém se apossar de boi, jumento, ovelha, roupa ou qualquer outro bem perdido, mas alguém disser: “Isto me pertence”, as duas partes envolvidas levarão o caso aos juízes. Aquele a quem os juízes declararem culpado restituirá o dobro ao seu próximo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 10, 'Se alguém der ao seu próximo o seu jumento, ou boi, ou ovelha ou qualquer outro animal para ser guardado, e o animal morrer, for ferido ou for levado, sem que ninguém o veja,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 11, 'a questão entre eles será resolvida prestando-se um juramento diante do Senhor de que um não lançou mão da propriedade do outro. O dono terá que aceitar isso e nenhuma restituição será exigida.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 12, 'Mas se o animal tiver sido roubado do seu próximo, este terá que fazer restituição ao dono.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 13, 'Se tiver sido despedaçado por um animal selvagem, ele trará como prova o que restou dele; e não terá que fazer restituição.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 14, 'Se alguém pedir emprestado ao seu próximo um animal, e este for ferido ou morrer na ausência do dono, terá que fazer restituição.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 15, 'Mas se o dono estiver presente, o que tomou emprestado não terá que restituí-lo. Se o animal tiver sido alugado, o preço do aluguel cobrirá a perda.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 16, 'Se um homem seduzir uma virgem que ainda não tenha compromisso de casamento e deitar-se com ela, terá que pagar o preço do seu dote, e ela será sua mulher.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 17, 'Mas se o pai recusar-se a entregá-la, ainda assim o homem terá que pagar o equivalente ao dote das virgens.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 18, 'Não deixem viver a feiticeira.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 19, 'Todo aquele que tiver relações sexuais com animal terá que ser executado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 20, 'Quem oferecer sacrifício a qualquer outro deus, e não unicamente ao Senhor, será destruído.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 21, 'Não maltratem nem oprimam o estrangeiro, pois vocês foram estrangeiros no Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 22, 'Não prejudiquem as viúvas nem os órfãos;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 23, 'porque se o fizerem, e eles clamarem a mim, eu certamente atenderei ao seu clamor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 24, 'Com grande ira matarei vocês à espada; suas mulheres ficarão viúvas e seus filhos, órfãos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 25, 'Se fizerem empréstimo a alguém do meu povo, a algum necessitado que viva entre vocês, não cobrem juros dele; não emprestem visando lucro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 26, 'Se tomarem como garantia o manto do seu próximo, devolvam-no até o pôr-do-sol,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 27, 'porque o manto é a única coberta que ele possui para o corpo. Em que mais se deitaria? Quando ele clamar a mim, eu o ouvirei, pois sou misericordioso.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 28, 'Não blasfemem contra Deus nem amaldiçoem uma autoridade do seu povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 29, 'Não retenham as ofertas de suas colheitas. Consagrem-me o primeiro filho de vocês');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 30, 'e a primeira cria das vacas, das ovelhas e das cabras. Durante sete dias a cria ficará com a mãe, mas, no oitavo dia, entreguem-na a mim.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 22, 31, 'Vocês serão meu povo santo. Não comam a carne de nenhum animal despedaçado por feras no campo; joguem-na aos cães.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 1, 'Ninguém faça declarações falsas e não seja cúmplice do ímpio, sendo-lhe testemunha mal-intencionada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 2, 'Não acompanhe a maioria para fazer o mal. Ao testemunhar num processo, não perverta a justiça para apoiar a maioria,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 3, 'nem para favorecer o pobre num processo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 4, 'Se você encontrar perdido o boi ou o jumento que pertence ao seu inimigo, leve-o de volta a ele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 5, 'Se você vir o jumento de alguém que o odeia caído sob o peso de sua carga, não o abandone, procure ajudá-lo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 6, 'Não perverta o direito dos pobres em seus processos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 7, 'Não se envolva em falsas acusações nem condene à morte o inocente e o justo, porque não absolverei o culpado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 8, 'Não aceite suborno, pois o suborno cega até os que têm discernimento e prejudica a causa do justo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 9, 'Não oprima o estrangeiro. Vocês sabem o que é ser estrangeiro, pois foram estrangeiros no Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 10, 'Plantem e colham em sua terra durante seis anos,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 11, 'mas no sétimo deixem-na descansar sem cultivá-la. Assim os pobres do povo poderão comer o que crescer por si, e o que restar ficará para os animais do campo. Façam o mesmo com as suas vinhas e com os seus oli­vais.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 12, 'Em seis dias façam os seus trabalhos, mas no sétimo não trabalhem, para que o seu boi e o seu jumento possam descansar, e o seu escravo e o estrangeiro renovem as forças.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 13, 'Tenham o cuidado de fazer tudo o que lhes ordenei. Não invoquem o nome de outros deuses; não se ouçam tais nomes dos seus lábios.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 14, 'Três vezes por ano vocês me celebrarão festa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 15, 'Celebrem a festa dos pães sem fermento; durante sete dias comam pão sem fermento, como eu lhes ordenei. Façam isso na época determinada do mês de abibe, pois nesse mês vocês saíram do Egito. Ninguém se apresentará a mim de mãos vazias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 16, 'Celebrem a festa da colheita dos primeiros frutos do seu trabalho de semeadura. Celebrem a festa do encerramento da colheita quando, no final do ano, vocês armazenarem as colheitas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 17, 'Três vezes por ano todos os homens devem comparecer diante do Senhor, o Soberano.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 18, 'Não ofereçam o sangue de um sacrifício feito em minha honra com pão fermentado. A gordura das ofertas de minhas festas não deverá ser guardada até a manhã seguinte.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 19, 'Tragam ao santuário do Senhor, o seu Deus, o melhor dos primeiros frutos das suas colheitas. Não cozinhem o cabrito no leite da própria mãe.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 20, 'Eis que envio um anjo à frente de vocês para protegê-los por todo o caminho e fazê-los chegar ao lugar que preparei.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 21, 'Prestem atenção e ouçam o que ele diz. Não se rebelem contra ele, pois não perdoará as suas transgres­sões, pois nele está o meu nome.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 22, 'Se vocês ouvirem atentamente o que ele disser e fizerem tudo o que lhes ordeno, serei inimigo dos seus inimigos, e adversário dos seus adversários.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 23, 'O meu anjo irá à frente de vocês e os fará chegar à terra dos amorreus, dos hititas, dos ferezeus, dos cananeus, dos heveus e dos jebuseus, e eu os exterminarei.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 24, 'Não se curvem diante dos deuses deles, nem lhes prestem culto, nem sigam as suas práticas. Destruam-nos totalmente e quebrem as suas colunas sagradas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 25, 'Pres­tem culto ao Senhor, o Deus de vocês, e ele os abençoará, dando-lhes alimento e água. Tirarei a doença do meio de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 26, 'Em sua terra nenhuma grávida perderá o filho, nem haverá mulher estéril. Farei completar-se o tempo de duração da vida de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 27, 'Mandarei adiante de vocês o meu terror, que porá em confusão todas as nações que vocês encontrarem. Farei que todos os seus inimigos virem as costas e fujam.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 28, 'Causarei pânico entre os heveus, os cananeus e os hititas para expulsá-los de diante de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 29, 'Não os expulsarei num só ano, pois a terra se tornaria desolada e os animais selvagens se multiplicariam, ameaçando vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 30, 'Eu os expulsarei aos poucos, até que vocês sejam numerosos o suficiente para tomarem posse da terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 31, 'Estabelecerei as suas fronteiras desde o mar Vermelho até o mar dos filisteus, e desde o deserto até o Eufrates. Entregarei em suas mãos os povos que vivem na terra, os quais vocês expulsarão de diante de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 32, 'Não façam aliança com eles nem com os seus deuses.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 23, 33, 'Não deixem que esses povos morem na terra de vocês, senão eles os levarão a pecar contra mim, porque prestar culto aos deuses deles será uma armadilha para vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 24, 1, 'Depois Deus disse a Moisés: Subam o monte para encontrar-se com o Senhor, você e Arão, Nadabe e Abiú, e setenta autoridades de Israel. Adorem à distância.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 24, 2, 'Somente Moisés se aproximará do Senhor; os outros não. O povo também não subirá com ele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 24, 3, 'Quando Moisés se dirigiu ao povo e transmitiu-lhes todas as palavras e ordenanças do Senhor, eles responderam em uníssono: “Faremos tudo o que o Senhor ordenou”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 24, 4, 'Mo­isés, então, escreveu tudo o que o Senhor dissera. Na manhã seguinte Moisés levantou-se, construiu um altar ao pé do monte e ergueu doze colunas de pedra, representando as doze tribos de Israel.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 24, 5, 'Em seguida enviou jovens israelitas, que ofereceram holocaustos e novi­lhos como sacrifícios de comunhão ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 24, 6, 'Moisés colocou metade do sangue em tigelas e a outra metade derramou sobre o altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 24, 7, 'Em seguida, leu o Livro da Aliança para o povo, e eles disseram: “Faremos fielmente tudo o que o Senhor ordenou”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 24, 8, 'Depois Moisés aspergiu o sangue sobre o povo, dizendo: “Este é o sangue da aliança que o Senhor fez com vocês de acordo com todas essas palavras”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 24, 9, 'Moisés, Arão, Nadabe, Abiú e setenta autoridades de Israel subiram');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 24, 10, 'e viram o Deus de Israel, sob cujos pés havia algo semelhante a um pavimento de safira, como o céu em seu esplendor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 24, 11, 'Deus, porém, não estendeu a mão para punir esses líderes do povo de Israel; eles viram a Deus, e depois comeram e beberam.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 24, 12, 'Disse o Senhor a Moisés: “Suba o monte, venha até mim, e fique aqui; e lhe darei as tábuas de pedra com a lei e os mandamentos que escrevi para a instrução do povo”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 24, 13, 'Moisés partiu com Josué, seu auxiliar, e subiu ao monte de Deus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 24, 14, 'Disse ele ­às autori­dades de Israel: “Esperem-nos aqui, até que retornemos. Arão e Hur ficarão com vocês; quem tiver alguma questão para resolver, pode­rá procurá-los”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 24, 15, 'Quando Moisés subiu, a nuvem cobriu o monte,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 24, 16, 'e a glória do Senhor permaneceu sobre o monte Sinai. Durante seis dias a nuvem cobriu o monte. No sétimo dia o Senhor cha­mou Moisés do interior da nuvem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 24, 17, 'Aos olhos dos israelitas a glória do Senhor parecia um fogo consumidor no topo do monte.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 24, 18, 'Moisés entrou na nuvem e foi subindo o monte. E permaneceu no monte quarenta dias e quarenta noites.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 1, 'Disse o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 2, 'Diga aos israelitas que me tragam uma oferta. Receba-a de todo aquele cujo coração o compelir a dar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 3, 'Estas são as ofertas que deverá receber deles: ouro, prata e bronze,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 4, 'fios de tecidos azul, roxo e vermelho, linho fino, pêlos de cabra,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 5, 'peles de carneiro tingidas de vermelho, couro, madeira de acácia,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 6, 'azeite para iluminação; especiarias para o óleo da unção e para o incenso aromático;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 7, 'pedras de ônix e outras pedras preciosas para serem encravadas no colete sa­cerdotal e no peitoral.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 8, 'E farão um santuário para mim, e eu habitarei no meio deles.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 9, 'Façam tudo como eu lhe mostrar, conforme o modelo do tabernáculo e de cada utensílio.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 10, 'Faça uma arca de madeira de acácia com um metro e dez centímetros de comprimento, setenta centímetros de largura e setenta centímetros de altura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 11, 'Revista-a de ouro puro, por dentro e por fora, e faça uma moldura de ouro ao seu redor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 12, 'Mande fundir quatro argolas de ouro para ela e prenda-as em seus quatro pés, com duas argolas de um lado e duas do outro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 13, 'Depois faça varas de madeira de acácia, revista-as de ouro');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 14, 'e coloque-as nas argolas laterais da arca, para que possa ser carregada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 15, 'As varas permanecerão nas argolas da arca; não devem ser retiradas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 16, 'Então coloque dentro da arca as tábuas da aliança que lhe darei.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 17, 'Faça uma tampa de ouro puro com um metro e dez centímetros de comprimento por setenta centímetros de largura,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 18, 'com dois querubins de ouro batido nas extremidades da tampa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 19, 'Faça um querubim numa extremidade e o segundo na outra, formando uma só peça com a tampa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 20, 'Os querubins devem ter suas asas estendidas para cima, cobrindo com elas a tampa. Ficarão de frente um para o outro, com o rosto voltado para a tampa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 21, 'Coloque a tampa sobre a arca, e dentro dela as tábuas da aliança que darei a você.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 22, 'Ali, sobre a tampa, no meio dos dois querubins que se encontram sobre a arca da aliança, eu me encontrarei com você e lhe darei todos os meus mandamentos destina­dos aos israelitas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 23, 'Faça uma mesa de madeira de acácia com noventa centímetros de comprimento, qua­renta e cinco centímetros de largura e setenta centímetros de altura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 24, 'Revista-a de ouro puro e faça uma moldura de ouro ao seu redor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 25, 'Faça também ao seu redor uma borda com a largura de quatro dedos e uma moldura de ouro para essa borda.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 26, 'Faça quatro argolas de ouro para a mesa e prenda-as nos quatro cantos dela, onde estão os seus quatro pés.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 27, 'As argolas devem ser presas próximas da borda para que sustentem as varas usadas para carregar a mesa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 28, 'Faça as varas de madeira de acácia, revestindo-as de ouro; com elas se carregará a mesa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 29, 'Faça de ouro puro os seus pratos e o recipiente para incenso, as suas tigelas e as bacias nas quais se derramam as ofertas de bebidas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 30, 'Coloque sobre a mesa os pães da Presença, para que estejam sempre diante de mim.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 31, 'Faça um candelabro de ouro puro e batido. O pedestal, a haste, as taças, as flores e os botões do candelabro formarão com ele uma só peça.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 32, 'Seis braços sairão do candelabro: três de um lado e três do outro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 33, 'Haverá três taças com formato de flor de amêndoa num dos braços, cada uma com botão e flor, e três taças com formato de flor de amêndoa no braço seguinte, cada uma com botão e flor. Assim será com os seis braços que saem do candelabro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 34, 'Na haste do candelabro haverá quatro taças com formato de flor de amên­doa, cada uma com botão e flor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 35, 'Haverá um botão debaixo de cada par dos seis braços que saem do candelabro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 36, 'Os braços com seus botões formarão uma só peça com o candelabro, tudo feito de ouro puro e batido.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 37, 'Faça-lhe também sete lâmpadas e coloque-as nele para que iluminem a frente dele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 38, 'Seus cortadores de pavio e seus apagado­res serão de ouro puro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 39, 'Com trinta e cinco quilos de ouro puro faça o candelabro e todos esses utensílios.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 25, 40, 'Tenha o cuidado de fazê-lo segundo o modelo que lhe foi mostrado no monte.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 1, 'Faça o tabernáculo com dez cortinas internas de linho fino trançado e de fios de tecidos azul, roxo e vermelho, e nelas mande bordar querubins.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 2, 'Todas as cortinas internas terão a mesma medida: doze metros e sessenta centímetros de comprimento e um metro e oitenta centímetros de largura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 3, 'Prenda ­cinco dessas cortinas internas uma com a outra e faça o mesmo com as outra cinco.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 4, 'Faça laçadas de tecido azul ao longo da borda da cortina interna, na extremidade do primeiro conjunto de cortinas internas; o mesmo será feito à cortina interna na extremidade do outro conjunto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 5, 'Faça cinqüenta laçadas numa cortina interna e cinqüenta laçadas na cortina interna que está na extremidade do outro conjunto, de modo que as laçadas estejam opostas umas às outras.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 6, 'Faça também cinqüenta colchetes de ouro com os quais se prenderão as cortinas internas uma na outra, para que o tabernáculo seja um todo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 7, 'Com o total de onze cortinas internas de pêlos de cabra faça uma tenda para cobrir o tabernáculo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 8, 'As onze cortinas internas terão o mesmo tamanho: treze metros e meio de comprimento e um metro e oitenta centímetros de largura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 9, 'Prenda de um lado cinco cortinas internas e também as outras seis do outro lado. Dobre em duas partes a sexta cortina interna na frente da tenda.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 10, 'Faça cinqüenta laçadas ao longo da borda da cortina interna na extremidade do primeiro conjunto de cortinas, e também ao longo da borda da cortina interna do outro conjunto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 11, 'Em seguida faça cinqüenta colchetes de bronze e ponha-os nas laçadas para unir a tenda como um todo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 12, 'Quan­to à sobra no comprimento das cortinas internas da tenda, a meia cortina interna que sobrar será pendurada na parte de trás do tabernáculo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 13, 'As dez cortinas internas serão quarenta e cinco centímetros mais compridas de cada lado; e o que sobrar será pendurado nos dois lados do tabernáculo, para cobri-lo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 14, 'Faça também para a tenda uma cobertura de pele de carneiro tingida de vermelho, e por cima desta uma cobertura de couro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 15, 'Faça armações verticais de madeira de acácia para o tabernáculo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 16, 'Cada armação terá quatro metros e meio de comprimento por setenta centímetros de largura,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 17, 'com dois encaixes paralelos um ao outro. Todas as armações do tabernáculo devem ser feitas dessa maneira.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 18, 'Faça vinte armações para o lado sul do tabernáculo');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 19, 'e quarenta bases de prata debaixo delas: duas bases para cada armação, uma debaixo de cada encaixe.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 20, 'Para o outro lado, o lado norte do tabernáculo, faça vinte armações');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 21, 'e quarenta bases de prata, duas debaixo de cada armação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 22, 'Faça seis armações para o lado ocidental do tabernáculo,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 23, 'e duas armações na parte de trás, nos cantos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 24, 'As armações nesses dois cantos serão duplas, desde a parte inferior até a superior, colocadas numa única argola; ambas serão assim.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 25, 'Desse modo, haverá oito armações e dezesseis bases de prata, duas debaixo de cada armação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 26, 'Faça também travessões de madeira de acácia: cinco para as armações de um lado do tabernáculo,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 27, 'cinco para as do outro lado e cinco para as do lado ocidental, na parte de trás do tabernáculo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 28, 'O travessão central se estenderá de uma extremidade à outra entre as armações.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 29, 'Revista de ouro as armações e faça argolas de ouro para sustentar os travessões, os quais também terão que ser revestidos de ouro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 30, 'Faça o tabernáculo de acordo com o modelo que lhe foi mostrado no monte.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 31, 'Faça um véu de linho fino trançado e de fios de tecidos azul, roxo e vermelho, e mande bordar nele querubins.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 32, 'Pendure-o com ganchos de ouro em quatro colunas de madeira de acácia revestidas de ouro e fincadas em quatro bases de prata.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 33, 'Pendure o véu pelos colchetes e coloque atrás do véu a arca da aliança. O véu separará o Lugar Santo do Lugar Santíssimo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 34, 'Coloque a tampa sobre a arca da aliança no Lugar Santíssimo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 35, 'Coloque a mesa do lado de fora do véu, no lado norte do tabernáculo; e o candelabro em frente dela, no lado sul.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 36, 'Para a entrada da tenda faça uma cortina de linho fino trançado e de fios de tecidos azul, roxo e vermelho, obra de bordador.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 26, 37, 'Faça ganchos de ouro para essa cortina e cinco colunas de madeira de acácia revestidas de ouro. Mande fundir para eles cinco bases de bronze.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 27, 1, 'Faça um altar de madeira de acácia. Será quadrado, com dois metros e vinte e cinco centímetros de largura e um metro e trinta e cinco centímetros de altura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 27, 2, 'Faça uma ponta em forma de chifre em cada um dos quatro cantos, formando uma só peça com o altar, que será revestido de bronze.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 27, 3, 'Faça de bronze todos os seus utensílios: os recipientes para recolher cinzas, as pás, as bacias de aspersão, os garfos para carne e os braseiros.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 27, 4, 'Faça também para ele uma grelha de bronze em forma de rede e uma argola de bronze em cada um dos quatro cantos da grelha.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 27, 5, 'Coloque-a abaixo da beirada do altar, de maneira que fique a meia altura do altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 27, 6, 'Faça varas de madeira de acácia para o altar e revista-as de bronze.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 27, 7, 'Estas varas serão colocadas nas argolas, dos dois lados do altar, quando este for carregado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 27, 8, 'Faça o altar oco e de tábuas, conforme lhe foi mostrado no monte.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 27, 9, 'Faça um pátio para o tabernáculo. O lado sul terá quarenta e cinco metros de comprimento, e cortinas externas de linho fino trançado,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 27, 10, 'com vinte colunas e vinte bases de bronze, com ganchos e ligaduras de prata nas colunas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 27, 11, 'O lado norte também terá quarenta e cinco metros de comprimento e cortinas externas, com vinte colunas e vinte bases de bronze, com ganchos e ligaduras de prata nas colunas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 27, 12, 'O lado ocidental, com as suas cortinas externas, terá vinte e dois metros e meio de largura, com dez colunas e dez bases.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 27, 13, 'O lado oriental, que dá para o nascente, também terá vinte e dois metros e meio de largura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 27, 14, 'Haverá cortinas de seis metros e setenta e cinco centí­metros de comprimento num dos lados da entrada, com três colunas e três bases,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 27, 15, 'e cortinas externas de seis metros e setenta e cinco centímetros de comprimento no outro lado, também com três colunas e três bases.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 27, 16, 'À entrada do pátio, haverá uma cortina de nove metros de comprimento, de linho fino trançado e de fios de tecidos azul, roxo e vermelho, obra de bordador, com quatro colu­nas e quatro bases.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 27, 17, 'Todas as colunas ao redor do pátio terão ligaduras, ganchos de prata e bases de bron­ze.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 27, 18, 'O pátio terá quarenta e cinco metros de comprimento e vinte e dois metros e meio de largura, com cortinas de linho fino trançado de dois metros e vinte e cinco centímetros de altura e bases de bronze.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 27, 19, 'Todos os utensílios para o serviço do tabernáculo, inclusive todas as estacas da tenda e as do pátio, serão feitos de bronze.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 27, 20, 'Ordene aos israelitas que lhe tragam azeite puro de olivas batidas para a iluminação, para que as lâmpadas fiquem sempre acesas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 27, 21, 'Na Tenda do Encontro, do lado de fora do véu que se encontra diante das tábuas da aliança, Arão e seus filhos manterão acesas as lâmpadas diante do Senhor, do entardecer até de manhã. Este será um decreto perpétuo entre os israelitas, geração após geração.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 1, 'Chame seu irmão Arão e separe-o dentre os israelitas, e também os seus filhos Nadabe e Abiú, Eleazar e Itamar, para que me sirvam como sacerdotes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 2, 'Para o seu irmão Arão, faça vestes sagradas que lhe confiram dignidade e honra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 3, 'Diga a todos os homens capazes, aos quais dei habilidade, que façam vestes para a consagração de Arão, para que me sirva como sacerdote.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 4, 'São estas as vestes que farão: um peitoral, um colete sacerdotal, um manto, uma túnica bordada, um turbante e um cinturão. Para que o sacerdote Arão e seus fi­lhos me sirvam como sacerdotes, eles farão essas vestes sagradas');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 5, 'e usarão linho fino, fios de ouro e fios de tecidos azul, roxo e vermelho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 6, 'Faça o colete sacerdotal de linho fino trançado, de fios de ouro e de fios de tecidos azul, roxo e vermelho, trabalho artesanal.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 7, 'Terá duas ombreiras atadas às suas duas extremida­des para uni-lo bem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 8, 'O cinturão e o colete que por ele é preso serão feitos da mesma peça. O cinturão também será de linho fino trançado, de fios de ouro e de fios de tecidos azul, roxo e ver­melho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 9, 'Grave em duas pedras de ônix os nomes dos filhos de Israel,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 10, 'por ordem de nascimento: seis nomes numa pedra e seis na outra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 11, 'Grave os nomes dos filhos de Israel nas duas pedras como o lapidador grava um selo. Em seguida prenda-as com filigranas de ouro,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 12, 'costurando-as nas ombreiras do colete sacerdotal, como pedras memoriais para os filhos de Israel. Assim Arão levará os nomes em seus ombros como memorial diante do Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 13, 'Faça filigranas de ouro');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 14, 'e duas correntes de ouro puro, entrelaçadas como uma corda; e prenda as correntes às filigranas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 15, 'Faça um peitoral de decisões, trabalho artesanal. Faça-o como o colete sacerdotal: de linho fino trançado, de fios de ouro e de fios de tecidos azul, roxo e vermelho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 16, 'Será quadrado, com um palmo de comprimento e um palmo de largura, e dobrado em dois.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 17, 'Em seguida, fixe nele quatro fileiras de pedras preciosas. Na primeira fileira haverá um rubi, um topázio e um berilo;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 18, 'na segunda, uma turquesa, uma safira e um diamante;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 19, 'na terceira, um jacinto, uma ágata e uma ametista;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 20, 'na quarta, um crisólito, um ônix e um jaspe. ');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 21, 'Serão doze pedras, uma para cada um dos nomes dos filhos de Israel, cada uma gravada como um selo, com o nome de uma das doze tribos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 22, 'Faça para o peitoral correntes de ouro puro trançadas como cordas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 23, 'Faça também duas argolas de ouro e prenda-as às duas extremidades do peitoral.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 24, 'Prenda as duas correntes de ouro às argolas nas extremidades do peitoral,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 25, 'e as outras extremidades das correntes, às duas filigranas, unindo-as às peças das ombreiras do colete sacerdotal, na parte da frente.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 26, 'Faça outras duas argolas de ouro e prenda-as às outras duas extremidades do peitoral, na borda interna, próxima ao colete sacerdotal.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 27, 'Faça mais duas argolas de ouro e prenda-as na parte inferior das ombreiras, na frente do colete sacerdotal, próximas da costura, logo acima do cinturão do colete sacerdotal.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 28, 'As argolas do peitoral serão amarradas às argolas do colete com um cordão azul, ligando o peitoral ao cinturão, para que não se separe do colete sacerdotal.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 29, 'Toda vez que Arão entrar no Lugar Santo, levará os nomes dos filhos de Israel sobre o seu coração no peitoral de decisões, como memorial permanente perante o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 30, 'Ponha também o Urim e o Tumim no peitoral das decisões, para que estejam sobre o coração de Arão sempre que ele entrar na presença do Senhor. Assim, Arão levará sempre sobre o cora­ção, na presença do Senhor, os meios para tomar decisões em Israel.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 31, 'Faça o manto do colete sacerdotal inteiramente de fios de tecido azul,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 32, 'com uma abertura para a cabeça no centro. Ao redor dessa abertura haverá uma dobra tecida, como uma gola, para que não se rasgue.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 33, 'Faça romãs de fios de tecidos azul, roxo e vermelho em volta da borda do manto, intercaladas com pequenos sinos de ouro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 34, 'Os sinos de ouro e as romãs se alternarão por toda a volta da borda do manto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 35, 'Arão o vestirá quando ministrar. O som dos sinos será ouvido quando ele entrar no Lugar Santo diante do Senhor e quando sair, para que não morra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 36, 'Faça um diadema de ouro puro e grave nele como se grava um selo: Consagrado ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 37, 'Prenda-o na parte da frente do turbante com uma fita azul.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 38, 'Estará sobre a testa de Arão; assim ele levará a culpa de qualquer pecado que os israelitas cometerem em relação às coisas sagradas, ao fazerem todas as suas ofertas. Estará sempre sobre a testa de Arão, para que as ofertas sejam aceitas pelo Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 39, 'Teça a túnica e o turbante com linho fino. O cinturão será feito por um bordador.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 40, 'Faça também túnicas, cinturões e gorros para os filhos de Arão, para conferir-lhes honra e dignidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 41, 'Depois de vestir seu irmão Arão e os filhos dele, unja-os e consagre-os, para que me sirvam como sacerdotes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 42, 'Faça-lhes calções de linho que vão da cintura até a coxa, para cobrirem a sua nudez.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 28, 43, 'Arão e seus filhos terão que vesti-los sempre que entrarem na Tenda do Encontro ou quando se aproximarem do altar para ministrar no Lu­gar Santo, para que não incorram em culpa e morram. Este é um decreto perpétuo para Arão e para os seus descendentes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 1, 'Assim você os consagrará, para que me sirvam como sacerdotes: separe um novilho e dois cordeiros sem defeito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 2, 'Com a melhor farinha de trigo, sem fermento, faça pães e bolos amassados com azeite, e pães finos, untados com azeite.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 3, 'Coloque-os numa cesta e ofereça-os dentro dela; também ofereça o novilho e os dois cordeiros.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 4, 'Depois traga Arão e seus filhos à entrada da Tenda do En­contro e mande-os lavar-se.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 5, 'Pegue as vestes e vista Arão com a túnica e o peitoral. Prenda o colete sacerdotal sobre ele com o cinturão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 6, 'Ponha-lhe o turbante na cabeça e prenda a coroa sagrada ao turbante.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 7, 'Unja-o com o óleo da unção, derramando-o sobre a cabeça de Arão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 8, 'Traga os filhos dele, vista cada um com uma túnica');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 9, 'e um gorro na cabeça. Ponha também os cinturões em Arão e em seus filhos. O sacerdócio lhes pertence como ordenança perpétua. Assim você dedicará Arão e seus filhos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 10, 'Traga o novilho para a frente da Tenda do Encontro. Arão e seus filhos colocarão as mãos sobre a cabeça do novilho,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 11, 'e você o sacrificará na presença do Senhor, defronte da Tenda do Encontro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 12, 'Com o dedo, coloque um pouco do sangue do novilho nas pontas do altar e derrame o resto do sangue na base do altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 13, 'Depois tire toda a gordura que cobre as vísceras, o lóbulo do fígado, e os dois rins com a gordura que os envolve, e queime-os no altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 14, 'Mas queime a carne, o couro e o excremento do novilho fora do acampamento; é oferta pelo pecado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 15, 'Separe um dos cordeiros sobre cuja cabeça Arão e seus filhos terão que colocar as mãos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 16, 'Sacrifique-o, pegue o sangue e jogue-o nos lados do altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 17, 'Corte o cordeiro em pedaços, lave as vísceras e as pernas e coloque-as ao lado da cabeça e das outras partes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 18, 'Depois queime o cordeiro inteiro sobre o altar; é holocausto dedicado ao Senhor; é oferta de aroma agradável dedicada ao Senhor preparada no fogo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 19, 'Pegue depois o outro cordeiro. Arão e seus filhos colocarão as mãos sobre a cabeça do animal,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 20, 'e você o sacrificará. Pegue do sangue e coloque-o na ponta da orelha direita de Arão e dos seus filhos, no polegar da mão direita e do pé direito de cada um deles. Depois derrame o resto do sangue nos lados do altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 21, 'Pegue, então, um pouco do sangue do altar e um pouco do óleo da unção, e faça aspersão com eles sobre Arão e suas vestes, sobre seus filhos e as vestes deles. Assim serão consagrados, ele e suas vestes, seus filhos e as vestes deles.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 22, 'Tire desse cordeiro a gordura, a parte gorda da cauda, a gordura que cobre as vísceras, o lóbulo do fígado, os dois rins e a gordura que os envolve, e a coxa direita. Este é o cordeiro da oferta de ordenação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 23, 'Da cesta de pães sem fermento, que está diante do Senhor, tire um pão, um bolo assado com azei­te e um pão fino.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 24, 'Coloque tudo nas mãos de Arão e de seus filhos, e apresente-os como oferta ritualmente movida perante o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 25, 'Em seguida retome-o das mãos deles e queime os pães no altar com o holocausto de aroma agradável ao Senhor; é oferta dedicada ao Senhor preparada no fogo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 26, 'Tire o peito do cordeiro para a ordenação de Arão e mova-o perante o Senhor, como gesto ritual de apresentação; essa parte pertencerá a você.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 27, 'Consagre aquelas partes do cordeiro da ordenação que pertencem a Arão e a seus filhos: o peito e a coxa movidos como oferta.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 28, 'Essas partes sempre serão dadas pelos israelitas a Arão e a seus filhos. É a contribuição obrigatória que lhes farão, das suas ofertas de comunhão ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 29, 'As vestes sagradas de Arão passarão aos seus descendentes, para que as vistam quando forem un­gidos e consagrados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 30, 'O filho que o suceder como sacerdote e vier à Tenda do Encontro para ministrar no Lugar Santo terá que usá-las durante sete dias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 31, 'Pegue o cordeiro da ordenação e cozinhe a sua carne num lugar sagrado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 32, 'À entrada da Tenda do Encontro, Arão e seus filhos deve­rão comer a carne do cordeiro e o pão que está na cesta.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 33, 'Eles comerão dessas ofertas com as quais se fez propiciação para sua ordenação e consagração; somente os sacerdotes poderão comê-las, pois são sagradas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 34, 'Se sobrar car­ne do cordeiro da ordenação ou pão até a manhã seguinte, queime a sobra. Não se deve comê-los, visto que são sagrados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 35, 'Para a ordenação de Arão e seus filhos, faça durante sete dias tudo o que lhe man­dei.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 36, 'Sacrifique um novilho por dia como oferta pelo pecado para fazer propiciação. Purifique o altar, fazendo propiciação por ele, e unja-o para consagrá-lo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 37, 'Durante sete dias faça propiciação pelo altar, consagrando-o. Então o altar será santíssimo, e tudo o que nele tocar será santo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 38, 'Eis o que você terá que sacrificar regularmente sobre o altar: a cada dia dois cordeiros de um ano.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 39, 'Ofereça um de manhã e o outro ao entardecer.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 40, 'Com o primeiro cordeiro ofereça um jarro da melhor farinha misturada com um litro de azeite de olivas batidas, e um litro de vinho como ofer­ta derramada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 41, 'Ofereça o outro cordeiro ao entardecer com uma oferta de cereal e uma oferta derramada, como de manhã. É oferta de aroma agradável ao Senhor preparada no fogo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 42, 'De geração em geração esse holocausto deverá ser feito regularmente à entrada da Tenda do Encontro, diante do Senhor. Nesse local eu os encontrarei e falarei com você;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 43, 'ali me encontrarei com os israelitas, e o lugar será consagrado pela minha glória.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 44, 'Assim consagrarei a Tenda do Encontro e o altar, e consagrarei também Arão e seus filhos para me servirem como sacerdotes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 45, 'E habitarei no meio dos israelitas e serei o seu Deus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 29, 46, 'Saberão que eu sou o Senhor, o seu Deus, que os tirou do Egito para habitar no meio deles. Eu sou o Senhor, o seu Deus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 1, 'Faça um altar de madeira de acácia para queimar incenso.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 2, 'Será quadrado, com quarenta e cinco centímetros de cada lado e noventa centímetros de altura; suas pontas formarão com ele uma só peça.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 3, 'Revista de ou­ro puro a parte superior, todos os lados e as pontas, e faça uma moldura de ouro ao seu re­dor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 4, 'Faça duas argolas de ouro de cada lado do altar, abaixo da moldura, que sustentem as varas utilizadas para carregá-lo,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 5, 'e use madeira de acácia para fazer as varas e revista-as de ouro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 6, 'Coloque o altar em frente do véu que se encontra diante da arca da aliança, diante da tampa que está sobre ele, onde me encontrarei com você.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 7, 'Arão queimará incenso aromático sobre o altar todas as manhãs, quando vier cuidar das lâmpadas,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 8, 'e também quando acendê-las ao entardecer. Será queimado incenso continuamente perante o Senhor, pelas suas gerações.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 9, 'Não ofereçam nesse altar nenhum outro tipo de incenso nem holocausto nem oferta de cereal nem derramem sobre ele ofertas de bebidas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 10, 'Uma vez por ano, Arão fará propiciação sobre as pontas do altar. Essa propiciação anual será realizada com o sangue da oferta para propiciação pelo pecado, geração após geração. Esse altar é santíssimo ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 11, 'Disse então o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 12, 'Quando você fizer o recenseamento dos israelitas, cada um deles terá que pagar ao Senhor um preço pelo resgate por sua vida quando for contado. Dessa forma nenhuma praga virá sobre eles quando você os contar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 13, 'Cada recenseado contribuirá com seis gramas, com base no peso padrão do santuário, que tem doze gramas. Os seis gramas são uma oferta ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 14, 'Todos os alistados, da idade de vinte anos para cima, darão ao Senhor essa oferta.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 15, 'Os ricos não contribuirão com mais, nem os pobres darão menos que seis gramas, quando apresentarem a oferta ao Senhor como propiciação por sua vida.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 16, 'Receba dos israelitas o preço da propiciação e use-o para o serviço da Tenda do Encontro. Será um memorial perante o ­Senhor em favor dos israelitas, para fazerem propiciação por suas vidas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 17, 'Disse então o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 18, 'Faça uma bacia de bronze com uma base de bronze, para se lavarem. Coloque-a entre a Tenda do Encontro e o altar, e mande enchê-la de água.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 19, 'Arão e seus filhos lavarão as mãos e os pés com a água da bacia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 20, 'Toda vez que entrarem na Tenda do Encontro, terão que lavar-se com água, para que não morram. Quando também se aproximarem do altar para ministrar ao Senhor, apresentando uma oferta preparada no fogo,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 21, 'lavarão as mãos e os pés para que não morram. Esse é um decreto perpétuo, para Arão e os seus descendentes, geração após geração.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 22, 'Em seguida o Senhor disse a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 23, 'Junte as seguintes especiarias: seis quilos de mirra líquida, a metade disso, ou seja, três quilos de canela, três quilos de cana aromática,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 24, 'seis quilos de cássia, com base no peso padrão do santuário, e um galão de azeite de oliva.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 25, 'Faça com eles o óleo sagrado para as unções, uma mistura de aromas, obra de perfumista. Este será o óleo sagrado para as unções.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 26, 'Use-o para ungir a Tenda do Encontro, a arca da aliança,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 27, 'a mesa e todos os seus utensílios, o candelabro e os seus utensílios, o altar do incenso,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 28, 'o altar do holocausto e todos os seus utensílios, e a bacia com a sua base.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 29, 'Você os consagrará e serão santíssimos, e tudo o que neles tocar se tornará santo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 30, 'Unja Arão e seus filhos e consagre-os para que me sirvam como sacerdotes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 31, 'Diga aos israelitas: Este será o meu óleo sagrado para as unções, geração após geração.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 32, 'Não o derramem sobre nenhum outro homem, e não façam nenhum outro óleo com a mesma composição. É óleo sagrado, e assim vocês devem considerá-lo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 33, 'Quem fizer óleo como esse ou usá-lo em alguém que não seja sacerdote, será eliminado do meio do seu povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 34, 'Disse ainda o Senhor a Moisés: Junte as seguintes essências: bálsamo, ônica, gálbano e incenso puro, todos em quantidades iguais,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 35, 'e faça um incenso de mistura aromática, obra de perfumista. Levará sal e será puro e santo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 36, 'Moa parte dele, até virar pó, e coloque-o diante das tábuas da aliança, na Tenda do Encontro, onde me encontrarei com você. O incenso lhes será santíssimo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 37, 'Não façam nenhum outro incenso com a mesma composição para uso pessoal; considerem-no sagrado, reservado para o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 30, 38, 'Quem fizer um incenso semelhante, para usufruir sua fragrância, será eliminado do seu povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 31, 1, 'Disse então o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 31, 2, 'Eu escolhi Bezalel, filho de Uri, filho de Hur, da tribo de Judá,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 31, 3, 'e o enchi do Espírito de Deus, dando-lhe destreza, habilidade e plena capacidade artística');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 31, 4, 'para desenhar e executar trabalhos em ouro, prata e bronze,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 31, 5, 'para talhar e esculpir pedras, para entalhar madeira e exe­cutar todo tipo de obra artesanal.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 31, 6, 'Além disso, designei Aoliabe, filho de Aisamaque, da tribo de Dã, para auxiliá-lo. Tam­bém capacitei todos os artesãos para que executem tudo o que lhe ordenei:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 31, 7, 'a Tenda do Encontro, a arca da aliança e a tampa que está sobre ela, e todos os outros utensílios da tenda —');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 31, 8, 'a mesa com os seus utensílios, o candelabro de ouro puro e os seus utensílios, o altar do incenso,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 31, 9, 'o altar do holocausto com os seus utensílios, a bacia com a sua base —');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 31, 10, 'as vestes litúrgicas, tanto as vestes sagradas para Arão, o sacerdote, como as vestes para os seus filhos, quando servirem como sacerdotes,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 31, 11, 'e o óleo para as unções e o incenso aromático para o Lugar Santo. Tudo deve ser feito exatamente como eu lhe ordenei.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 31, 12, 'Disse ainda o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 31, 13, 'Di­ga aos israelitas que guardem os meus sábados. Isso será um sinal entre mim e vocês, geração após geração, a fim de que saibam que eu sou o Senhor, que os santifica.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 31, 14, 'Guardem o sábado, pois para vocês é santo. Aquele que o profanar terá que ser executado; quem fizer algum trabalho nesse dia será eliminado do meio do seu povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 31, 15, 'Em seis dias qualquer trabalho poderá ser feito, mas o sétimo dia é o sábado, o dia de descanso, consagrado ao Senhor. Quem fizer algum trabalho no sábado terá que ser executado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 31, 16, 'Os israelitas terão que guardar o sábado, eles e os seus descendentes, como uma aliança perpétua.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 31, 17, 'Isso será um sinal perpétuo entre mim e os israelitas, pois em seis dias o Senhor fez os céus e a terra, e no sétimo dia ele não trabalhou e descansou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 31, 18, 'Quando o Senhor terminou de falar com Moisés no monte Sinai, deu-lhe as duas tábuas da aliança, tábuas de pedra, escritas pelo dedo de Deus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 1, 'O povo, ao ver que Moisés demora­va a descer do monte, juntou-se ao redor de Arão e lhe disse: “Venha, faça para nós deuses que nos conduzam, pois a esse Moi­sés, o homem que nos tirou do Egito, não sabemos o que lhe aconteceu”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 2, 'Respondeu-lhes Arão: “Tirem os brincos de ouro de suas mulheres, de seus filhos e de suas filhas e tragam-nos a mim”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 3, 'Todos tiraram os seus brincos de ouro e os levaram a Arão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 4, 'Ele os recebeu e os fundiu, transformando tudo num ídolo, que modelou com uma ferramenta própria, dando-lhe a forma de um bezerro. Então disseram: “Eis aí os seus deuses, ó Israel, que tiraram vocês do Egito!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 5, 'Vendo isso, Arão edificou um altar diante do bezerro e anunciou: “Amanhã haverá uma festa dedicada ao Senhor”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 6, 'Na manhã seguinte, ofereceram holocaustos e sacrifícios de comunhão. O povo se assentou para comer e beber, e levantou-se para se entregar à farra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 7, 'Então o Senhor disse a Moisés: Desça, porque o seu povo, que você tirou do Egito, corrompeu-se.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 8, 'Muito depressa se desviaram daquilo que lhes ordenei e fizeram um ídolo em forma de bezerro, curvaram-se diante dele, ofereceram-lhe sacrifícios, e disseram: “Eis aí, ó Israel, os seus deuses que tiraram vocês do Egito”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 9, 'Disse o Senhor a Moisés: Tenho visto que este povo é um povo obstinado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 10, 'Deixe-me agora, para que a minha ira se acenda contra eles, e eu os destrua. Depois farei de você uma grande nação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 11, 'Moisés, porém, suplicou ao Senhor, o seu Deus, clamando: Ó Senhor, por que se acenderia a tua ira contra o teu povo, que tiraste do Egito com grande poder e forte mão?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 12, 'Por que diriam os egípcios: “Foi com intenção maligna que ele os libertou, para matá-los nos montes e bani-los da face da terra”? Arrepende-te do fogo da tua ira! Tem piedade, e não tragas este mal sobre o teu povo!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 13, 'Lembra-te dos teus servos Abraão, Isaque e Israel, aos quais juraste por ti mesmo: “Farei que os seus descendentes sejam numerosos como as estrelas do céu e lhes darei toda esta terra que lhes prometi, que será a sua herança para sempre”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 14, 'E sucedeu que o Senhor arrependeu-se do mal que ameaçara trazer sobre o povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 15, 'Então Moisés desceu do monte, levando nas mãos as duas tábuas da aliança; estavam escritas em ambos os lados, frente e verso.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 16, 'As tábuas tinham sido feitas por Deus; o que nelas estava gravado fora escrito por Deus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 17, 'Quando Josué ouviu o barulho do povo gritando, disse a Moisés: “Há barulho de guerra no acampamento”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 18, 'Respondeu Moisés: “Não é canto de vitória, nem canto de derrota; mas ouço o som de canções!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 19, 'Quando Moisés aproximou-se do acampamento e viu o bezerro e as danças, irou-se e jogou as tábuas no chão, ao pé do monte, quebrando-as.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 20, 'Pegou o bezerro que eles ti­nham feito e o destruiu no fogo; depois de moê-lo até virar pó, espalhou-o na água e fez com que os israelitas a bebessem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 21, 'E perguntou a Arão: “Que lhe fez esse povo para que você o levasse a tão grande peca­do?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 22, 'Respondeu Arão: Não te enfureças, meu senhor; tu bem sabes como esse povo é propenso para o mal.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 23, 'Eles me disseram: “Faça para nós deuses que nos conduzam, pois não sabemos o que aconteceu com esse Moisés, o homem que nos tirou do Egito”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 24, 'Então eu lhes disse: Quem tiver enfeites de ouro, traga-os para mim. O povo trouxe-me o ouro, eu o joguei no fogo e surgiu esse bezerro!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 25, 'Moisés viu que o povo estava desenfreado e que Arão o tinha deixado fora de controle, tendo se tornado objeto de riso para os seus inimigos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 26, 'Então ficou em pé, à entrada do acampamento, e disse: “Quem é pelo Senhor, junte-se a mim”. Todos os levitas se juntaram a ele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 27, 'Declarou-lhes também: “Assim diz o Senhor, o Deus de Israel: “Pegue cada um sua espada, percorra o acampamento, de tenda em tenda, e mate o seu irmão, o seu amigo e o seu vizinho””.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 28, 'Fizeram os levitas conforme Moisés ordenou, e naquele dia morreram cerca de três mil dentre o povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 29, 'Disse então Moisés: “Hoje vocês se consagraram ao Senhor, pois nenhum de vocês poupou o seu filho e o seu irmão, de modo que o Senhor os abençoou neste dia”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 30, 'No dia seguinte Moisés disse ao povo: “Vocês cometeram um grande pecado. Mas agora subirei ao Senhor, e talvez possa oferecer propiciação pelo pecado de vocês”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 31, 'Assim, Moisés voltou ao Senhor e disse: Ah, que grande pecado cometeu este povo! Fizeram para si deuses de ouro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 32, 'Mas agora, eu te rogo, perdoa-lhes o pecado; se não, risca-me do teu livro que escreveste.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 33, 'Respondeu o Senhor a Moisés: Riscarei do meu livro todo aquele que pecar contra mim.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 34, 'Agora vá, guie o povo ao lugar de que lhe falei, e meu anjo irá à sua frente. Todavia, quando chegar a hora de puni-los, eu os punirei pelos pecados deles.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 32, 35, 'E o Senhor feriu o povo com uma praga porque quiseram que Arão fizesse o bezerro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 33, 1, 'Depois ordenou o Senhor a Moi­sés: Saia deste lugar, com o povo que você tirou do Egito, e vá para a terra que prometi com juramento a Abraão, a Isaque e a Jacó, dizendo: Eu a darei a seus descendentes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 33, 2, 'Mandarei à sua frente um anjo e expulsarei os cananeus, os amor­reus, os hititas, os ferezeus, os heveus e os jebuseus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 33, 3, 'Vão para a terra onde há leite e mel com fartura. Mas eu não irei com vocês, pois vocês são um povo obstinado, e eu poderia destruí-los no caminho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 33, 4, 'Quando o povo ouviu essas palavras terríveis, começou a chorar, e ninguém usou enfeite algum.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 33, 5, 'Isso porque o Senhor ordenara que Moisés dissesse aos israelitas: “Vocês são um povo obstinado. Se eu fosse com vocês, ainda que por um só momento, eu os destruiria. Agora tirem os seus enfeites, e eu decidirei o que fazer com vocês”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 33, 6, 'Por isso, do monte Horebe em diante, os israelitas não usaram mais nenhum enfeite.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 33, 7, 'Moisés costumava montar uma tenda do lado de fora do acampamento; ele a chamava Tenda do Encontro. Quem quisesse consultar o Senhor ia à tenda, fora do acampamento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 33, 8, 'Sempre que Moisés ia até lá, todo o povo se levantava e ficava em pé à entrada de suas tendas, observando-o, até que ele entrasse na tenda.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 33, 9, 'Assim que Moisés entrava, a coluna de nuvem descia e ficava à entrada da tenda, enquanto o Senhor falava com Moisés.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 33, 10, 'Quan­do o povo via a coluna de nuvem parada à entrada da ten­da, todos prestavam adoração em pé, cada qual na entrada de sua própria tenda.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 33, 11, 'O Senhor falava com Moisés face a face, como quem fala com seu amigo. Depois Moisés voltava ao acam­pamento; mas Josué, filho de Num, que lhe servia como auxiliar, não se afastava da tenda.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 33, 12, 'Disse Moisés ao Senhor: Tu me ordenaste: “Conduza este povo”, mas não me permites saber quem enviarás comigo. Disseste: “Eu o conheço pelo nome e de você tenho me agradado”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 33, 13, 'Se me vês com agrado, revela-me os teus propósitos, para que eu te conheça e continue sendo aceito por ti. Lembra-te de que esta nação é o teu povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 33, 14, 'Respondeu o Senhor: “Eu mesmo o acompanharei, e lhe darei descanso”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 33, 15, 'Então Moisés lhe declarou: Se não fores conosco, não nos envies.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 33, 16, 'Como se saberá que eu e o teu povo podemos contar com o teu favor, se não nos acompanhares? Que mais poderá distinguir a mim e a teu povo de todos os demais povos da face da terra?');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 33, 17, 'O Senhor disse a Moisés: “Farei o que me pede, porque tenho me agradado de você e o conheço pelo nome”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 33, 18, 'Então disse Moisés: “Peço-te que me mostres a tua glória”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 33, 19, 'E Deus respondeu: “Diante de você farei passar toda a minha bondade, e diante de você proclamarei o meu nome: o Senhor. Terei misericórdia de quem eu quiser ter misericór­dia, e terei compaixão de quem eu quiser ter com­paixão”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 33, 20, 'E acrescentou: “Você não poderá ver a minha face, porque ninguém poderá ver-me e continuar vivo”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 33, 21, 'E prosseguiu o Senhor: Há aqui um lugar perto de mim, onde você ficará, em cima de uma rocha.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 33, 22, 'Quando a minha glória passar, eu o colocarei numa fenda da rocha e o cobrirei com a minha mão até que eu tenha acabado de passar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 33, 23, 'Então tirarei a minha mão e você verá as minhas costas; mas a minha face ninguém poderá ver.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 1, 'Disse o Senhor a Moisés: Talhe duas tábuas de pedra semelhantes às primeiras, e nelas escreverei as palavras que estavam nas primeiras tábuas que você quebrou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 2, 'Esteja pronto pela manhã para subir ao monte Sinai. E lá mesmo, no alto do monte, apresente-se a mim.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 3, 'Ninguém poderá ir com você nem ficar em lugar algum do monte; nem mesmo ovelhas e bois deverão pastar diante do monte.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 4, 'Assim Moisés lavrou duas tábuas de pedra semelhantes às primeiras e subiu ao monte Sinai, logo de manhã, como o Senhor lhe havia ordenado, levando nas mãos as duas tábuas de pedra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 5, 'Então o Senhor desceu na nuvem, permaneceu ali com ele e proclamou o seu nome: o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 6, 'E passou diante de Moisés, proclamando: Senhor, Senhor, Deus compassivo e misericordioso, paciente, cheio de amor e de fidelidade,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 7, 'que mantém o seu amor a milhares e perdoa a maldade, a rebelião e o pecado. Contudo, não deixa de punir o culpado; castiga os filhos e os netos pelo pecado de seus pais, até a terceira e a quarta gerações.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 8, 'Imediatamente Moisés prostrou-se com o rosto em terra, e o adorou, dizendo:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 9, '“Senhor, se de fato me aceitas com agrado, que o Senhor nos acompanhe. Mesmo sendo esse um povo obs­tinado, perdoa a nossa maldade e o nosso peca­do e faze de nós a tua herança”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 10, '“Faço com você uma aliança”, disse o Senhor. “Diante de todo o seu povo farei maravilhas jamais realizadas na presença de nenhum outro povo do mundo. O povo no meio do qual você habita verá a obra maravilhosa que eu, o Senhor, farei.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 11, 'Obedeça às ordens que hoje lhe dou. Expulsarei de diante de você os amorreus, os cananeus, os hititas, os ferezeus, os heveus e os jebuseus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 12, 'Acautele-se para não fazer acordo com aqueles que vivem na terra para a qual você está indo, pois eles se tornariam uma armadilha.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 13, 'Ao contrário, derrube os altares deles, quebre as suas colunas sagradas e corte os seus postes sagrados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 14, 'Nun­ca adore nenhum outro deus, porque o Senhor, cujo nome é Zeloso, é de fato Deus zeloso.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 15, 'Acautele-se para não fazer acordo com aqueles que já vivem na terra; pois quando eles se prostituírem, seguindo os seus deuses e lhes oferecerem sacrifícios, convidarão você e poderão levá-lo a comer dos seus sacrifícios');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 16, 'e a escolher para os seus filhos mulheres dentre as filhas deles. Quando elas se prostituírem, seguindo os seus deuses, poderão levar os seus filhos a se prostituírem também.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 17, 'Não faça ídolos de metal para você.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 18, 'Celebre a festa dos pães sem fermento. Durante sete dias coma pão sem fermento, como lhe ordenei. Faça isso no tempo certo, no mês de abibe, porquanto naquele mês você saiu do Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 19, 'O primeiro a nascer de cada ventre me pertence, todos os machos dentre as primeiras crias dos seus rebanhos: bezerros, cordeiros e cabritos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 20, 'Resgate com um cordeiro cada primeiro jumentinho que nascer; mas se não o resgatar, quebre-lhe o pescoço. Resgate todos os seus primogênitos. Ninguém compareça perante mim de mãos vazias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 21, 'Trabalhe seis dias, mas descanse no sétimo; tanto na época de arar como na da colheita.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 22, 'Celebre a festa das semanas, na ocasião dos primeiros frutos da colheita do trigo, e a festa do encerramento da colheita, no fim do ano.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 23, 'Três vezes por ano todos os homens do seu povo comparecerão diante do Soberano, o Senhor, o Deus de Israel.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 24, 'Expulsarei nações de diante de você e ampliarei o seu território. Quando você subir três vezes por ano para apresentar-se ao Senhor, o seu Deus, ninguém cobiçará a sua terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 25, 'Não me ofereça o sangue de nenhum sacrifício misturado com algo fermentado, e não deixe sobra alguma do sacrifício da festa da Páscoa até a manhã seguinte.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 26, '“Traga o melhor dos primeiros frutos da terra ao santuário do Senhor, o seu Deus. “Não cozinhe o cabrito no leite da pró­pria mãe.”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 27, 'Disse o Senhor a Moisés: “Escreva essas palavras; porque é de acordo com elas que faço aliança com você e com Israel”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 28, 'Moisés ficou ali com o Senhor quarenta dias e quarenta noites, sem comer pão e sem beber água. E escreveu nas tábuas as palavras da aliança: os Dez Mandamentos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 29, 'Ao descer do monte Sinai com as duas tábuas da aliança nas mãos, Moisés não sabia que o seu rosto resplandecia por ter conversado com o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 30, 'Quando Arão e todos os israelitas viram Moisés com o rosto resplandecente, tiveram medo de aproximar-se dele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 31, 'Ele, porém, os chamou; Arão e os líderes da comunidade atenderam, e Moisés falou com eles.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 32, 'Depois, todos os israelitas se aproximaram, e ele lhes transmitiu todos os mandamentos que o Senhor lhe tinha dado no monte Sinai.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 33, 'Quando acabou de falar com eles, cobriu o rosto com um véu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 34, 'Mas toda vez que entrava para estar na presença do Senhor e falar com ele, tirava o véu até sair. Sempre que saía e contava aos israelitas tudo o que lhe havia sido ordenado,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 34, 35, 'eles viam que o seu rosto resplandecia. Então, de novo Moisés cobria o rosto com o véu até entrar de novo para falar com o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 1, 'Moisés reuniu toda a comunidade de Israel e lhes disse: Estas são as coisas que o Senhor os mandou fazer:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 2, 'Em seis dias qualquer trabalho poderá ser feito, mas o sétimo dia lhes será santo, um sábado de descanso consagrado ao Senhor. Todo aquele que trabalhar nesse dia terá que ser morto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 3, 'Nem sequer acendam fogo em nenhuma de suas casas no dia de sábado!');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 4, 'Disse Moisés a toda a comunidade de Israel: Foi isto que o Senhor ordenou:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 5, '“Separem dentre os seus bens uma oferta para o Senhor. Todo aquele que, de coração, estiver disposto, trará como oferta ao Senhor ­ ouro, prata e bronze;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 6, 'fios de tecidos azul, roxo e vermelho; linho fino e pêlos de cabra;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 7, 'peles de carneiro tingidas de vermelho e couro; madeira de acácia;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 8, 'óleo para a iluminação; especiarias para o óleo da unção e para o incenso aromático;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 9, 'pe­dras de ônix e outras pedras preciosas para serem encravadas no colete sacerdotal e no peitoral.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 10, 'Todos os que dentre vocês forem capazes virão fazer tudo quanto o Senhor orde­nou:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 11, 'o tabernáculo com sua tenda e sua cobertura, os ganchos, as armações, os travessões, as colunas e as bases;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 12, 'a arca com suas varas, a tampa e o véu que a protege;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 13, 'a mesa com suas varas e todos os seus utensílios, e os pães da Presença;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 14, 'o candelabro com seus utensílios, as lâmpadas e o óleo para iluminação;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 15, 'o altar do incenso com suas varas, o óleo da unção e o incenso aromático; a cortina divisória à entrada do tabernáculo;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 16, 'o altar de holocaustos com sua grelha de bronze, suas varas e todos os seus utensílios; a bacia de bronze e sua base;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 17, 'as cortinas externas do pátio com suas colunas e bases, e a cortina da entrada para o pátio;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 18, 'as estacas do tabernáculo e do pátio e suas cordas;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 19, 'as vestes litúrgicas para ministrar no Lugar Santo, tanto as vestes sagradas de Arão, o sacerdote, como as vestes de seus filhos, para quando servirem como sacerdotes”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 20, 'Então toda a comunidade de Israel saiu da presença de Moisés,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 21, 'e todos os que estavam dispostos, cujo coração os impeliu a isso, trouxeram uma oferta ao Senhor para a obra da Tenda do Encontro, para todos os seus serviços e para as vestes sagradas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 22, 'Todos os que se dispuseram, tanto homens como mulheres, trouxeram jóias de ouro de todos os tipos: broches, brincos, anéis e ornamentos; e apresentaram seus objetos de ouro como oferta ritualmente movida perante o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 23, 'Todos os que possuíam fios de tecidos azul, roxo e vermelho, ou linho fino, ou pêlos de cabra, peles de carneiro tingidas de vermelho, ou couro, trouxeram-nos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 24, 'Aqueles que apresentaram oferta de prata ou de bronze trouxeram-na como oferta ao Senhor, e todo aquele que possuía madeira de acácia para qualquer das partes da obra, também a trouxe.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 25, 'Todas as mulheres capazes teceram com suas mãos e trouxeram o que haviam feito: tecidos azul, roxo e vermelho e linho fino.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 26, 'Todas as mulheres que se dispuseram e que tinham habilidade teceram os pêlos de cabra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 27, 'Os líderes trouxeram pedras de ônix e outras pedras preciosas para serem encravadas no colete sacerdotal e no peitoral.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 28, 'Trouxeram também especiarias e azeite de oliva para a iluminação, para o óleo da unção e para o incenso aromático.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 29, 'Todos os israelitas que se dispuseram, tanto homens como mulheres, trouxeram ao Senhor ofertas voluntárias para toda a obra que o Senhor, por meio de Moisés, ordenou-lhes que fizessem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 30, 'Disse então Moisés aos israelitas: O Senhor escolheu Bezalel, filho de Uri, neto de Hur, da tribo de Judá,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 31, 'e o encheu do Espírito de Deus, dando-lhe destreza, habilidade e plena capacidade artística,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 32, 'para desenhar e executar trabalhos em ouro, prata e bronze,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 33, 'para talhar e lapidar pedras e entalhar madeira para todo tipo de obra artesanal.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 34, 'E concedeu tanto a ele como a Aoliabe, filho de Aisamaque, da tribo de Dã, a habilidade de ensinar os outros.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 35, 35, 'A todos esses deu capacidade para realizar todo tipo de obra como artesãos, projetistas, bordadores de linho fino e de fios de tecidos azul, roxo e vermelho, e como tecelões. Eram capazes de projetar e executar ­qualquer trabalho artesanal.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 1, '“Assim Bezalel, Aoliabe e todos os homens capazes, a quem o ­Senhorconcedeu destreza e habilidade para fazerem toda a obra de construção do santuário, realiza­rão a obra como o Senhor ordenou”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 2, 'Então Moisés chamou Bezalel e Aolia­be e todos os homens capazes a quem o Senhor dera habilidade e que estavam dispos­tos a vir realizar a obra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 3, 'Receberam de Moisés todas as ofertas que os israelitas tinham trazido para a obra de construção do santuário. E o povo continuava a trazer voluntariamente ofertas, manhã após manhã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 4, 'Por isso, todos os artesãos habilidosos que trabalhavam no santuário interromperam o trabalho');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 5, 'e disseram a Moi­sés: “O povo está trazendo mais do que o suficiente para realizar a obra que o Senhor ordenou”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 6, 'Então Moisés ordenou que fosse feita esta proclamação em todo o acampamento: “Nenhum homem ou mulher deverá fazer mais nada para ser oferecido ao santuário”. Assim, o povo foi impedido de trazer mais,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 7, 'pois o que já haviam recebido era mais que suficiente para realizar toda a obra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 8, 'Todos os homens capazes dentre os trabalhadores fizeram o tabernáculo com dez cortinas internas de linho fino trançado e de fios de tecidos azul, roxo e vermelho, com os queru­bins bordados sobre eles.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 9, 'Todas as cortinas internas tinham o mesmo tamanho: doze metros e sessenta centímetros de comprimento por um metro e oitenta centímetros de largura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 10, 'Prenderam cinco cortinas internas, e fizeram o mesmo com as outras cinco.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 11, 'Em seguida fizeram laçadas de tecido azul ao longo da borda da última cortina interna do primeiro conjunto de cortinas internas, fazendo o mesmo com o segundo conjunto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 12, 'Fizeram também cinqüenta laçadas na primeira cortina interna e cinqüenta laçadas na última cortina interna do segundo conjunto; as laçadas estavam opostas umas às outras.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 13, 'Depois fizeram cinqüenta ganchos de ouro e com eles prenderam um conjunto de cortinas internas ao outro, para que o tabernáculo formasse um todo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 14, 'Com o total de onze cortinas internas de pêlos de cabra fizeram uma tenda para cobrir o tabernáculo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 15, 'As onze cortinas internas tinham a mesma medida: treze metros e meio de comprimento por um metro e oitenta centímetros de largura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 16, 'Prenderam cinco cortinas internas num conjunto e as outras seis noutro conjunto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 17, 'Depois fizeram cinqüenta laçadas em volta da borda da última cortina interna de um dos conjuntos e também na borda da última cortina interna do outro conjunto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 18, 'Fizeram também cinqüenta ganchos de bronze para unir a tenda, formando um todo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 19, 'Em seguida fizeram para a tenda uma cobertura de pele de carneiro tingida de vermelho, e por cima desta uma cobertura de couro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 20, 'Fizeram ainda armações verticais de madeira de acácia para o tabernáculo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 21, 'Cada armação tinha quatro metros e meio de comprimento por setenta centímetros de largura,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 22, 'com dois encaixes paralelos um ao outro. E fizeram todas as armações do tabernáculo dessa madeira.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 23, 'Fizeram também vinte armações para o lado sul do tabernáculo');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 24, 'e quarenta bases de prata para serem colocadas debaixo delas; duas bases para cada armação, uma debaixo de cada encaixe.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 25, 'Para o outro lado, o lado norte do tabernáculo, fizeram vinte armações');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 26, 'e quarenta bases de prata, duas debaixo de cada armação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 27, 'Fizeram ainda seis armações na parte de trás do tabernáculo, isto é, para o lado ocidental,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 28, 'e duas armações foram montadas nos cantos, na parte de trás do tabernáculo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 29, 'Nesses dois cantos as armações eram duplas, desde a parte inferior até a mais alta, colocadas numa só argola, ambas feitas do mesmo modo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 30, 'Havia, pois, oito armações e dezesseis bases de prata, duas debaixo de cada armação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 31, 'Também fizeram travessões de madeira de acácia: cinco para as armações de um lado do tabernáculo,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 32, 'cinco para as do outro lado e cinco para as do lado ocidental, na parte de trás do tabernáculo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 33, 'Fizeram o travessão central de uma extremidade à outra, passando pelo meio das armações.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 34, 'Revestiram de ouro as armações e fizeram argolas de ouro para sustentar os travessões, os quais também revestiram de ouro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 35, 'Fizeram o véu de linho fino trançado e de fios de tecidos azul, roxo e vermelho, e mandaram bordar nele querubins.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 36, 'Fizeram-lhe quatro colunas de madeira de acácia e as revestiram de ouro. Fizeram-lhe ainda ganchos de ouro e fundiram as suas bases de prata.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 37, 'Para a entrada da tenda fizeram uma cortina de linho fino trançado e de fios de tecidos azul, roxo e vermelho, obra de bordador,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 36, 38, 'e fizeram-lhe cinco colunas com ganchos. Revestiram de ouro as partes superior e lateral das colunas e fizeram de bronze as suas cinco bases.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 1, 'Bezalel fez a arca com madeira de acácia, com um metro e dez centímetros de com­primento, setenta centímetros de largura e setenta centímetros de altura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 2, 'Revestiu-a de ouro puro, por dentro e por fora, e fez uma moldura de ouro ao seu redor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 3, 'Fundiu quatro argo­las de ouro para ela, prendendo-as a seus quatro pés, com duas argolas de um lado e duas do outro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 4, 'Depois fez varas de madeira de acácia, revestiu-as de ouro');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 5, 'e colocou-as nas argolas laterais da arca para que pudesse ser carregada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 6, 'Fez a tampa de ouro puro com um metro e dez centímetros de comprimento por setenta centímetros de largura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 7, 'Fez também dois querubins de ouro batido nas extremidades da tampa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 8, 'Fez ainda um querubim numa extremidade e o segundo na outra, formando uma só peça com a tampa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 9, 'Os querubins tinham as asas estendidas para cima, cobrindo com elas a tampa. Estavam de frente um para o outro, com o rosto voltado para a tampa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 10, 'Fez a mesa com madeira de acácia com noventa centímetros de comprimento, quarenta e cinco centímetros de largura e setenta centímetros de altura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 11, 'Revestiu-a de ouro puro e fez uma moldura de ouro ao seu redor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 12, 'Fez também ao seu redor uma borda com a largura de quatro dedos e uma moldura de ouro para essa borda.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 13, 'Fun­diu quatro argolas de ouro para a mesa e prendeu-as nos quatro cantos, onde estavam os seus quatro pés.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 14, 'As argolas foram presas próximas da borda, para que sustentassem as varas usadas para carregar a mesa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 15, 'Fez as varas para carregar a mesa de madeira de acácia, revestidas de ouro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 16, 'E de ouro puro fez os utensílios para a mesa: seus pratos e recipientes para incenso, as tigelas e as bacias nas quais se derramam as ofertas de bebidas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 17, 'Fez o candelabro de ouro puro e batido. O pedestal, a haste, as taças, as flores e os botões formavam com ele uma só peça.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 18, 'Seis braços saíam do candelabro: três de um lado e três do outro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 19, 'Havia três taças com formato de flor de amêndoa, num dos braços, cada uma com botão e flor, e três taças com formato de flor de amêndoa no braço seguinte, cada uma com botão e flor. Assim era com os seis braços que saem do candelabro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 20, 'Na haste do candelabro havia quatro taças com formato de flor de amêndoa, cada uma com flor e botão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 21, 'Havia um botão debaixo de cada par dos seis braços que saíam do candelabro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 22, 'Os braços com seus botões formavam uma só peça com o candela­bro, tudo feito de ouro puro e batido.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 23, 'Fez de ouro puro suas sete lâmpadas, seus cortadores de pavio e seus apagadores.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 24, 'Com trinta e cinco quilos de ouro puro fez o candelabro com seus botões e todos esses uten­sílios.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 25, 'Fez ainda o altar do incenso de madeira de acácia. Era quadrado, com quarenta e cinco centímetros de cada lado e noventa centímetros de altura; suas pontas formavam com ele uma só peça.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 26, 'Revestiu de ouro puro a parte superior, todos os lados e as pontas, e fez uma moldura de ouro ao seu redor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 27, 'Fez também duas argolas de ouro de cada lado do altar, abaixo da moldura, para sustentar as varas utilizadas para carregá-lo,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 28, 'e usou madeira de acácia para fazer as varas e revestiu-as de ouro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 37, 29, 'Fez ainda o óleo sagrado para as unções e o incenso puro e aromático, obra de perfumista.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 1, 'Fez um altar de madeira de acácia para os holocaustos, com um metro e trinta e cinco centímetros de altura; era quadrado, com dois metros e vinte e cinco centímetros de cada lado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 2, 'E fez uma ponta em forma de chifre em cada um dos quatro cantos, formando uma só peça com o altar, o qual revestiu de bronze.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 3, 'De bronze fez todos os seus utensílios: os recipientes para recolher cinzas, as pás, as bacias de aspersão, os garfos para carne e os braseiros.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 4, 'Fez uma grelha de bronze para o altar em forma de rede, abaixo da sua beirada, a meia altura do altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 5, 'Fundiu quatro argolas de bronze para sustentar as varas nos quatro cantos da grelha de bronze.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 6, 'Fez as varas de madeira de acácia, revestiu-as de bronze');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 7, 'e colocou-as nas argolas, nos dois lados do altar, para que o pudessem carregar. O altar era oco, feito de tábuas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 8, 'Fez a bacia de bronze e a sua base com os espelhos das mulheres que serviam à entrada da Tenda do Encontro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 9, 'Fez também o pátio. O lado sul tinha quarenta e cinco metros de comprimento e cortinas externas de linho fino trançado,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 10, 'com vinte colunas e vinte bases de bronze, com ganchos e ligaduras de prata nas colunas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 11, 'O lado norte também tinha quarenta e cinco metros de comprimento, com vinte colunas e vinte bases de bronze. Os ganchos e as ligaduras das colunas eram de prata.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 12, 'O lado ocidental, com suas cortinas externas, tinha vinte e dois metros e meio de largura, com dez colunas e dez bases, com ganchos e ligaduras de prata nas colunas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 13, 'O lado oriental, que dá para o nascente, também tinha vinte e dois metros e meio de largura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 14, 'Havia cortinas de seis metros e setenta e cinco centímetros de comprimento num dos lados da entrada, com três colunas e três bases,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 15, 'e cortinas de seis metros e setenta e cinco centímetros de comprimento no outro lado da entrada do pátio, também com três colunas e três bases.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 16, 'Todas as cortinas ao redor do pátio eram feitas de linho fino trançado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 17, 'As bases das colunas eram de bronze. Os ganchos e as ligaduras das colunas eram de prata, e o topo das colunas também eram revestidos de prata; de modo que todas as colunas do pátio tinham ligaduras de prata.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 18, 'Na entrada do pátio havia uma cortina de linho fino trançado e de fios de tecidos azul, roxo e vermelho, obra de bordador. Tinha nove metros de comprimento e, à semelhança das cortinas do pátio, tinha dois metros e vinte e cinco centímetros de altura,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 19, 'com quatro colu­nas e quatro bases de bronze. Seus ganchos e ligaduras eram de prata, e o topo das colunas também era revestido de prata.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 20, 'Todas as estacas da tenda do tabernáculo e do pátio que o rodeava eram de bronze.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 21, 'Esta é a relação do material usado para o tabernáculo, o tabernáculo da aliança, registrada por ordem de Moisés pelos levitas, sob a direção de Itamar, filho de Arão, o sacerdote.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 22, 'Bezalel, filho de Uri, neto de Hur, da tribo de Judá, fez tudo o que o Senhor tinha ordenado a Moisés.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 23, 'Com ele estava Aoliabe, filho de Aisamaque, da tribo de Dã, artesão e projetista, e também bordador em linho fino e de fios de tecidos azul, roxo e vermelho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 24, 'O peso total do ouro recebido na oferta movida e utilizado para a obra do santuário foi de uma tonelada, com base no peso padrão do santuário.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 25, 'O peso da prata recebida dos que foram contados no recenseamento da comunidade foi superior a três toneladas e meia, com base no peso padrão do santuário:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 26, 'seis gramas para cada um dos recenseados, isto é, para seiscentos e três mil, quinhentos e cinqüenta homens de vinte anos de idade para cima.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 27, 'As três toneladas e meia de prata foram usadas para fundir as bases do santuário e do véu: cem bases feitas das três toneladas e meia, trinta e cinco quilos para cada base.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 28, 'Vinte quilos e trezentos gramas foram usados para fazer os ganchos para as colunas, para revestir a parte superior das colunas e para fazer as suas ligaduras.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 29, 'O peso do bronze da oferta movida foi de duas toneladas e meia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 30, 'Ele o utilizou para fazer as bases da entrada da Tenda do Encontro, o altar de bronze, a sua grelha e todos os seus utensílios,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 2, 38, 31, 'as bases do pátio ao redor e da sua entrada, e todas as estacas do tabernáculo e do pátio em derredor.');
INSERT INTO BIBLIA_BOOK (name) VALUES ('Levítico');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 1, 1, 'Da Tenda do Encontro o Senhor chamou Moisés e lhe ordenou:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 1, 2, 'Diga o seguinte aos israelitas: Quando alguém trouxer um animal como oferta ao Senhor, que seja do gado ou do rebanho de ovelhas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 1, 3, 'Se o holocausto for de gado, oferecerá um macho sem defeito. Ele o apresentará à entrada da Tenda do Encontro, para que seja aceito pelo Senhor,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 1, 4, 'e porá a mão sobre a cabeça do animal do holocausto para que seja aceito como propiciação em seu lugar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 1, 5, 'Então o novilho será morto perante o Senhor, e os sacerdotes, descendentes de Arão, trarão o sangue e o derramarão em todos os lados do altar, que está à entrada da Tenda do Encontro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 1, 6, 'Depois se tirará a pele do animal, que será cortado em pedaços.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 1, 7, 'Então os descendentes do sacerdote Arão acen­derão o fogo do altar e arrumarão a lenha sobre o fogo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 1, 8, 'Em seguida arrumarão os pedaços, inclusive a cabeça e a gordura, sobre a lenha que está no fogo do altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 1, 9, 'As vísceras e as pernas serão lavadas com água. E o sacerdote queimará tudo isso no altar. É um holocausto, oferta preparada no fogo, de aroma agradável ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 1, 10, 'Se a oferta for um holocausto do rebanho, quer de cordeiros quer de cabritos, oferecerá um macho sem defeito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 1, 11, 'O animal será morto no lado norte do altar, perante o Senhor; os sacerdotes, descendentes de Arão, derramarão o sangue nos lados do altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 1, 12, 'Então o animal será cortado em pedaços. O sacerdote arrumará os pedaços, inclusive a cabeça e a gordura, sobre a lenha que está no fogo do altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 1, 13, 'As vísceras e as pernas serão lavadas com água. O sacerdote trará tudo isso como oferta e o queimará no altar. É um holocausto, oferta prepara­da no fogo, de aroma agradável ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 1, 14, 'Se a sua oferta ao Senhor for um holocausto de aves, traga uma rolinha ou um pombinho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 1, 15, 'O sacerdote trará a ave ao altar, destroncará o pescoço dela e a queimará, e deixará escorrer o sangue da ave na parede do altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 1, 16, 'Ele retirará o papo com o seu conteúdo e o jogará no lado leste do altar, onde ficam as cinzas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 1, 17, 'Rasgará a ave pelas asas, sem dividi-la totalmente, e então o sacerdote a queimará sobre a lenha acesa no altar. É um holocausto, oferta preparada no fogo, de aroma agradável ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 2, 1, 'Quando alguém trouxer uma oferta de cereal ao Senhor, terá que ser da melhor farinha. Sobre ela derramará óleo, colocará incenso');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 2, 2, 'e a levará aos descendentes de Arão, os sacerdotes. Um deles apanhará um punhado da melhor farinha com óleo e com todo o incenso, e os queimará no altar como porção memorial. É oferta preparada no fogo, de aroma agradável ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 2, 3, 'O que restar da oferta de cereal pertence a Arão e a seus descendentes; é parte santíssima das ofertas dedicadas ao Senhor preparadas no fogo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 2, 4, 'Se um de vocês trouxer uma oferta de cereal assada no forno, seja da melhor farinha: bolos feitos sem fermento, amassados com óleo, ou pães finos sem fermento e untados com óleo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 2, 5, 'Se a sua oferta de cereal for preparada numa assadeira, seja da melhor farinha, amassada com óleo e sem fermento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 2, 6, 'Divida-a em pedaços e derrame óleo sobre ela; é uma oferta de cereal.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 2, 7, 'Se a sua oferta de cereal for cozida numa panela, seja da melhor farinha com óleo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 2, 8, 'Traga ao Senhor a oferta de cereal feita desses ingredientes e apresente-a ao sacer­dote, que a levará ao altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 2, 9, 'Ele apanhará a porção memorial da oferta de cereal e a queimará no altar; é oferta preparada no fogo, de aroma agradável ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 2, 10, 'O restante da oferta de cereal pertence a Arão e a seus descendentes; é parte santíssima das ofertas dedicadas ao Senhor preparadas no fogo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 2, 11, 'Nenhuma oferta de cereal que vocês trouxerem ao Senhor será feita com fermento, pois vocês não queimarão fermento nem mel como oferta preparada no fogo ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 2, 12, 'Podem trazê-los como oferta dos primeiros frutos ao Senhor, mas não podem oferecê-los no altar como aroma agradável.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 2, 13, 'Temperem com sal todas as suas ofertas de cereal. Não excluam de suas ofertas de cereal o sal da aliança do seu Deus; acrescentem sal a todas as suas ofertas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 2, 14, 'Se você trouxer ao Senhor uma oferta de cereal dos primeiros frutos, ofereça grãos esmagados de cereal novo, tostados no fogo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 2, 15, 'So­bre ela derrame óleo e coloque incenso; é oferta de cereal.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 2, 16, 'O sacerdote queimará a porção memorial do cereal esmagado e do óleo, juntamente com todo o incenso, como uma oferta ao Senhor preparada no fogo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 3, 1, 'Quando a oferta de alguém for sacrifício de comunhão, assim se fará: se oferecer um animal do gado, seja macho ou fêmea, apresentará ao Senhor um animal sem defeito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 3, 2, 'Porá a mão sobre a cabeça do animal, que será morto à entrada da Tenda do Encontro. Os descendentes de Arão, os sacerdotes, derrama­rão o sangue nos lados do altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 3, 3, 'Desse sacrifício de comunhão, oferta preparada no fogo, ele trará ao Senhor toda a gordura que cobre as vísceras e está ligada a elas,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 3, 4, 'os dois rins com a gordura que os cobre e que está perto dos lom­bos, e o lóbulo do fígado, que ele removerá junto com os rins.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 3, 5, 'Os descendentes de Arão queimarão tudo isso em cima do holocausto que está sobre a lenha acesa no altar como oferta preparada no fogo, de aroma agradável ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 3, 6, 'Se oferecer um animal do rebanho como sacrifício de comunhão ao Senhor, trará um macho ou uma fêmea sem defeito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 3, 7, 'Se oferecer um cordeiro, ele o apresentará ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 3, 8, 'Porá a mão sobre a cabeça do animal, que será morto diante da Tenda do Encontro. Então os descen­dentes de Arão derramarão o sangue nos lados do altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 3, 9, 'Desse sacrifício de comunhão, oferta preparada no fogo, ele trará ao Senhora gordura, tanto a da cauda gorda cortada rente à espinha, como toda a gordura que cobre as vísceras e está ligada a elas,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 3, 10, 'os dois rins com a gordura que os cobre e que está perto dos lom­bos, e o lóbulo do fígado, que ele removerá junto com os rins.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 3, 11, 'O sacerdote os queimará no altar como alimento oferecido ao Senhor, preparado no fogo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 3, 12, 'Se a sua oferta for um cabrito, ele o apre­sentará ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 3, 13, 'Porá a mão sobre a cabeça do animal, que será morto diante da Tenda do Encontro. Então os descendentes de Arão derramarão o sangue nos lados do altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 3, 14, 'Des­se animal, que é uma oferta preparada no fogo, trará ao Senhor a gordura que cobre as vísceras e está ligada a elas,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 3, 15, 'os dois rins com a gordura que os cobre e que está perto dos lom­bos, e o lóbulo do fígado, que ele removerá junto com os rins.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 3, 16, 'O sacerdote os queimará no altar como alimento, como oferta preparada no fogo, de aroma agradável. Toda a gordura será do Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 3, 17, '“Este é um decreto perpétuo para as suas gerações, onde quer que vivam: Não comam gordura alguma, nem sangue algum”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 1, 'O Senhor ordenou a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 2, 'Diga aos israelitas: Quando alguém pecar sem inten­ção, fazendo o que é proibido em qualquer dos mandamentos do Senhor, assim se fará:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 3, 'Se for o sacerdote ungido que pecar, trazendo culpa sobre o povo, trará ao Senhor um novilho sem defeito como oferta pelo pecado que cometeu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 4, 'Apresentará ao Senhor o novilho à entrada da Tenda do Encontro. Porá a mão sobre a cabeça do novilho, que será morto perante o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 5, 'Então o sacerdote ungido pegará um pouco do sangue do novilho e o levará à Tenda do Encontro;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 6, 'molhará o dedo no sangue e o aspergirá sete vezes perante o Senhor, diante do véu do santuário.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 7, 'O sacerdote porá um pouco do sangue nas pontas do altar do incenso aromático que está perante o Senhor na Tenda do Encontro. Derramará todo o restante do sangue do novilho na base do altar do holocausto, na entrada da Tenda do Encontro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 8, 'Então retirará toda a gordura do novilho da ofer­ta pelo pecado: a gordura que cobre as vís­ceras e está ligada a elas,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 9, 'os dois rins com a gordura que os cobre e que está perto dos lom­bos, e o lóbulo do fígado, que ele removerá junto com os rins,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 10, 'como se retira a gordura do boi sacrificado como oferta de comunhão. En­tão o sacerdote queimará essas partes no altar dos holocaustos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 11, 'Mas o couro do novilho e toda a sua carne, bem como a cabeça e as pernas, as vísce­ras e os excrementos,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 12, 'isto é, tudo o que restar do novilho, ele levará para fora do acampa­mento, a um local cerimonialmente puro, onde se lançam as cinzas. Ali os queimará sobre a lenha de uma fogueira, sobre o monte de cinzas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 13, 'Se for toda a comunidade de Israel que pecar sem intenção, fazendo o que é proibido em qualquer dos mandamentos do Senhor, ainda que não tenha consciência disso, a comunidade será culpada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 14, 'Quando tiver consciência do pecado que cometeu, a comunidade trará um novilho como oferta pelo pecado e o apresenta­rá diante da Tenda do Encontro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 15, 'As autorida­des da comunidade porão as mãos sobre a cabe­ça do novilho perante o Senhor. E o novilho será morto perante o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 16, 'Então o sacerdote ungido levará um pouco do sangue do no­vilho para a Tenda do Encontro;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 17, 'molhará o dedo no sangue e o aspergirá sete vezes perante o Senhor, diante do véu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 18, 'Porá o sangue nas pontas do altar que está perante o Senhor na Tenda do Encontro e derramará todo o restante do sangue na base do altar dos holocaustos, na entrada da Tenda do Encontro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 19, 'Então retirará toda a gordura do animal e a queimará no altar,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 20, 'e fará com este novilho como se faz com o novilho da oferta pelo pecado. Assim o sacerdote fará propiciação por eles, e serão perdoados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 21, 'Depois levará o novilho para fora do acampamento e o queimará como queimou o primeiro. É oferta pelo pecado da comunidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 22, 'Quando for um líder que pecar sem intenção, fazendo o que é proibido em qualquer dos mandamentos do Senhor, o seu Deus, será culpado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 23, 'Quando o conscientizarem do seu pecado, trará como oferta um bode sem defeito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 24, 'Porá a mão sobre a cabeça do bode, que será morto no local onde o holocausto é sacrificado, perante o Senhor. Esta é a oferta pelo pecado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 25, 'Então o sacerdote pegará com o dedo um pouco do sangue da oferta pelo pecado e o porá nas pontas do altar dos holocaustos, e derramará o restante do sangue na base do altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 26, 'Queimará toda a gordura no altar, como queimou a gordura do sacrifício de comunhão. Assim o sacerdote fará propiciação pelo pecado do líder, e este será perdoado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 27, 'Se for alguém da comunidade que pecar sem intenção, fazendo o que é proibido em qualquer dos mandamentos do Senhor, o seu Deus, será culpado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 28, 'Quando o conscientizarem do seu pecado, trará como oferta pelo peca­do que cometeu uma cabra sem defeito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 29, 'Porá a mão sobre a cabeça do animal da oferta pelo pecado, que será morto no lugar dos holocaustos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 30, 'Então o sacerdote pegará com o dedo um pouco do sangue e o porá nas pontas do altar dos holocaustos, e derramará o restante do sangue na base do altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 31, 'Então retirará toda a gordura, como se retira a gordura do sacrifício de comu­nhão; o sacerdote a queimará no altar como aroma agradável ao Senhor. Assim o sacerdote fará propiciação por esse homem, e ele será perdoado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 32, 'Se trouxer uma ovelha como oferta pelo pecado, terá que ser sem defeito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 33, 'Porá a mão sobre a cabeça do animal, que será morto como oferta pelo pecado no lugar onde é sacrificado o holocausto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 34, 'Então o sacerdote pegará com o dedo um pouco do sangue da oferta pelo pecado e o porá nas pontas do altar dos holocaustos, e derramará o restante do sangue na base do altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 4, 35, 'Retirará toda a gordura, como se retira a gordura do cordeiro do sacrifício de comunhão; o sacerdote a queimará no altar, em cima das ofertas dedicadas ao Senhor, preparadas no fogo. Assim o sacerdote fará em favor dele propiciação pelo pecado que cometeu, e ele será perdoado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 5, 1, 'Se alguém pecar porque, tendo sido testemunha de algo que viu ou soube, não o declarou, sofrerá as conseqüências da sua iniqüidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 5, 2, 'Se alguém tocar qualquer coisa impura, seja um cadáver de animal selvagem ou de animal do rebanho ou de uma das pequenas criaturas que povoam a terra, ainda que não tenha consciência disso, ele se tornará impuro e será culpado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 5, 3, 'Se alguém tocar impureza humana, qualquer coisa que o torne impuro, sem ter consciência disso, quando o souber será culpado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 5, 4, 'Se alguém impensadamente jurar fazer algo bom ou mau, em qualquer assunto que alguém possa jurar descuidadamente, ainda que não tenha consciência disso, quando o souber será culpado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 5, 5, 'Quando alguém for culpado de qualquer dessas coisas, confessará em que pecou');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 5, 6, 'e, pelo pecado que cometeu, trará ao Senhor uma ovelha ou uma cabra do rebanho como oferta de reparação; e em favor dele o sacerdote fará propiciação pelo pecado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 5, 7, 'Se não tiver recursos para oferecer uma ovelha, trará pela culpa do seu pecado duas rolinhas ou dois pombinhos ao Senhor: um como oferta pelo pecado e o outro como holocausto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 5, 8, 'Ele os trará ao sacerdote, que apresentará primeiro a oferta de sacrifício pelo pecado. Ele destroncará o pescoço da ave, sem arrancar-lhe a cabeça totalmente.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 5, 9, 'A seguir aspergirá no lado do altar o sangue da oferta pelo pecado e deixará escorrer o restante do sangue na base do altar. É oferta pelo pecado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 5, 10, 'O sacerdote então oferecerá a outra ave como holocausto, de acordo com a forma prescrita, e fará propiciação em favor dele pelo pecado que cometeu, e ele será perdoado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 5, 11, 'Se, contudo, não tiver recursos para oferecer duas rolinhas ou dois pombinhos, trará como oferta pelo pecado um jarro da melhor farinha como oferta pelo pecado. Mas sobre ela não derramará óleo nem colocará incenso, porquanto é oferta pelo pecado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 5, 12, 'Ele a trará ao sacerdote, que apanhará um punhado dela como porção memorial e queimará essa porção no altar, em cima das ofertas dedicadas ao Senhor, preparadas no fogo. É oferta pelo pecado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 5, 13, 'Assim o sacerdote fará propiciação em favor dele por qualquer desses pecados que tiver cometido, e ele será perdoado. O restante da oferta pertence ao sacerdote, como no caso da oferta de cereal.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 5, 14, 'O Senhor disse a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 5, 15, 'Quando alguém cometer um erro, pecando sem in­tenção em qualquer coisa consagrada ao Senhor, trará ao Senhor um carneiro do reba­nho, sem defeito, avaliado em prata com base no peso padrão do santuário, como oferta pela culpa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 5, 16, 'Fará restituição pelo que deixou de fazer em relação às coisas consagradas, acrescentará um quinto do valor e o entregará ao sacerdote. Este fará propiciação por ele com o carneiro da oferta pela culpa, e ele será perdoado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 5, 17, 'Se alguém pecar, fazendo o que é proibido em qualquer dos mandamentos do Senhor, ainda que não o saiba, será culpado e sofrerá as conseqüências da sua iniqüidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 5, 18, 'Do rebanho ele trará ao sacerdote um carneiro, sem defeito e devidamente avaliado, como oferta pela culpa. Assim o sacerdote fará propiciação em favor dele pelo erro que cometeu sem intenção, e ele será perdoado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 5, 19, 'É oferta pela culpa, pois com certeza tornou-se culpado perante o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 1, 'Disse ainda o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 2, 'Se alguém pecar, cometendo um erro contra o Senhor, enganando o seu próximo no que diz respeito a algo que lhe foi confiado ou deixado como penhor ou roubado, ou se lhe extorquir algo,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 3, 'ou se achar algum bem perdido e mentir a respeito disso, ou se jurar falsamente a respeito de qualquer coisa, cometendo pecado;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 4, 'quando assim pecar, tornando-se por isso culpado, terá que devolver o que roubou ou tomou mediante extorsão, ou o que lhe foi confiado, ou os bens perdidos que achou,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 5, 'ou qualquer coisa sobre a qual tenha jurado falsamente. Fará restituição plena, acrescentará a isso um quinto do valor e dará tudo ao proprietário no dia em que apresentar a sua oferta pela culpa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 6, 'E por sua culpa trará ao sacerdote uma oferta dedicada ao Senhor: um carneiro do rebanho, sem defeito e devidamente avaliado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 7, 'Dessa forma o sacerdote fará propiciação por ele perante o Senhor, e ele será perdoado de qualquer dessas coisas que fez e que o tornou culpa­do.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 8, 'O Senhor disse a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 9, 'Dê este mandamento a Arão e a seus filhos, a regulamentação acerca do holocausto: Ele terá que ficar queimando até de manhã sobre as brasas do altar, onde o fogo terá que ser mantido aceso.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 10, 'O sacerdote vestirá suas roupas de linho e os calções de linho por baixo, retirará as cinzas do holocausto que o fogo consumiu no altar e as colocará ao lado do altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 11, 'Depois trocará de roupa e levará as cinzas para fora do acampamento, a um lugar cerimonialmente puro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 12, 'Mantenha-se aceso o fogo no altar; não deve ser apagado. Toda manhã o sacerdote acrescentará lenha, arrumará o holocausto sobre o fogo e queimará sobre ele a gordura das ofertas de comunhão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 13, 'Mantenha-se o fogo continuamente aceso no altar; não deve ser apagado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 14, 'Esta é a regulamentação da oferta de cereal: Os filhos de Arão a apresentarão ao Senhor, em frente do altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 15, 'O sacerdote apanhará um punhado da melhor farinha com óleo, juntamente com todo o incenso que está sobre a oferta de cereal, e queimará no altar a porção memorial como aroma agradável ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 16, 'Arão e seus filhos comerão o restante da oferta, mas deverão comê-lo sem fermento e em lugar sagrado, no pátio da Tenda do Encontro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 17, 'Essa oferta não será assada com fermento; eu a dei a eles como porção das ofertas feitas a mim com fogo. É santíssima, como a oferta pelo pecado e como a oferta pela culpa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 18, 'Somente os homens descendentes de Arão poderão comer da porção das ofertas dedicadas ao Senhor, preparadas no fogo. É um decreto perpétuo para as suas gerações. Tudo o que nelas tocar se tornará santo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 19, 'O Senhor disse também a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 20, 'Esta é a oferta que Arão e os seus descendentes terão que trazer ao Senhor no dia em que ele for ungido: um jarro da melhor farinha, como na oferta regular de cereal, metade pela manhã e metade à tarde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 21, 'Prepare-a com óleo numa assadeira; traga-a bem misturada e apresente a oferta de cereal partida em pedaços, como aroma agradável ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 22, 'Todo sacerdote ungido, dos descendentes de Arão, também preparará essa oferta. É a porção do Senhor por decreto perpétuo e será totalmente queima­da.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 23, 'Toda oferta de cereal do sacerdote será totalmente queimada; não será comida.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 24, 'O Senhor disse a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 25, 'Diga a Arão e aos seus filhos a regulamentação da oferta pelo pecado: O animal da oferta pelo pecado será morto perante o Senhor no local onde é sacrificado o holocausto; é uma oferta santíssima.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 26, 'O sacerdote que oferecer o animal o comerá em lugar sagrado, no pátio da Tenda do Encontro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 27, 'Tudo o que tocar na carne se tornará santo; se o sangue respingar na roupa, será lavada em lugar sagrado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 28, 'A vasilha de barro em que a carne for cozida deverá ser quebrada; mas, se for cozida numa vasilha de bronze, a vasilha deverá ser esfregada e enxaguada com água.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 29, 'Somente os homens da família dos sacerdotes poderão comê-la; é uma oferta santíssima.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 6, 30, 'Mas toda oferta pelo pecado, cujo sangue for trazido para a Tenda do Encontro para propiciação no Lugar Santo, não será comida; terá que ser queimada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 1, 'Esta é a regulamentação da oferta pela culpa, que é oferta santíssima:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 2, 'O animal da oferta pela culpa será morto no local onde são sacrificados os holocaustos, e seu sangue será derramado nos lados do altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 3, 'Toda a sua gordura será oferecida: a parte gorda da cauda e a gordura que cobre as vísceras,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 4, 'os dois rins com a gordura que os cobre e que está perto dos lom­bos, e o lóbulo do fígado, que será removido juntamente com os rins.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 5, 'O sacerdote os queimará no altar como oferta dedicada ao Senhor, preparada no fogo. É oferta pela culpa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 6, 'Somente os homens da família dos sacerdotes poderão comê-la, mas deve ser comida em lugar sagrado; é oferta santíssima.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 7, 'A mesma regulamentação aplica-se tanto à oferta pelo pecado quanto à oferta pela culpa: a carne pertence ao sacerdote que faz propiciação pela culpa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 8, 'O sacerdote que oferecer um holocausto por alguém ficará com o couro do animal.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 9, 'Toda oferta de cereal, assada num forno ou cozida numa panela ou numa assadeira, pertence ao sacerdote que a oferecer,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 10, 'e toda oferta de cereal, amassada com óleo ou não, pertence igualmente aos descendentes de Arão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 11, 'Esta é a regulamentação da oferta de comunhão que pode ser apresentada ao Senhor:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 12, 'Se alguém a fizer por gratidão, então, junto com sua oferta de gratidão, terá que oferecer bolos sem fermento e amassados com óleo, pães finos sem fermento e untados com óleo, e bolos da melhor farinha bem amassados e misturados com óleo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 13, 'Juntamente com sua oferta de comunhão por gratidão, apresentará uma oferta que inclua bolos com fermento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 14, 'De cada oferta trará uma contribuição ao Senhor, que será dada ao sacerdote que asperge o sangue das ofertas de comunhão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 15, 'A carne da sua oferta de comunhão por gratidão será comida no dia em que for oferecida; nada poderá sobrar até o amanhecer.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 16, 'Se, contudo, sua oferta for resultado de um voto ou for uma oferta voluntária, a carne do sacrifício será comida no dia em que for oferecida, e o que sobrar poderá ser comido no dia seguinte.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 17, 'Mas a carne que sobrar do sacrifício até o terceiro dia será queimada no fogo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 18, 'Se a carne da oferta de comunhão for comida ao terceiro dia, ela não será aceita. A oferta não será atribuída àquele que a ofereceu, pois a carne estará estragada; e quem dela comer sofrerá as conseqüências da sua iniqüidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 19, 'A carne que tocar em qualquer coisa impura não será comida; será queimada no fo­go. A carne do sacrifício, porém, poderá ser comida por quem estiver puro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 20, 'Mas se alguém que, estando impuro, comer da carne da oferta de comunhão que pertence ao Senhor, será eliminado do meio do seu povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 21, 'Se alguém tocar em alguma coisa impura, seja impureza humana, seja de animal, seja qualquer outra coisa impura e proibida, e comer da carne da oferta de comunhão que pertence ao Senhor, será eliminado do meio do seu povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 22, 'E disse o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 23, 'Diga aos israelitas: Não comam gordura alguma de boi, carneiro ou cabrito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 24, 'A gordura de um animal encontrado morto ou despedaçado por animais selvagens pode ser usada para qualquer outra finalidade, mas nunca poderá ser comida.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 25, 'Quem comer a gordura de um animal dedica­do ao Senhor numa oferta preparada no fogo, será eliminado do meio do seu povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 26, 'Onde quer que vocês vivam, não comam o sangue de nenhuma ave nem de animal.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 27, 'Quem comer sangue será eliminado do meio do seu povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 28, 'Disse mais o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 29, 'Diga aos israelitas: Todo aquele que trouxer sacrifício de comunhão ao Senhor terá que dedicar parte dele ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 30, 'Com suas próprias mãos trará ao Senhor as ofertas preparadas no fogo; trará a gordura juntamente com o peito, e o moverá perante o Senhor como gesto ritual de apresentação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 31, 'O sacerdote queimará a gordura no altar, mas o peito pertence a Arão e a seus descendentes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 32, 'Vocês deverão dar a coxa direita das ofertas de comunhão ao sacerdote como contribuição.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 33, 'O descendente de Arão que oferecer o sangue e a gordura da oferta de comunhão receberá a coxa direita como porção.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 34, 'Das ofertas de comunhão dos israelitas, tomei o peito que é movido ritualmente e a coxa que é ofertada, e os dei ao sacerdote Arão e a seus descendentes por decreto perpétuo para os israelitas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 35, 'Essa é a parte das ofertas dedicadas ao Senhor, preparadas no fogo, destinada a Arão e a seus filhos no dia em que foram apresentados para servirem ao Senhor como sacerdotes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 36, 'Foi isso que o Senhor ordenou dar a eles, no dia em que foram ungidos dentre os israelitas. É um decreto perpétuo para as suas gerações.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 37, 'Essa é a regulamentação acerca do holocausto, da oferta de cereal, da oferta pelo pecado, da oferta pela culpa, da oferta de ordenação e da oferta de comunhão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 7, 38, 'O Senhor entregou-a a Moisés no monte Sinai, no dia em que ordenou aos israelitas que trouxessem suas ofertas ao Senhor, no deserto do Sinai.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 1, 'O Senhor disse a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 2, 'Traga Arão e seus filhos, suas vestes, o óleo da unção, o novilho para a oferta pelo pecado, os dois carneiros e o cesto de pães sem fermento;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 3, 'e reúna toda a comunidade à entrada da Tenda do Encontro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 4, 'Moisés fez como o Senhor lhe tinha ordenado, e a comunidade reuniu-se à entrada da Tenda do Encontro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 5, 'Então Moisés disse à comunidade: “Foi isto que o Senhor mandou fazer”;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 6, 'e levou Arão e seus filhos à frente e mandou-os banhar-se com água;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 7, 'pôs a túnica em Arão, colocou-lhe o cinto e o manto, e pôs sobre ele o colete sacerdotal; depois a ele prendeu o manto sacerdotal com o cinturão;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 8, 'colocou também o peitoral, e nele pôs o Urim e o Tumim;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 9, 'e colocou o turbante na cabeça de Arão com a lâmina de ouro, isto é, a coroa sagrada, na frente do turbante, conforme o Senhor tinha ordenado a Moisés.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 10, 'Depois Moisés pegou o óleo da unção e ungiu o tabernáculo e tudo o que nele havia, e assim os consagrou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 11, 'Aspergiu sete vezes o óleo sobre o altar, ungindo o altar e todos os seus utensílios e a bacia com o seu suporte, para consagrá-los.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 12, 'Derramou o óleo da unção sobre a cabeça de Arão para ungi-lo e consagrá-lo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 13, 'Trouxe então os filhos de Arão à frente, vestiu-os com suas túnicas e cintos, e colocou-lhes gorros, conforme o Senhor lhe havia ordenado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 14, 'Em seguida trouxe o novilho para a oferta pelo pecado, e Arão e seus filhos puseram as mãos sobre a cabeça do novilho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 15, 'Moisés sacrificou o novilho, e com o dedo pôs um pouco do sangue em todas as pontas do altar para purificá-lo. Derramou o restante do sangue na base do altar e assim o consagrou para fazer propiciação por ele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 16, 'Moisés pegou também toda a gordura que cobre as vísceras, o lóbulo do fígado e os dois rins com a gordura que os cobre, e os queimou no altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 17, 'Mas o novilho com o seu couro, a sua carne e o seu excremento, ele queimou fora do acampamento, conforme o Senhor lhe havia ordenado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 18, 'Mandou trazer então o carneiro para o holocausto, e Arão e seus filhos puseram as mãos sobre a cabeça do carneiro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 19, 'A seguir Moisés sacrificou o carneiro e derramou o san­gue nos lados do altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 20, 'Depois, cortou o carne­iro em pedaços; queimou a cabeça, os pedaços e a gordura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 21, 'Lavou as vísceras e as pernas, e queimou o carneiro inteiro sobre o altar, como holocausto, oferta de aroma agradável ao Senhor, preparada no fogo, conforme o Senhor lhe havia ordenado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 22, 'A seguir mandou trazer o outro carneiro, o carneiro para a oferta de ordenação, e Arão e seus filhos colocaram as mãos sobre a cabeça do carneiro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 23, 'Moisés sacrificou o carneiro e pôs um pouco do sangue na ponta da orelha direita de Arão, no polegar da sua mão direita e no polegar do seu pé direito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 24, 'Moisés também mandou que os filhos de Arão se aproximassem, e sobre cada um pôs um pouco do sangue na ponta da orelha direita, no polegar da mão direita e no polegar do pé direito; e derramou o restante do sangue nos lados do altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 25, 'Apanhou a gordura, a cauda gorda, toda a gordura que cobre as vísceras, o lóbulo do fígado, os dois rins e a gordura que os cobre e a coxa direita.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 26, 'Então, do cesto de pães sem fermento que estava perante o Senhor, apanhou um pão comum, outro feito com óleo e um pão fino, e os colocou sobre as porções de gordura e sobre a coxa direita.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 27, 'Pôs tudo nas mãos de Arão e de seus filhos e moveu esses alimentos perante o Senhor como gesto ritual de apresentação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 28, 'Depois Moisés os pegou de volta das mãos deles e queimou tudo no altar, em cima do holocausto, como uma oferta de ordenação, preparada no fogo, de aroma agradável ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 29, 'Moisés pegou também o peito que era a sua própria porção do carneiro da ordenação, e o moveu perante o Senhor como gesto ritual de apresentação, como o Senhor lhe havia ordenado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 30, 'A seguir pegou um pouco do óleo da unção e um pouco do sangue que estava no altar e os aspergiu sobre Arão e suas vestes, bem como sobre seus filhos e suas vestes. Assim consagrou Arão e suas vestes, e seus filhos e suas vestes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 31, 'Moisés então disse a Arão e a seus filhos: Cozinhem a carne na entrada da Tenda do Encontro, onde a deverão comer com o pão do cesto das ofertas de ordenação, conforme me foi ordenado: “Arão e seus filhos deverão comê-la”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 32, 'Depois queimem o restante da carne e do pão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 33, 'Não saiam da entrada da Tenda do Encontro por sete dias, até que se completem os dias da ordenação de vocês, pois essa cerimônia de ordenação durará sete dias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 34, 'O que se fez hoje foi ordenado pelo Senhor para fazer pro­piciação por vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 35, 'Vocês terão que permanecer dia e noite à entrada da Tenda do Encontro por sete dias e obedecer às exigências do Senhor, para que não morram; pois isso me foi ordenado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 8, 36, 'Arão e seus filhos fizeram tudo o que o Senhor tinha ordenado por meio de Moi­sés.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 9, 1, 'Oito dias depois Moisés convocou Arão, seus filhos e as autoridades de Israel.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 9, 2, 'E disse a Arão: Traga um bezerro para a oferta pelo pecado e um carneiro para o holocausto, ambos sem defeito, e apresente-os ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 9, 3, 'Depois diga aos israelitas: Tragam um bode para oferta pelo pecado; um bezerro e um cordeiro, ambos de um ano de idade e sem defeito, para holocausto;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 9, 4, 'e um boi e um carneiro para oferta de comunhão, para os sacrificar perante o Senhor, juntamente com a oferta de cereal amassada com óleo; pois hoje o Senhor aparecerá a vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 9, 5, 'Levaram então tudo o que Moisés tinha determinado para a frente da Tenda do Encontro, e a comunidade inteira aproximou-se e ficou em pé perante o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 9, 6, 'Disse-lhes Moisés: “Foi isso que o Senhor ordenou que façam, para que a glória do Senhor apareça a vocês”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 9, 7, 'Disse Moisés a Arão: “Venha até o altar e ofereça o seu sacrifício pelo pecado e o seu holocausto, e faça propiciação por você mesmo e pelo povo; ofereça o sacrifício pelo povo e faça propiciação por ele, conforme o Senhor ordenou”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 9, 8, 'Arão foi até o altar e ofereceu o bezerro como sacrifício pelo pecado por si mesmo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 9, 9, 'Seus filhos levaram-lhe o sangue, e ele molhou o dedo no sangue e o pôs nas pontas do altar; depois derramou o restante do sangue na base do altar,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 9, 10, 'onde queimou a gordura, os rins e o lóbulo do fígado da oferta pelo pecado, confor­me o Senhor tinha ordenado a Moisés;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 9, 11, 'a carne e o couro, porém, queimou fora do acampamento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 9, 12, 'Depois sacrificou o holocausto. Seus filhos lhe entregaram o sangue, e ele o derra­mou nos lados do altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 9, 13, 'Entregaram-lhe em seguida o holocausto pedaço por pedaço, inclu­sive a cabeça, e ele os queimou no altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 9, 14, 'Lavou as vísceras e as pernas e as queimou em cima do holocausto sobre o altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 9, 15, 'Depois Arão apresentou a oferta pelo povo. Pegou o bode para a oferta pelo pecado do povo e o ofereceu como sacrifício pelo pecado, como fizera com o primeiro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 9, 16, 'Apresentou o holocausto e ofereceu-o conforme fora prescrito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 9, 17, 'Também apresentou a oferta de cereal, pegou um punhado dela e a queimou no altar, além do holocausto da manhã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 9, 18, 'Matou o boi e o carneiro como sacrifício de comunhão pelo povo. Seus filhos levaram-lhe o sangue, e ele o derramou nos lados do altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 9, 19, 'Mas as porções de gordura do boi e do carneiro, a cauda gorda, a gordura que cobre as vísceras, os rins e o lóbulo do fígado,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 9, 20, 'puseram em cima do peito; e Arão queimou essas porções no altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 9, 21, 'Em seguida, Arão moveu o peito e a coxa direita do animal perante o Senhor como gesto ritual de apresentação, conforme Moisés tinha ordenado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 9, 22, 'Depois Arão ergueu as mãos em direção ao povo e o abençoou. E, tendo oferecido o sacrifício pelo pecado, o holocausto e o sacrifício de comunhão, desceu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 9, 23, 'Assim Moisés e Arão entraram na Tenda do Encontro. Quando saíram, abençoaram o povo; e a glória do Senhor apareceu a todos eles.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 9, 24, 'Saiu fogo da presença do Senhor e consumiu o holocausto e as porções de gordura sobre o altar. E, quando todo o povo viu isso, gritou de alegria e prostrou-se com o rosto em terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 10, 1, 'Nadabe e Abiú, filhos de Arão, pegaram cada um o seu incensário, nos quais acenderam fogo, acrescentaram incenso, e trouxeram fogo profano perante o Senhor, sem que tivessem sido autorizados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 10, 2, 'Então saiu fogo da presença do Senhor e os consumiu. Morreram perante o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 10, 3, 'Moisés então disse a Arão: “Foi isto que o Senhor disse: “Aos que de mim se aproximam santo me mostrarei; à vista de todo o povo glorificado serei””. Arão, porém, ficou em silêncio.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 10, 4, 'Então Moisés chamou Misael e Elzafã, filhos de Uziel, tio de Arão, e lhes disse: “Venham cá; tirem os seus primos da frente do santuário e levem-nos para fora do acampamento”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 10, 5, 'Eles foram e os puxaram pelas túnicas, para fora do acampamento, conforme Moisés tinha ordenado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 10, 6, 'Então Moisés disse a Arão e a seus filhos Eleazar e Itamar: Não andem descabelados, nem rasguem as roupas em sinal de luto, senão vocês morrerão e a ira do Senhor cairá sobre toda a comunidade. Mas os seus parentes, e toda a nação de Israel, poderão chorar por aqueles que o Senhor destruiu pelo fogo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 10, 7, 'Não saiam da entrada da Tenda do Encontro, senão vocês morrerão, porquanto o óleo da unção do Senhor está sobre vocês. E eles fizeram con­forme Moisés tinha ordenado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 10, 8, 'Depois o Senhor disse a Arão:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 10, 9, 'Você e seus filhos não devem beber vinho nem outra bebida fermentada antes de entrar na Tenda do Encontro, senão vocês morrerão. É um decreto perpétuo para as suas gerações.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 10, 10, 'Vocês têm que fazer separação entre o santo e o profano, entre o puro e o impuro,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 10, 11, 'e ensinar aos israelitas todos os decretos que o Senhor lhes deu por me­io de Moisés.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 10, 12, 'Então Moisés disse a Arão e aos seus filhos que ficaram vivos, Eleazar e Itamar: Pe­guem a oferta de cereal que sobrou das ofertas dedicadas ao Senhor, preparadas no fogo, e comam-na sem fermento junto ao altar, pois é santíssima.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 10, 13, 'Comam-na em lugar sagrado, por­quanto é a porção que lhes cabe por decreto, a você e a seus filhos, das ofertas dedicadas ao Senhor, preparadas no fogo; pois assim me foi ordenado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 10, 14, 'O peito ritualmente movido e a coxa ofertada, você, seus filhos e suas filhas poderão comer num lugar cerimonialmente puro; essa porção foi dada a você e a seus filhos como parte das ofertas de comunhão dos israelitas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 10, 15, 'A coxa ofertada e o peito ritualmente movido devem ser trazidos junto com as porções de gordura das ofertas preparadas no fogo, para serem movidos perante o Senhor como gesto ritual de apresentação. Esta será a porção por decreto perpétuo para você e seus descendentes, confor­me o Senhor tinha ordenado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 10, 16, 'Quando Moisés procurou por toda parte o bode da oferta pelo pecado e soube que já fora queimado, irou-se contra Eleazar e Itamar, os filhos de Arão que ficaram vivos, e perguntou:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 10, 17, 'Por que vocês não comeram a carne da oferta pelo pecado no Lugar Santo? É santíssima; foi-lhes dada para retirar a culpa da comunidade e fazer propiciação por ela perante o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 10, 18, 'Como o sangue do animal não foi levado para dentro do Lugar Santo, vocês deviam tê-lo comido ali, conforme ordenei.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 10, 19, 'Arão respondeu a Moisés: “Hoje eles ofereceram o seu sacrifício pelo pecado e o seu holocausto perante o Senhor; mas, e essas coisas que aconteceram comigo? Será que teria agradado ao Senhor se eu tivesse comido a oferta pelo pecado hoje?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 10, 20, 'Essa explicação foi satisfatória para Moisés.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 1, 'Disse o Senhor a Moisés e a Arão:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 2, 'Digam aos israelitas: De todos os animais que vivem na terra, estes são os que vocês poderão comer:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 3, 'qualquer animal que tem casco fendido e dividido em duas unhas, e que rumina.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 4, 'Vocês não poderão comer aqueles que só ruminam nem os que só têm o casco fendido. O camelo, embora rumine, não tem casco fendido; considerem-no impuro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 5, 'O coelho, embora rumine, não tem casco fendido; é impuro para vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 6, 'A lebre, embora rumine, não tem casco fendido; considerem-na impura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 7, 'E o porco, embora tenha casco fendido e dividido em duas unhas, não rumina; considerem-no impuro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 8, 'Vocês não comerão a carne desses animais nem tocarão em seus cadáveres; considerem-nos impuros.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 9, 'De todas as criaturas que vivem nas águas do mar e dos rios, vocês poderão comer todas as que possuem barbatanas e escamas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 10, 'Mas todas as criaturas que vivem nos mares ou nos rios, que não possuem barbatanas e escamas, quer dentre todas as pequenas criaturas que povoam as águas, quer dentre todos os outros animais das águas, serão proibidas para vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 11, 'Por isso, não poderão comer sua carne e considerarão impuros os seus cadáveres.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 12, 'Tu­do o que vive na água e não possui barbatanas e escamas será proibido para vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 13, 'Estas são as aves que vocês considerarão impuras, das quais não poderão comer porque são proibidas: a águia, o urubu, a águia-marinha,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 14, 'o milhafre, o falcão,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 15, 'qualquer espé­cie de corvo,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 16, 'a coruja-de-chifre, a coruja-de-orelha-pequena, a coruja-orelhuda, qual­quer espécie de gavião,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 17, 'o mocho, a coruja-pescado­ra e o corujão,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 18, 'a coruja-branca, a coruja-do-deserto, o abutre,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 19, 'a cegonha, qualquer tipo de garça, a poupa e o morcego. ');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 20, 'Todas as pequenas criaturas que en­xameiam, que têm asas mas que se movem pelo chão, serão proibidas para vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 21, 'Dentre estas, porém, vocês poderão comer aquelas que têm pernas articuladas para saltar no chão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 22, 'Dessas vocês poderão comer os diversos tipos de gafanhotos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 23, 'Mas considerarão impuras todas as outras criaturas que enxameiam, que têm asas e se movem pelo chão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 24, 'Por meio delas vocês ficarão impuros; todo aquele que tocar em seus cadáveres estará impuro até a tarde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 25, 'Todo o que carregar o cadáver de alguma delas lavará as suas roupas e estará impuro até a tarde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 26, 'Todo animal de casco não dividido em duas unhas ou que não rumina é impuro para vocês; quem tocar qualquer um deles ficará impuro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 27, 'Todos os animais de quatro pés, que andam sobre a planta dos pés, são impuros para vocês; todo o que tocar os seus cadáveres ficará impuro até a tarde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 28, 'Quem carregar o cadáver de algum deles lavará suas roupas, e estará impuro até a tarde. São impuros para vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 29, 'Dos animais que se movem rente ao chão, estes vocês considerarão impuros: a doninha, o rato, qualquer espécie de lagarto grande,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 30, 'a lagartixa, o lagarto-pintado, o lagarto, o lagarto da areia e o camaleão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 31, 'De todos os que se movem rente ao chão, esses vocês considera­rão impuros. Quem neles tocar depois de mortos estará impuro até a tarde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 32, 'E tudo sobre o que um deles cair depois de morto, qualquer que seja o seu uso, ficará impuro, seja objeto feito de madeira, de pano, de couro ou de pano de saco. Deverá ser posto em água e estará impuro até a tarde, e então ficará puro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 33, 'Se um deles cair dentro de uma vasilha de barro, tudo o que nela houver ficará impuro, e vocês quebrarão a vasilha.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 34, 'Qualquer alimento sobre o qual cair essa água ficará impuro, e qualquer bebida que estiver dentro da vasilha ficará impura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 35, 'Tudo aquilo sobre o que o cadáver de um desses animais cair ficará impuro; se for um forno ou um fogão de barro vocês o quebrarão. Estão impuros, e vocês os considerarão como tais.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 36, 'Mas, se cair numa fonte ou numa cisterna onde se recolhe água, ela permanece pura; mas quem tocar no cadáver ficará impuro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 37, 'Se um cadáver cair sobre alguma semente a ser plantada, ela permanece pura;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 38, 'mas se foi derramada água sobre a semente, vocês a considerarão impura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 39, 'Quando morrer um animal que vocês têm permissão para comer, quem tocar no seu cadáver ficará impuro até a tarde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 40, 'Quem comer da carne do animal morto terá que lavar as suas roupas e ficará impuro até a tarde. Quem carregar o cadáver do animal terá que lavar as suas roupas, e ficará impuro até a tarde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 41, 'Todo animal que se move rente ao chão lhes será proibido e não poderá ser comido.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 42, 'Vocês não poderão comer animal algum que se move rente ao chão, quer se arraste sobre o ventre quer ande de quatro ou com o auxílio de muitos pés; são proibidos a vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 43, 'Não se contaminem com qualquer desses animais. Não se tornem impuros com eles nem deixem que eles os tornem impuros.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 44, 'Pois eu sou o Senhor, o Deus de vocês; consagrem-se e sejam santos, porque eu sou santo. Não se tornem impuros com qualquer animal que se move rente ao chão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 45, 'Eu sou o Senhor que os tirou da terra do Egito para ser o seu Deus; por isso, sejam santos, porque eu sou santo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 46, 'Essa é a regulamentação acerca dos animais, das aves, de todos os seres vivos que se movem na água e de todo animal que se move rente ao chão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 11, 47, 'Vocês farão separação entre o impuro e o puro, entre os animais que podem ser comidos e os que não podem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 12, 1, 'Disse o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 12, 2, 'Diga aos israelitas: Quando uma mulher engravidar e der à luz um menino, estará impura por sete dias, assim como está impura durante o seu período menstrual.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 12, 3, 'No oitavo dia o menino terá que ser circuncidado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 12, 4, 'Então a mulher aguardará trinta e três dias para ser purificada do seu sangramento. Não poderá tocar em nenhuma coisa sagrada e não poderá ir ao santuário, até que se completem os dias da sua purificação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 12, 5, 'Se der à luz uma menina, estará impura por duas semanas, como durante o seu período menstrual. Nesse caso aguardará sessenta e seis dias para ser purificada do seu sangramento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 12, 6, 'Quando se completarem os dias da sua purificação pelo nascimento de um menino ou de uma menina, ela trará ao sacerdote, à entrada da Tenda do Encontro, um cordeiro de um ano para o holocausto e um pombinho ou uma rolinha como oferta pelo pecado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 12, 7, 'Ele os oferecerá ao Senhor para fazer propiciação por ela, que ficará pura do fluxo do seu sangramento. Essa é a regulamentação para a mulher que der à luz um menino ou uma menina.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 12, 8, 'Se ela não tiver recursos para oferecer um cordeiro, poderá trazer duas rolinhas ou dois pombinhos, um para o holocausto e o outro para a oferta pelo pecado. Assim o sacerdote fará propiciação por ela, e ela ficará pura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 1, 'Disse o Senhor a Moisés e a Arão:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 2, 'Quando alguém tiver um inchaço, uma erupção ou uma mancha brilhante na pele que possa ser sinal de lepra, será levado ao sacerdote Arão ou a um dos seus filhos que seja sacerdo­te.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 3, 'Este examinará a parte afetada da pele, e, se naquela parte o pêlo tiver se tornado branco e o lugar parecer mais profundo do que a pele, é sinal de lepra. Depois de examiná-lo, o sacerdo­te o declarará impuro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 4, 'Se a mancha na pele for branca, mas não parecer mais profunda do que a pele e sobre ela o pêlo não tiver se tornado branco, o sacerdote o porá em isolamento por sete dias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 5, 'No sétimo dia o sacerdote o examinará e, se verificar que a parte afetada não se alterou nem se espalhou pela pele, o manterá em isolamento por mais sete dias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 6, 'Ao sétimo dia o sacerdote o examinará de novo e, se a parte afetada diminuiu e não se espalhou pela pele, o sacerdote o declarará puro; é apenas uma erupção. Então ele lavará as suas roupas, e estará puro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 7, 'Mas, se depois que se apresentou ao sacerdote para ser declarado puro a erupção se espalhar pela pele, ele terá que se apresentar novamente ao sacerdote.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 8, 'O sacerdote o examinará e, se a erupção espalhou-se pela pele, ele o declarará impuro; trata-se de lepra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 9, 'Quando alguém apresentar sinal de lepra, será levado ao sacerdote.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 10, 'Este o examinará e, se houver inchaço branco na pele, o qual tornou branco o pêlo, e se houver carne viva no inchaço,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 11, 'é lepra crônica na pele, e o sacerdote o declarará impuro. Não o porá em isolamento, porquanto já está impuro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 12, 'Se a doença se alastrar e cobrir toda a pele da pessoa infectada, da cabeça aos pés, até onde é possível ao sacerdote verificar,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 13, 'este a examinará e, se observar que a lepra cobriu todo o corpo, ele a declarará pura. Visto que tudo ficou branco, ela está pura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 14, 'Mas quando nela aparecer carne viva, ficará impura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 15, 'Quando o sacerdote vir a carne viva, ele a declarará impura. A carne viva é impura; trata-se de lepra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 16, 'Se a carne viva retroceder e a pele se tornar branca, a pessoa voltará ao sa­cerdote.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 17, 'Es­te a examinará e, se a parte afetada se tornou branca, o sacerdote declarará pura a pessoa infectada, a qual então estará pura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 18, 'Quando alguém tiver uma ferida purulenta em sua pele e ela sarar,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 19, 'e no lugar da ferida aparecer um inchaço branco ou uma mancha avermelhada, ele se apresentará ao sacerdote.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 20, 'Este examinará o local e, se parecer mais profundo do que a pele e o pêlo ali tiver se tornado branco, o sacerdote o declarará impuro. É sinal de lepra que se alastrou onde estava a ferida.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 21, 'Mas se, quando o sacerdote o examinar não houver nenhum pêlo branco e o lugar não estiver mais profundo do que a pele e tiver diminuído, então o sacerdote o porá em isolamento por sete dias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 22, 'Se de fato estiver se espalhando pela pele, o sacerdote o declarará impuro; é sinal de lepra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 23, 'Mas, se a mancha não tiver se alterado nem se espalhado, é apenas a cicatriz da ferida, e o sacerdote o declarará puro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 24, 'Quando alguém tiver uma queimadura na pele, e uma mancha avermelhada ou branca aparecer na carne viva da queimadura,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 25, 'o sacerdote examinará a mancha e, se o pêlo sobre ela tiver se tornado branco e ela parecer mais profunda do que a pele, é lepra que surgiu na queimadura. O sacerdote o declarará impuro; é sinal de lepra na pele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 26, 'Mas, se o sacerdote examinar a mancha e nela não houver pêlo branco e esta não estiver mais profunda do que a pele e tiver diminuído, então o sacerdote o porá em isolamento por sete dias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 27, 'No sétimo dia o sacerdote o examinará e, se a mancha tiver se espalhado pela pele, o sacerdote o declarará impuro; é sinal de lepra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 28, 'Se, todavia, a mancha não tiver se alterado nem se espalhado pela pele, mas tiver diminuído, é um inchaço da queimadura, e o sacerdote o declarará puro; é apenas a cicatriz da queimadura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 29, 'Quando um homem ou uma mulher tiver uma ferida na cabeça ou no queixo,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 30, 'o sacerdote examinará a ferida e, se ela parecer mais profunda do que a pele e o pêlo nela for amarelado e fino, o sacerdote declarará impura aquela pessoa; é sarna, isto é, lepra da cabeça ou do queixo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 31, 'Mas se, quando o sacerdote examinar o sinal de sarna este não parecer mais profundo do que a pele e não houver pêlo escuro nela, então o sacerdote porá a pessoa infectada em isolamento por sete dias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 32, 'No sétimo dia o sacerdote examinará a parte afetada e, se a sarna não tiver se espalhado e não houver pêlo amarelado nela e não parecer mais profunda do que a pele,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 33, 'a pessoa rapará os pêlos, exceto na parte afetada, e o sacerdote a porá em isolamento por mais sete dias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 34, 'No sétimo dia o sacerdote examinará a sarna e, se não tiver se espalhado mais e não parecer mais profunda do que a pele, o sacerdote declarará pura a pessoa. Esta lavará suas roupas e estará pura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 35, 'Mas, se a sarna se espalhar pela pele depois que a pessoa for declarada pura,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 36, 'o sacerdote a examinará e, se a sarna tiver se espalhado pela pele, o sacer­dote não precisará procurar pêlo amarelado; a pessoa está impura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 37, 'Se, entretanto, verificar que não houve alteração e cresceu pêlo escuro, a sarna está curada. A pessoa está pura, e o sacerdote a declarará pura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 38, 'Quando um homem ou uma mulher tiver manchas brancas na pele,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 39, 'o sacerdote examinará as manchas; se forem brancas e sem brilho, é um eczema que se alastrou; essa pessoa está pura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 40, 'Quando os cabelos de um homem caírem, ele está calvo, todavia puro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 41, 'Se lhe caírem os cabelos da frente da cabeça, ele está meio-calvo, porém puro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 42, 'Mas, se tiver uma ferida avermelhada na parte calva da frente ou de trás da cabeça, é lepra que se alastra pela calva da frente ou de trás da cabeça.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 43, 'O sacerdote o examinará e, se a ferida inchada na parte da frente ou de trás da calva for avermelhada como a lepra de pele,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 44, 'o homem está leproso e impuro. O sacerdote terá que declará-lo impuro devido à ferida na cabeça.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 45, 'Quem ficar leproso, apresentando quaisquer desses sintomas, usará roupas rasgadas, andará descabelado, cobrirá a parte inferior do rosto e gritará: “Impuro! Impuro!”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 46, 'Enquan­to tiver a doença, estará impuro. Viverá separado, fora do acampamento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 47, 'Quando aparecer mancha de mofo em alguma roupa, seja de lã, seja de li­nho,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 48, 'ou em qualquer peça tecida ou entrelaçada de linho ou de lã, ou em algum pedaço ou objeto de couro,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 49, 'se a mancha na roupa, ou no pedaço de couro, ou na peça tecida ou entrela­çada, ou em qualquer objeto de couro, for esverdeada ou avermelhada, é mancha de mofo que deverá ser mostrada ao sacerdote.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 50, 'O sacerdote examinará a mancha e isolará o objeto afetado por sete dias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 51, 'No sétimo dia examinará a mancha e, se ela tiver se espalhado pela roupa, ou pela peça tecida ou entrelaçada, ou pelo pedaço de couro, qualquer que seja o seu uso, é mofo corrosivo; o objeto está impuro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 52, 'Ele queimará a roupa, ou a peça tecida ou entrelaçada, ou qualquer objeto de couro que tiver a mancha, pois é mofo corrosivo; o objeto será queimado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 53, 'Mas se, quando o sacerdote o examinar, a mancha não tiver se espalhado pela roupa, ou pela peça tecida ou entrelaçada, ou pelo objeto de couro,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 54, 'ordenará que o objeto afetado seja lavado. Então ele o isolará por mais sete dias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 55, 'Depois de lavado o objeto afetado, o sacerdote o examinará e, se a mancha não tiver alterado sua cor, ainda que não tenha se espalhado, o objeto estará impuro. Queime-o com fogo, quer o mofo corrosivo tenha afetado um lado, quer o outro do objeto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 56, 'Se, quando o sacerdote o examinar, a mancha tiver diminuído depois de lavado o objeto, ele cortará a parte afetada da roupa, ou do pedaço de couro, ou da peça tecida ou entrelaçada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 57, 'Mas, se a mancha ainda aparecer na roupa, ou na peça tecida ou entrelaçada, ou no objeto de couro, é mofo que se alastra, e tudo o que tiver o mofo será queimado com fogo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 58, 'Mas se, depois de lavada, a mancha desaparecer da roupa, ou da peça tecida ou entrelaçada, ou do objeto de couro, o objeto afetado será lavado de novo, e então estará puro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 13, 59, 'Essa é a regulamentação acerca da mancha de mofo nas roupas de lã ou de linho, nas peças tecidas ou entrelaçadas, ou nos objetos de couro, para que sejam declarados puros ou impuros.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 1, 'Disse também o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 2, 'Esta é a regulamentação acerca da purificação de um leproso: Ele será levado ao sacerdote,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 3, 'que sairá do acampamento e o examinará. Se a pessoa foi curada da lepra,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 4, 'o sacerdote ordenará que duas aves puras, vivas, um pedaço de madeira de cedro, um pano vermelho e um ramo de hissopo sejam trazidos em favor daquele que será purificado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 5, 'Então o sacerdote ordenará que uma das aves seja morta numa vasilha de barro com água da fonte.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 6, 'Então pegará a ave viva e a molhará, com o pedaço de madeira de cedro, com o pano vermelho e com o ramo de hissopo, no sangue da ave morta em água corrente.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 7, 'Sete vezes ele aspergirá aquele que está sendo purificado da lepra e o declarará puro. Depois soltará a ave viva em campo aberto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 8, 'Aquele que estiver sendo purificado lavará as suas roupas, rapará todos os seus pêlos e se banhará com água; e assim estará puro. Depois disso poderá entrar no acampamento, mas ficará fora da sua tenda por sete dias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 9, 'No sétimo dia rapará todos os seus pêlos: o cabelo, a barba, as sobrancelhas e o restante dos pêlos. Lavará suas roupas e banhará o corpo com água; então ficará puro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 10, 'No oitavo dia pegará dois cordeiros sem defeito e uma cordeira de um ano sem defeito, juntamente com três jarros da melhor farinha amassada com óleo, como oferta de cereal, e uma caneca de óleo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 11, 'O sacerdote que faz a purificação apresentará ao Senhor, à en­trada da Tenda do Encontro, tanto aquele que estiver para ser purificado como as suas ofertas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 12, 'Então o sacerdote pegará um dos cordeiros e o sacrificará como oferta pela culpa, juntamente com a caneca de óleo; ele os moverá perante o Senhor como gesto ritual de apresentação e');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 13, 'matará o cordeiro no Lugar Santo, onde são sacrificados a oferta pelo pecado e o holocausto. Como se dá com a oferta pelo pecado, também a oferta pela culpa pertence ao sacerdote; é santíssima.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 14, 'O sacerdote porá um pouco do sangue da oferta pela culpa na ponta da orelha direita daquele que será purificado, no polegar da sua mão direita e no polegar do seu pé direito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 15, 'Então o sacerdote pegará um pouco de óleo da caneca e o derramará na palma da sua própria mão esquerda,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 16, 'molhará o dedo direito no óleo que está na palma da mão es­querda, e com o dedo o aspergirá sete vezes perante o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 17, 'O sacerdote ainda porá um pouco do óleo restante na palma da sua mão, na ponta da orelha direita daquele que está sendo purificado, no polegar da sua mão direita e no polegar do seu pé direito, em cima do sangue da oferta pela culpa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 18, 'O óleo que restar na palma da sua mão, o sacerdote derramará sobre a ca­beça daquele que está sendo purificado e fará propiciação por ele perante o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 19, 'Então o sacerdote sacrificará a oferta pelo pecado e fará propiciação em favor daquele que está sendo purificado da sua impureza. Depois disso, o sacerdote matará o animal do holocausto');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 20, 'e o oferecerá sobre o altar, junta­mente com a ofer­ta de cereal; e assim fará propiciação pelo ofertante, o qual estará puro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 21, 'Se, todavia, for alguém pobre, sem recursos para isso, pegará um cordeiro como oferta pela culpa, para ser movido e para fazer propiciação por ele, juntamente com um jarro da melhor farinha, amassada com óleo, como oferta de cereal, uma caneca de óleo');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 22, 'e duas rolinhas ou dois pombinhos, conforme os seus recursos, um como oferta pelo pecado e o outro como holocausto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 23, 'No oitavo dia ele os trará ao sacerdote, para a sua purificação, à entrada da Tenda do Encontro, perante o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 24, 'O sacerdote pegará o cordeiro da oferta pela culpa, com uma caneca de óleo, e os moverá perante o Senhor como gesto ritual de apresentação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 25, 'Matará o cordeiro da oferta pela culpa e pegará um pouco do sangue e o porá na ponta da orelha direita daquele que está sendo purificado, no polegar da sua mão direita e no polegar do seu pé direito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 26, 'O sacerdote derramará um pouco do óleo na palma da sua mão esquer­da,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 27, 'e com o dedo indicador direito aspergirá um pouco do óleo da palma da sua mão esquerda sete vezes perante o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 28, 'Ele porá o óleo da palma da sua mão nos mesmos lugares em que pôs o sangue da oferta pela culpa: na ponta da orelha direita daquele que está sendo purificado, no polegar da sua mão direita e no polegar do seu pé direito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 29, 'O que restar do óleo na palma da sua mão, o sacerdote derramará sobre a cabeça daquele que está sendo purificado, para fazer propiciação por ele perante o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 30, 'Depois sacrificará uma das rolinhas ou um dos pombinhos, conforme os seus recursos,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 31, 'um como oferta pelo pecado e o outro como holocausto, juntamente com a oferta de cereal. Assim o sacerdote fará propiciação perante o Senhor em favor daque­le que está sendo purificado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 32, 'Essa é a regulamentação para todo aquele que tem lepra e não tem recursos para fazer a oferta da sua purificação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 33, 'O Senhor disse a Moisés e a Arão:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 34, 'Quando vocês entrarem na terra de Canaã, que lhes dou como propriedade, e eu puser mancha de mofo numa casa, na terra que lhes pertence,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 35, 'o dono da casa irá ao sacerdote e dirá: Parece-me que há mancha de mofo em minha casa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 36, 'Antes de examinar o mofo, o sacerdote ordenará que desocupem a casa para que nada que houver na casa se torne impuro. Depois disso, o sacerdote irá examinar a casa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 37, 'Examinará as manchas nas paredes e, se elas forem esverdeadas ou avermelhadas e parecerem mais profundas do que a superfície da parede,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 38, 'o sacerdote sairá da casa e a deixará fechada por sete dias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 39, 'No sétimo dia voltará para examinar a casa. Se as manchas se houverem espalhado pelas paredes da casa,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 40, 'ordenará que as pedras contaminadas pelas manchas sejam retiradas e jogadas num local impuro, fora da cidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 41, 'Fará que a casa seja raspada por dentro e que o reboco raspado seja jogado num local impuro, fora da cidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 42, 'Depois coloca­rão outras pedras no lugar das primeiras, e rebo­carão a casa com barro novo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 43, 'Se as manchas tornarem a alastrar-se na casa depois de retiradas as pedras e de raspada e rebocada a casa,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 44, 'o sacerdote irá examiná-la e, se as manchas se espalharam pela casa, é mofo corrosivo; a casa está impura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 45, 'Ela terá que ser demolida: as pedras, as madeiras e todo o reboco da casa; tudo será levado para um local impuro, fora da cidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 46, 'Quem entrar na casa enquanto estiver fechada estará impuro até a tarde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 47, 'Aquele que dormir ou comer na casa terá que lavar as suas roupas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 48, 'Mas, se o sacerdote for examiná-la e as manchas não se houverem espalhado depois de rebocada a casa, declarará pura a casa, pois as man­chas de mofo desapareceram.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 49, 'Para purificar a casa, ele pegará duas aves, um pedaço de madeira de cedro, um pano vermelho e hissopo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 50, 'Depois matará uma das aves numa vasi­lha de barro com água da fonte.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 51, 'Então pegará o pedaço de madeira de cedro, o hissopo, o pa­no vermelho e a ave viva, e os molhará no sangue da ave morta e na água da fonte, e aspergirá a casa sete vezes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 52, 'Ele purificará a casa com o sangue da ave, com a água da fonte, com a ave viva, com o pedaço de madeira de cedro, com o hissopo e com o pano vermelho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 53, 'Depois solta­rá a ave viva em campo aberto, fora da cidade. Assim fará propiciação pela casa, e ela ficará pura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 54, 'Essa é a regulamentação acerca de qual­quer tipo de lepra, de sarna,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 55, 'de mofo nas roupas ou numa casa');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 56, 'e de inchaço, erupção ou mancha brilhante,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 14, 57, 'para se determinar quando uma coisa é pura ou impura. Essa é a regulamentação acerca de qualquer tipo de lepra e de mofo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 1, 'O Senhor disse a Moisés e a Arão:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 2, 'Digam o seguinte aos israelitas: Quando um homem tiver um fluxo que sai do corpo, o fluxo é impuro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 3, 'Ele ficará impuro por causa do seu fluxo, quer continue, quer fique retido.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 4, 'A cama em que um homem com fluxo se deitar ficará impura, e qualquer coisa em que se sentar ficará impura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 5, 'Quem tocar na cama dele, lavará as suas roupas e se banhará com água, e ficará impuro até a tarde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 6, 'Todo aquele que se sentar sobre qualquer coisa na qual esse homem se sentou, lavará suas roupas e se ba­nhará com água, e estará impuro até a tarde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 7, 'Quem tocar no homem que tiver um fluxo lavará as suas roupas e se banhará com água, e ficará impuro até a tarde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 8, 'Se o homem cuspir em alguém que está puro, este lavará as suas roupas e se banhará com água, e ficará impuro até a tarde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 9, 'Tudo aquilo em que o homem se sentar quando montar um animal estará impuro,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 10, 'e todo aquele que tocar em qualquer coisa que tenha estado debaixo dele ficará impuro até a tarde; quem pegar essas coisas lavará as suas roupas e se banhará com água, e ficará impuro até a tarde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 11, 'Qualquer pessoa em quem o homem com fluxo tocar sem lavar as mãos, lavará as suas roupas e se banhará com água, e ficará impura até a tarde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 12, 'A vasilha de barro na qual ele tocar será quebrada; se tocar numa vasilha de madeira, ela será lavada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 13, 'Quando um homem sarar de seu fluxo, contará sete dias para a sua purificação; lavará as suas roupas e se banhará em água corrente, e ficará puro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 14, 'No oitavo dia pegará duas rolinhas ou dois pombinhos e irá perante o Senhor, à entrada da Tenda do Encontro, e os dará ao sacerdote.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 15, 'O sacerdote os sacrificará, um como oferta pelo pecado e o outro como holocausto, e assim fará propiciação perante o Senhor em favor do homem, por causa do fluxo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 16, 'Quando de um homem sair o sêmen, banhará todo o seu corpo com água, e ficará impuro até a tarde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 17, 'Qualquer peça de roupa ou de couro em que houver sêmen será lavada com água, e ficará impura até a tarde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 18, 'Quando um homem se deitar com uma mulher e lhe sair o sêmen, ambos terão que se banhar com água, e estarão impuros até a tarde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 19, 'Quando uma mulher tiver fluxo de sangue que sai do corpo, a impureza da sua menstruação durará sete dias, e quem nela tocar ficará impuro até a tarde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 20, 'Tudo sobre o que ela se deitar durante a sua menstruação ficará impuro, e tudo sobre o que ela se sentar ficará impuro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 21, 'Todo aquele que tocar em sua cama lavará as suas roupas e se banhará com água, e ficará impuro até a tarde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 22, 'Quem tocar em alguma coisa sobre a qual ela se sentar lavará as suas roupas e se banhará com água, e estará impuro até a tarde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 23, 'Quer seja a cama, quer seja qualquer coisa sobre a qual ela esteve sentada, quando alguém nisso tocar estará impuro até a tarde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 24, 'Se um homem se deitar com ela e a menstruação dela nele tocar, estará impuro por sete dias; qualquer cama sobre a qual ele se deitar estará impura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 25, 'Quando uma mulher tiver um fluxo de sangue por muitos dias fora da sua menstruação normal, ou um fluxo que continue além desse período, ela ficará impura enquanto durar o corrimento, como nos dias da sua menstruação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 26, 'Qualquer cama em que ela se deitar enquanto continuar o seu fluxo estará impura, como acontece com a sua cama durante a sua menstruação, e tudo sobre o que ela se sentar estará impuro, como durante a sua menstruação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 27, 'Quem tocar em alguma dessas coisas ficará impuro; lavará as suas roupas e se banhará com água, e ficará impuro até a tarde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 28, 'Quando sarar do seu fluxo, contará sete dias, e depois disso estará pura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 29, 'No oitavo dia pegará duas rolinhas ou dois pom­binhos e os levará ao sacerdote, à entrada da Tenda do Encontro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 30, 'O sacerdote sacrificará um como oferta pelo pecado e o outro como holocausto, e assim fará propiciação em favor dela, perante o Senhor, devido à impureza do seu fluxo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 31, '“Mantenham os israelitas separados das coisas que os tornam impuros, para que não morram por contaminar com sua impureza o meu tabernáculo, que está entre eles”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 32, 'Essa é a regulamentação acerca do homem que tem fluxo e daquele de quem sai o sêmen, tornando-se impuro,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 15, 33, 'da mulher em sua menstruação, do homem ou da mulher que têm fluxo e do homem que se deita com uma mulher que está impura.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 1, 'O Senhor falou com Moisés depois que morreram os dois filhos de Arão, por haverem se aproximado do Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 2, 'O Senhor disse a Moisés: Diga a seu irmão Arão que não entre a toda hora no Lugar Santíssimo, atrás do véu, diante da tampa da arca, para que não mor­ra; pois aparecerei na nuvem, acima da tampa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 3, 'Arão deverá entrar no Lugar Santo com um novilho como oferta pelo pecado e com um carneiro como holocausto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 4, 'Ele vestirá a túnica sagrada de linho, com calções também de linho por baixo; ­porá o cinto de linho na cintura e também o turbante de linho. Essas vestes são sagradas; por isso ele se banhará com água antes de vesti-las.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 5, 'Receberá da comunida­de de Israel dois bodes como oferta pelo pecado e um carneiro como holocausto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 6, 'Arão sacrificará o novilho como oferta pelo seu próprio pecado, para fazer propiciação por si mesmo e por sua família.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 7, 'Depois pegará os dois bodes e os apresentará ao Senhor, à entrada da Tenda do Encontro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 8, 'E lançará sortes quanto aos dois bodes: uma para o Senhor e a outra para Azazel.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 9, 'Arão trará o bode cuja sorte caiu para o Senhor e o sacrificará como oferta pelo pecado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 10, 'Mas o bode sobre o qual caiu a sorte para Azazel será apresentado vivo ao Senhor para fazer propiciação, e será enviado para Azazel no deserto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 11, 'Arão trará o novilho como oferta por seu próprio pecado para fazer propiciação por si mesmo e por sua família, e ele o oferecerá como sacrifício pelo seu próprio pecado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 12, 'Pegará o incensário cheio de brasas do altar que está perante o Senhor e dois punhados de incenso aromático em pó, e os levará para trás do véu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 13, 'Porá o incenso no fogo perante o Senhor, e a fumaça do incenso cobrirá a tampa que está acima das tábuas da aliança, a fim de que não morra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 14, 'Pegará um pouco do sangue do novilho e com o dedo o aspergirá sobre a parte da frente da tampa; depois, com o dedo aspergirá o sangue sete vezes, diante da tampa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 15, 'Então sacrificará o bode da oferta pelo pecado, em favor do povo, e trará o sangue para trás do véu; fará com o sangue o que fez com o sangue do novilho; ele o aspergirá sobre a tampa e na frente dela.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 16, 'Assim fará propiciação pelo Lugar Santíssimo por causa das impurezas e das rebeliões dos israelitas, quaisquer que tenham sido os seus pecados. Fará o mesmo em favor da Tenda do Encontro, que está entre eles no meio das suas impurezas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 17, 'Ninguém estará na Tenda do Encontro quando Arão entrar para fazer propiciação no Lugar Santíssimo, até a saída dele, depois que fizer propiciação por si mesmo, por sua família e por toda a as­sembléia de Israel.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 18, 'Depois irá ao altar que está perante o Senhor e pelo altar fará propiciação. Pegará um pouco do sangue do novilho e do sangue do bode e o porá em todas as pontas do altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 19, 'Com o dedo aspergirá o sangue sete vezes sobre o altar para purificá-lo e santificá-lo das impurezas dos israelitas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 20, 'Quando Arão terminar de fazer propiciação pelo Lugar Santíssimo, pela Tenda do Encontro e pelo altar, trará para a frente o bode vivo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 21, 'Então colocará as duas mãos sobre a cabeça do bode vivo e confessará todas as ini­qüidades e rebeliões dos israelitas, todos os seus pecados, e os porá sobre a cabeça do bode. Em seguida enviará o bode para o deserto aos cuidados de um homem designado para isso.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 22, 'O bode levará consigo todas as iniqüidades deles para um lugar solitário. E o homem soltará o bode no deserto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 23, 'Depois Arão entrará na Tenda do Encontro, tirará as vestes de linho que usou para entrar no Santo dos Santos e as deixará ali.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 24, 'Ele se banhará com água num lugar sagrado e vestirá as suas próprias roupas. Então sairá e sacrificará o holocausto por si mesmo e o holo­causto pelo povo, para fazer propiciação por si mesmo e pelo povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 25, 'Também queimará sobre o altar a gordura da oferta pelo pecado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 26, 'Aquele que soltar o bode para Azazel lavará as suas roupas e se banhará com água, e depois poderá entrar no acampamento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 27, 'O no­vilho e o bode da oferta pelo pecado, cujo sangue foi trazido ao Lugar Santíssimo para fazer propiciação, serão levados para fora do acampa­mento; o couro, a carne e o excremento deles serão queimados com fogo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 28, 'Aquele que os queimar lavará as suas roupas e se banhará com água; depois poderá entrar no acampamento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 29, 'Este é um decreto perpétuo para vocês: No décimo dia do sétimo mês vocês se humilharão e não poderão realizar trabalho algum, nem o natural da terra, nem o estrangeiro residente.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 30, 'Porquanto nesse dia se fará propiciação por vocês, para purificá-los. Então, perante o Senhor, vocês estarão puros de todos os seus pecados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 31, 'Este lhes será um sábado de descanso, quando vocês se humilha­rão; é um decreto perpétuo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 32, 'O sacerdote que for ungido e ordenado para suceder seu pai co­mo sumo sacerdote fará a propiciação. Porá as vestes sagradas de linho');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 33, 'e fará propiciação pelo Lugar Santíssimo, pela Tenda do Encontro, pelo altar, por todos os sacerdotes e por todo o povo da assembléia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 16, 34, '“Este é um decreto perpétuo para vocês: A propiciação será feita uma vez por ano, por todos os pecados dos israelitas”. E tudo foi feito conforme o Senhor tinha ordenado a Moisés.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 17, 1, 'O Senhor disse a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 17, 2, 'Diga a Arão e seus filhos e a todos os israelitas o que o Senhor ordenou:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 17, 3, 'Qualquer israelita que sacrificar um boi, um cordeiro ou um cabrito dentro ou fora do acampamento,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 17, 4, 'e não o trouxer à entrada da Tenda do Encontro para apresentá-lo como oferta ao Senhor, diante do tabernáculo do Senhor, será considerado culpado de sangue; derramou sangue e será eliminado do meio do seu povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 17, 5, 'Os sacrifícios, que os israelitas agora fazem em campo aberto, passarão a trazer ao Senhor, entregando-os ao sacerdote, para oferecê-los ao Senhor, à entrada da Tenda do Encontro, e os sacrificarão como ofertas de comunhão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 17, 6, 'O sacerdote aspergirá o sangue no altar do Senhor, à entrada da Tenda do Encontro, e queimará a gordura como aroma agradável ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 17, 7, 'Não oferecerão mais sacrifícios aos ídolos em forma de bode, aos quais prestam culto imoral. Este é um decreto perpétuo para eles e para as suas gerações.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 17, 8, 'Diga-lhes: Todo israelita ou estrangeiro residente que oferecer holocausto ou sacrifício,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 17, 9, 'e não o trouxer à entrada da Tenda do Encontro para oferecê-lo ao Senhor, será eliminado do meio do seu povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 17, 10, 'Todo israelita ou estrangeiro residente que comer sangue de qualquer animal, contra esse eu me voltarei e o eliminarei do meio do seu povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 17, 11, 'Pois a vida da carne está no sangue, e eu o dei a vocês para fazerem propiciação por si mesmos no altar; é o sangue que faz propiciação pela vida.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 17, 12, 'Por isso digo aos israelitas: Nenhum de vocês pode­rá comer sangue, nem também o estrangeiro residente.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 17, 13, 'Qualquer israelita ou estrangeiro residente que caçar um animal ou ave que se pode comer, derramará o sangue e o cobrirá com terra,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 17, 14, 'porque a vida de toda carne é o seu sangue. Por isso eu disse aos israelitas: Vocês não poderão comer o sangue de nenhum animal, porque a vida de toda carne é o seu sangue; todo aquele que o comer será eliminado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 17, 15, 'Todo aquele que, natural da terra ou estrangeiro, comer um animal encontrado morto ou despedaçado por animais selvagens, lavará suas roupas e se banhará com água, e ficará impuro até a tarde; então estará puro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 17, 16, 'Mas, se não lavar suas roupas nem se banhar, sofrerá as conseqüências da sua iniqüidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 1, 'Disse o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 2, 'Diga o seguinte aos israelitas: Eu sou o Senhor, o Deus de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 3, 'Não procedam como se proce­de no Egito, onde vocês moraram, nem como se procede na terra de Canaã, para onde os estou levando. Não sigam as suas práticas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 4, 'Pratiquem as minhas ordenanças, obedeçam aos meus decretos e sigam-nos. Eu sou o Senhor, o Deus de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 5, 'Obedeçam aos meus decretos e ordenanças, pois o homem que os praticar viverá por eles. Eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 6, 'Ninguém poderá se aproximar de uma parenta próxima para se envolver sexualmente com ela. Eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 7, 'Não desonre o seu pai, envolvendo-se sexualmente com a sua mãe. Ela é sua mãe; não se envolva sexualmente com ela.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 8, 'Não se envolva sexualmente com a mulher do seu pai; isso desonraria seu pai.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 9, 'Não se envolva sexualmente com a sua irmã, filha do seu pai ou da sua mãe, tenha ela nascido na mesma casa ou em outro lugar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 10, 'Não se envolva sexualmente com a filha do seu filho ou com a filha da sua filha; isso desonraria você.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 11, 'Não se envolva sexualmente com a filha da mulher do seu pai, gerada por seu pai; ela é sua irmã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 12, 'Não se envolva sexualmente com a irmã do seu pai; ela é parenta próxima do seu pai.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 13, 'Não se envolva sexualmente com a irmã da sua mãe; ela é parenta próxima da sua mãe.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 14, 'Não desonre o irmão do seu pai aproximando-se da sua mulher para com ela se envolver sexualmente; ela é sua tia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 15, 'Não se envolva sexualmente com a sua nora. Ela é mulher do seu filho; não se envolva sexualmente com ela.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 16, 'Não se envolva sexualmente com a mulher do seu irmão; isso desonraria seu irmão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 17, 'Não se envolva sexualmente com uma mulher e sua filha. Não se envolva sexual­mente com a filha do seu filho ou com a filha da sua filha; são parentes próximos. É perversidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 18, 'Não tome por mulher a irmã da sua mulher, tornando-a rival, envolvendo-se sexual­mente com ela, estando a sua mulher ainda viva.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 19, 'Não se aproxime de uma mulher para se envolver sexualmente com ela quando ela estiver na impureza da sua menstruação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 20, 'Não se deite com a mulher do seu próximo, contaminando-se com ela.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 21, 'Não entregue os seus filhos para serem sacrificados a Moloque. Não profanem o nome do seu Deus. Eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 22, 'Não se deite com um homem como quem se deita com uma mulher; é repugnante.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 23, 'Não tenha relações sexuais com um animal, contaminando-se com ele. Mulher nenhuma se porá diante de um animal para ajuntar-se com ele; é depravação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 24, 'Não se contaminem com nenhuma dessas coisas, porque assim se contaminaram as nações que vou expulsar da presença de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 25, 'Até a terra ficou contaminada; e eu castiguei a sua iniqüidade, e a terra vomitou os seus habitantes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 26, 'Mas vocês obedecerão aos meus decretos e às minhas leis. Nem o natural da terra nem o estrangeiro residente entre vocês farão nenhuma dessas abominações,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 27, 'pois todas estas abominações foram praticadas pelos que habita­ram essa terra antes de vocês; por isso a terra ficou contaminada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 28, 'E, se vocês contaminarem a terra, ela os vomitará, como vomitou os povos que ali estavam antes de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 29, 'Todo aquele que fizer alguma destas abominações, aqueles que assim procederem serão eliminados do meio do seu povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 18, 30, 'Obe­deçam aos meus preceitos, e não pratiquem os costumes repugnantes praticados antes de vocês, nem se contaminem com eles. Eu sou o Senhor, o Deus de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 1, 'Disse ainda o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 2, 'Diga o seguinte a toda comunidade de Israel: Sejam santos porque eu, o Senhor, o Deus de vocês, sou santo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 3, 'Respeite cada um de vocês a sua mãe e o seu pai, e guarde os meus sábados. Eu sou o Senhor, o Deus de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 4, 'Não se voltem para os ídolos, nem façam para si deuses de metal. Eu sou o Senhor, o Deus de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 5, 'Quando vocês oferecerem um sacrifício de comunhão ao Senhor, ofereçam-no de modo que seja aceito em favor de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 6, 'Terá que ser comido no dia em que o oferecerem, ou no dia seguinte; o que sobrar até o terceiro dia será queimado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 7, 'Se alguma coisa for comida no terceiro dia, estará estragada e não será aceita.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 8, 'Quem a comer sofrerá as conseqüências da sua iniqüidade, porque profanou o que é santo ao Senhor; será eliminado do meio do seu povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 9, 'Quando fizerem a colheita da sua terra, não colham até as extremidades da sua lavoura, nem ajuntem as espigas caídas de sua colheita.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 10, 'Não passem duas vezes pela sua vinha, nem apanhem as uvas que tiverem caído. Deixem-nas para o necessitado e para o estrangeiro. Eu sou o Senhor, o Deus de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 11, 'Não furtem. Não mintam. Não enganem uns aos outros.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 12, 'Não jurem falsamente pelo meu nome, profanando assim o nome do seu Deus. Eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 13, 'Não oprimam nem roubem o seu próximo. Não retenham até a manhã do dia se­guinte o pagamento de um diarista.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 14, 'Não amaldiçoem o surdo nem ponham pedra de tropeço à frente do cego, mas temam o seu Deus. Eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 15, 'Não cometam injustiça num julgamento; não favoreçam os pobres, nem procurem agradar os grandes, mas julguem o seu próximo com justiça.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 16, 'Não espalhem calúnias entre o seu povo. Não se levantem contra a vida do seu próximo. Eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 17, 'Não guardem ódio contra o seu irmão no coração; antes repreendam com franqueza o seu próximo para que, por causa dele, não sofram as conseqüências de um pecado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 18, 'Não procurem vingança, nem guardem rancor contra alguém do seu povo, mas ame cada um o seu próximo como a si mesmo. Eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 19, 'Obedeçam às minhas leis. Não cruzem diferentes espécies de animais. Não plantem duas espécies de semen­tes na sua lavoura. Não usem roupas feitas com dois tipos de tecido.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 20, 'Se um homem deitar-se com uma escrava prometida a outro homem, mas que não tenha sido resgatada nem tenha recebido sua liberdade, aplique-se a devida punição. Contudo não serão mortos, porquanto ela não havia sido libertada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 21, 'O homem, porém, trará ao Senhor, à entrada da Tenda do Encontro, um carneiro como oferta pela culpa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 22, 'Com o carneiro da oferta pela culpa o sacerdote fará propiciação por ele perante o Senhor, pelo pecado que cometeu; assim o pecado que ele cometeu será perdoado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 23, 'Quando vocês entrarem na terra e plantarem qualquer tipo de árvore frutífera, considerem proibidas as suas frutas. Durante três anos vocês as considerarão proibidas; não poderão comê-las.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 24, 'No quarto ano todas as suas frutas serão santas; será uma oferta de louvor ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 25, 'No quinto ano, porém, vocês pode­rão comer as suas frutas. Assim a sua colheita aumentará. Eu sou o Senhor, o Deus de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 26, 'Não comam nada com sangue. Não pratiquem adivinhação nem feiti­çaria.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 27, 'Não cortem o cabelo dos lados da cabeça, nem aparem as pontas da barba.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 28, 'Não façam cortes no corpo por causa dos mortos, nem tatuagens em si mesmos. Eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 29, 'Ninguém desonre a sua filha tornando-a uma prostituta, se não, a terra se entregará à prostituição e se encherá de perversidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 30, 'Guardem os meus sábados e reverenciem o meu santuário. Eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 31, 'Não recorram aos médiuns, nem busquem a quem consulta espíritos, pois vocês serão contaminados por eles. Eu sou o Senhor, o Deus de vo­cês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 32, 'Levantem-se na presença dos idosos, honrem os anciãos, temam o seu Deus. Eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 33, 'Quando um estrangeiro viver na terra de vocês, não o maltratem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 34, 'O estrangeiro resi­dente que viver com vocês deverá ser tratado como o natural da terra. Amem-no como a si mesmos, pois vocês foram estrangeiros no Egito. Eu sou o Senhor, o Deus de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 35, 'Não usem medidas desonestas quando medirem comprimento, peso ou quantidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 36, 'Usem balanças de pesos honestos, tanto para cereais quanto para líquidos. Eu sou o Senhor, o Deus de vocês, que os tirei da terra do Egito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 19, 37, '“Obedeçam a todos os meus decretos e a todas as minhas leis e pratiquem-nos. Eu sou o Senhor”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 1, 'Disse o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 2, 'Diga aos israelitas: Qualquer israelita ou estrangeiro residente em Israel que entregar um dos seus filhos a Moloque, terá que ser executado. O povo da terra o apedrejará.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 3, 'Voltarei o meu rosto contra ele e o eliminarei do meio do seu po­vo; pois deu os seus filhos a Moloque, contaminando assim o meu santuário e profanando o meu santo nome.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 4, 'Se o povo deliberadamente fechar os olhos quando alguém entregar um dos seus filhos a Moloque, e deixar de executá-lo,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 5, 'voltarei o meu rosto contra aquele homem e contra o seu clã, e eliminarei do meio do seu povo tanto ele quanto todos os que o seguem, prostituindo-se com Moloque.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 6, 'Voltarei o meu rosto contraquem consulta espíritos e contra quem procurar médiunspara segui-los, prostituindo-se com eles. Eu o eliminarei do meio do seu povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 7, 'Consagrem-se, porém, e sejam santos, porque eu sou o Senhor, o Deus de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 8, 'Obedeçam aos meus decretos e pratiquem-nos. Eu sou o Senhor que os santifica.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 9, 'Se alguém amaldiçoar seu pai ou sua mãe, terá que ser executado. Por ter amaldiçoa­do o seu pai ou a sua mãe, merece a morte.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 10, 'Se um homem cometer adultério com a mulher de outro homem, com a mulher do seu próximo, tanto o adúltero quanto a adúltera terão que ser executados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 11, 'Se um homem se deitar com a mulher do seu pai, desonrou seu pai. Tanto o homem quanto a mulher terão que ser executados, pois merecem a morte.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 12, 'Se um homem se deitar com a sua nora, ambos terão que ser executados. O que fizeram é depravação; merecem a morte.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 13, 'Se um homem se deitar com outro homem como quem se deita com uma mulher, ambos praticaram um ato repugnante. Terão que ser executados, pois merecem a mor­te.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 14, 'Se um homem tomar uma mulher e a mãe dela, comete perversidade. Tanto ele quanto elas serão queimados com fogo, para que não haja perversidade entre vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 15, 'Se um homem tiver relações sexuais com um animal, terá que ser executado, e vocês matarão também o animal.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 16, 'Se uma mulher se aproximar de algum animal para ajuntar-se com ele, vocês matarão a mulher e o animal. Ambos terão que ser executados, pois merecem a morte.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 17, 'Se um homem tomar por mulher sua irmã, filha de seu pai ou de sua mãe, e se envolver sexualmente com ela, pratica um ato vergonhoso. Serão eliminados à vista de todo o povo. Esse homem desonrou sua irmã e sofrerá as conseqüências da sua iniqüidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 18, 'Se um homem se deitar com uma mulher durante a menstruação e com ela se envolver sexualmente, ambos serão eliminados do meio do seu povo, pois expuseram o sangramento dela.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 19, 'Não se envolva sexualmente com a irmã de sua mãe, nem com a irmã de seu pai; pois quem se envolver sexualmente com uma parenta próxima sofrerá as conseqüências da sua iniqüidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 20, 'Se um homem se deitar com a mulher do seu tio, desonrou seu tio. Eles sofrerão as conseqüências do seu pecado; morrerão sem filhos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 21, 'Se um homem tomar por mulher a mulher do seu irmão, comete impureza; desonrou seu irmão. Ficarão sem filhos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 22, 'Obedeçam a todos os meus decretos e leis e pratiquem-nos, para que a terra para onde os estou levando para nela habitarem não os vomite.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 23, 'Não sigam os costumes dos povos que vou expulsar de diante de vocês. Por terem feito todas essas coisas, causam-me repugnância.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 24, 'Mas a vocês prometi que herdarão a terra deles; eu a darei a vocês como herança, terra onde há leite e mel com fartura. Eu sou o Senhor, o Deus de vocês, que os separou dentre os povos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 25, 'Portanto, façam separação entre ani­mais puros e impuros e entre aves puras e impu­ras. Não se contaminem com animal, ou ave, ou com qual­quer criatura que se move rente ao chão, os quais separei de vocês por serem eles impuros.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 26, 'Vocês serão santos para mim, porque eu, o Senhor, sou santo, e os separei dentre os povos para serem meus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 20, 27, '“Os homens ou mulheres que, entre vocês, forem médiuns ou consultarem os espíritos, terão que ser executados. Serão apedrejados, pois merecem a morte”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 21, 1, 'Disse ainda o Senhor a Moisés: Diga o seguinte aos sacerdotes, os filhos de Arão: Um sacerdote não poderá tornar-se impuro por causa de alguém do seu povo que venha a morrer,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 21, 2, 'a não ser por um parente próximo, como mãe ou pai, filho ou filha, irmão,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 21, 3, 'ou irmã virgem dependente dele por ainda não ter marido; por causa dela, poderá tornar-se impuro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 21, 4, 'Não poderá tornar-se impuro e contaminar-se por causa de parentes por casamento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 21, 5, 'Os sacerdotes não raparão a cabeça, nem apararão as pontas da barba, nem farão cortes no corpo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 21, 6, 'Serão santos ao seu Deus, e não profanarão o nome do seu Deus. Pelo fato de apresentarem ao Senhor as ofertas preparadas no fogo, ofertas de alimento do seu Deus, serão santos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 21, 7, 'Não poderão tomar por mulher uma prostituta, uma moça que tenha perdido a virgindade, ou uma mulher divorciada do seu marido, porque o sacerdote é santo ao seu Deus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 21, 8, 'Considerem-no santo, porque ele oferece o alimento do seu Deus. Considerem-no santo, porque eu, o Senhor, que os santifico, sou santo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 21, 9, 'Se a filha de um sacerdote se corromper, tornando-se prostituta, desonra seu pai; deverá morrer queimada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 21, 10, 'O sumo sacerdote, aquele entre seus irmãos sobre cuja cabeça tiver sido derramado o óleo da unção, e que tiver sido consagrado para usar as vestes sacerdotais, não andará descabelado, nem rasgará as roupas em sinal de luto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 21, 11, 'Não entrará onde houver um cadáver. Não se tornará impuro, nem mesmo por causa do seu pai ou da sua mãe;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 21, 12, 'e não deixará o santuário do seu Deus, nem o profanará, porquanto foi consagrado pelo óleo da unção do seu Deus. Eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 21, 13, 'A mulher que ele tomar terá que ser virgem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 21, 14, 'Não poderá ser viúva, nem divorciada, nem moça que perdeu a virgindade, nem prostituta, mas terá que ser uma virgem do seu próprio povo,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 21, 15, 'assim ele não profanará a sua descendência entre o seu povo. Eu sou o Senhor, que o santifico.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 21, 16, 'Disse ainda o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 21, 17, 'Diga a Arão: Pelas suas gerações, nenhum dos seus descendentes que tenha algum defeito poderá aproximar-se para trazer ao seu Deus ofer­tas de alimento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 21, 18, 'Nenhum homem que tenha algum defeito poderá aproximar-se: ninguém que seja cego ou aleijado, que tenha o rosto defeituoso ou o corpo deformado;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 21, 19, 'ninguém que tenha o pé ou a mão defeituosos,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 21, 20, 'ou que seja corcunda ou anão, ou que tenha qualquer defeito na vista, ou que esteja com feridas purulentas ou com fluxo, ou que tenha testículos defeituosos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 21, 21, 'Nenhum descendente do sacerdo­te Arão que tenha qualquer defeito poderá aproximar-se para apresentar ao Senhor ofertas preparadas no fogo. Tem defeito; não poderá aproximar-se para trazê-las ao seu Deus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 21, 22, 'Poderá comer o alimento santíssimo de seu Deus, e também o alimento santo;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 21, 23, 'contudo, por causa do seu defeito, não se aproximará do véu nem do altar, para que não profane o meu santuário. Eu sou o Senhor, que os santifico.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 21, 24, 'Foi isso que Moisés falou a Arão e a seus filhos e a todos os israelitas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 1, 'Disse o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 2, 'Diga a Arão e a seus filhos que tratem com respeito as ofertas sagradas que os israelitas me consagra­rem, para que não profanem o meu santo nome. Eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 3, 'Avise-lhes que se, em suas futuras gerações, algum dos seus descendentes estiver impuro quando se aproximar das ofertas sagradas que os israelitas consagrarem ao Senhor, será eliminado da minha presença. Eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 4, 'Nenhum descendente de Arão que tenha lepra ou fluxo no corpo poderá comer das ofertas sagradas até que esteja purificado. Também estará impuro se tocar em algo contaminado por um cadáver, ou se lhe sair o sêmen,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 5, 'ou se tocar em alguma criatura, ou em alguém que o torne impuro, seja qual for a impureza.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 6, 'Aquele que neles tocar ficará impuro até a tar­de. Não poderá comer das ofertas sagradas, a menos que se tenha banhado com água.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 7, 'Depois do pôr-do-sol estará puro, e então poderá comer as ofertas sagradas, pois são o seu alimento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 8, 'Também não poderá comer animal encontrado morto ou despedaçado por animais selvagens, pois se tornaria impuro por causa deles. Eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 9, 'Os sacerdotes obedecerão aos meus preceitos, para que não sofram as conseqüênci­as do seu pecado nem sejam executados por tê-los profanado. Eu sou o Senhor, que os santifico.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 10, 'Somente o sacerdote e a sua família poderão comer da oferta sagrada; não poderá comê-la o seu hóspede, nem o seu empregado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 11, 'Mas, se um sacerdote comprar um escravo, ou se um escravo nascer em sua casa, esse escravo poderá comer do seu alimento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 12, 'Se a filha de um sacerdote se casar com alguém que não seja sacerdote, não poderá comer das ofertas sagradas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 13, 'Mas, se a filha de um sacerdote ficar viúva ou se divorciar, e não tiver filhos, e voltar a viver na casa do pai como na sua juventude, poderá comer do alimento do pai, mas dele não poderá comer ninguém que não seja da família do sacerdote.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 14, 'Se alguém, sem intenção, comer uma oferta sagrada, fará restituição da oferta ao sacerdote e lhe acrescentará um quinto do seu valor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 15, 'Os sacerdotes não profanarão as ofertas sagradas que os israelitas apresentam ao Senhor,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 16, 'permitindo-lhes comê-las e trazendo assim sobre eles culpa que exige reparação. Eu sou o Senhor que os santifico.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 17, 'Disse o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 18, 'Diga o seguinte a Arão e a seus filhos e a todos os israelitas: Se algum de vocês, seja israelita, seja estrangeiro residente em Israel, apre­sentar uma oferta como holocausto ao Senhor, quer para cumprir voto, quer como oferta voluntária,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 19, 'apresentará um macho sem defeito do rebanho, isto é, um boi, um carneiro ou um bode, a fim de que seja aceito em seu favor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 20, 'Não tragam nenhum animal defeituoso, porque não será aceito em favor de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 21, 'Quando alguém trouxer um animal do gado ou do rebanho de ovelhas como oferta de comunhão para o Senhor, em cumprimento de voto, ou como oferta voluntária, para ser aceitá­vel o animal terá que ser sem defeito e sem mácula.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 22, 'Não ofereçam ao Senhor animal cego, aleijado, mutilado, ulceroso, cheio de feridas purulentas ou com fluxo. Não coloquem nenhum desses animais sobre o altar como oferta ao Senhor, preparada no fogo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 23, 'Todavia, poderão apresentar como oferta voluntária um boi ou um carneiro ou um cabrito deformados ou atrofiados, mas no caso do cumprimento de voto não serão aceitos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 24, 'Não poderão oferecer ao Senhor um animal cujos testículos estejam machucados, esmagados, despedaçados ou cortados. Não façam isso em sua própria terra,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 25, 'nem aceitem animais como esses das mãos de um estrangeiro para oferecê-los como alimento do seu Deus. Não serão aceitos em favor de vocês, pois são deformados e apresentam defeitos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 26, 'Disse ainda o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 27, 'Quando nascer um bezerro, um cordeiro ou um cabrito, ficará sete dias com sua mãe. Do oitavo dia em diante será aceito como oferta ao Senhor preparada no fogo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 28, 'Não matem uma vaca ou uma ovelha ou uma cabra e sua cria no mesmo dia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 29, 'Quando vocês oferecerem um sacrifício de gratidão ao Senhor, ofereçam-no de maneira que seja aceito em favor de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 30, 'Será comido naquele mesmo dia; não deixem nada até a manhã seguinte. Eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 31, 'Obedeçam aos meus mandamentos e coloquem-nos em prática. Eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 32, 'Não profanem o meu santo nome. Eu serei reconhecido como santo pelos israelitas. Eu sou o Senhor, eu os santifico,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 22, 33, 'eu os tirei do Egito para ser o Deus de vocês. Eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 1, 'Disse o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 2, 'Diga o seguinte aos israelitas: Estas são as minhas fes­tas, as festas fixas do Senhor, que vocês proclamarão como reuniões sagradas:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 3, 'Em seis dias realizem os seus trabalhos, mas o sétimo dia é sábado, dia de descanso e de reunião sagrada. Não realizem trabalho algum; onde quer que morarem, será sábado dedicado ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 4, 'Estas são as festas fixas do Senhor, as reuniões sagradas que vocês proclamarão no tempo devido:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 5, 'a Páscoa do Senhor, que começa no entardecer do décimo quarto dia do primeiro mês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 6, 'No décimo quinto dia daquele mês começa a festa do Senhor, a festa dos pães sem fermento; durante sete dias vocês comerão pães sem fermento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 7, 'No primeiro dia façam uma reunião sagrada e não realizem trabalho algum.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 8, 'Durante sete dias apresentem ao Senhor ofertas preparadas no fogo. E no sétimo dia façam uma reunião sagrada e não realizem trabalho algum.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 9, 'Disse o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 10, 'Diga o seguinte aos israelitas: Quando vocês entrarem na terra que lhes dou e fizerem colheita, tragam ao sacerdote um feixe do primeiro cereal que colherem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 11, 'O sacerdote moverá ritualmente o feixe perante o Senhor para que seja aceito em favor de vocês; ele o moverá no dia seguinte ao sábado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 12, 'No dia em que moverem o feixe, vocês oferecerão em holocausto ao Senhor um cordeiro de um ano de idade e sem defeito.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 13, 'Apresentem também uma oferta de cereal de dois jarros da melhor farinha amassada com óleo, oferta ao Senhor preparada no fogo, de aroma agra­dável, e uma oferta derramada de um litro de vinho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 14, 'Vocês não poderão comer pão al­gum, nem cereal tostado, nem cereal novo, até o dia em que trouxerem essa oferta ao Deus de vocês. Este é um decreto perpétuo para as suas gerações, onde quer que morarem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 15, 'A partir do dia seguinte ao sábado, o dia em que vocês trarão o feixe da oferta ritualmente movida, contem sete semanas completas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 16, 'Contem cinqüenta dias, até um dia depois do sétimo sábado, e então apresentem uma oferta de cereal novo ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 17, 'Onde quer que morarem, tragam de casa dois pães feitos com dois jarros da melhor farinha, cozidos com fermento, como oferta movida dos primeiros frutos ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 18, 'Junto com os pães apresentem sete cordeiros, cada um com um ano de idade e sem defeito, um novilho e dois carneiros. Eles serão um holocausto ao Senhor, juntamente com as suas ofertas de cereal e ofertas derramadas; é oferta preparada no fogo, de aroma agradável ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 19, 'Depois sacrifiquem um bode como oferta pelo pecado e dois cordeiros, cada um com um ano de idade, como oferta de comunhão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 20, 'O sacerdote moverá os dois cordeiros perante o Senhor como gesto ritual de apresentação, juntamente com o pão dos primeiros frutos. São uma oferta sagrada ao Senhor e pertencem ao sacerdote.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 21, 'Naquele mesmo dia vocês proclamarão uma reunião sagrada e não realizarão trabalho algum. Este é um decreto perpétuo para as suas gerações, onde quer que vocês morarem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 22, '“Quando fizerem a colheita da sua terra, não colham até as extremidades da sua lavoura, nem ajuntem as espigas caídas da sua colheita. Deixem-nas para o necessitado e para o estrangeiro. Eu sou o Senhor, o Deus de vocês”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 23, 'Disse o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 24, 'Diga também aos israelitas: No primeiro dia do sétimo mês vocês terão um dia de descanso, uma reunião sagrada, celebrada com toques de trombeta.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 25, 'Não realizem trabalho algum, mas apresentem ao Senhor uma oferta preparada no fogo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 26, 'Disse o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 27, 'O décimo dia deste sétimo mês é o Dia da Expiação. Façam uma reunião sagrada e humilhem-se, e apresentem ao Senhor uma oferta preparada no fogo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 28, 'Não realizem trabalho algum nesse dia, porque é o Dia da Expiação, quando se faz propiciação por vocês perante o Senhor, o Deus de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 29, 'Quem não se humilhar nesse dia será eliminado do seu povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 30, 'Eu destruirei do meio do seu povo todo aquele que realizar algum trabalho nesse dia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 31, 'Vocês não realizarão trabalho algum. Este é um decreto perpétuo para as suas gerações, onde quer que vocês morarem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 32, 'É um sábado de descanso para vocês, e vocês se humilharão. Desde o entardecer do nono dia do mês até o entardecer do dia seguinte vocês guardarão esse sábado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 33, 'Disse o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 34, 'Diga ainda aos israelitas: No décimo quinto dia deste sétimo mês começa a festa das cabanas do Senhor, que dura sete dias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 35, 'No primeiro dia haverá reunião sagrada; não realizem trabalho algum.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 36, 'Durante sete dias apresentem ao Senhor ofertas preparadas no fogo, e no oitavo dia façam outra reunião sagrada, e também apresentem ao Senhor uma oferta preparada no fogo. É reunião solene; não realizem trabalho algum.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 37, '(Estas são as festas fixas do Senhor, que vocês proclamarão como reuniões sagradas para trazerem ao Senhor ofertas preparadas no fogo, holocaustos e ofertas de cereal, sacrifícios e ofertas derramadas exigidas para cada dia.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 38, 'Isso fora as do sábado do Senhor e fora as dádivas e os votos de vocês, e todas as ofertas voluntárias que vocês derem ao Senhor. )');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 39, 'Assim, começando no décimo quinto dia do sétimo mês, depois de terem colhido o que a terra produziu, celebrem a festa do Senhor durante sete dias; o primeiro dia e também o oitavo serão dias de descanso.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 40, 'No primeiro dia vocês apanharão os melhores frutos das árvores, folhagem de tamareira, galhos frondosos e salgueiros, e se alegrarão perante o Senhor, o Deus de vocês, durante sete dias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 41, 'Celebrem essa festa do Senhor durante sete dias todos os anos. Este é um decreto perpétuo para as suas gerações; celebrem-na no sétimo mês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 42, 'Morem em tendas durante sete dias; todos os israelitas de nascimento morarão em tendas,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 43, 'para que os descendentes de vocês saibam que eu fiz os israelitas morarem em tendas quando os tirei da terra do Egito. Eu sou o Senhor, o Deus de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 23, 44, 'Assim anunciou Moisés aos israelitas as festas fixas do Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 24, 1, 'Disse o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 24, 2, 'Orde­ne aos israelitas que lhe tragam azeite puro de oli­va batida para as lâmpadas, para que fiquem sempre acesas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 24, 3, 'Na Tenda do Encontro, do lado de fora do véu que esconde as tábuas da aliança, Arão manterá as lâmpadas continuamente acesas diante do Senhor, desde o entardecer até a manhã seguinte. Este é um decreto perpé­tuo para as suas gerações.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 24, 4, 'Mantenha sempre em ordem as lâmpadas no candelabro de ouro puro perante o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 24, 5, 'Apanhe da melhor farinha e asse doze pães, usando dois jarros para cada pão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 24, 6, 'Coloque-os em duas fileiras, com seis pães em cada uma, sobre a mesa de ouro puro perante o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 24, 7, 'Junto a cada fileira coloque um pouco de incenso puro como porção memorial para representar o pão e ser uma oferta ao Senhor preparada no fogo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 24, 8, 'Esses pães serão colocados regularmente perante o Senhor, cada sábado, em nome dos israelitas, como aliança perpétua.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 24, 9, 'Pertencem a Arão e a seus descendentes, que os comerão num lugar sagrado, porque é parte santíssima de sua porção regular das ofertas dedicadas ao Senhor, preparadas no fogo. É decreto perpétuo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 24, 10, 'Aconteceu que o filho de uma israelita e de um egípcio saiu e foi para o meio dos isra­elitas. No acampamento houve uma briga entre ele e um israelita.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 24, 11, 'O filho da israelita blasfemou o Nome com uma maldição; então o levaram a Moisés. O nome de sua mãe era Selomite, filha de Dibri, da tribo de Dã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 24, 12, 'Deixaram-no preso até que a vontade do Senhor lhes fosse declarada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 24, 13, 'Então o Senhor disse a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 24, 14, 'Leve o que blasfemou para fora do acampamento. Todos aqueles que o ouviram colocarão as mãos sobre a cabeça dele, e a comunidade toda o apedrejará.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 24, 15, 'Diga aos israelitas: Se alguém amaldiçoar seu Deus, será responsável pelo seu pecado;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 24, 16, 'quem blasfemar o nome do Senhor terá que ser executado. A comunidade toda o apedrejará. Seja estrangeiro, seja natural da terra, se blasfemar o Nome, terá que ser morto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 24, 17, 'Se alguém ferir uma pessoa ao ponto de matá-la, terá que ser executado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 24, 18, 'Quem ma­tar um animal fará restituição: vida por vida.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 24, 19, 'Se alguém ferir seu próximo, deixando-o defeituoso, assim como fez lhe será feito:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 24, 20, 'fratura por fratura, olho por olho, dente por dente. Assim como feriu o outro, deixando-o defeituoso, assim também será ferido.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 24, 21, 'Quem matar um animal fará restituição, mas quem matar um homem será morto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 24, 22, 'Vocês terão a mesma lei para o estrangeiro e para o natural da terra. Eu sou o Senhor, o Deus de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 24, 23, 'Depois que Moisés falou aos israelitas, levaram o que blasfemou para fora do acampamento e o apedrejaram. Os israelitas fizeram conforme o Senhor tinha ordenado a Moisés.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 1, 'Então disse o Senhor a Moisés no monte Sinai:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 2, 'Diga o seguinte aos israelitas: Quando vocês entrarem na terra que lhes dou, a própria terra guardará um sábado para o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 3, 'Durante seis anos semeiem as suas lavouras, aparem as suas vinhas e façam a colheita de suas plantações.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 4, 'Mas no sétimo ano a terra terá um sábado de descanso, um sábado dedicado ao Senhor. Não semeiem as suas lavouras, nem aparem as suas vinhas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 5, 'Não colham o que crescer por si, nem colham as uvas das suas vinhas, que não serão podadas. A terra terá um ano de descanso.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 6, 'Vocês se sustentarão do que a terra produzir no ano de descanso, você, o seu escravo, a sua escrava, o trabalhador contratado e o residente temporário que vive entre vocês,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 7, 'bem como os seus rebanhos e os animais selvagens de sua terra. Tudo o que a terra produzir poderá ser comido.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 8, 'Contem sete semanas de anos, sete vezes sete anos; essas sete semanas de anos totalizam quarenta e nove anos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 9, 'Então façam soar a trombeta no décimo dia do sétimo mês; no Dia da Expiação façam soar a trombeta por toda a terra de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 10, 'Consagrem o qüinquagésimo ano e proclamem libertação por toda a terra a todos os seus moradores. Este lhes será um ano de jubileu, quando cada um de vocês voltará para a propriedade da sua família e para o seu próprio clã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 11, 'O qüinquagésimo ano lhes será jubileu; não semeiem e não ceifem o que cresce por si mesmo nem colham das vinhas não podadas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 12, 'É jubileu, e lhes será santo; comam apenas o que a terra produzir.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 13, 'Nesse ano do Jubileu cada um de vo­cês voltará para a sua propriedade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 14, 'Se vocês venderem alguma propriedade ao seu próximo ou se comprarem alguma propriedade dele, não explorem o seu irmão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 15, 'O que comprarem do seu próximo será avaliado com base no número de anos desde o Jubileu. E ele fará a venda com base no número de anos que restam de colheitas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 16, 'Quando os anos forem muitos, vocês deverão aumentar o preço, mas quando forem poucos, deverão diminuir o preço, pois o que ele está lhes vendendo é o número de colheitas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 17, 'Não explorem um ao outro, mas temam o Deus de vocês. Eu sou o Senhor, o Deus de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 18, 'Pratiquem os meus decretos e obedeçam às minhas ordenanças, e vocês viverão com segurança na terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 19, 'Então a terra dará o seu fruto, e vocês comerão até fartar-se e ali viverão em segurança.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 20, 'Vocês poderão perguntar: “Que iremos comer no sétimo ano, se não plantarmos nem fizermos a colheita?”');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 21, 'Saibam que eu lhes enviarei a minha bênção no sexto ano, e a terra produzirá o suficiente para três anos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 22, 'Quando vocês estiverem plantando no oitavo ano, comerão ainda da colheita anterior e dela continuarão a comer até a colheita do nono ano.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 23, 'A terra não poderá ser vendida definitivamente, porque ela é minha, e vocês são apenas estrangeiros e imigrantes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 24, 'Em toda terra em que tiverem propriedade, concedam o direi­to de resgate da terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 25, 'Se alguém do seu povo empobrecer e vender parte da sua propriedade, seu parente mais próximo virá e resgatará aquilo que o seu compatriota vendeu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 26, 'Se, contudo, um homem não tiver quem lhe resgate a terra, mas ele mes­mo prosperar e adquirir recursos para resgatá-la,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 27, 'calculará os anos desde que a vendeu e devolverá a diferença àquele a quem a vendeu; então poderá voltar para a sua propriedade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 28, 'Mas, se não adquirir recursos para devolver-lhe o valor, a propriedade que vendeu permanecerá em posse do comprador até o ano do Jubileu. Será devolvida no Jubileu, e ele então poderá voltar para a sua propriedade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 29, 'Se um homem vender uma casa numa cidade murada, terá o direito de resgate até que se complete um ano após a venda. Nesse período poderá resgatá-la.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 30, 'Se não for resgatada antes de se completar um ano, a casa da cidade murada pertencerá definitivamente ao comprador e aos seus descendentes; não será devolvida no Jubileu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 31, 'Mas as casas dos povoados sem muros ao redor serão consideradas campo aberto. Poderão ser resgatadas e serão devolvidas no Jubileu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 32, 'No caso das cidades dos levitas, eles sempre terão direito de resgatar suas casas nas cidades que lhes pertencem.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 33, 'Assim, a propriedade dos levitas, isto é, uma casa vendida em qualquer cidade deles, é resgatável e deverá ser devolvida no Jubileu, porque as casas das cidades dos levitas são propriedade deles entre os israelitas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 34, 'Mas as pastagens pertencentes às suas cidades não serão vendidas; são propriedade permanente deles.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 35, 'Se alguém do seu povo empobrecer e não puder sustentar-se, ajudem-no como se faz ao estrangeiro e ao residente temporário, para que possa continuar a viver entre vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 36, 'Não cobrem dele juro algum, mas temam o seu Deus, para que o seu próximo continue a viver entre vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 37, 'Vocês não poderão exigir dele juros nem emprestar-lhe mantimento visando lucro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 38, 'Eu sou o Senhor, o Deus de vocês, que os tirou da terra do Egito para dar-lhes a terra de Canaã e para ser o seu Deus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 39, 'Se alguém do seu povo empobrecer e se vender a algum de vocês, não o façam trabalhar como escravo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 40, 'Ele deverá ser tratado como trabalhador contratado ou como residente temporário; trabalhará para quem o comprou até o ano do Jubileu.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 41, 'Então ele e os seus filhos estarão livres, e ele poderá voltar para o seu próprio clã e para a propriedade dos seus antepassados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 42, 'Pois os israelitas são meus servos, a quem tirei da terra do Egito; não poderão ser vendidos como escravos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 43, 'Não dominem impiedosamente sobre eles, mas temam o seu Deus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 44, 'Os seus escravos e as suas escravas deverão vir dos povos que vivem ao redor de vocês; deles vocês poderão comprar escravos e escravas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 45, 'Também poderão comprá-los entre os filhos dos residentes temporários que vivem entre vocês e entre os que pertencem aos clãs deles, ainda que nascidos na terra de vocês; eles se tornarão sua propriedade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 46, 'Vocês poderão deixá-los como herança para os seus filhos e poderão fazê-los escravos para sempre, mas sobre os seus irmãos israelitas vocês não pode­rão dominar impiedosamente.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 47, 'Se um estrangeiro ou um residente temporário entre vocês enriquecer e alguém do seu povo empobrecer e se vender a esse estrangeiro ou a alguém que pertence ao clã desse estrangeiro,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 48, 'manterá o direito de resgate mesmo depois de se vender. Um dos seus parentes poderá resgatá-lo:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 49, 'ou tio, ou primo, ou qualquer parente próximo poderá resgatá-lo. Se, todavia, prosperar, poderá resgatar a si mesmo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 50, 'Ele e o seu comprador contarão o tempo desde o ano em que se vendeu até o ano do Jubileu. O preço do resgate se baseará no salário de um empregado contratado por aquele número de anos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 51, 'Se restarem muitos anos, pagará o seu resgate proporcionalmente ao preço de compra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 52, 'Se restarem apenas poucos anos até o ano do Jubileu, fará o cálculo, e pagará o seu resgate proporcionalmente aos anos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 53, 'Ele deverá ser tratado como um empregado contratado anualmente; não permitam que o seu senhor domine impiedosamente sobre ele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 54, 'Se não for resgatado por nenhuma dessas maneiras, ele e os seus filhos estarão livres no ano do Jubileu,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 25, 55, 'porque os israelitas são meus servos, os quais tirei da terra do Egito. Eu sou o Senhor, o Deus de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 1, 'Não façam ídolos, nem imagens, nem colunas sagradas para vocês, e não colo­quem nenhuma pedra esculpida em sua terra para curvar-se diante dela. Eu sou o Senhor, o Deus de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 2, 'Guardem os meus sábados e reverenciem o meu santuário. Eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 3, 'Se vocês seguirem os meus decretos e obedecerem aos meus mandamentos, e os colocarem em prática,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 4, 'eu lhes mandarei chuva na estação certa, e a terra dará a sua colheita e as árvores do campo darão o seu fruto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 5, 'A debulha prosseguirá até a colheita das uvas, e a colheita das uvas prosseguirá até a época da plantação, e vocês comerão até ficarem satisfeitos e viverão em segurança em sua terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 6, 'Estabelecerei paz na terra, e vocês se deitarão, e ninguém os amedrontará. Farei desaparecer da terra os animais selvagens, e a espa­da não passará pela sua terra.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 7, 'Vocês perseguirão os seus inimigos, e estes cairão à espada diante de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 8, 'Cinco de vocês perseguirão cem, cem de vocês perseguirão dez mil, e os seus inimigos cairão à espada diante de vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 9, 'Eu me voltarei para vocês e os farei prolíferos; e os multiplicarei e guardarei a minha aliança com vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 10, 'Vocês ainda estarão comendo da colheita armazenada no ano anterior, quando terão que se livrar dela para dar espaço para a nova colheita.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 11, 'Estabelecerei a minha habitação entre vocês e não os rejeitarei.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 12, 'Andarei entre vocês e serei o seu Deus, e vocês serão o meu povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 13, 'Eu sou o Senhor, o Deus de vocês, que os tirou da terra do Egito para que não mais fossem escravos deles; quebrei as traves do jugo que os prendia e os fiz andar de cabeça erguida.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 14, 'Mas, se vocês não me ouvirem e não colocarem em prática todos esses mandamen­tos,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 15, 'e desprezarem os meus decretos, rejeitarem as minhas ordenanças, deixarem de colocar em prática todos os meus mandamentos e forem infiéis à minha aliança,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 16, 'então assim os tratarei: eu lhes trarei pavor repentino, doenças e febre que lhes tirarão a visão e lhes definharão a vida. Vocês semearão inutilmente, porque os seus inimigos comerão as suas sementes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 17, 'O meu rosto estará contra vocês, e vocês serão derrotados pelos inimigos; os seus adversários os dominarão, e vocês fugirão mesmo quando ninguém os estiver perseguindo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 18, 'Se depois disso tudo vocês não me ouvirem, eu os castigarei sete vezes mais pelos seus pecados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 19, 'Eu lhes quebrarei o orgulho rebelde e farei que o céu sobre vocês fique como ferro e a terra de vocês fique como bronze.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 20, 'A força de vocês será gasta em vão, porque a terra não lhes dará colheita, nem as árvores da terra lhes darão fruto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 21, 'Se continuarem se opondo a mim e recusarem ouvir-me, eu os castigarei sete vezes mais, conforme os seus pecados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 22, 'Mandarei contra vocês animais selvagens que matarão os seus filhos, acabarei com os seus rebanhos e reduzirei vocês a tão poucos que os seus cami­nhos ficarão desertos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 23, 'Se apesar disso vocês não aceitarem a minha disciplina, mas continuarem a oporse a mim,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 24, 'eu mesmo me oporei a vocês e os cas­tigarei sete vezes mais por causa dos seus pecados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 25, 'E trarei a espada contra vocês para vingar a aliança. Quando se refugiarem em suas cidades, eu lhes mandarei uma praga, e vocês serão entregues em mãos inimigas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 26, 'Quando eu lhes cortar o suprimento de pão, dez mulheres assarão o pão num único forno e repartirão o pão a peso. Vocês comerão, mas não ficarão satisfeitos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 27, 'Se apesar disso tudo vocês ainda não me ouvirem, mas continuarem a opor-se a mim,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 28, 'então com furor me oporei a vocês, e eu mesmo os castigarei sete vezes mais por causa dos seus pecados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 29, 'Vocês comerão a carne dos seus filhos e das suas filhas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 30, 'Destruirei os seus altares idólatras, despedaçarei os seus altares de incenso e empilharei os seus cadáveres sobre os seus ídolos mortos, e rejeitarei vocês.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 31, 'Deixarei as cidades de vocês em ruínas e arrasarei os seus santuários, e não terei prazer no aroma das suas ofertas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 32, 'Desolarei a terra ao ponto de ficarem perplexos os seus inimigos que vierem ocupá-la.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 33, 'Espa­lharei vocês entre as nações e empunharei a espada contra vocês. Sua terra ficará desolada, e as suas cidades, em ruínas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 34, 'Então a terra desfrutará os seus anos sabáticos enquanto estiver desolada e enquanto vocês estiverem na terra dos seus inimigos; e a terra descansará e desfrutará os seus sábados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 35, 'Enquanto estiver desolada, a terra terá o descanso sabático que não teve quando vocês a habitavam.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 36, 'Quanto aos que sobreviverem, eu lhes encherei o coração de tanto medo na terra do inimigo, que o som de uma folha levada pelo vento os porá em fuga. Correrão como quem foge da espada, e cairão, sem que ninguém os persiga.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 37, 'Tropeçarão uns nos outros, como que fugindo da espada, sem que ninguém os esteja perseguindo. Assim vocês não poderão subsistir diante dos inimigos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 38, 'Vocês perecerão entre as nações, e a terra dos seus inimigos os devorará.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 39, 'Os que sobreviverem apodrecerão na terra do inimigo por causa dos seus pecados, e também por causa dos pecados dos seus antepassados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 40, 'Mas, se confessarem os seus pecados e os pecados dos seus antepassados, sua infidelidade e oposição a mim,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 41, 'que me levaram a oporme a eles e a enviá-los para a terra dos seus inimigos; se o seu coração obstinado se humilhar, e eles aceitarem o castigo do seu pecado,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 42, 'eu me lembrarei da minha aliança com Jacó, da minha aliança com Isaque, e da minha aliança com Abraão, e também me lembrarei da terra,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 43, 'que por eles será abandonada e desfrutará os seus sábados enquanto permanecer desolada. Receberão o castigo pelos seus pecados porque desprezaram as minhas ordenanças e rejeitaram os meus decretos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 44, 'Apesar disso, quando estiverem na terra do inimigo, não os desprezarei, nem os rejeitarei, para destruí-los totalmente, quebrando a minha aliança com eles, pois eu sou o Senhor, o Deus deles.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 45, 'Mas por amor deles eu me lembrarei da aliança com os seus antepassados que tirei da terra do Egito à vista das nações, para ser o Deus deles. Eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 26, 46, 'São esses os decretos, as ordenanças e as leis que o Senhor estabeleceu no monte Sinai entre ele próprio e os israelitas, por inter­médio de Moisés.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 1, 'Disse também o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 2, 'Diga o seguinte aos israelitas: Se alguém fizer um voto especial, dedicando pessoas ao Senhor, faça-o conforme o devido valor;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 3, 'atribua aos homens entre vinte e sessenta anos o valor de seiscentos gramas de prata, com base no peso padrão do santuário;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 4, 'e, se for mulher, atribua-lhe o valor de trezentos e sessenta gra­mas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 5, 'Se for alguém que tenha entre cinco e vinte anos, atribua aos homens o valor de du­zentos e quarenta gramas e às mulheres o valor de cento e vinte gramas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 6, 'Se for alguém que tenha entre um mês e cinco anos de idade, atribua aos meninos o valor de sessenta gramas de prata e às meninas o valor de trinta e seis gramas de prata.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 7, 'Se for alguém que tenha de sessenta anos para cima, atribua aos homens o valor de cento e oitenta gramas e às mulheres o valor de cento e vinte gramas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 8, 'Se quem fizer o voto for pobre demais para pagar o valor especificado, apresentará a pessoa ao sacerdote, que estabelecerá o valor de acordo com as possibilidades do homem que fez o voto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 9, 'Se o que ele prometeu mediante voto for um animal aceitável como oferta ao Senhor, um animal assim dado ao Senhor torna-se santo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 10, 'Ele não poderá trocá-lo nem substituir um animal ruim por um bom, nem um animal bom por um ruim; caso troque um animal por outro, tanto o substituto quanto o substituído se tornarão santos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 11, 'Se o que ele prometeu mediante voto for um animal impuro, não aceitável como oferta ao Senhor, o animal será apresentado ao sacerdote,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 12, 'que o avaliará por suas qualidades. A avaliação do sacerdote determinará o valor do animal.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 13, 'Se o dono desejar resgatar o animal, terá que acrescentar um quin­to ao seu valor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 14, 'Se um homem consagrar a sua casa ao Senhor, o sacerdote avaliará a casa por suas qualidades. A avaliação do sacerdote determinará o valor da casa.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 15, 'Se o homem que consagrar a sua casa quiser resgatá-la, terá que acres­centar um quinto ao seu valor, e a casa voltará a ser sua.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 16, 'Se um homem consagrar ao Senhor parte das terras da sua família, sua avaliação será de acordo com a semeadura: seiscentos gramas de prata para cada barril de semente de cevada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 17, 'Se consagrar a sua terra durante o ano do Jubileu, o valor será integral.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 18, 'Mas, se a consagrar depois do Jubileu, o sacerdote calculará o valor de acordo com o número de anos que faltarem para o ano do Jubileu seguinte, e o valor será reduzido.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 19, 'Se o homem que consagrar a sua terra desejar resgatá-la, terá que acres­centar um quinto ao seu valor, e a terra voltará a ser sua.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 20, 'Mas se não a resgatar, ou se a tiver vendido, não poderá mais ser resgatada;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 21, 'quando a terra for liberada no Jubileu, será santa, consagrada ao Senhor, e se tornará propriedade dos sacerdotes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 22, 'Se um homem consagrar ao Senhor terras que tenha comprado, terras que não fazem parte da propriedade da sua família,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 23, 'o sacerdote determinará o valor de acordo com o tempo que falta para o ano do Jubileu; o homem pagará o valor no mesmo dia, consagrando-o ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 24, 'No ano do Jubileu as terras serão devolvidas àquele de quem ele as comprou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 25, 'Todos os valores serão calculados com base no peso padrão do santuário, que são doze gramas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 26, 'Ninguém poderá consagrar a primeira cria de um animal, pois já pertence ao Senhor; seja cria de vaca, seja de cabra, seja de ovelha, pertence ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 27, 'Mas se for a cria de um animal impuro, poderá resgatá-la pelo valor estabelecido, acrescentando um quinto a esse valor. Se não for resgatada, será vendida pelo valor estabelecido.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 28, 'Todavia, nada que um homem possua e consagre ao Senhor, seja homem, seja animal, sejam terras de sua propriedade, poderá ser vendido ou resgatado; todas as coisas assim consagradas são santíssimas ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 29, 'Nenhuma pessoa consagrada para a destruição poderá ser resgatada; terá que ser executada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 30, 'Todos os dízimos da terra, seja dos cereais, seja das frutas, pertencem ao Senhor; são consagrados ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 31, 'Se um homem desejar resgatar parte do seu dízimo, terá que acrescentar um quinto ao seu valor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 32, 'O dízimo dos seus rebanhos, um de cada dez animais que passem debaixo da vara do pastor, será consagrado ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 33, 'O dono não poderá retirar os bons dentre os ruins, nem fazer qualquer troca. Se fizer alguma troca, tanto o animal quanto o substituto se tornarão consagrados e não poderão ser resgatados.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 3, 27, 34, 'São esses os mandamentos que o Senhor ordenou a Moisés, no monte Sinai, para os israelitas.');
INSERT INTO BIBLIA_BOOK (name) VALUES ('Números');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 1, 'O Senhor falou a Moisés na Tenda do Encontro, no deserto do Sinai, no primeiro dia do segundo mês do segundo ano, depois que os israelitas saíram do Egito. Ele disse:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 2, 'Façam um recenseamento de toda a comunidade de Israel, pelos seus clãs e famílias, alistando todos os homens, um a um, pelo nome.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 3, 'Você e Arão contarão todos os homens que possam servir no exército, de vinte anos para cima, organizados segundo as suas divisões.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 4, 'Um homem de cada tribo, o chefe dos grupos de famílias, deverá ajudá-los.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 5, 'Estes são os nomes dos homens que os ajudarão: de Rúben, Elizur, filho de Sedeur;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 6, 'de Simeão, Selumiel, filho de Zurisadai;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 7, 'de Judá, Naassom, filho de Aminadabe;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 8, 'de Issacar, Natanael, filho de Zuar;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 9, 'de Zebulom, Eliabe, filho de Helom;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 10, 'dos filhos de José: de Efraim, Elisama, filho de Amiúde; de Manassés, Gamaliel, filho de Pedazur;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 11, 'de Benjamim, Abidã, filho de Gideoni;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 12, 'de Dã, Aieser, filho de Amisadai;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 13, 'de Aser, Pagiel, filho de Ocrã;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 14, 'de Gade, Eliasafe, filho de Deuel;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 15, 'de Naftali, Aira, filho de Enã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 16, 'Foram esses os escolhidos dentre a comunidade, líderes das tribos dos seus antepassados, chefes dos clãs de Israel.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 17, 'Moisés e Arão reuniram os homens nomeados');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 18, 'e convocaram toda a comunidade no primeiro dia do segundo mês. Os homens de vinte anos para cima inscreveram-se conforme os seus clãs e as suas famílias, um a um, pelo nome,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 19, 'conforme o Senhor tinha ordenado a Moisés. E assim ele os contou no deserto do Sinai, na seguinte ordem:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 20, 'Dos descendentes de Rúben, o filho mais velho de Israel: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os registros de seus clãs e famílias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 21, 'O número dos da tribo de Rúben foi 46. 500.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 22, 'Dos descendentes de Simeão: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os registros de seus clãs e famílias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 23, 'O número dos da tribo de Simeão foi 59. 300.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 24, 'Dos descendentes de Gade: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os registros de seus clãs e famílias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 25, 'O número dos da tribo de Gade foi 45. 650.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 26, 'Dos descendentes de Judá: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os registros de seus clãs e famílias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 27, 'O número dos da tribo de Judá foi 74. 600.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 28, 'Dos descendentes de Issacar: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os registros de seus clãs e famílias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 29, 'O número dos da tribo de Issacar foi 54. 400.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 30, 'Dos descendentes de Zebulom: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os registros de seus clãs e famílias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 31, 'O número dos da tribo de Zebulom foi 57. 400.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 32, 'Dos filhos de José: Dos descendentes de Efraim: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os registros de seus clãs e famílias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 33, 'O número dos da tribo de Efraim foi 40. 500.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 34, 'Dos descendentes de Manassés: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os registros de seus clãs e famílias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 35, 'O número dos da tribo de Manassés foi 32. 200.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 36, 'Dos descendentes de Benjamim: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os registros de seus clãs e famílias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 37, 'O número dos da tribo de Benjamim foi 35. 400.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 38, 'Dos descendentes de Dã: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os registros de seus clãs e famílias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 39, 'O número dos da tribo de Dã foi 62. 700.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 40, 'Dos descendentes de Aser: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os registros de seus clãs e famílias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 41, 'O número dos da tribo de Aser foi 41. 500.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 42, 'Dos descendentes de Naftali: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os registros de seus clãs e famílias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 43, 'O número dos da tribo de Naftali foi 53. 400.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 44, 'Esses foram os homens contados por Moisés e por Arão e pelos doze líderes de Israel, cada um representando a sua família.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 45, 'Todos os israelitas de vinte anos para cima que podiam servir no exército foram contados de acordo com as suas famílias.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 46, 'O total foi 603. 550 homens.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 47, 'As famílias da tribo de Levi, porém, não foram contadas juntamente com as outras,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 48, 'pois o Senhor tinha dito a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 49, 'Não faça o recenseamento da tribo de Levi nem a relacione entre os demais israelitas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 50, 'Em vez disso, designe os levitas como responsáveis pelo tabernáculo que guarda as tábuas da aliança, por todos os seus utensílios e por tudo o que pertence a ele. Eles transportarão o tabernáculo e todos os seus utensílios; cuidarão dele e acamparão ao seu redor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 51, 'Sempre que o tabernáculo tiver que ser removido, os levitas o desmontarão e, sempre que tiver que ser armado, os levitas o farão. Qualquer pessoa não autorizada que se aproximar do tabernáculo terá que ser executada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 52, 'Os israelitas armarão as suas tendas organizadas segundo as suas divisões, cada um em seu próprio acampamento e junto à sua bandeira.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 53, 'Os levitas, porém, armarão as suas tendas ao redor do tabernáculo que guarda as tábuas da aliança, para que a ira divina não caia sobre a comunidade de Israel. Os levitas terão a responsabilidade de cuidar do tabernáculo que guarda as tábuas da aliança.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 1, 54, 'Os israelitas fizeram tudo exatamente como o Senhor tinha ordenado a Moisés.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 1, 'O Senhor disse a Moisés e a Arão:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 2, '“Os israelitas acamparão ao redor da Tenda do Encontro, a certa distância, cada homem junto à sua bandeira com os emblemas da sua família”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 3, 'A leste, os exércitos de Judá acamparão junto à sua bandeira. O líder de Judá será Naassom, filho de Aminadabe.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 4, 'Seu exército é de 74. 600 homens.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 5, 'A tribo de Issacar acampará ao lado de Judá. O líder de Issacar será Natanael, filho de Zuar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 6, 'Seu exército é de 54. 400 homens.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 7, 'A tribo de Zebulom virá em seguida. O líder de Zebulom será Eliabe, filho de Helom.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 8, 'Seu exército é de 57. 400 homens.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 9, 'O número total dos homens recenseados do acampamento de Judá, de acordo com os seus exércitos, foi 186. 400. Esses marcharão primeiro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 10, 'Ao sul estarão os exércitos do acampamento de Rúben, junto à sua bandeira. O lí­der de Rúben será Elizur, filho de Sedeur.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 11, 'Seu exército é de 46. 500 homens.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 12, 'A tribo de Simeão acampará ao lado de Rúben. O líder de Simeão será Selumiel, filho de Zurisadai.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 13, 'Seu exército é de 59. 300 homens.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 14, 'A tribo de Gade virá em seguida. O líder de Gade será Eliasafe, filho de Deuel.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 15, 'Seu exército é de 45. 650 homens.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 16, 'O número total dos homens recenseados do acampamento de Rúben, de acordo com os seus exércitos, foi 151. 450. Esses marcharão em segundo lugar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 17, 'Em seguida os levitas marcharão levando a Tenda do Encontro no meio dos outros acampamentos, na mesma ordem em que acamparem, cada um em seu próprio lugar, junto à sua bandeira.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 18, 'A oeste estarão os exércitos do acampamento de Efraim, junto à sua bandeira. O líder de Efraim será Elisama, filho de Amiúde.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 19, 'Seu exército é de 40. 500 homens.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 20, 'A tribo de Manassés acampará ao lado de Efraim. O líder de Manassés será Gamaliel, filho de Pedazur.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 21, 'Seu exército é de 32. 200 homens.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 22, 'A tribo de Benjamim virá em seguida. O líder de Benjamim será Abidã, filho de Gideoni.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 23, 'Seu exército é de 35. 400 homens.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 24, 'O número total dos homens recenseados do acampamento de Efraim, de acordo com os seus exércitos, foi 108. 100. Esses marcharão em terceiro lugar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 25, 'Ao norte estarão os exércitos do acampamento de Dã, junto à sua bandeira. O líder de Dã será Aieser, filho de Amisadai.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 26, 'Seu exército é de 62. 700 homens.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 27, 'A tribo de Aser acampará ao lado de Dã. O líder de Aser será Pagiel, filho de Ocrã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 28, 'Seu exército é de 41. 500 homens.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 29, 'A tribo de Naftali virá em seguida. O líder de Naftali será Aira, filho de Enã.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 30, 'Seu exército é de 53. 400 homens.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 31, 'O número total dos homens recenseados do acampamento de Dã, de acordo com os seus exércitos, foi 157. 600. Esses marcharão por último, junto às suas bandeiras.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 32, 'Foram esses os israelitas contados de acordo com as suas famílias. O número total dos que foram contados nos acampamentos, de acordo com os seus exércitos, foi 603. 550.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 33, 'Os levitas, contudo, não foram contados com os outros israelitas, conforme o Senhor tinha ordenado a Moisés.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 2, 34, 'Assim os israelitas fizeram tudo o que o Senhor tinha ordenado a Moisés; eles acampavam junto às suas bandeiras e depois partiam, cada um com o seu clã e com a sua família.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 1, 'Esta é a história da descendência de Arão e de Moisés, quando o Senhor falou com Moisés no monte Sinai.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 2, 'Os nomes dos filhos de Arão são Nadabe, o mais velho, Abiú, Eleazar e Itamar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 3, 'São esses os nomes dos filhos de Arão, que foram ungidos para o sacerdócio e que foram ordenados sacerdotes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 4, 'Nadabe e Abiú, entretanto, caíram mortos perante o Senhor quando lhe trouxeram uma oferta com fogo profano, no deserto do Sinai. Como não tinham filhos, somente Eleazar e Itamar serviram como sacerdotes durante a vida de Arão, seu pai.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 5, 'O Senhor disse a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 6, 'Mande chamar a tribo de Levi e apresente-a ao sacerdote Arão para auxiliá-lo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 7, 'Eles cuidarão das obrigações próprias da Tenda do Encontro, fazendo o serviço do tabernáculo para Arão e para toda a comunidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 8, 'Tomarão conta de todos os utensílios da Tenda do Encontro, cumprindo as obrigações dos israelitas no serviço do tabernáculo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 9, 'Dedique os levitas a Arão e a seus filhos; eles serão escolhidos entre os israelitas para serem inteiramente dedicados a Arão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 10, 'Encarregue Arão e os seus filhos de cuidar do sacerdócio; qualquer pessoa não autorizada que se aproximar do santuário terá que ser executada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 11, 'Disse também o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 12, 'Eu mesmo escolho os levitas dentre os israelitas em lugar do primeiro filho de cada mulher israelita. Os levitas são meus,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 13, 'pois todos os primogênitos são meus. Quando feri todos os primogênitos no Egito, separei para mim mesmo todo primogênito de Israel, tanto entre os homens como entre os rebanhos. Serão meus. Eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 14, 'E o Senhor disse ainda a Moisés no deserto do Sinai:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 15, '“Conte os levitas pelas suas famílias e clãs. Serão contados todos os do sexo masculino de um mês de idade para cima”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 16, 'Então Moisés os contou, conforme a ordem que recebera do Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 17, 'São estes os nomes dos filhos de Levi: Gérson, Coate e Merari.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 18, 'São estes os nomes dos clãs gersonitas: Libni e Simei.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 19, 'São estes os nomes dos clãs coatitas: Anrão, Isar, Hebrom e Uziel.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 20, 'E estes são os nomes dos clãs meraritas: Mali e Musi. Foram esses os líderes dos clãs levitas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 21, 'A Gérson pertenciam os clãs dos libnitas e dos simeítas; eram esses os clãs gersonitas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 22, 'O número de todos os que foram contados do sexo masculino, de um mês de idade para cima, foi 7. 500.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 23, 'Os clãs gersonitas tinham que acampar a oeste, atrás do tabernáculo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 24, 'O líder das famílias dos gersonitas era Eliasafe, filho de Lael.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 25, 'Na Tenda do Encontro os gersonitas tinham a responsabilidade de cuidar do tabernáculo, da tenda, da sua cobertura, da cortina da entrada da Tenda do Encontro,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 26, 'das cortinas externas do pátio, da cortina da entrada do pátio que rodeia o tabernáculo e o altar, das cordas, e de tudo o que estava relacionado com esse serviço.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 27, 'A Coate pertenciam os clãs dos anramitas, dos isaritas, dos hebronitas e dos uzielitas; eram esses os clãs coatitas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 28, 'O número de todos os do sexo masculino, de um mês de idade para cima, foi 8. 600. Os coatitas tinham a responsabilidade de cuidar do santuário.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 29, 'Os clãs coatitas tinham que acampar no lado sul do tabernáculo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 30, 'O líder das famílias dos clãs coatitas era Elisafã, filho de Uziel.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 31, 'Tinham a responsabilidade de cuidar da arca, da mesa, do candelabro, dos altares, dos utensílios do santuário com os quais ministravam, da cortina e de tudo o que estava relacionado com esse serviço.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 32, 'O principal líder dos levitas era Eleazar, filho do sacerdote Arão. Ele tinha a responsabilidade de supervisionar os encarregados de cuidar do santuário.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 33, 'A Merari pertenciam os clãs dos malitas e dos musitas; eram esses os clãs meraritas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 34, 'O número de todos os que foram contados do sexo masculino, de um mês de idade para cima, foi 6. 200.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 35, 'O líder das famílias dos clãs meraritas era Zuriel, filho de Abiail; eles tinham que acampar no lado norte do tabernáculo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 36, 'Os meraritas tinham a responsabilidade de cuidar das armações do tabernáculo, de seus travessões, das colunas, das bases, de todos os seus utensílios e de tudo o que estava relacionado com esse serviço,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 37, 'bem como das colunas do pátio ao redor, com suas bases, suas estacas e suas cordas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 38, 'E acamparam a leste do tabernáculo, em frente da Tenda do Encontro, Moisés, Arão e seus filhos. Tinham a responsabilidade de cuidar do santuário em favor dos israelitas. Qualquer pessoa não autorizada que se aproximasse do santuário teria que ser executada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 39, 'O número total de levitas contados por Moisés e Arão, conforme a ordem do Senhor, segundo os clãs deles, todos os do sexo masculino, de um mês de idade para cima, foi 22. 000.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 40, 'E o Senhor disse a Moisés: Conte todos os primeiros filhos dos israelitas, do sexo masculino, de um mês de idade para cima e faça uma relação de seus nomes.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 41, 'Dedique a mim os levitas em lugar de todos os primogênitos dos israelitas, e os rebanhos dos levitas, em lugar de todas as primeiras crias dos rebanhos dos israelitas. Eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 42, 'E Moisés contou todos os primeiros filhos dos israelitas, conforme o Senhor lhe havia ordenado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 43, 'O número total dos primeiros filhos do sexo masculino, de um mês de idade para cima, relacionados pelo nome, foi 22. 273.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 44, 'Disse também o Senhor a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 45, 'Dedique os levitas em lugar de todos os primogênitos dos israelitas, e os rebanhos dos levitas em lugar dos rebanhos dos israelitas. Os levitas serão meus. Eu sou o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 46, 'Para o resgate dos primeiros 273 filhos dos israelitas que excedem o número de levitas,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 47, 'recolha sessenta gramas de prata, com base no peso padrão do santuário, que são doze gramas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 48, 'Entregue a Arão e aos seus filhos a prata para o resgate do número excedente de israelitas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 49, 'Assim Moisés recolheu a prata para o resgate daqueles que excederam o número dos levitas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 50, 'Dos primeiros filhos dos israelitas ele recolheu prata no peso de quase dezesseis quilos e meio, com base no peso padrão do santuário.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 3, 51, 'Moisés entregou a Arão e aos filhos dele a prata para o resgate, conforme a ordem que recebera do Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 1, 'Disse o Senhor a Moisés e a Arão:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 2, 'Façam um recenseamento dos coatitas na tribo de Levi, pelos seus clãs e famílias;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 3, 'contem todos os homens entre trinta e cinqüenta anos, aptos para servir, para que façam o serviço da Tenda do Encontro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 4, 'O serviço dos coatitas na Tenda do Encontro será o cuidado das coisas santíssimas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 5, 'Quando o acampamento tiver que mudar, Arão e os seus filhos entrarão e descerão o véu protetor e com ele cobrirão a arca da aliança.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 6, 'Depois a cobrirão com couro, estenderão um pano inteiramente azul sobre ela e colocarão as varas no lugar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 7, 'Sobre a mesa da Presença eles estenderão um pano azul e colocarão os pratos, os recipientes para incenso, as tigelas e as bacias para as ofertas derramadas, e os pães da Presença, que devem estar sempre sobre ela.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 8, 'Sobre tudo isso estenderão um pano vermelho e o cobrirão com couro. Depois colocarão as varas no lugar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 9, 'Pegarão também um pano azul e cobrirão o candelabro usado para iluminação, as suas candeias, as suas tesouras de aparo, os seus apagadores e todos os jarros para o seu suprimento de óleo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 10, 'Em seguida o embrulharão com todos os seus utensílios numa cobertura de couro e o colocarão num suporte para carregar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 11, 'Sobre o altar de ouro estenderão um pano azul e o cobrirão com couro. E colocarão as suas varas no lugar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 12, 'Apanharão todos os utensílios usados na ministração no santuário, depois os embrulharão num pano azul e os cobrirão com couro; a seguir os colocarão num suporte para carregar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 13, 'Tirarão a cinza do altar de bronze e estenderão sobre ele um pano roxo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 14, 'Colocarão sobre ele todos os utensílios usados na ministração no altar: os braseiros, os garfos de carne, as pás e as bacias da aspersão. Sobre ele estende­rão uma cobertura de couro e colocarão as varas no lugar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 15, 'Quando Arão e os seus filhos terminarem de cobrir os utensílios sagrados e todos os artigos sagrados, e o acampamento estiver pronto para partir, os coatitas virão carregá-los. Mas não tocarão nas coisas sagradas; se o fizerem, morrerão. São esses os utensílios da Tenda do Encontro que os coatitas carregarão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 16, '“Eleazar, filho do sacerdote Arão, ficará encarregado do azeite para a iluminação, do incenso aromático, da oferta costumeira de cereal e do óleo da unção. Ficará encarregado de todo o tabernáculo e de tudo o que nele há, isto é, seus utensílios e seus artigos sagrados”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 17, 'O Senhor disse ainda a Moisés e a Arão:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 18, 'Não permitam que o ramo dos clãs coatitas seja eliminado dentre os levitas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 19, 'Mas, para que continuem vivos e não morram quando se aproximarem das coisas santíssimas, Arão e os seus filhos entrarão no santuário e designarão a cada homem a sua tarefa e o que deverá carregar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 20, 'Os coatitas não entrarão para ver as coisas sagradas, nem por um breve momento, para que não morram.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 21, 'E o Senhor disse a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 22, 'Faça também um recenseamento dos gersonitas, pelas suas famílias e clãs;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 23, 'conte todos os homens entre trinta e cinqüenta anos, aptos para servir, para que façam o serviço da Tenda do Encontro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 24, 'Este é o serviço dos clãs gersonitas, o que devem fazer e carregar:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 25, 'Eles levarão as cortinas internas do tabernáculo, a Tenda do Encontro, a sua cobertura, a cobertura externa de couro, as cortinas da entrada da Tenda do Encontro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 26, 'Farão tudo o que for necessário com aquelas coisas e com as cortinas externas do pátio que rodeia o tabernáculo e o altar, com a cortina da entrada, com as cordas e com todos os utensílios usados em seu serviço.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 27, 'Todo o serviço deles, tudo o que devem fazer e carregar estará sob a direção de Arão e de seus filhos. Designe como responsabilidade deles tudo o que tiverem que carregar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 28, 'Es­se é o serviço dos clãs gersonitas na Tenda do Encontro. Suas atividades estarão sob a supervisão de Itamar, filho do sacerdote Arão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 29, 'Conte os meraritas conforme os seus clãs e famílias,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 30, 'todos os homens entre trinta e cinqüenta anos, aptos para servir, para que façam o serviço da Tenda do Encontro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 31, 'Esta é a responsabilidade deles no serviço que deverão realizar na Tenda do Encontro: carregar as armações do tabernáculo, seus travessões, suas colunas e suas bases,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 32, 'bem como as colunas do pátio, que rodeia a tenda, com suas bases, suas estacas e suas cordas; todos os seus utensílios e tudo o que está relacionado com o seu uso. Designe a cada um aquilo que deverá levar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 33, 'Esse é o serviço dos clãs meraritas. Todo o serviço deles na Tenda do Encontro estará sob a supervisão de Itamar, filho do sacerdote Arão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 34, 'Moisés, Arão e os líderes da comunidade contaram os coatitas, conforme seus clãs e famílias,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 35, 'todos os homens entre trinta e cinqüenta anos, aptos para servir, para que fizessem o serviço da Tenda do Encontro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 36, 'Foram contados, conforme os seus clãs, 2. 750 homens.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 37, 'Esse foi o total de recenseados dos clãs coatitas que serviam na Tenda do Encontro. Moisés e Arão os contaram de acordo com a ordem do Senhor, anunciada por Moisés.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 38, 'Os gersonitas foram contados conforme os seus clãs e famílias,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 39, 'todos os homens entre trinta e cinqüenta anos, aptos para servir, para fazer o serviço da Tenda do Encontro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 40, 'Foram contados conforme os seus clãs e famílias 2. 630.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 41, 'Esse foi o total de recenseados dos clãs gersonitas que serviam na Tenda do Encon­tro. Moisés e Arão os contaram de acordo com a ordem do Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 42, 'Os meraritas foram contados conforme os seus clãs e famílias,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 43, 'todos os homens entre trinta e cinqüenta anos, aptos para servir, para fazer o serviço da Tenda do Encontro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 44, 'Foram contados conforme os seus clãs 3. 200.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 45, 'Esse foi o total de recenseados dos clãs meraritas que serviam na Tenda do Encontro. Moisés e Arão os contaram de acordo com a ordem do Senhor, anunciada por Moisés.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 46, 'Assim Moisés, Arão e os líderes de Israel contaram todos os levitas conforme os seus clãs e famílias;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 47, 'todos os homens entre trinta e cinqüenta anos de idade que vieram para servir e carregar a Tenda do Encontro');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 48, 'somavam 8. 580.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 4, 49, 'Conforme a ordem do Senhor anunciada por Moisés, a cada um foi designado o seu trabalho e foi dito o que deveria carregar. Assim foram todos contados, conforme o Senhor tinha ordenado a Moisés.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 1, 'O Senhor disse a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 2, 'Ordene aos israelitas que mandem para fora do acampamento todo aquele que tiver lepra, ou que tiver um fluxo, ou que se tornar impuro por tocar um cadáver.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 3, 'Mande-os para fora do acampamento, tanto homens como mulheres, para que não contaminem o seu próprio acampamento, onde habito entre eles.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 4, 'Os israelitas assim fizeram e os mandaram para fora do acampamento, como o Senhor tinha ordenado a Moisés.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 5, 'E o Senhor disse a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 6, 'Diga aos israelitas: Quando um homem ou uma mulher prejudicar outra pessoa e, portanto, ofender o Senhor, será culpado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 7, 'Confessará o pecado que cometeu, fará restituição total, acrescentará um quinto a esse valor e entregará tudo isso a quem ele prejudicou.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 8, 'Mas, se o prejudicado não tiver nenhum parente próximo para receber a restituição, esta pertencerá ao Senhor e será entregue ao sacerdote, juntamente com o carneiro com o qual se faz propiciação pelo culpado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 9, 'Todas as contribuições, ou seja, todas as dádivas sagradas que os israelitas trouxerem ao sacerdote, pertencerão a ele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 10, 'As dádivas sagradas de cada pessoa pertencem a ela, mas o que ela der ao sacerdote pertencerá ao sacerdote.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 11, 'Então o Senhor disse a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 12, 'Diga o seguinte aos israelitas: Se a mulher de alguém se desviar e lhe for infiel,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 13, 'e outro homem deitar-se com ela, e isso estiver oculto de seu marido, e a impureza dela não for descoberta, por não haver testemunha contra ela nem ter ela sido pega no ato;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 14, 'se o marido dela tiver ciúmes e suspeitar de sua mulher, esteja ela pura ou impura,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 15, 'ele a levará ao sacerdote, com uma oferta de um jarro de farinha de cevada em favor dela. Não derramará azeite nem porá incenso sobre a farinha, porque é uma oferta de cereal pelo ciúme, para que se revele a verdade sobre o pecado.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 16, 'O sacerdote trará a mulher e a colocará perante o Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 17, 'Então apanhará um pouco de água sagrada num jarro de barro e colocará na água um pouco do pó do chão do tabernáculo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 18, 'Depois de colocar a mulher perante o Senhor, o sacerdote soltará o cabelo dela e porá nas mãos dela a oferta memorial, a oferta pelo ciúme, enquanto ele mesmo terá em sua mão a água amarga que traz maldição.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 19, 'Então o sacerdote fará a mulher jurar e lhe dirá: Se nenhum outro homem se deitou com você e se você não foi infiel nem se tornou impura enquanto casada, que esta água amarga que traz maldição não lhe faça mal.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 20, 'Mas, se você foi infiel enquanto casada e se contaminou por ter se deitado com um homem que não é seu mari­do —');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 21, 'então o sacerdote fará a mulher pronunciar este juramento com maldição — que o Senhorfaça de você objeto de maldição e de desprezo no meio do povo fazendo que a sua barriga inche e que você jamais tenha filhos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 22, 'Que esta água que traz maldição entre em seu corpo, inche a sua barriga e a impeça de ter filhos. Então a mulher dirá: Amém. Assim seja.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 23, 'O sacerdote escreverá essas maldições num documento e depois as lavará na água amarga.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 24, 'Ele a fará beber a água amarga que traz maldição, e essa água entrará nela, causando-lhe amargo sofrimento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 25, 'O sacerdote apanhará das mãos dela a oferta de cereal pelo ciúme, a moverá ritualmente perante o Senhor e a trará ao altar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 26, 'En­tão apanhará um punhado da oferta de cereal como oferta memorial e a queimará sobre o altar; depois disso fará a mulher beber a água.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 27, 'Se ela houver se contaminado, sendo infiel ao seu marido, quando o sacerdote fizer que ela beba a água que traz maldição, essa água entrará nela e causará um amargo sofrimento; sua barriga inchará e ela, incapaz de ter filhos, se tornará objeto de maldição entre o seu povo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 28, 'Se, porém, a mulher não houver se contaminado, mas estiver pura, não sofrerá punição e será capaz de ter filhos.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 29, 'Esse é, pois, o ritual quanto ao ciúme, quando uma mulher for infiel e se contaminar enquanto casada,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 30, 'ou quando o ciúme se apoderar de um homem porque suspeita de sua mulher. O sacerdote a colocará perante o Senhor e a fará passar por todo esse ritual.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 5, 31, 'Se a suspeita se confirmar ou não, o marido estará inocente; mas a mulher sofrerá as conseqüências da sua iniqüidade.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 1, 'O Senhor disse ainda a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 2, 'Diga o seguinte aos israelitas: Se um homem ou uma mulher fizer um voto especial, um voto de separação para o Senhor como nazireu,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 3, 'terá que se abster de vinho e de outras bebidas fermentadas e não poderá beber vinagre feito de vinho ou de outra bebida fermentada. Não poderá beber suco de uva nem comer uvas nem passas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 4, 'Enquanto for nazireu, não poderá comer nada que venha da videira, nem mesmo as sementes ou as cascas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 5, 'Durante todo o período de seu voto de separação, nenhuma lâmina será usada em sua cabeça. Até que termine o período de sua separação para o Senhor ele estará consagrado e deixará crescer o cabelo de sua cabeça.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 6, 'Durante todo o período de sua separação para o Senhor, não poderá aproximar-se de um cadáver.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 7, 'Mesmo que o seu próprio pai ou mãe ou irmã ou irmão morra, ele não poderá tornar-se impuro por causa deles, pois traz sobre a cabeça o símbolo de sua separação para Deus.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 8, 'Durante todo o período de sua separação, estará consagrado ao Senhor.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 9, 'Se alguém morrer repentinamente perto dele, contaminando assim o cabelo que consagrou, ele terá que rapar a cabeça sete dias depois, dia da sua purificação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 10, 'No oitavo dia, trará duas rolinhas ou dois pombinhos ao sacerdote, à entrada da Tenda do Encontro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 11, 'O sacerdote oferecerá um como oferta pelo pecado e o outro como holocausto, para fazer propiciação por ele, pois pecou ao se aproximar de um cadáver. Naquele mesmo dia o nazireu reconsagrará a sua cabeça.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 12, 'Ele se dedicará ao Senhorpelo período de sua separação e trará um cordeiro de um ano de idade como oferta de reparação. Não se contarão os dias anteriores porque ficou contaminado durante a sua separação.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 13, 'Este é o ritual do nazireu quando terminar o período de sua separação: ele será trazido à entrada da Tenda do Encontro.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 14, 'Ali apresentará a sua oferta ao Senhor: um cordeiro de um ano e sem defeito como holocausto, uma cordeira de um ano e sem defeito como oferta pelo pecado, um carneiro sem defeito como oferta de comunhão,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 15, 'juntamente com a sua oferta de cereal, com a oferta derramada e com um cesto de pães sem fermento, bolos feitos da melhor farinha amassada com azeite e pães finos untados com azeite.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 16, 'O sacerdote os apresentará ao Senhor e oferecerá o sacrifício pelo pecado e o holocausto.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 17, 'Apresentará o cesto de pães sem fermento e oferecerá o cordeiro como sacrifício de comunhão ao Senhor, juntamente com a oferta de cereal e a oferta derramada.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 18, 'Em seguida, à entrada da Tenda do Encontro, o nazireu rapará o cabelo que consagrou e o jogará no fogo que está embaixo do sacrifício da oferta de comunhão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 19, 'Depois que o nazireu rapar o cabelo da sua consagração, o sacerdote lhe colocará nas mãos um ombro cozido do carneiro, um bolo e um pão fino tirados do cesto, ambos sem fermento.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 20, 'O sacerdote os moverá perante o Senhor como gesto ritual de apresentação; são santos e pertencem ao sacerdote, bem como o peito que foi movido e a coxa. Depois disso o nazireu poderá beber vinho.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 21, '“Esse é o ritual do voto de nazireu e da oferta dedicada ao Senhor de acordo com a sua separação, sem contar qualquer outra coisa que ele possa dedicar. Cumprirá o voto que tiver feito de acordo com o ritual do nazireu”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 22, 'O Senhor disse a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 23, 'Diga a Arão e aos seus filhos: Assim vocês abençoarão os israelitas:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 24, 'O Senhor te abençoe e te guarde;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 25, 'o Senhor faça resplandecer o seu rosto sobre tie te conceda graça;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 26, 'o Senhor volte para ti o seu rosto e te dê paz.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 6, 27, '“Assim eles invocarão o meu nome sobre os israelitas, e eu os abençoarei”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 1, 'Quando Moisés acabou de armar o tabernáculo, ele o ungiu e o consagrou, juntamente com todos os seus utensílios. Também ungiu e consagrou o altar com todos os seus utensílios.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 2, 'Então os líderes de Israel, os chefes das famílias que eram os líderes das tribos encarregados do recenseamento, apresentaram ofertas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 3, 'Trouxeram as suas dádivas ao Senhor: seis carroças cobertas e doze bois, um boi de cada líder e uma carroça de cada dois líderes; e as apresentaram diante do tabernáculo.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 4, 'O Senhor disse a Moisés:');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 5, '“Aceite as ofertas deles para que sejam usadas no trabalho da Tenda do Encontro. Entregue-as aos levitas, conforme exigir o trabalho de cada homem”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 6, 'Então Moisés recebeu as carroças e os bois e os entregou aos levitas.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 7, 'Deu duas carroças e quatro bois aos gersonitas, conforme exigia o trabalho deles,');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 8, 'e quatro carroças e oito bois aos meraritas, conforme exigia o trabalho deles. Estavam todos sob a supervisão de Itamar, filho do sacerdote Arão.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 9, 'Mas aos coatitas Moisés não deu nada, pois eles deveriam carregar nos ombros os objetos sagrados pelos quais eram responsáveis.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 10, 'Quando o altar foi ungido, os líderes trouxeram as suas ofertas para a dedicação do altar, e as apresentaram diante dele.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 11, 'Pois o Senhor tinha dito a Moisés: “Cada dia um líder deverá trazer a sua oferta para a dedicação do altar”.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 12, 'No primeiro dia, Naassom, filho de Aminadabe, da tribo de Judá, trouxe a sua oferta.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 13, 'A oferta dele foi um prato de prata de um quilo e quinhentos e sessenta gramas e uma bacia de prata para as aspersões, de oitocentos e quarenta gramas, ambos pesados com base no peso padrão do santuário, cada um cheio da melhor farinha amassada com óleo, como oferta de cereal;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 14, 'uma vasilha de ouro de cento e vinte gramas, cheia de incenso;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 15, 'um novilho, um carneiro e um cordeiro de um ano como holocausto;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 16, 'um bode como oferta pelo pecado;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 17, 'e dois bois, cinco carneiros, cinco bodes e cinco cordeiros de um ano para serem oferecidos como sacrifício de comunhão. Essa foi a oferta de Naassom, filho de Aminadabe.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 18, 'No segundo dia, Natanael, filho de Zuar e líder de Issacar, trouxe a sua oferta.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 19, 'A oferta dele foi um prato de prata de um quilo e quinhentos e sessenta gramas e uma bacia de prata para as aspersões, de oitocentos e quarenta gramas, ambos pesados com base no peso padrão do santuário, cada um cheio da melhor farinha amassada com óleo, como oferta de cereal;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 20, 'uma vasilha de ouro de cento e vinte gramas, cheia de incenso;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 21, 'um novilho, um carneiro e um cordeiro de um ano como holocausto;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 22, 'um bode como oferta pelo pecado;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 23, 'e dois bois, cinco carneiros, cinco bodes e cinco cordeiros de um ano para serem oferecidos como sacrifício de comunhão. Essa foi a oferta de Natanael, filho de Zuar.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 24, 'No terceiro dia, Eliabe, filho de Helom e líder de Zebulom, trouxe a sua oferta.');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 25, 'A oferta dele foi um prato de prata de um quilo e quinhentos e sessenta gramas e uma bacia de prata para as aspersões, de oitocentos e quarenta gramas, ambos pesados com base no peso padrão do santuário, cada um cheio da melhor farinha amassada com óleo, como oferta de cereal;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 26, 'uma vasilha de ouro de cento e vinte gramas, cheia de incenso;');
INSERT INTO BIBLIA_VERSICLE (version_id, book_id, chapter_id, versicle_id, text) VALUES (1, 4, 7, 27, 'um novilho, um carneiro e um cordeiro de um ano como holocausto;');
